INSERT INTO `book` VALUES (999, '978-7-5039-7294-2', '在美学内外猜想', '周宪著', '文化艺术出版社', 42, '美学', NULL, NULL, NULL, '文艺研究小丛书', NULL, '147页', '19cm', NULL, NULL, '本书收录了《艺术史与艺术理论的紧张》《审美论回归之路》《艺术跨媒介性与艺术统一性——艺术理论学科知识建构的方法论》《英语美学的历史谱系》四篇文章。', '文集', 'B83-53', '本书适用于美学研究人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1000, '978-7-5714-2390-2', '催眠赋能', '(美)约翰·卡帕斯著', '北京科学技术出版社', 75, '催眠术', NULL, 'Ⅱ', '轻松改善你的性生活', 'HMI科学催眠丛书', NULL, '173页', '24cm', NULL, NULL, '本书作者讲述了自我催眠技术，教会读者如何从你的行为上判断你是情绪型性特征还是躯体型性特征；过去糟糕的经历如何引发现在的问题；如何通过自我催眠来解决常见的性生活方面的问题等。', NULL, 'B841.4', '本书适用于大众读者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1001, '978-7-121-44646-7', '情绪驯养手册', '杨帆，扈芷晴著', '电子工业出版社', 68, '情绪', NULL, NULL, NULL, NULL, NULL, '12,201页', '24cm', NULL, '影响力', '本书带领读者深入了解自己的情绪“野兽”，并通过情绪“驯养”技术，扭转不良情绪给个人发展带来的不利影响，以情绪管理更好地驱动人生。', '通俗读物', 'B842.6-49', '本书适用于大众读者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1002, '978-7-5113-8632-8', '每天当好一个情绪稳定的成年人', '焦亮著', '中国华侨出版社', 38, '情绪', NULL, NULL, NULL, NULL, NULL, '179页', '21cm', NULL, NULL, '本书分析了情绪失控对人生各个方面的危害，如阻碍事业发展、影响婚姻生活、使不良情绪无止境地蔓延、丧失积极进取的勇气等等，同时阐述了改善情绪的各种方法，是一部成年人提升自我的优秀心灵读本。', '通俗读物', 'B842.6-49', '本书适用于大众读者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1003, '978-7-5180-8315-2', '情绪心理学入门', '松丽编著', '中国纺织出版社有限公司', 39.8, '情绪', '完全图解版', NULL, NULL, NULL, NULL, '213页', '21cm', NULL, NULL, '本书从心理学的角度出发，不但分析了情绪的心理学密码，还全方位地提供了控制情绪的方法。', '通俗读物', 'B842.6-49', '本书适用于大众读者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1004, '978-7-5222-0356-0', '应用行为分析与儿童行为管理', '郭延庆著', '华夏出版社有限公司', 88, '儿童教育', NULL, NULL, NULL, NULL, '2版', '239页', '24cm', NULL, NULL, '本书在保留应用行为分析基本原理、预防和解决问题行为的具体实践方法等原有内容的基础上，新增了过去十年里作者的总结和思考，探讨了行为主义背后的生命观，阐述了应用行为分析在孤独症教育干预中的实践，提出了教育干预应秉持的基本理念，并将应用行为分析与我国本土文化相结合，帮助读者从多个角度观察应用行为分析，进而了解它的全貌，并把握教育中的基本原则。', NULL, 'B844.1', '本书适用于儿童教育研究人员', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1005, '978-7-5226-1125-9', '做更好的自己', '成杰著', '中国水利水电出版社', 49.8, '成功心理', '从优秀到卓越的十项精进', NULL, NULL, NULL, NULL, '16,190页', '21cm', NULL, '智博尚书', '本书讲述的十项精进分别为：认真、用心、努力、负责任；学习、成长、精进、追求卓越；永远积极正面，远离所有负面；付出才会杰出，行动才会出众；听话照做、服从命令、没有借口；言行一致、知行合一、用心践行；尽心尽力、竭尽所能、全力以赴；坚守承诺、坚持到底、绝不放弃；用爱心做事业，用感恩心做人；每天进步1%就是迈向卓越的开始。', NULL, 'B848.4', '本书适用于大众读者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1006, '978-7-115-60285-5', '不公平优势', '(英)阿什·阿里(Ash Ali)，(英)哈桑·库巴(Hasan Kubba)著', '人民邮电出版社', 69.8, '成功心理', '如何找到阻力最小的成功路径', NULL, NULL, NULL, NULL, '30,282页', '21cm', NULL, '图灵教育', '本书提出了不公平优势的概念与由五种不公平优势构成的MILES框架，并结合大量案例，介绍了如何利用自己的不公平优势获得商业上的成功，同时，作为成功的创业者，两位作者还提供了快速创业入门指南。', '通俗读物', 'B848.4-49', '本书适用于个人贡献者和创业者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1007, '978-7-5104-7527-6', '不必等风来，你跑起来就有风', '文长长著', '新世界出版社', 52.8, '成功心理', NULL, NULL, NULL, NULL, NULL, '12,233页', '21cm', NULL, '悦风', '本书写出了当代年轻女性在成长过程中会遇到的不安与困惑：如何坦诚面对在爱情、婚姻里患得患失的自己；如何和有身材焦虑的自己相处；普通无背景的女孩如何通过努力让自己过得好一点、爱得顺畅一些；如何在纷繁复杂的社会变化中，少点焦虑、不安，多点平静。', '通俗读物', 'B848.4-49', '本书适用于普通大众', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1008, '978-7-5180-9349-6', '成就坚强的自己', '王济帆编著', '中国纺织出版社有限公司', 45, '成功心理', NULL, NULL, NULL, NULL, NULL, '222页', '21cm', NULL, NULL, '本书是一本帮助我们锤炼内心的励志读物，给我们讲述了如何成为内心强大的人，帮助我们获得幸福的人生。', '通俗读物', 'B848.4-49', '本书适用于大众读者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1009, '978-7-5229-0116-9', '从拖延到自律', '杨波著', '中国纺织出版社有限公司', 55, '成功心理', '用清单战胜拖延', NULL, NULL, NULL, NULL, '230页', '21cm', NULL, NULL, '本书为拖延症患者提供解决方法：清单，它可以在堆积如山的事务中筛选出真正重要的、值得去做的事，对筛选出的任务进行优先等级排序，并且针对每一项任务制订合理的计划，让工作和生活变得井然有序的同时，提高效能，减少拖延。', '通俗读物', 'B848.4-49', '本书适用于大众读者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1010, '978-7-5201-9460-0', '宗教皈信', '(美)刘易斯·兰博(Lewis R. Rambo)著', '社会科学文献出版社·人文分社', 98, '宗教学', NULL, NULL, NULL, NULL, NULL, '30,262页', '24cm', NULL, '张祥华和王翠玲夫妇赞助出版', '本书作者通过汲取心理学、社会学、人类学、历史学、神学、宗教研究和传教学等学科的研究成果，并结合对不同背景的大量皈信者进行访谈的第一手资料，对世界范围的宗教皈信情况进行了评估和分析。书中研究了不同的皈信研究理论，探讨了文化和社会因素在皈信过程中扮演的角色，描述了不同宗教和不同学科关于宗教皈信的观点。', NULL, 'B920', '本书适用于宗教学研究人员', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1011, '978-7-5117-4240-7', '图腾艺术史', '岑家梧著', '中央编译出版社', 58, '图腾', NULL, NULL, NULL, '美学与艺术丛书', NULL, '236页', '21cm', NULL, NULL, '本书主要叙述了图腾制的地理分布、图腾的文字、图腾的装饰、图腾的雕刻、图腾的图画、图腾的跳舞、图腾的音乐等。', NULL, 'B933', '本书适用于艺术史研究人员', '精装', '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1012, '978-7-213-10953-9', '与时代肝胆相照', '之江轩编著', '浙江人民出版社', 150, '社会科学', NULL, NULL, NULL, NULL, NULL, '3册(14,1384页)', '23cm', NULL, '浙江宣传', '本书所收录245篇文章，皆选自9月7日至12月31日“浙江宣传”微信公众号推送文章，包括《文艺批评，说真话为何这么难》《此地，一泉“清白”越千年》《因为中国，现代化多了一个新选项》《“梅花”面前的底线思维》《灾害面前，文物古迹不能“亡羊补牢”》《“海边长城”筑起的是防台精神》《少儿节目怎么才算好》等。', '文集', 'C53', '本书适用于社科工作者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1013, '978-7-5012-6563-3', '博望天下', '北京大学区域与国别研究院主编', '世界知识出版社', 89, '社会科学', NULL, '第1辑', NULL, NULL, NULL, '368页', '24cm', NULL, NULL, '本书由“燕南66优创”团队领衔创作，优选澎湃新闻“再看世界”专栏中20余篇作品予以扩写、修改和充实，形成了一部全新的区域国别研究社会型学术成果合集。这些青年学人及其作品是北京大学区域与国别研究院社会型学术实践发展历程的最好诠释。他们追踪全球动态，聚焦世人关心，剖析变局表象下的深层逻辑，呈现舆论漩涡中的冷静思考，分享艰难世事中的希望曙光，持续引领专业、客观、独特的区域国别研究思潮。', '文集', 'C53', '本书适用于大众读者', NULL, '2022.10', 'chi', NULL);
INSERT INTO `book` VALUES (1014, '978-7-5607-7581-4', '山东社会科学研究', '刘致福主编', '山东大学出版社', 160, '社会科学', NULL, '2021', NULL, NULL, NULL, '2册(708页)', '24cm', NULL, NULL, '本书分为上、下两册，内容覆盖政治、经济、社会、文化等多种学术领域，收录了来自全国11个省份、30余座城市的高校、研究机构及各级党政机关的社会科学工作者的优秀学术论文。论文内容聚焦国家战略和重大现实问题，展示了优秀的学术思想碰撞与研讨交流成果。', '文集', 'C53', '本书适用于大众读者', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1015, '978-7-5037-9971-6', '中国基本单位统计年鉴', '国家统计局普查中心编', '中国统计出版社有限公司', 250, '统计资料', NULL, '2022', NULL, NULL, NULL, '283页', '30cm', '1光盘', NULL, '本书分为两部分：第一部分是全国各类法人单位按地区、国民经济行业等进行各种分组汇总而成的综合资料；第二部分是全国各类企业法人单位按地区、国民经济行业、登记注册类型、营业状态等进行各种分组汇总而成的综合资料。', '年鉴', 'C832-54', '本书适用于各级政府和经济管理部门人员及企业生产经营及投资决策和科研机构人员', '精装', '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1016, '978-7-5230-0041-0', '首届《统计研究》优秀论文评选获奖论文选编', '国家统计局统计科学研究所编', '中国统计出版社有限公司', 148, '统计学', NULL, NULL, NULL, NULL, NULL, '581页', '30cm', NULL, NULL, '本书内容包括统计基本理论与统计改革、国民经济核算体系研究、统计方法的应用与创新、经济社会统计分析等方面，收录了《中国数字经济产业统计分类问题研究》《中国农村贫困标准与贫困监测》《基于深度学习LSTM神经网络的全球股票指数预测研究》等文章。', '文集', 'C8-53', '本书适用于统计工作者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1017, '978-7-5327-8767-8', '再会，公共人', '(美)理查德·桑内特(Richard Sennett)著', '上海译文出版社有限公司', 69, '社会学', NULL, NULL, NULL, NULL, NULL, '554页', '19cm', NULL, '译文经典 世纪出版', '本书展示了现代社会特有的公共生活现状，从城市人口、建筑交通、户外空间、环境失衡等方面揭示出人们的紧张和焦虑，由此证明了现代社会普遍存在的自我迷恋是公共生活衰落的结果，并最终归结为公共领域的人格入侵。', NULL, 'C91', '本书适用于社会学研究人员', '精装', '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1018, '978-7-5594-5216-0', '关系高手', '李轩洋著', '江苏凤凰文艺出版社', 49.8, '人际关系学', '20天打造可变现的人脉圈子', NULL, NULL, NULL, NULL, '211页', '21cm', NULL, '凤凰传媒 鳍豚传媒', '本书从找准位置、拓展数量、优化质量、提升效率4个关系网络维度，帮助我们解决社交问题，精准应对社交日常，升级社交力。书中提供了一套人际交往策略，让我们能够从心理根源到操作层面突破社交障碍、学会与陌生人建立关系、与熟人深入关系、建立优质可变现的人脉圈。', NULL, 'C912.11', '本书适用于大众读者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1019, '978-7-5207-2864-5', '你好，孤独', '(日)枡野俊明著', '东方出版社', 56, '人际关系', NULL, NULL, NULL, NULL, NULL, '155页', '21cm', NULL, NULL, '本书作者融合自身体验，剖析所谓“孤独感”的本质，教给读者直面孤独，打破孤独的幻象。同时，专门分析了家庭中的孤独，亲密关系中的孤独，工作场合的孤独。', '通俗读物', 'C912.11-49', '本书适用于大众读者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1020, '978-7-5220-1723-5', '信息迷航', '(美)查尔斯·R. 查芬(Charles R. Chaffin)著', '中国金融出版社', 46, '信息技术', '在信息时代找回理智与自我', NULL, NULL, NULL, NULL, '179页', '24cm', NULL, 'WILEY', '本书对这个信息时代的生活进行了360度的审视，回答了：我们如何利用社交媒体作为深入联系的工具，而不是让其成为我们的“社交目的地”；我们如何将自己从许多分心的习惯中解脱出来；我们如何将事实与意见、事件本质与耸人听闻区分开来，成为更知情的选民和公民；当科技带来无穷无尽的选择时，我们如何作出正确的选择；当我们不断地被痛苦和苦难的图像与视频所困扰时，我们如何保持同情心等问题。作者将信息时代的每一个组成部分带入生活，点明面对的挑战并提供包括反思和作出积极改变的解决方案。', NULL, 'C913', '本书适用于金融领域从业人员', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1021, '978-7-02-017556-7', '章鱼学会冷静', '(美)杰西卡·鲍姆(Jessica Baum)著', '人民文学出版社', 59, '恋爱', NULL, NULL, NULL, NULL, NULL, '12,268页', '21cm', NULL, NULL, '本书作者用心理学领域的突破性研究，引导我们在亲密关系中为自己画像，了解恐惧、嫉妒和不适当的依赖，帮助我们建立边界，建立内在力量，建立更富有安全感、更令人满意的关系。', '通俗读物', 'C913.1-49', '本书适用于大众读者', '精装', '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1022, '978-7-121-44374-9', '把自己视作产品', '闫泽华著', '电子工业出版社', 78, '网络公司', '互联网大厂求职、进阶之道', NULL, NULL, NULL, NULL, '14,288页', '23cm', NULL, '影响力', '本书共4部分24章，内容涉及你总该经历一次大厂；发现细分赛道的大厂；大厂想要怎样的人；网申和招聘系统揭秘；持续迭代进大厂；用OKR管理你的职场人生等。', NULL, 'C913.2', '本书适用于网络公司从业者', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1023, '978-7-5222-0384-3', '临终抉择', '(美)艾德华·科瑞根(Edward T. Creagan)，(美)珊德拉·温德尔(Sandra Wendel)著', '华夏出版社有限公司', 59.8, '临终关怀', NULL, NULL, NULL, NULL, NULL, '285页', '21cm', NULL, '正向改变医疗系列 中国社会福利基金会安宁缓和医疗专项基金', '本书共31章，内容涉及临终时会发生什么；死亡，究竟是什么；我们难道没有死的权利吗；不应忽视临终者的人生故事；缓和疗法和安宁照护如何演变为临终护理的重要组成部分；什么是安宁照护；什么是以患者为中心的临终照护等。', NULL, 'C913.9', '本书适用于大众读者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1024, '978-7-5228-1346-2', '魁阁学刊', '谢寿光，关凯主编', '社会科学文献出版社·群学出版分社', 98, '社会学', NULL, '2022年第2辑 总第7辑', NULL, NULL, NULL, '192页', '24cm', NULL, '云南大学主办 J集刊', '本书以“非虚构写作的社会学与人类学意涵”为主题，探讨了非虚构写作的社会意义、场景建构、操作方式，口述史访谈者的自我定位及访谈风格，口述史方法的主体间性，口述史作为一种研究方法对于学科公共性构建的价值等内容。', '文集', 'C91-53', '本书适用于社会学研究人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1025, '978-7-5228-1279-3', '社区心理学研究', '黄希庭主编', '社会科学文献出版社·政法传媒分社', 98, '社区服务', NULL, '第十四卷', NULL, NULL, NULL, '276页', '24cm', NULL, '中文社会科学引文索引（CSSCI）来源集刊 中国心理学会社区心理学专业委员会 重庆市人文社会科学重点研究基地 西南大学心理学与社会发展研究中心主办 J集刊', '本书分为五个栏目，“社区心理学理论”部分从理论出发，探究了云南汉族大学生的祖根意识、社区认同与社区参与、公共服务满意度与生育意愿等；“社区心理服务模式”研究了内嵌式社区矫正的心理－社会整合服务；“社区工作人员心理”分别从疫情下的社区公共工作者、社会工作者、女性社会工作者的角度探究社区工作人员的心理；“社区心理服务”探究了失能老人、社区老年人孤独感、新入职消防员、教师的心理需求及据此提出的建议；“网络社区心理”探讨了网络欺凌的产生、扩散、消退机制。', NULL, 'C916-05', '本书适用于社区服务研究人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1026, '978-7-5638-3429-7', '涉外秘书实务', '张丽琍主编', '首都经济贸易大学出版社', 42, '秘书学', NULL, NULL, NULL, NULL, '4版', '291页', '24cm', NULL, '高等院校秘书学专业精品系列教材', '本书分为9章，内容包括：涉外秘书概述、涉外秘书礼仪、办公室日常事务、文书写作与运转管理、会议管理工作、差旅安排等。', '教材', 'C931.46', '本书适用于高等院校秘书学专业学生', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1027, '978-7-5216-3148-7', '人才战略', '何欣著', '中国法制出版社', 69, '人才', '首席人才官的人才经营、业务伙伴与后梯队时代', NULL, NULL, NULL, NULL, '327页', '21cm', NULL, NULL, '本书是一本从首席人才官层面解构人才经营的策略书，也是一本从业务管理者与人力资源双视野布局人才战略的工具书。除了大量的人才管理实操工具、案例之外，本书还从商业管理、经济学角度出发提出了一些新观点，直击人才管理的本质，如招聘甄选时的“羊群效应”、人才内部交流的“飞虎队”“人才毛利率”、组织与人才的“良性博弈”等。', NULL, 'C964.2', '本书适用于人才发展研究人员', '精装', '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1028, '978-7-200-17529-5', '国家研究', '俞可平主编', '北京出版社', 90, '帝国', NULL, '第1辑 2022年', '帝国', NULL, NULL, '244页', '28cm', NULL, '北京大学中国政治学研究中心主办 创刊号', '本书收录了《世界历史上的著名帝国》《帝国兴起的政治逻辑：国家组织理论的视角》《从“尚武”到“崇文”：中华帝国治国文化的转换》《朱仁帝国主义及其批评者》《帝国认同的建构及其限度》《酋邦与前国家政治系统的变迁》等文章。', '文集', 'D033-53', '本书适用于政治研究者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1029, '978-7-5615-8686-0', '应急管理基础理论', '江航，林建清主编', '厦门大学出版社', 46, '突发事件', NULL, NULL, NULL, NULL, NULL, '239页', '26cm', NULL, '应急管理系列教材 福建省教育厅新文科研究与改革实践项目“新文科经管法跨界融合创新人才培养模式探索与实践”项目 集美大学四新建设研究与改革实践项目“‘文理渗透、文工结合’下应急管理专业建设的探索与实践”项目 集美大学教材建设项目“应急管理学基础理论”项目资助', '本书围绕应急管理中的“一案三制”(制定修订应急预案，建立健全应急体制、机制和法制)这一核心内容，对应急管理相关理论进行阐述。全书对海事领域应急管理的概念进行界定，是将一般应急管理理论与实践拓展应用于海事领域的一次创新尝试。', '教材', 'D035.29', '本书适用于应急管理专业本科生、从事与应急管理相关的政府部门、企事业单位人员', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1030, '978-7-115-59916-2', '数字政府架构设计参考', '王翔著', '人民邮电出版社', 89.9, '电子政务', NULL, NULL, NULL, NULL, NULL, '303页', '23cm', NULL, '信通社区', '本书介绍了数字政府发展的现状、评价指标、典型框架，系统性介绍了数字政府架构设计方法，包括基本流程、模型、框架、架构风格。书中从解构动机入手，为数字政府战略架构、数据架构、业务架构、应用架构、技术架构、物理架构、安全架构、运维架构、规则架构、实施和迁移给出实施参考。', NULL, 'D035-39', '本书适用于参与数字政府建设工作的政府人员及服务数字政府建设的企业、咨询机构的工作人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1031, '978-7-119-13253-2', '中国共产党章程', NULL, '外文出版社有限责任公司', 30, NULL, NULL, NULL, NULL, NULL, NULL, '127页', '26cm', NULL, NULL, '本书收录了《中国共产党章程》，共十一章，内容包括：党员、党的组织制度、党的中央组织、党的地方组织、党的基层组织、党的干部、党的纪律、党的纪律检查机关、党组、党和共产主义青年团的关系、党徽党旗。', NULL, 'D21', '本书适用于广大党员干部', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1032, '978-7-119-13230-3', '高举中国特色社会主义伟大旗帜 为全面建设社会主义现代化国家而团结奋斗', '习近平[著]', '外文出版社有限责任公司', 48, NULL, '在中国共产党第二十次全国代表大会上的报告（2022年10月16日）', NULL, NULL, NULL, NULL, '158页', '26cm', NULL, NULL, '本书收录了在中国共产党第二十次全国代表大会上的报告，内容包括“过去五年的工作和新时代十年”“开辟马克思主义中国化时代化新境界”“新时代新征程中国共产党的使命任务”“实现建军一百年奋斗目标，开创国防和军队现代化新局面”等。', NULL, 'D220', '本书适用于广大党员干部', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1033, '978-7-5035-7421-4', '历史自信', '王英梅著', '中共中央党校出版社', 68, NULL, '增强做中国人的志气、骨气和底气', NULL, NULL, NULL, NULL, '265页', '24cm', NULL, '时代华语国际', '本书从中华民族最有底气历史自信、党的百年求索增加了历史自信、站在两个百年历史交汇处我们怎样增加并坚定历史自信等方面论述了什么是历史自信、为什么要坚定历史自信、怎样坚定历史自信，如何保持“赶考”路上的清醒坚定、用马克思主义之“矢”射新时代中国之“的”等。', NULL, 'D23', '本书适用于广大党员、干部和全体人民', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1034, '978-7-5098-6183-7', '中国共产党上饶市历史大事记', '中共上饶市委党史党建研究室编', '中共党史出版社', 98, NULL, '2012-2020', NULL, NULL, NULL, NULL, '633页', '29cm', NULL, NULL, '本书反映了上饶市地方党组织带领当地人民在2012年至2020年期间建设的重要活动和重要事件。按照时间发展顺序，用纵叙的方法，反映了这一时期在经济建设、政治建设、文化建设、社会建设、生态文明建设方面取得的主要成绩。', NULL, 'D235.563', '本书适用于广大领导干部', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1035, '978-7-5051-5304-2', '“党建+”的逻辑统一', '方开燕著', '红旗出版社', 68, NULL, NULL, NULL, NULL, NULL, NULL, '513页', '24cm', NULL, NULL, '本书作者以多年的调研与观察报道经验为基础，遵循从实践到理论，再以理论指导实践的创作原则，对于全球化背景下中国特色社会主义发展中的党建成果和经济发展的互促互进、逻辑统一进行了哲学思辨和分析。全书分为六大章节，分别从“逻辑的本质”“形式逻辑与辩证逻辑的统一”“经济的逻辑”“党建的逻辑”“党建+经济的逻辑统一”“党建+经济逻辑统一的生产方式：公有资本逻辑”等方面进行阐述。', NULL, 'D26', '本书适用于广大党建工作者', NULL, '2022.09', 'chi', NULL);
INSERT INTO `book` VALUES (1036, '978-7-5098-6093-9', '不信邪 不怕鬼 不怕压', '本书选编组编', '中共党史出版社', 48, NULL, NULL, NULL, NULL, NULL, NULL, '254页', '23cm', NULL, '快·学习', '本书系统性地选编近年来的多篇文章，主要从传统源流、一脉相承、斗争方向、斗争精神、斗争本领等方面，对“不信邪、不怕鬼、不怕压”的伟大斗争精神进行全方位、多角度的解读阐释。', NULL, 'D261.2', '本书适用于广大党员干部', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1037, '978-7-5051-5233-5', '见微知著', '中共浙江省委宣传部主编', '红旗出版社', 49, NULL, '微型党课一本通', NULL, NULL, NULL, NULL, '243页', '21cm', NULL, NULL, '本书以浙江省微型党课大赛成功举办13届的经验为出发点，从组织者、参赛者、评委专家3个维度，介绍微型党课大赛区域典型案例、历届优秀讲师的经典教案和评委的精辟点评，为微型党课谁来讲、如何讲、讲什么提供可资借鉴的文本，也为理论宣讲品牌建设提供实操指南。', NULL, 'D261.42', '本书适用于广大党员干部', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1038, '978-7-5197-7201-7', '强化党规研究 推进依规治党', '夏小和，潘牧天主编', '法律出版社', 58, NULL, NULL, NULL, NULL, '上海政法学院党内法规研究丛书', NULL, '317页', '21cm', NULL, '独角兽', '本书收录了论依规治党与依法治国相统一、党内法规普法问题研究、党内规范性文件的对外公开制度研究、寻常百年中国共产党党内法规制度建设、新时代地方依规治党问题研究、强化离退休党员管理的党内法规制度建设研究等文章。', NULL, 'D262.13', '本书适用于广大党员干部', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1039, '978-7-5171-4341-3', '功勋张富清', '张孺海主编', '中国言实出版社', 78, NULL, NULL, NULL, NULL, NULL, NULL, '16,217页', '24cm', NULL, '融媒书', '本书以张富清同志重要历史时期现场图片(视频资料)、影视舞台剧照、各艺术作品的精彩片段(附部分原创作品)等为重要载体，向读者展示了“共和国勋章”获得者张富清被发现的来龙去脉，以及张富清同志的生平事迹，关于张富清同志的相关电视剧、歌剧、话剧，相关媒体报道等内容。', NULL, 'D263-64', '本书适用于人物研究人员', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1040, '978-7-313-22662-4', '都市青年文化价值观研究', '曹永荣，徐剑著', '上海交通大学出版社', 68, '城市', NULL, NULL, NULL, '上海交通大学人文社会科学成果文库', NULL, '10,277页', '24cm', NULL, NULL, '本书对我国都市青年的价值观进行了系统的调查和梳理，首先，围绕“人类命运共同体”的理念构建了“青年价值观量表”；其次，在中国的大型城市开展系统的调查，通过深度分析，掌握中国都市青年的价值观走向；最后，提出构建以“人类命运共同体”理念为内核的中华文明价值体系，并引领智能时代的话语权。', NULL, 'D432.62', '本书适用于研究文化价值观的学者和青年学子', NULL, '2022.10', 'chi', NULL);
INSERT INTO `book` VALUES (1041, '978-7-5228-0719-5', '新时代中国妇女发展报告', '刘利群，张立主编', '社会科学文献出版社·皮书出版分社', 198, '妇女工作', NULL, '2022', '妇女参与经济社会发展分析', '皮书系列', NULL, '452页', '24cm', NULL, '妇女蓝皮书 No.1 2022版', '本书围绕中国式现代化进程中的性别平等与妇女全面发展议题，依据《中国妇女发展纲要(2011-2020年)》各领域的主要目标，从国家层面和部分省级层面分析评估新时代10年来妇女与经济社会同步发展的显著成就，并结合各领域发展趋势提出建设性意见；聚焦扎实推动共同富裕、实施乡村振兴战略、开展科技强国建设和数字社会建设、全民抗击新冠肺炎疫情等涉及国家经济社会发展重要领域中的“她”力量和性别平等新景象，前瞻性分析研究在以中国式现代化推进中华民族伟大复兴中国梦新征程中，如何进一步发挥亿万妇女主体性作用，为全面建设社会主义', NULL, 'D442.6', '本书适用于妇女工作研究人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1042, '978-7-5115-7199-1', '时代之问 中国答卷', '人民日报国际部编', '人民日报出版社', 78, '时事评论', '人民日报国际评论“和音”', NULL, NULL, NULL, NULL, '10,486页', '24cm', NULL, NULL, '本书撷取具有代表性的典型篇章，分为“中国故事”“多边关系”“周边关系”及“抗疫故事”四部分，勾勒出中国同世界各国命运相连、休戚与共的格局与胸怀，阐释国际社会如何从伙伴关系、安全格局、经济发展、文明交流、生态建设等方面构建人类命运共同体。', '文集', 'D5-53', '本书适用于大众读者', NULL, '2022.09', 'chi', NULL);
INSERT INTO `book` VALUES (1043, '978-7-5154-1182-8', '中国式现代化', '(意)弗斯科·贾尼尼著', '当代中国出版社', 38, '现代化建设', '路径、成就与挑战', NULL, NULL, NULL, NULL, '142页', '19cm', NULL, '国外人士看新时代 世界中国学研究联合会出版项目 讲述中国', '本书作者通过数据，对中国的经济成就、工业发展、创新能力和脱贫成就进行了呈现。作者认为中国式现代化不仅丰富了发展中国家迈向现代化的路径选择，对维护世界和平和世界社会主义运动的发展，也具有重大意义。尽管中国未来的发展将面临诸如挑战，但中国的社会主义现代化进程是不可阻遏的。', NULL, 'D61', '本书适用于现代化建设研究人员', NULL, '2022.04', 'chi', NULL);
INSERT INTO `book` VALUES (1044, '978-7-5217-5178-9', '读懂中国式现代化', '林毅夫等著', '中信出版集团股份有限公司', 78, '现代化建设', '科学内涵与发展路径', NULL, NULL, NULL, NULL, '313页', '23cm', NULL, '北大国发院经济洞察系列 商业家', '本书由林毅夫等著名经济学家领衔，汇集众多专家学者的研究成果，以经济视角解读中国式现代化，特别是对共同富裕、高质量发展、市场体系构建、双碳目标、人口趋势、现代企业治理等重要概念与现实议题进行了深度探讨和科学洞察，为读者理解中国式现代化的内涵和路径，把握发展大势提供参考。', NULL, 'D61', '本书适用于现代化建设研究人员', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1045, '978-7-5090-1615-2', '中国共产党与世界马克思主义政党论坛实录', '刘建超主编', '当代世界出版社', 78, '马克思主义', NULL, NULL, NULL, NULL, NULL, '165页', '24cm', NULL, '习近平新时代中国特色社会主义思想与二十一世纪马克思主义本土化时代化新发展', '本书共分4篇，是对中国共产党与世界马克思主义政党论坛成果进行整理编译而成，表现出各国政党愿同中国共产党一道，增进互信、加强协作，共同肩负起应对世界之变、时代之变、历史之变的职责使命。', '文集', 'D61-53', '本书适用于马克思主义研究人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1046, '978-7-5194-6940-5', '洪泽改革开放实录', '中共淮安市洪泽区委党史工作办公室编', '光明日报出版社', 78, '改革开放', NULL, '第四辑', NULL, NULL, NULL, '256页', '24cm', NULL, '光明版', '本书收集整理了2022年淮安市洪泽区委统战部、区法院、区农业农村局、区卫健委、团区委、区退役军人事务局、区供电及部分乡镇等单位稿件，记述了改革开放以来，洪泽相关部门在农村、统战、法院、共青团、供电、乡镇发展等方面的探索及成绩。', NULL, 'D619.533', '本书适用于改革开放研究人员', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1047, '978-7-5218-1594-8', '人民代表大会制度理论与实践', '汪闻生等著', '经济科学出版社', 73, '人民代表大会制', NULL, NULL, NULL, '中国道路', NULL, '250页', '23cm', NULL, '国家出版基金项目 “十三五”国家重点出版物出版规划项目', '本书阐述了我国人民代表大会制度的基本理论，从不同角度分析了人大职权的政治性，描述了人民代表大会的形成发展过程，基于人民代表大会的组织结构、职权及其制度、制度的运行，提出了推动新时代人民代表大会制度改革发展完善的结语。', NULL, 'D621', '本书适用于人民代表大会制度研究人员', NULL, '2021.10', 'chi', NULL);
INSERT INTO `book` VALUES (1048, '978-7-5228-0660-0', '南方治理评论', '陈潭主编', '社会科学文献出版社·人文分社', 148, '地方政府', NULL, '第8辑', NULL, NULL, NULL, '184页', '24cm', NULL, '广州大学南方治理研究院主办', '本书收录了《政策动力机制的比较研究》《网络信息影响力形成中的经验曲线效应及其政策启示》《大数据时代末梢反腐的治理路径研究——以“互联网+监督”模式为例》《区块链的颠覆力量：如何走出高等教育改革困境》《流行病学转变趋势与人口健康：关于健康中国的决定因素分析》《家庭照料方式对子女就业的影响》等文章。', '文集', 'D625.651-53', '本书适用于公共管理研究人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1049, '978-7-300-31270-5', '公务员礼仪', '金正昆著', '中国人民大学出版社', 49.8, '公务员', '微课版', NULL, NULL, NULL, '5版', '230页', '26cm', NULL, '普通高等教育“十一五”国家级规划教材 21世纪实用礼仪系列教材', '本书内容包括办公礼仪、接待礼仪、社交礼仪、形象礼仪、外事礼仪五章，介绍了公务员所应遵守的礼仪规范。', '教材', 'D630.3', '本书适用于公务员', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1050, '978-7-5120-4957-4', '瓯海公安志', '瓯海公安志编纂委员会编', '线装书局', 398, '公安工作', '1982-2020', NULL, NULL, NULL, NULL, '57,12,556页', '29cm', NULL, NULL, '本书记述瓯海公安机关忠实践行全心全意为人民服务宗旨，坚决捍卫国家政治安全、维护社会安定、保障人民安宁的光辉战斗历程，记载1982年以来瓯海公安大事、要事、新事、特事，既展示了建县以来瓯海公安事业的发端、发展进程，又反映了瓯海公安人在党的领导下取得的骄人战绩。', NULL, 'D631', '本书适用于广大公安从业人员', '精装', '2022.10', 'chi', NULL);
INSERT INTO `book` VALUES (1051, '978-7-5179-1224-8', '民族团结进步在柳州', '柳州市民族宗教事务委员会编', '中国摄影出版社', 80, '民族团结', NULL, NULL, NULL, NULL, NULL, '133页', '26cm', NULL, NULL, '本书由入选的35名摄影家拍摄的360余幅作品组成，呈现了柳州城乡各族群众齐心向党、团结奋进的风貌，体现了“开明开放、敢为人先，创新创业、自强不息”的城市精神。', '摄影集', 'D633-64', '本书适用于摄影爱好者', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1052, '978-7-5130-8455-0', '数字政府服务社会', '中国计算机用户协会政务信息化分会组织编写', '知识产权出版社有限责任公司', 98, '电子政务', '2021、2022年度数据政府治理应用主题优秀案例汇编', NULL, NULL, NULL, NULL, '287页', '26cm', NULL, NULL, '本书分为政府治理、公共服务和智慧法院三篇，通过30多个经典案例，从政府和IT企业两个视角展示了当前数字政府实践的新需求、新特征和新经验。', '汇编', 'D63-39', '本书适用于党政机关工作人员、技术人员和IT企业服务人员', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1053, '978-7-100-21728-6', '风险灾害危机研究', '童星，张海波主编', '商务印书馆', 75, '社会管理', NULL, '第十五辑', NULL, NULL, NULL, '214页', '24cm', NULL, '中文社会科学引文索引（CSSCI）来源集刊', '本书包括“风险灾害危机多学科交叉研究”“综合应急管理制度研究”“公共安全与大数据研究”“风险感知与决策研究”四个专栏，收录了《新冠疫情背景下农户生计恢复策略选择的影响因素及作用路径——基于生计韧性视角的探讨》《全球疫情防控下我国对外人道主义援助制度优化研究》《中外危机领导力研究知识图谱与聚类维度的对比考察——基于CiteSpace的文献计量分析》等文章。', '文集', 'D63-53', '本书适用于社会管理研究人员', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1054, '978-7-5228-0240-4', '思想政治教育心理疏导模式及技术', '潘莉著', '社会科学文献出版社·政法传媒分社', 98, '思想政治教育', NULL, NULL, NULL, NULL, NULL, '217页', '24cm', NULL, '国家社科基金青年项目“思想政治教育向度的心理疏导模式及技术研究”结项成果', '本书扎根中国共产党实施心理疏导的实践活动，融合马克思主义理论、教育学、心理学、社会学等多学科理论，采用量化描述与质性分析相结合的方法，叙述了一个完整的思想政治教育心理疏导的故事。', NULL, 'D64', '本书适用于思想政治教育研究人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1055, '978-7-5130-8463-5', '城市住宅小区治理研究', '高伟著', '知识产权出版社有限责任公司', 79, '城市', NULL, NULL, NULL, NULL, NULL, '169页', '24cm', NULL, '国家自然科学基金地区基金项目“我国城市社区业主自治的溢出效应及其形成机理研究”部分研究成果', '本书以小区治理为研究对象，结合调研数据和统计分析方法辨析了小区治理的概念，探究了影响小区治理的因素，给出了优化我国当前小区治理的政策建议。同时，还总结了国内外的优秀经验，在把握我国小区治理阶段性特征的基础上，给出了可操作的实践建议。', NULL, 'D669.3', '本书适用于相关研究者和行业从业者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1056, '978-7-5607-7613-2', '戒毒治疗师', '汤洪源著', '山东大学出版社', 89, '戒毒', '吸毒相关人格障碍共病矫治案例', NULL, NULL, NULL, NULL, '252页', '24cm', NULL, NULL, '本书介绍了人格特征、吸食毒品和人格障碍共病的关系、应用医学、心理学、管理学、教育学、社会学、法学、康复训练学等不同学科的理论与实践，以及综合矫治吸毒相关人格障碍共病的方法。', NULL, 'D669.8', '本书适用于戒毒工作者、戒毒志愿者、医护人员、心理咨询师、戒毒治疗师等', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1057, '978-7-5222-0430-7', '最后的家天下', '杨猛著', '华夏出版社有限公司', 79, '政治制度史', '少壮亲贵与宣统政局', NULL, NULL, '北京师范大学史学探索丛书', NULL, '349页', '24cm', NULL, NULL, '本书尝试以宣统朝少壮亲贵的政治实践为考察对象，以时间为线索，将宣统朝分为摄政开启、立宪提速、走向危机三个阶段，展现了少壮亲贵与宣统政局的互动关系。', NULL, 'D691.2', '本书适用于政治制度史研究人员', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1058, '978-7-108-07396-9', '汉帝国的建立与刘邦集团', '李开元著', '生活·读书·新知三联书店', 69, '官制', '军功受益阶层研究', NULL, NULL, '三联·哈佛燕京学术丛书', '增订版', '10,345页', '22cm', NULL, NULL, '本书从秦汉帝国的起源观照中华帝国起源，以军功受益阶层作为切入视角，反映了作者的深思与巧思。作者从刘邦集团的结构入手，着力于刘邦集团在西汉初期的政治地位，得出一个可以超越西汉的历史概念：军功受益阶层。', NULL, 'D691.42', '本书适用于官制研究人员', '精装', '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1059, '978-7-5445-6819-7', '魏晋南北朝将军制与都督制论稿', '张鹤泉著', '长春出版社', 128, '官制', NULL, NULL, NULL, '松江丛书', NULL, '422页', '24cm', NULL, NULL, '本书考察了军号将军与州剌史、都督诸州军事、征讨都督的关系。对征讨都督的产生、设置特点及在征讨作战中的作用做了阐释，展示了征讨都督的发展脉络。并对征讨都督向东魏的征讨行台、向北周行军元帅、行军总管的演变做了考证。', NULL, 'D691.42', '本书适用于官制研究人员', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1060, '978-7-5445-6820-3', '北朝封爵制度论稿', '张鹤泉著', '长春出版社', 168, '官制', NULL, NULL, NULL, '松江丛书', NULL, '2册(573页)', '24cm', NULL, NULL, '本书对北魏前期封爵的等级、封爵的虚封特征、封爵等级与虚封地的关系、封爵封授范围的限定、王爵的继承问题做了阐释，并对孝文帝改革后，实封爵的等级、王爵的封授、开国爵的封授、散爵的封授与限定、开国爵的食邑数量问题做了考证。', NULL, 'D691.42', '本书适用于官制研究人员', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1061, '978-7-5228-0874-1', '生计与制度', '杨培娜著', '社会科学文献出版社·历史学分社', 78.8, '沿海', '明清闽粤滨海社会秩序', NULL, NULL, NULL, NULL, '433页', '23cm', NULL, '教育部行动计划司局专项项目“明清东南海洋经略与海疆社会” 国家社科基金项目“清代海洋渔政与海疆社会治理研究”研究成果之一 中山大学历史人类学研究中心出版经费支持 九色鹿', '本书以勾勒王朝制度演变为主线，尝试触摸海陆之间籍籍无名的濒海生民的活动脉搏，旨在以滨海人群生计及社会组织机制为出发点，探讨明清闽粤边海之地如何在多种力量的冲突、妥协、共谋中，逐渐消解对中央王朝的疏离状态，实现滨海社会秩序的更新再造，呈现滨海之地多样而复杂的经济和社会历史形态。', NULL, 'D691.75', '本书适用于历史研究人员', '精装', '2022.09', 'chi', NULL);
INSERT INTO `book` VALUES (1062, '978-7-03-073634-5', '物宜人和', '刘尊志著', '科学出版社', 588, '家庭', '考古学视角下的秦汉家庭', NULL, NULL, NULL, NULL, '3册(24,1333页)', '26cm', NULL, '教育部普通高等学校人文社会科学重点研究基地南开大学中国社会史研究中心资助出版 教育部人文社会科学重点研究基地重大项目研究成果', '本书以秦汉考古资料为基本参考，结合文献等资料，运用考古学及相关的研究理论和方法，从多个方面对秦汉时期家庭进行了研究和分析，探讨了与秦汉家庭有关的构成内容、内部关系、家庭保障、日常生活物质内容、精神信仰、对外交往及特殊内容等，分析了秦汉时期“物宜人和”的家庭特征，阐述了家国一体的关系和家国情怀的内涵，从考古学的视角复原了秦汉时期家庭的内容、特征及内涵、外延。', NULL, 'D691.91', '本书适用于从事考古、文博、历史和社会生活研究等方面的专家、学者和相关专业院校的师生', '精装', '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1063, '978-7-5228-0669-3', '公益与美好生活', '文军主编', '社会科学文献出版社·群学出版分社', 158, '志愿', '公益慈善行动下的共同体建构', NULL, NULL, '城市社会与文化研究丛书', NULL, '366页', '24cm', NULL, '华东师范大学教育发展基金会“紫江公益慈善人才培养专项基金”资助 华东师范大学社会发展学院 紫江基金会', '本书立足于近三年来全球流行的新冠肺炎疫情这一大背景，运用扎实的经验调查和大量文献资料，从社区和社群两种共同体入手，探讨了中国公益慈善实践活动中的共同体建构及其多重面向，彰显了重大突发公共卫生事件下中国公益力量的共同体应对。', NULL, 'D699.3', '本书适用于社会服务研究人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1064, '978-7-5115-7506-7', '消失的安全', '胡晔著', '人民日报出版社', 95, '国家安全', NULL, NULL, NULL, '人民日报学术文库', NULL, '235页', '24cm', NULL, '美国核安全体系建构研究（1939-1952） 光明版', '本书以广义核安全为研究对象，从国内核安全、国际核安全、核战略三个维度探讨二战后期至冷战初期美国核安全体系的建构过程。', NULL, 'D771.235', '本书适用于国家安全研究者', '精装', '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1065, '978-7-5090-1703-6', '环地中海学刊', '马晓霖主编', '当代世界出版社', 79, '地中海问题', NULL, '第1辑', NULL, NULL, NULL, '208页', '23cm', NULL, '浙江外国语学院环地中海研究院（教育部高校国别和区域研究备案中心）主办', '本书收录了《建构中国特色和世界领先的环地中海学》《建立环地中海学的必要性和可行性》《宗教与世俗：埃及政治文明演进的历史轨迹》《中地中海路线非法移民问题及欧盟的应对——以“索菲娅行动”为例》等文章。', '文集', 'D815.9-53', '本书适用于地中海问题研究人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1066, '978-7-5228-0933-5', '中国与葡语国家合作研究报告', '丁浩，尚雪娇主编', '社会科学文献出版社·国别区域分社', 128, '中外关系', NULL, '2021', NULL, NULL, NULL, '218页', '24cm', NULL, NULL, '本书旨在对2020年中国与葡语国家的合作进行系统分析研究，并对未来的合作进行展望。', NULL, 'D822.31', '本书适用于中外关系研究人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1067, '978-7-101-15919-6', '近代中外条约关系通史', '李育民主编', '中华书局', 990, '中外关系', NULL, NULL, NULL, NULL, NULL, '7册', '24cm', NULL, '国家出版基金项目 国家社会科学基金重大项目 近代中外条约关系通史 湖南师范大学中国史一流学科资助', '本书研究近代中外条约关系通史的历史演变及其历史影响，根据近代中外条约演进的内在规律，将其历史发展划分为六个时段。分阶段探讨鸦片战争后条约关系的酝酿，中国国际秩序的两重转换历程，即从宗藩体制到条约关系，以及条约关系从不平等到强权政治阴影下的基本平等，论析各个阶段的条约交涉，条约关系的演化、趋势、特点和规律等。', NULL, 'D829.15', '本书适用于国际关系史研究人员', '精装', '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1068, '978-7-5130-8409-3', '语言与法律', '(美)桑福德·尚恩(Sanford Schane)著', '知识产权出版社有限责任公司', 88, '法律语言学', NULL, NULL, NULL, NULL, NULL, '10,202页', '23cm', NULL, '中国政法大学第五批青年教师学术创新团队及教育部人文社会科学规划基金项目资助', '本书主要以语言学、哲学理论分析英美法的经典案例，探讨了词汇的多义性、语境及语言的模糊性等对法律实施的影响，探析法学本体研究之外解决法律问题的新思路。', NULL, 'D90-055', '本书适用于法律语言学研究人员', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1069, '978-7-5197-7385-4', '师大法学', '张志铭主编', '法律出版社', 98, '法学', NULL, '2021 第1辑·总第9辑', NULL, NULL, NULL, '416页', '23cm', NULL, '“龙图法律教育基金”资助 独角兽', '本书设有“师大法学论坛”“译文”“专题研讨(公司法修订)”“论文”四个栏目，收录了《公司法现代化的走向》《债、物、财产、组织：民法之“两仪四象”》《公司法制的潮流》《社会的至上和法律与发展的失败》《我国董事监督义务之规范困境与具体构建》等文章。', '文集', 'D90-53', '本书适用于法学理论研究人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1070, '978-7-5130-7937-2', '金融犯罪与存在主义哲学', '(加)米歇尔·迪翁(Michel Dion)著', '知识产权出版社有限责任公司', 99, '金融犯罪', NULL, NULL, NULL, '犯罪学前沿译丛', NULL, '286页', '24cm', NULL, '“侦查学”国家一流专业建设成果 江苏省十四五“国家安全学”重点学科建设成果 江苏高校“青蓝工程”资助研究成果 公安部公安理论及软科学研究计划重点项目“金融安全视阈下的新型风险型经济犯罪侦查与对策研究”研究成果 Springer', '本书明确了金融犯罪在人类自我定义方式或其对人类概念定义方式选择方面的影响，揭示了金融犯罪的增长是如何促成了人类学差距的扩大，以及金融犯罪现象是如何扭曲了我们对人类的理解方式。', NULL, 'D914.330.4', '本书适用于金融犯罪研究人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1071, '978-7-5130-8260-0', '犯罪侧写', '(美)劳伦·M. 巴罗(Lauren M. Barrow)，(美)罗纳德·A. 鲁福(Ronald A. Rufo)，(美)索尔·阿拉姆布拉(Saul Arambula)著', '知识产权出版社有限责任公司', 89, '犯罪学', '理论与实践', NULL, NULL, '犯罪学前沿译丛', NULL, '252页', '24cm', NULL, '“侦查学”国家一流专业建设成果 江苏省十四五“国家安全学”重点学科建设成果 江苏高校“青蓝工程”资助研究成果 江苏省高校哲学社会科学研究基金项目“新型城镇化进程中犯罪风险分析及防控——基于犯罪经济学视角”', '本书共分十二章，内容包括：逻辑推理实务、童年警示信号、动机与犯罪类型、性侵犯与动机性犯罪、连环及暴怒杀手、英雄情结杀手、犯罪现场线索及调查、犯罪地理侧写、受害者特征等。', NULL, 'D917', '本书适用于犯罪学研究人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1072, '978-7-5197-7312-0', '国家统一法律职业资格制度研究', '姜海涛编著', '法律出版社', 88, '法律工作者', NULL, NULL, NULL, NULL, NULL, '12,271页', '23cm', NULL, '司法部部级科研项目最终研究成果 独角兽', '本书立足中国国情和法律职业队伍建设实际，研究中外法律职业资格考试(司法考试)制度发展历程和实施效果，阐释我国统一法律职业资格考试制度考试管理与考试组织各项规范要求，分析论证构建培养选拔和职业训练“一体化”法治人才养成机制的必要性和可行性，研究提出完善国家统一法律职业资格考试模式、制定法律职业胜任能力标准、建立法律职业统一培训制度、推动形成法学教育、资格考试与职前培训统一管理的法治人才培养选拔体制等相关建议。', NULL, 'D92', '本书适用于广大法律工作者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1073, '978-7-5581-8458-1', '社会主义核心价值观融入法治体系构建研究', '蒋青青著', '吉林出版集团股份有限公司', 58, '社会主义法制', NULL, NULL, NULL, NULL, NULL, '203页', '24cm', NULL, NULL, '本书共包含六个章节，阐述了社会主义核心价值观的基本理论、中国特色社会主义法治体系构建的基本理论、社会主义核心价值观与法治体系构建的逻辑关系、社会主义核心价值观融入法治国家建设、社会主义核心价值观融入法治政府建设、社会主义核心价值观融入法治社会建设等内容。', NULL, 'D920.0', '本书适用于法治建设研究人员', NULL, '2021.06', 'chi', NULL);
INSERT INTO `book` VALUES (1074, '978-7-5008-7955-8', '宪法', '学习强会编', '中国工人出版社', 18, '宪法', NULL, NULL, NULL, NULL, NULL, '88页', '21cm', NULL, '全国职工“八五”普法简明读本 1', '本书帮助广大职工了解、掌握《宪法》中的职工权益，内容包括我国的国体是什么；我国的基本经济制度是什么；《宪法》是如何规定土地的归属的；公民在法律面前一律平等的含义是什么等。', '通俗读物', 'D921.04', '本书适用于广大职工', NULL, '2022.10', 'chi', NULL);
INSERT INTO `book` VALUES (1075, '978-7-5216-2930-9', '行政法史料汇编', '关保英主编', '中国法制出版社', 45, '经济法', '1949-1965', '3', '经济行政法', NULL, NULL, '278页', '21cm', NULL, NULL, '本书编选、整理中华人民共和国成立初期(1949-1965)经济行政方面的行政法规、部门规章以及规范性文件，呈现当时我国行政法的主要任务是什么，是如何设计运行的，体现了何种时代特征，以及是如何探索出一套符合我国实际的经济行政法治体系的。', '史料', 'D922.102', '本书适用于法律工作者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1076, '978-7-5216-2931-6', '行政法史料汇编', '梁玥主编', '中国法制出版社', 45, '税法', '1949-1965', '1', '税务行政法', NULL, NULL, '313页', '21cm', NULL, NULL, '本书编选、整理中华人民共和国成立初期(1949-1965)税务行政方面的行政法规、部门规章以及规范性文件，并做适当的技术性分类。力求呈现新中国成立初期在税努方面的行政法律制度内容，以期呈现当时我国行政法的主要任务是什么，是如何设计运行的，体现了何种时代特征，以及是如何探索出一套符合我国实际的税务行政法治体系的。', '史料', 'D922.102', '本书适用于法律工作者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1077, '978-7-5216-2948-4', '行政法史料汇编', '关保英主编', '中国法制出版社', 45, '社会管理', '1949-1965', '6', '社会行政法', NULL, NULL, '233页', '21cm', NULL, NULL, '本书编选、整理中华人民共和国成立初期(1949-1965)社会行政方面的行政法规、部门规章以及规范性文件，并做适当的技术性分类，力求呈现新中国成立初期在社会方面的行政法律制度内容，以期呈现当时我国行政法的主要任务是什么，是如何设计运行的，体现了何种时代特征，以及是如何探索出一套符合我国实际的社会行政法治体系的。', '史料', 'D922.102', '本书适用于法律工作者', NULL, '2022.12', 'chi', NULL);
