INSERT INTO `book` VALUES (1081, '978-7-5197-7361-8', '中华人民共和国行政执法法律法规全书', '法律出版社法规中心编', '法律出版社', 98, '行政执法', NULL, NULL, NULL, NULL, NULL, '741页', '23cm', NULL, '2023年版法律法规全书系列 32 独角兽', '本书收录改革开放以来至2022年11月期间公布的现行有效的与行政执法相关的法律、行政法规、司法解释、部门规章及政策规定。内容包括综合、行政执法监督(细分为公安、交通，市场监督管理，城乡规划、自然资源、生态环境，卫生健康、医疗保障，教育、文化旅游，应急管理、劳动和社会保障，税收等)、行政执法事项指导目录、行政诉讼、国家赔偿等，分类细致，全面覆盖行政执法的方方面面。', '汇编', 'D922.119', '本书适用于广大法律工作者', NULL, '2023.01', 'chi', NULL);
