INSERT INTO `book` VALUES (1083, '978-7-5216-3211-8', '教育救助法制研究', '张超著', '中国法制出版社', 59, '教育法', NULL, NULL, NULL, NULL, NULL, '290页', '24cm', NULL, '北京工业大学学生资助中心支持和高校思想政治工作专项资金资助', '本书首先介绍了教育救助法制研究的背景、意义、概念和性质，以及国内外研究的现状、研究方法，之后对教育救助法制的理论基础、教育救助法制的现状与问题、教育救助法律主体、教育救助给付与标准、教育救助程序、教育救助法律责任、教育救助权利救济进行了分析和研究，最后提出了完善教育救助法律制度的未来立法构想。', NULL, 'D922.164', '本书适用于教育法研究人员', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1084, '978-7-5228-0784-3', '国家教育考试法治化研究', '彭宇文等著', '社会科学文献出版社', 159, '考试', NULL, NULL, NULL, '当代教育研究丛书', NULL, '393页', '24cm', NULL, '国家教育考试科研规划2019年度重点课题“依法治考的理论与实践研究”研究成果', '本书围绕普通高考、研究生招考、成人高考、自学考试等国家教育考试中的依法治考问题进行研究，阐释了国家教育考试法治化建设的若干理论与实践问题。', NULL, 'D922.164', '本书适用于教育法研究人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1085, '978-7-5216-3182-1', '中华人民共和国体育法释义', '张勇，王瑞连主编', '中国法制出版社', 65, '体育法', NULL, NULL, NULL, NULL, NULL, '16,432页', '21cm', NULL, '权威解读', '本书共12章，包含总则、全民健身、青少年和学校体育、竞技体育、反兴奋剂、体育组织等。', NULL, 'D922.165', '本书适用于法律工作者', NULL, '2023.01', 'chi', NULL);
