INSERT INTO `book` VALUES (1090, '978-7-5197-7163-8', '科创板技术秘密审查与技术秘密刑事保护', '李德成，白露著', '法律出版社', 68, '科学技术', NULL, NULL, NULL, NULL, NULL, '265页', '24cm', NULL, '金诚同达30周年文集 独角兽', '本书以科创板技术秘密审查和刑事保护为主题，主要通过案例分析的形式，展现科创板技术秘密审查实务中的重点、要点。作者将多年在此领域的实务经验进行分享，一方面提供有可操作性的核心技术秘密问询审查项目等的实务要点，另一方面通过案例分析的形式，将律师实务中常见的重点问题、疑难问题一一列举、解读，旨在为相关法律、企业工作人员提供实务参考。', NULL, 'D922.174', '本书适用于广大法律工作者', NULL, '2022.11', 'chi', NULL);
