INSERT INTO `book` VALUES (1100, '978-7-5136-7203-0', '走向本质合规', '杜江波著', '中国经济出版社', 148, '企业法', NULL, NULL, NULL, NULL, NULL, '524页', '26cm', NULL, NULL, '本书依托大型工业企业的实际和场景，梳理了企业合规管理的主要领域，如安全生产、生态环境保护、税务、反不正当竞争、反垄断及涉外合规等，阐释了不同领域合规经营的总体发展形势以及国外可资借鉴的典型案例。', NULL, 'D922.291.914', '本书适用于政府机构、企业组织、社会团体等干部员工', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1101, '978-7-5197-7283-3', '困境企业拯救法律指南', '关振海[等]著', '法律出版社', 90, '企业法', NULL, NULL, NULL, '盈科全国业务指导委员会系列丛书', NULL, '395页', '24cm', NULL, '盈科 独角兽', '本书从企业拯救的宏观认识、司法拯救的内容等基础知识出发，梳理重整模式选择、重整程序启动、破产管理人履职、遴选重整投资人、制订重整计划等具体问题以及流程的基本要求，为困境企业实现脱困提供务实的指南。同时，本书也收录了最高人民法院和各地方人民法院发布的部分破产重整典型案例，并附有具体的法律文书范本。', '指南', 'D922.291.914', '本书适用于广大法律工作者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1102, '978-7-5216-3171-5', '中华人民共和国公司法及司法解释全书', NULL, '中国法制出版社', 98, '公司法', NULL, NULL, NULL, NULL, NULL, '706页', '23cm', NULL, '2023法律法规全书系列 2023年版', '本书涵盖公司领域常用法律、行政法规、国务院文件、部门规章、规范性文件、司法解释及相关示范文本，书中收录文件均为全国人大及其常委会、国务院及部委经过清理修改后的现行有效标准文本。', NULL, 'D922.291.915', '本书适用于法律工作者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1103, '978-7-5197-7258-1', '中华人民共和国公司法律法规全书', '法律出版社法规中心编', '法律出版社', 98, '公司法', NULL, NULL, NULL, NULL, '13版', '653页', '23cm', NULL, '2023年版法律法规全书系列 14 独角兽', '本书收录改革开放以来至2022年11月期间公布的现行有效的全部与公司相关的法律、行政法规、司法解释，重要的部门规章、相关政策规定。内容包括总类、工商登记、公司股份募集与上市、公司债券、公司并购重组、公司管理、公司财务会计、公司清算破产、与企业相关的法律规定、法律责任等。', '汇编', 'D922.291.919', '本书适用于广大法律工作者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1104, '978-7-5197-7366-3', '中华人民共和国企业合规法律法规全书', '法律出版社法规中心编', '法律出版社', 118, '企业法', NULL, NULL, NULL, NULL, NULL, '859页', '23cm', NULL, '2023年版法律法规全书系列 33 独角兽', '本书根据企业合规常见的领域，分二十个合规领域，收录了截至2022年12月公布的与企业合规相关的法律法规、部门规章、司法解释及国际条约等。', '汇编', 'D922.291.919', '本书适用于广大法律工作者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1105, '978-7-100-21581-7', '反不正当竞争法价值论', '王艳芳著', '商务印书馆', 88, '反不正当竞争', NULL, NULL, NULL, '知识产权法研究丛书', NULL, '21,240页', '21cm', NULL, NULL, '本书以反不正当竞争法的价值取向为主线，以比较法研究为方法，以国内法研究为支点，以具体制度分析为支撑，分别从国内与国外、理论与实践、历史与现实以及“道”与“术”等方面，进行了分析研究，并提出了诸个观点。', NULL, 'D922.294', '本书适用于知识产权法研究人员', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1106, '978-7-5216-3163-0', '道路交通安全法一本通', '法规应用研究中心编', '中国法制出版社', 44, '道路交通安全法', NULL, NULL, NULL, '法律一本通丛书', NULL, '404页', '21cm', NULL, NULL, '本书以中华人民共和国道路交通安全法为中心，将行政法及最新司法解释等法律文件逐条分析，成为学习和适用该法的实用图书。', NULL, 'D922.296.4', '本书适用于法律工作者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1107, '978-7-5197-7262-8', '中华人民共和国文化和旅游法律法规全书', '法律出版社法规中心编', '法律出版社', 98, '旅游业', NULL, NULL, NULL, NULL, '4版', '694页', '23cm', NULL, '2023年版法律法规全书系列 20 独角兽', '本书分为两篇，收录截至2022年11月以来公布的现行有效的与文化和旅游相关的法律、行政法规、司法解释、部门规章及政策规定。', '汇编', 'D922.296.9', '本书适用于法律工作者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1108, '978-7-5216-3103-6', '中华人民共和国道路交通法律法规全书', NULL, '中国法制出版社', 98, '道路交通安全法', NULL, NULL, NULL, NULL, NULL, '728页', '23cm', NULL, '2023法律法规全书系列 2023年版', '本书涵盖道路交通领域常用法律、行政法规、国务院文件、部门规章、规范性文件、司法解释及相关示范文本，书中收录文件均为全国人大及其常委会、国务院及部委经过清理修改后的现行有效标准文本，方便读者及时掌握最新法律文件。', '汇编', 'D922.296.9', '本书适用于广大法律工作者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1109, '978-7-5216-2964-4', '土地管理法一本通', '法规应用研究中心编', '中国法制出版社', 39, '土地管理法', NULL, NULL, NULL, '法律一本通丛书', '9版', '355页', '21cm', NULL, NULL, '本书以中华人民共和国土地管理法为中心，将土地管理法及最新司法解释等法律文件逐条分析，成为学习和适用土地管理法的实用图书。', NULL, 'D922.304', '本书适用于高校师生、普通读者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1110, '978-7-5216-3112-8', '中华人民共和国土地法律法规全书', NULL, '中国法制出版社', 98, '土地法', NULL, NULL, NULL, NULL, NULL, '11,670页', '23cm', NULL, '2023法律法规全书系列 2023年版', '本书涵盖土地管理领域常用法律、行政法规、国务院文件、部门规章、规范性文件、司法解释及相关示范文本，书中收录文件均为全国人大及其常委会、国务院及部委经过清理修改后的现行有效标准文本，方便读者及时掌握最新法律文件。全书法律文件按照紧密程度排列，而不是文件公布的时间顺序排列，以方便读者集中对某一类问题查找使用，避免疏漏重点法律文件的相关规定。全书重点法律文件附加条文主旨，指引读者快速找到目标条文，了解条文规定的主要内容。本书重在全面收录法律文件，同时为确保内容准确，所有文件无注释加工内容。', '汇编', 'D922.309', '本书适用于广大法律工作者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1111, '978-7-5216-3132-6', '农村土地承包法一本通', '法规应用研究中心编', '中国法制出版社', 43, '农村土地承包法', NULL, NULL, NULL, '法律一本通丛书', NULL, '374页', '21cm', NULL, NULL, '本书以中华人民共和国农村土地承包法为中心，将农村土地承包法及最新司法解释等法律文件逐条分析，内容涉及家庭承包；其他方式承包；正义的解决和法律责任等。', NULL, 'D922.32', '本书适用于法律工作者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1112, '978-7-5216-3123-4', '中华人民共和国房地产法律法规全书', NULL, '中国法制出版社', 98, '房地产法', NULL, NULL, NULL, NULL, NULL, '10,709页', '23cm', NULL, '2023法律法规全书系列 2023年版', '本书在前一版本的基础上，根据最新国家法律、行政法规、部门规章、司法解释及相关文件的制定和修改情况，进行了相应的增删和修订。新增、更新文件：《房地产开发企业资质管理规定》《中央财政农村危房改造补助资金管理暂行办法》《住房和城乡建设行政处罚程序规定》《住房和城乡建设部办公厅、国家发展改革委办公厅、财政部办公厅关于进一步明确城镇老旧小区改造工作要求的通知》《住房和城乡建设部、财政部、人民银行关于实施住房公积金阶段性支持政策的通知》等。', '汇编', 'D922.389', '本书适用于法律工作者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1113, '978-7-5216-3217-0', '政府土地房屋征收项目专项法律服务指引', '福建仰格律师事务所著', '中国法制出版社', 65, '国有土地', '以晋江经验为例', NULL, NULL, NULL, NULL, '307页', '24cm', NULL, '晋江经验20周年', '本书包括四个部分，第一部分是征收项目办理流程指引，以问答及流程图形式呈现；第二部分是结合晋江经验，介绍晋江市在推进项目征收工作中的先进做法；第三部分是征收项目常用法律文书汇总；第四部分是征收项目常用法律法规汇总。', NULL, 'D922.394', '本书适用于法律工作者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1114, '978-7-5197-7237-6', '农业法概论', '曾睿主编', '法律出版社', 58, '农业法', NULL, NULL, NULL, NULL, NULL, '359页', '21cm', NULL, '独角兽', '本书分析了我国的农业用地法律制度、农村建设用地法律制度、农业安全法律制度、农业生产经营法律制度、农业生产资料管理法律制度、农产品流通法律制度、农村金融法律制度、农业科技与教育法律制度、农村社会保障法律制度等。', NULL, 'D922.4', '本书适用于广大法律工作者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1115, '978-7-5216-3164-7', '劳动法一本通', '法规应用研究中心编', '中国法制出版社', 45, '劳动法', NULL, NULL, NULL, '法律一本通丛书', NULL, '384页', '21cm', NULL, NULL, '本书以中华人民共和国劳动法为中心，将劳动法及最新司法解释等法律文件逐条分析，还新增了近来收集到的最高人民法院及其研究室、最高人民检察院及其法律政策研究室、公安部发布的行政规范性文件。', NULL, 'D922.504', '本书适用于法律工作者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1116, '978-7-5130-8290-7', '特种设备事故鉴定', '李山桥编著', '知识产权出版社有限责任公司', 168, '设备事故', NULL, NULL, NULL, '产品质量鉴定系列丛书', NULL, '285页', '24cm', NULL, NULL, '本书根据特种设备事故特点，归纳、建立了特种设备事故鉴定证据类别和鉴定项目；依据司法鉴定、认证认可、特种设备核准等行政管理要求，介绍了特种设备质量和事故鉴定机构运作模式、鉴定活动的基本技术程序；以证据法学的观点，规范特种设备质量和事故鉴定活动，提供合法、科学获取鉴定证据的基本方法；通过具体例证，将物证技术、特种设备原理、失效分析、检验试验等技术活动有机的转化为质量和事故鉴定法律证据，为鉴定意见、作证提供证据支持。', NULL, 'D922.544', '本书适用于鉴定机构、鉴定人员、事故应急处理人员、保险勘验人员', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1117, '978-7-5197-7255-0', '中华人民共和国安全生产法律法规全书', '法律出版社法规中心编', '法律出版社', 98, '安全生产', NULL, NULL, NULL, NULL, '9版', '747页', '24cm', NULL, '2023年版法律法规全书系列 10 独角兽', '本书收录改革开放以来至2022年11月期间公布的现行有效的全部与安全生产相关的法律、行政法规、部门规章及重要的司法解释、规范性文件。内容按照法律法规的规范领域，分为上篇“安全生产综合性规定”和下篇“各行业各领域安全生产规定”。', '汇编', 'D922.549', '本书适用于法律工作者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1118, '978-7-5197-7257-4', '中华人民共和国自然资源法律法规全书', '法律出版社法规中心编', '法律出版社', 98, '自然资源保护法', NULL, NULL, NULL, NULL, '4版', '744页', '23cm', NULL, '2023年版法律法规全书系列 19 独角兽', '本书收录改革开放以来至2022年11月期间公布的现行有效的与自然资源相关的重要法律、行政法规、部门规章、司法解释及相关政策规定。内容包括土地管理，矿产资源管理，草原资源管理，森林管理、湿地保护，海洋管理，水资源，测绘，监察与违法案件处理等。', '汇编', 'D922.609', '本书适用于广大法律工作者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1119, '978-7-5216-3014-5', '中华人民共和国妇女权益保障法新旧对照与重点解读', NULL, '中国法制出版社', 20, '妇女权益保障法', NULL, NULL, NULL, NULL, NULL, '164页', '21cm', NULL, NULL, '本书根据最新公布的《妇女权益保障法》，制作新旧对照表，并对每条内容做重点解读，方便读者参照新旧法和重点解读有针对性地进行学习。', NULL, 'D922.7', '本书适用于广大法律工作者', NULL, '2022.10', 'chi', NULL);
INSERT INTO `book` VALUES (1120, '978-7-5109-3625-8', '个人信息侵权违法性判定研究', '蔡一博著', '人民法院出版社', 66, '隐私权', NULL, NULL, NULL, NULL, NULL, '253页', '24cm', NULL, NULL, '本书坚持以问题意识为研究导向，主要聚焦个人信息侵权违法的地位、判定方法和类型化分析等问题，并以“合法性基础+合法的处理行为”的研究为主线。本书从域内外涉个人信息侵权案例中精选百余个案例进行类型化研究，分析涉个人信息现行法律规定的相关内容，针对法律规范的理解适用及个人信息违法处理侵权救济的实践困难，主要围绕个人信息处理行为违法性的认定，研究处理行为的客体及利益类型化、技术分析与法律评价、合法性基础的识别与判定、违法性要件的评价标准等问题，并提出了具有操作性的认定方法。', NULL, 'D923.04', '本书适用于法官', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1121, '978-7-5154-1218-4', '当代中国民法典编纂研究', '梁慧星著', '当代中国出版社', 98, '民法', NULL, NULL, NULL, '当代中国法学家文库', NULL, '468页', '24cm', NULL, NULL, '本书以当代中国民法典的编纂为线索，选取了梁慧星先生不同时期的重要著述，按照经济合同法的修订、统一合同法的制定、物权法的制定、侵权责任法的制定、民法总则的制定、民法典的编纂分编选排，每一篇文章都凝结了作者的法学思想与学术精神。这些文章记录了改革开放后，当代中国民事立法逐步发展并最终完成民法典编纂的进程，记录了当代中国法学学术逐渐走向繁荣的历史，承载了法学家的家国情怀与责任担当。同时，本书还选取了梁慧星先生关于法学方法论的开创性研究。', NULL, 'D923.04', '本书适用于民法研究人员', '精装', '2022.09', 'chi', NULL);
INSERT INTO `book` VALUES (1122, '978-7-5216-2808-1', '村民民法典实用手册', NULL, '中国法制出版社', 32, '民法', '以案释法版', NULL, NULL, NULL, NULL, '229页', '21cm', NULL, '民法典“六进”系列', '本书以《民法典》为学习蓝本，选取与村民相关的民法典知识，从土地承包经营、宅基地、婚姻家庭、遗产继承、侵权赔偿等与村民日常生活密切相关的角度展开论述。精选生活实践中的真实案例，还原案例场景。结合《民法典》及相关司法解释，对案例进行法理分析，让读者做法律明白人。', NULL, 'D923.05', '本书适用于大众读者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1123, '978-7-5216-3115-9', '中华人民共和国民法典及司法解释全书', NULL, '中国法制出版社', 98, '民法', NULL, NULL, NULL, NULL, NULL, '721页', '23cm', NULL, '2023法律法规全书系列 2023年版', '本书涵盖民事领域相关的常用法律、行政法规、国务院文件、部门规章、规范性文件、司法解释，及最高人民法院公布的典型案例；书中收录文件均为经过清理修改的现行有效文本，方便读者及时掌握最新法律文件。', NULL, 'D923.05', '本书适用于法律工作者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1124, '978-7-5197-7245-1', '中华人民共和国民事法律法规全书', '法律出版社法规中心编', '法律出版社', 98, '民法', NULL, NULL, NULL, NULL, '15版', '671页', '23cm', NULL, '2023年版法律法规全书系列 26 独角兽', '本书收录改革开放以来至2022年11月期间公布的现行有效的民事法律和重要的行政法规、部门规章和司法解释，覆盖民事法律的方方面面。全书分为两大部分：上篇“民事实体法”与《民法典》体例保持一致。结合司法实践加设涉外民事关系的法律适用部分。下篇“民事程序法”分为诉讼和仲裁两类，其中诉讼部分强调法院审案的具体操作规则，收录了大量常用司法解释并细分小类。', '汇编', 'D923.09', '本书适用于法律工作者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1125, '978-7-5216-3016-9', '民法典背景下商业银行担保实务问题100问·法律评析·实务指南', '胡龙，刘晔主编', '中国法制出版社', 78, '担保法', NULL, NULL, NULL, NULL, NULL, '340页', '24cm', NULL, NULL, '本书重点梳理商业银行常见的担保实务问题，结合新的立法司法观点及商业银行业务实践，阐明法律观点、提示法律风险、提出风控建议；并以商业银行为视角，基于多年为商业银行提供法律服务的经验，从中总结、整理了100个易产生风险的实务问题，通过问题、法律解析、审查要点、裁判观点、法律延伸五个部分的内容作出解答。', NULL, 'D923.25', '本书适用于法律工作者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1126, '978-7-5197-7307-6', '企业知识产权管理和风险防控答疑', '罗宗奎主编', '法律出版社', 56, '企业', NULL, NULL, NULL, '盈科全国业务指导委员会系列丛书', NULL, '182页', '24cm', NULL, '盈科 独角兽', '本书包括企业对知识产权的定位、专利管理和风险防控、商标管理与风险防控、著作权管理与风险防控、商业秘密管理和不正当竞争风险防控、其他知识产权管理和风险防控六章内容。', NULL, 'D923.404', '本书适用于广大法律工作者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1127, '978-7-5216-3166-1', '商标法、专利法、著作权法一本通', '法规应用研究中心编', '中国法制出版社', 35, '商标法', NULL, NULL, NULL, '法律一本通丛书', NULL, '12,296页', '21cm', NULL, NULL, '本书以中华人民共和国商标法、专利法、著作权法为中心，将商标法、专利法、著作权法及最新司法解释等法律文件逐条分析，还新增了近来收集到的最高人民法院及其研究室、最高人民检察院及其法律政策研究室、公安部发布的行政规范性文件。', NULL, 'D923.404', '本书适用于法律工作者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1128, '978-7-5216-3197-5', '中国人工智能合规建设与知识产权法律实务', '王红燕著', '中国法制出版社', 58, '人工智能', NULL, NULL, NULL, NULL, NULL, '12,231页', '24cm', NULL, '人工智能AI+法律', '本书分为上下两编，上编为人工智能与合规建设，下编为人工智能与知识产权，分别从人工智能领域的数据合规、开源技术、算法推荐技术、个人信息与隐私保护、科技伦理、出口管制、著作权、专利、商标、商业秘密、不正当竞争、投融资并购、科创板上市等多个维度展开论述，旨在结合目前人工智能技术的保护现状和诉讼纠纷案例，通过上述多个维度的分析和探讨，明晰法律风险，给予人工智能企业实践建议和合规指导，释放知识产权价值，协助共同推进人工智能产业的发展。', NULL, 'D923.404', '本书适用于法律工作者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1129, '978-7-5216-3110-4', '中华人民共和国知识产权法律法规全书', NULL, '中国法制出版社', 96, '知识产权法', NULL, NULL, NULL, NULL, NULL, '668页', '23cm', NULL, '2023法律法规全书系列 2023年版', '本书涵盖知识产权领域常用法律、行政法规、国务院文件、部门规章、规范性文件、司法解释及相关示范文本，书中收录文件均为全国人大及其常委会、国务院及部委经过清理修改后的现行有效标准文本，方便读者及时掌握最新法律文件。', '汇编', 'D923.409', '本书适用于法律工作者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1130, '978-7-5130-8492-5', '作品实质性相似侵权判定', '初萌著', '知识产权出版社有限责任公司', 68, '版权', NULL, NULL, NULL, NULL, NULL, '11,334页', '21cm', NULL, NULL, '本书从事实与法律、局部与整体、相似与差异、行为与结果、大众与专家、共性与个性六个维度剖析既有争议，展现实质性相似问题的全貌。', NULL, 'D923.41', '本书适用于知识产权领域从业人员(如研究人员、法官、律师等)及对版权尤其是抄袭判定感兴趣的读者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1131, '978-7-5130-8481-9', '中国数字版权保护与发展报告', '白连永主编', '知识产权出版社有限责任公司', 78, '电子出版物', NULL, '2022', NULL, NULL, NULL, '283页', '24cm', NULL, NULL, '本书分为总报告、行业篇、城市篇、专题篇四个部分，对2021年度我国数字版权保护与发展状况进行了回顾与分析，对当前数字版权领域的热点与焦点问题进行了专题研究。', NULL, 'D923.414', '本书适用于版权保护研究人员', NULL, '2022.12', 'chi', NULL);
