INSERT INTO `book` VALUES (1133, '978-7-5130-8507-6', '实用新型专利权评价报告实务手册', '朱晓琳主编', '知识产权出版社有限责任公司', 78, '专利权', NULL, NULL, NULL, NULL, '2版', '234页', '24cm', NULL, NULL, '本书介绍了实用新型专利权评价报告制度以及评价报告的作出和更正程序中所涉及的启动程序、检索要求、评价标准、报告的撰写、表格填写等内容，同时丰富了实用新型的专利库检索策略、非专利库检索策略、权利要求保护范围的确定、创造性判断实操方式、更正程序中涉及的情形等内容，以及相应的典型案例。', '手册', 'D923.42-62', '本书适用于专利审查员、社会公众、创新主体、专利工作者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1134, '978-7-5130-8428-4', '知识产权法律制度研究', '国家知识产权局条法司编', '知识产权出版社有限责任公司', 88, '知识产权法', NULL, '2022', NULL, NULL, NULL, '229页', '23cm', NULL, NULL, '本书分为知识产权制度、专利、商标、其他4个专题，汇集14篇文章，围绕近年来的知识产权领域热点问题展开研究。', '文集', 'D923.4-53', '本书适用于从事专利法研究的人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1135, '978-7-5136-7051-7', '新常态下民间借贷危机的内在机理及治理路径与对策研究', '肖韶峰著', '中国经济出版社', 88, '民间借贷', NULL, NULL, NULL, NULL, NULL, '276页', '24cm', NULL, NULL, '本书通过对各地民间借贷风波和危机的梳理，探讨其内在机理，分析其传导机制，研究其处理机制；同时总结各地应对和处置民间借贷风波的经验，为地方政府和相关部门防范民间借贷风险、化解危机、稳定地方经济社会提供对策建议。', NULL, 'D923.64', '本书适用于广大法律工作者', NULL, '2022.08', 'chi', NULL);
INSERT INTO `book` VALUES (1136, '978-7-5197-7401-1', '建设工程施工合同审判要素解析', '牛富文，严慧勇等编', '法律出版社', 70, '建筑施工', NULL, NULL, NULL, NULL, NULL, '19,355页', '24cm', NULL, '独角兽', '本书以建设工程施工合同纠纷案件常见审判要素为载体，破解建工案件审判难点，内容涉及：建设工程施工合同的订立、建设工程施工合同主体认定、建设工程施工合同效力认定、建设工程施工合同解释原则和方法等。', NULL, 'D923.65', '本书适用于广大法律工作者', NULL, '2022.12', 'chi', NULL);
