INSERT INTO `book` VALUES (1138, '978-7-5216-3156-2', '刑法一本通', '法规应用研究中心编', '中国法制出版社', 79, '刑法', NULL, NULL, NULL, '法律一本通丛书', '9版', '28,750页', '21cm', NULL, NULL, '本书分为刑法的任务、基本原则和适用范围；犯罪；刑罚；刑罚的具体运用；其他规定；危害国家安全罪；危害公共安全罪等。', NULL, 'D924.04', '本书适用于法律工作者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1139, '978-7-5197-7337-3', '中华人民共和国刑法及司法解释全书', '法律出版社法规中心编', '法律出版社', 98, '中华人民共和国刑法', NULL, NULL, NULL, NULL, '13版', '19,769页', '23cm', NULL, '2023年版法律法规全书系列 6 独角兽', '本书收录1979年刑法颁布至2022年12月期间公布的现行有效的刑法法律和司法解释、司法文件，内容包括：刑法的适用范围、犯罪、刑罚、罪名规定与立案标准、破坏社会主义市场经济秩序罪、侵犯财产罪、妨害社会管理秩序罪等。', NULL, 'D924.05', '本书适用于广大法律工作者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1140, '978-7-5216-3125-8', '中华人民共和国刑法及司法解释全书', NULL, '中国法制出版社', 106, '中华人民共和国刑法', NULL, NULL, NULL, NULL, NULL, '19,743页', '23cm', NULL, '2023法律法规全书系列 2023年版', '本书涵盖刑法领域常用法律、行政法规、国务院文件、部门规章、规范性文件、司法解释及相关示范文本，书中收录文件均为全国人大及其常委会、国务院及部委经过清理修改后的现行有效标准文本，方便读者及时掌握最新法律文件。', NULL, 'D924.05', '本书适用于广大法律工作者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1141, '978-7-5216-2551-6', '轻罪刑事政策研究', '郭理蓉著', '中国法制出版社', 68, '量刑', NULL, NULL, NULL, NULL, NULL, '259页', '24cm', NULL, '国家社科基金项目成果', '本书立足于我国现阶段犯罪结构的变化和实现国家治理现代化目标的要求，以犯罪分层理论为基础，梳理轻罪刑事政策的域外趋势与本土演变，重点探讨在宽严相济刑事政策背景下，构建以定罪政策、刑罚政策与诉讼政策为基本框架的轻罪刑事政策，以及在轻罪刑事政策指导下我国轻罪立法与轻罪司法的完善。', NULL, 'D924.134', '本书适用于法律工作者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1142, '978-7-5216-3205-7', '交通风险语境下间接危害行为犯罪化研究', '姜敏著', '中国法制出版社', 45, '交通肇事', NULL, NULL, NULL, NULL, NULL, '346页', '21cm', NULL, NULL, '本书对我国刑法规制交通犯罪的立法现状及困境进行了分析，并引入英美法系的危害原则及其衍生的间接危害行为，对我国刑法应对交通风险的立法，尤其是没有实害结果犯罪立法的完善进行研究，在此基础上，根据交通犯罪领域的间接危害行为的种类、特征，提出完善我国交通犯罪刑事立法的解决路径。', NULL, 'D924.324', '本书适用于立法研究人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1143, '978-7-5197-7348-9', '中华人民共和国民事诉讼法及司法解释全书', '法律出版社法规中心编', '法律出版社', 98, '中华人民共和国民事诉讼法', NULL, NULL, NULL, NULL, '9版', '16,699页', '23cm', NULL, '2023年版法律法规全书系列 3 独角兽', '本书收录自1982年《民事诉讼法(试行)》颁布至2021年12月期间公布的现行有效的民事诉讼法律和司法解释、重要司法文件，在《民事诉讼法》体系的基础上，新增了若干实践中常用的分类，如诉讼时效、审限与文书、涉港澳台民事诉讼、海事诉讼、刑事附带民事诉讼、民商事仲裁等。', NULL, 'D925.105', '本书适用于法律工作者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1144, '978-7-5216-2970-5', '人民调解实战技法应用', '黎清海著', '中国法制出版社', 39, '民事纠纷', NULL, NULL, NULL, NULL, NULL, '201页', '21cm', NULL, NULL, '本书分为实务必备、技法攻略、感悟札记三个篇章，专注于新时代调解理论与实践的研究，通过对各门类、数千个调解典型案例的集成解析，总结提炼出既具有鲜明个性特征。', NULL, 'D925.114.4', '本书适用于各类人民调解组织和广大人民调解员', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1145, '978-7-5228-1013-3', '中国司法制度发展报告', '陈甦，田禾主编', '社会科学文献出版社', 139, '司法制度', NULL, 'No.4 (2022)', NULL, '皮书系列', NULL, '338页', '24cm', NULL, '法治蓝皮书 No.4 2022版', '本书设置了年度报告、司法体制改革、司法实践经验、公益诉讼制度和司法社会治理五个专题，梳理了中国司法制度一年来的新成效、面临的新问题，并对2023年中国司法制度发展的新趋势进行了预测和展望。', NULL, 'D926', '本书适用于司法领域相关人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1146, '978-7-5197-7346-5', '首都检察案例参阅', '北京市人民检察院法律政策研究室编', '法律出版社', 80, '检察机关', NULL, '第二辑', NULL, NULL, NULL, '424页', '24cm', NULL, '独角兽', '本书内容围绕检察人员办案过程中的典型和疑难问题，结合真实案例进行分析和解读，每个案例的分析由“基本案情”“主要问题”“意见分析”等几个部分组成，对争议焦点和解决方法进行了阐述。', NULL, 'D926.32', '本书适用于广大检察办案人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1147, '978-7-5508-3958-8', '湖州契约档案文献图鉴', '湖州市档案馆编', '西泠印社出版社', 100, '契约', NULL, NULL, NULL, NULL, NULL, '153页', '29cm', NULL, NULL, '本书对一百五十余份涉及地契、房契、税契、杂契等四类契约档案，按照记载内容分门别类，采用图文结合形式，并配合品鉴解读，展现契约承载的文化内涵，呈现湖州经济、社会在历史长河中的变迁。', '史料', 'D927.434.36-64', '本书适用于档案工作者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1148, '978-7-214-27700-8', '清代中国的法与审判', '(日)滋贺秀三著', '江苏人民出版社', 88, '法制史', NULL, NULL, NULL, '海外中国研究丛书', NULL, '364页', '23cm', NULL, '凤凰传媒', '本书以清代的诉讼和审判为中心，对法制度与审判的实践形态、作为与国家权力对应的民间组织秩序、含儒家经义与民间习惯要素的法源与处理纠纷的原则等问题，进行了发掘与理论探讨。', NULL, 'D929.49', '本书适用于法制史研究人员', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1149, '978-7-5197-7060-0', '大清律例', '陈颐点校', '法律出版社', 198, '清律', '同治九年', NULL, NULL, NULL, NULL, '22,651页', '24cm', NULL, '大清律例源流辑校 国家出版基金项目 独角兽', '本书以道光二十七年(1847)递修本为底版，将咸丰二年(1852)、同治九年(1870)刑部刊刻的两版《大清律纂修条例》所载条例分门别类附刊于原本各条之后，并于各卷相应律目下方盖咸丰二年(1852)、同治九年(1870)的戳记，在各相应条例页眉盖咸丰二年(1852)改、并、删、续，同治九年(1870)改、并、删、续的戳记，即为本书所用底本。本书将所有修改、修并、续纂条例补入相应位置，删去原条例，并以脚注注明；咸丰二年(1852)、同治九年(1870)删除的条例，不予录入。条例次序仍依原书，续纂条例按咸丰二', NULL, 'D929.49', '本书适用于法律史研究人员', '精装', '2022.10', 'chi', NULL);
INSERT INTO `book` VALUES (1150, '978-7-5197-7097-6', '《大清律例》《大清现行刑律》合编', '陈颐，洪佳期点校', '法律出版社', 598, '清律', NULL, NULL, NULL, NULL, NULL, '3册(1511页)', '24cm', NULL, '大清律例源流辑校 国家出版基金项目 独角兽', '本书将《大清律例》与《钦定大清现行刑律》对照合刊，附以历次删改案语，意在完整呈现“现行刑律”的两个面向，为更深入的理解近代中国法制转型提供重要的文献基础。本书使用对照合刊形式，左栏为《大清律例》律例，右栏为《钦定大清现行刑律》律例。律文如“现行刑律”仍旧未改者，仍保留两栏对照。例文系“现行刑律”删除，或“现行刑律”续纂者，则通栏排版。例文未改(“仍旧”)者，亦以通栏排版，但同时列明相应《大清律例》《钦定大清现行刑律》编号、例目。各栏首行皆标注“大清律例”或“现行刑律”。', '汇编', 'D929.49', '本书适用于广大法律工作者', '精装', '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1151, '978-7-301-24499-9', '中国法制史大辞典', '蒲坚编著', '北京大学出版社', 292, '法制史', NULL, NULL, NULL, NULL, '重排本', '131,1714页', '27cm', NULL, NULL, '本书所收条目内容包括：从传说时代法律萌芽开始至夏代以后汉唐直至中华人民共和国成立前夕的法律制度，其中包括历代行政立法和近代宪政活动中的宪政立法、军事立法、经济立法、刑事立法、民事立法(婚姻、家庭、继承立法皆涵盖其中，故未单独列出)、法理学(律学)、诉讼立法、司法监察制度等，以及历代律令典籍、律学家、主要法学名著及与法制有关的社会组织、称谓、机构、朝代、年号和文献中常见的古族名、古国名等。', '词典', 'D929-61', '本书适用于从事中国法制史教学与科研的青年学者', '精装', '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1152, '978-7-5130-8406-2', '欧洲专利局审查特点及通知书探析', '石科峰，周兵，杨方田主编', '知识产权出版社有限责任公司', 88, '专利', NULL, NULL, NULL, NULL, NULL, '253页', '24cm', NULL, NULL, '本书对欧洲专利局的专利制度和申请流程进行介绍，并重点对欧洲专利局专利的实质条款和指南进行解读分析，与中国专利局的相关规定进行对比，便于相关知识产权工作者充分了解向欧洲专利局申请专利的具体流程及欧洲专利局专利审查的特点，从而有针对性地撰写高质量的欧洲专利申请；着重对欧洲专利局各种类型的通知书进行研究分析，使读者充分了解欧洲专利局专利审查各阶段各种类型通知书的特点，并对如何进行答复进行建议，以期帮助读者准确有效地答复欧洲专利局的审查通知书并最终获得授权，使相关技术创新能够及时在欧洲市场获得专利权的保护。', NULL, 'D950.344', '本书适用于专利申请研究人员', NULL, '2022.10', 'chi', NULL);
INSERT INTO `book` VALUES (1153, '978-7-04-057957-4', '国际商法', '吴建斌，肖冰，彭岳编著', '高等教育出版社', 55, '国际商法', NULL, NULL, NULL, NULL, '4版', '360页', '26cm', NULL, '高等学校国际经济与贸易专业主要课程教材', '本书共九章，分为导论、国际商事组织法、合同法、国际货物买卖法、国际货物买卖合同、海上运输、海上保险、国际支付和国际商事仲裁。', '教材', 'D996.1', '本书适用于高等学校经济学、金融学、法学等专业的本科生、研究生', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1154, '978-7-5197-6933-8', '国际投资法', '王贵国著', '法律出版社', 128, '国际投资法学', '中国视角', NULL, NULL, NULL, NULL, '489页', '24cm', NULL, '独角兽', '本书对国际投资与法律相关的概述、外商直接投资控制系统、对外国投资者和外国投资的理解和界定、管辖、绝对待遇标准和相对待遇标准、征收、违反义务后的赔偿、国家责任和执行等方面进行了相关的分析和论述，并结合我国的相关情况提出建议。', NULL, 'D996.4', '本书适用于广大法律工作者', NULL, '2022.08', 'chi', NULL);
INSERT INTO `book` VALUES (1155, '978-7-5136-5139-4', '马克思主义生产关系结构理论及其当代价值', '张作云著', '中国经济出版社', 98, '马克思主义', NULL, NULL, NULL, NULL, NULL, '331页', '24cm', NULL, NULL, '本书共十六章，内容涉及马克思生产关系结构理论的创立；马克思主义经典作家关于生产关系结构问题的论述；马克思恩格斯批判折衷主义；国际生产关系的形成、发展、演进及其当代特点；我国现阶段社会主义生产关系结构体系及其特点等。', NULL, 'F014.1', '本书适用于马克思主义研究人员', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1156, '978-7-300-30996-5', '经济增长动能', '罗伯特·默顿·索洛(Robert M. Solow)著', '中国人民大学出版社', 42, '经济增长', '干中学的启示', NULL, NULL, '诺贝尔经济学奖获得者丛书', NULL, '96页', '24cm', NULL, '“十三五”国家重点出版物出版规划项目', '本书以肯尼思·J. 阿罗的经典论文《干中学的经济含义》为开端，向人们展示了阿罗的思想是如何融入现代经济增长理论的，并以此为出发点，对此后蓬勃发展的经济增长理论做了批判性的思考，使其成为当前热门的研究话题。作者随后提出了一个新的理论，该理论将“干中学”(用“持续改进”的概念进行识别)与离散“创新”的独立过程相结合。', NULL, 'F061.2', '本书适用于经济增长研究人员', '精装', '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1157, '978-7-301-33482-9', '内生可持续增长理论研究', '陈昆亭，周焱著', '北京大学出版社', 58, '经济增长', NULL, NULL, NULL, NULL, NULL, '247页', '24cm', NULL, '国家社会科学基金重大项目“推动经济发展质量、效率、动力持续增强的机制研究”资助', '本书从长视野对近代工业革命进程进行观察，透析东西方主流经济体从“大分流”到“大合流”的过程，研究英美式长周期律特征及其内在规律，并针对传统增长理论存在的问题提出四个发展方向并进行讨论：长周期观察：文化、人口、制度与一致增长思想；长期增长可持续性；周期波动与增长；有限需求与增长。基于这些方面的讨论，给出了英美式长周期律形成内在机制的一种解释，并结合这些机制，提出中国避免传统周期律的一些建议。', NULL, 'F061.2', '本书适用于宏观经济领域的硕士生、博士生和研究人员', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1158, '978-7-121-44588-0', '数字迁徙', '冯伟编著', '电子工业出版社', 89, '信息经济学', NULL, NULL, NULL, NULL, NULL, '34,282页', '22cm', NULL, NULL, '本书将带读者看清时代变迁的“基本盘”，找到“数字迁徙”的“驱动力”，一探世界主要国家“数字竞备”的得与失，企业“数字生存”的成与败，明晰我国数字化转型的底层逻辑，形成几点数字共识，畅谈未来“数字世界”的新蓝图，吹响开启“数字迁徙”征程的集结号，共同奔向数字文明的“星辰大海”。', NULL, 'F062.5', '本书适用于信息经济学研究人员', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1159, '978-7-5028-5382-2', '亚当·斯密经济学', '(英)亚当·斯密著', '地震出版社', 148, '古典资产阶级政治经济学', NULL, NULL, NULL, NULL, NULL, '866页', '23cm', NULL, '兴盛乐 地震版', '本书收录亚当·斯密的《国富论》和《道德情操论》两部经典作品。其中《国富论》提出了市场经济的自行调节理论，即“看不见的手”，标志着古典政治经济学理论体系的建立。《道德情操论》则是亚当·斯密的伦理学著作。《国富论》阐述了经济人的行为，谈的是自利与不合作(竞争)；《道德情操论》则阐述了道德人的行为，谈的是同感心、同理心、同情心。两部作品相辅相成、互为补充，有利于更好地解决“亚当·斯密问题”，探究经济学与伦理学之间的关系。', NULL, 'F091.33', '本书适用于政治经济学研究人员', NULL, '2022.10', 'chi', NULL);
INSERT INTO `book` VALUES (1160, '978-7-5220-1783-9', '东北亚经济发展报告', '东北财经大学东北亚经济研究院著', '中国金融出版社', 60, '东北亚经济圈', NULL, '2021', NULL, NULL, NULL, '220页', '24cm', NULL, NULL, '本书分为主报告和专题两大部分。主报告从东北亚经济发展的新变化、东北亚各国经济形势、中国东北经济形势、未来东北经济发展重点四个方面进行阐述。专题部分共有三个专题，专题一是“沈长哈大”协同发展，构建东北区域协调发展新格局；专题二是东北地区网状北水南调战略研究；专题三是东北振兴——东北三省土地政策研究及改革试验区建设。', NULL, 'F114.46', '本书适用于区域经济合作研究人员', NULL, '2022.10', 'chi', NULL);
INSERT INTO `book` VALUES (1161, '978-7-5136-7115-6', '坚持和完善公有制为主体 多种所有制经济共同发展基本经济制度研究', '洪功翔著', '中国经济出版社', 98, '中国经济', NULL, NULL, NULL, NULL, NULL, '355页', '24cm', NULL, NULL, '本书在理论层面说清楚了坚持公有制为主体、多种所有制经济共同发展基本经济制度的必要性和意义，在实证层面证明了为什么能够坚持公有制为主体的基本经济制度，以及为什么多种所有制经济能够更好地共同发展，在对策层面提出了坚持和完善公有制为主体、多种所有制经济共同发展基本经济制度的具体举措。', NULL, 'F121', '本书适用于经济学研究人员', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1162, '978-7-5194-6649-7', '当代经济发展改革研究', '郭志仪著', '光明日报出版社', 98, '中国经济', NULL, NULL, NULL, '光明社科文库', NULL, '323页', '24cm', NULL, '光明版', '本书第一部分是作者从实证的角度研究世界经济发展对我国的启示。第二部分是关于人口与发展的理论与现实问题。第三部分是关于发展战略与发展模式的研究。', '文集', 'F121-53', '本书适用于经济改革研究者', '精装', '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1163, '978-7-5218-4160-2', '中国宏观经济分析与预测', '中国季度宏观经济模型（CQMM）课题组著', '经济科学出版社', 106, '中国经济', NULL, '2022年', '提高中等收入群体收入增速与促进共同富裕', '厦门大学宏观经济研究丛书', NULL, '421页', '24cm', NULL, '教育部人文社会科学重点研究基地基金资助项目 ESP', '本书共有三篇十六章内容。第一篇是“回顾篇”，包含第一章内容。主要是回顾和总结2021年中国宏观经济运行情况以及2022年宏观经济展望。第二篇是“研究与分析篇”，包含第二章至第十三章共十二章内容。其中，第二章围绕PPI与CPI的传导机制及其效应问题展开。第三章、第四章和第五章侧重宏观政策的经济效应研究。第六章进一步讨论了中国国债期货与现货市场间的动态价格发现与不对称波动性溢出的问题。第七章、第八章、第九章和第十章集中关注“双循环”新发展格局、“一带一路”和对外贸易问题。第十一章和第十二章重点讨论了数字经济与', NULL, 'F123.16', '本书适用于宏观经济研究人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1164, '978-7-5218-4084-1', '共同富裕的理论创新与中国实践', '浙江省社会科学界联合会，孙冶方经济科学基金会，浙江财经大学编写', '经济科学出版社', 86, '共同富裕', NULL, NULL, NULL, NULL, NULL, '329页', '24cm', NULL, '第五届中国经济学家高端论坛报告（2022） ESP', '本书以共同富裕话题为中心，从不同角度对共同富裕进行解读。内容包括：高端论坛的主办方致辞、知名经济学家的主旨报告以及四个不同主题的分论坛报告。', NULL, 'F124.7', '本书适用于共同富裕研究人员', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1165, '978-7-5218-4015-5', '新时代中国实践与中国经验研究', '刘华军，孙曰瑶，杨骞等著', '经济科学出版社', 79, '中国经济', NULL, NULL, NULL, '转型时代的中国财经战略论丛', NULL, '306页', '23cm', NULL, '“十四五”时期国家重点出版物出版专项规划项目 财智睿读', '本书聚焦新时代中国实践与中国经验，以专题的方式，选择了创新驱动发展、能源革命、绿色发展、大气污染治理、农业绿色发展、乡村振兴、民生发展、减污降碳协同增效、3E绩效等9个专题，遵循“让事实说话，让数据说话”的原则，总结梳理中国实践历程，运用多样化的经济数据量化分析工具，展示了新时代中国发展成就，在阐释中国经验基础上，对标贯彻新发展理念、构建新发展格局、推动高质量发展的新要求，对新时期中国发展进行了展望。', '文集', 'F124-53', '本书适用于经济发展研究人员', NULL, '2022.09', 'chi', NULL);
INSERT INTO `book` VALUES (1166, '978-7-5136-7110-1', '“一带一路”经贸合作研究', '颜少君著', '中国经济出版社', 88, '“一带一路”', NULL, NULL, NULL, NULL, NULL, '311页', '24cm', NULL, NULL, '本书从共建“一带一路”经贸合作面临的时代背景的出发，分析了共建“一带一路”经贸合作的相关理论、取得的重要进展、面临的风险与挑战，并在此基础上提出了推动“一带一路”经贸合作高质量发展的思路与举措。', NULL, 'F125', '本书适用于“一带一路”相关政策制定者、研究者和从业者', NULL, '2022.10', 'chi', NULL);
INSERT INTO `book` VALUES (1167, '978-7-303-28195-4', '金砖力量', '胡必亮等著', '北京师范大学出版社', 118, '区域经济合作', NULL, NULL, NULL, NULL, NULL, '17,352页', '21cm', NULL, NULL, '本书十五个篇章，涵盖了减贫、粮食安全、工业化、贸易、投资、金融、基础设施建设、数字经济、可持续发展等众多领域，阐述了金砖国家十六年来在各个领域的紧密合作过程，取得的阶段性成果，介绍了金砖合作机制建立以来的成果总结和展望。', NULL, 'F125.5', '本书适用于国际合作研究者', '精装', '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1168, '978-7-5228-1292-2', '中国经济学', '李雪松主编', '社会科学文献出版社', 128, '中国经济', NULL, '2022年第4辑（总第4辑）', NULL, NULL, NULL, '304页', '24cm', NULL, '中国社会科学院数量经济与技术经济研究所主办 J集刊', '本书收录了《地方财政压力与企业杠杆率——以财政“省直管县”改革为自然实验的研究》《通向智能化低碳之路：智慧城市的减碳效应》《数字普惠金融与中国家庭信贷约束——基于微观数据的实证分析》《货币政策、融资约束与企业绿色技术创新》等文章。', '文集', 'F12-53', '本书适用于经济学研究人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1169, '978-7-5037-9970-9', '中国居民收支与生活状况报告', '国家统计局住户调查司[编著]', '中国统计出版社有限公司', 198, '居民家庭收支调查', NULL, '2022', NULL, NULL, NULL, '426页', '30cm', NULL, NULL, '本书共分为八章。第一章介绍全国住户收支与生活状况调查的调查内容和方法。第二章介绍十八大以来我国居民收支和生活状况发展成就。第三章介绍近年来尤其是2021年全国及分城乡居民收入增长情况、增长特点和收入结构变化等。第四章介绍近年来居民收入分配状况和居民收入差距等。第五章介绍2021年居民消费支出增长情况、增长特点、结构变化等。第六章介绍2021年居民生活状况，包括居住条件、生活设施、耐用消费品拥有情况、社区基础设施和基本公共服务等。第七章是各省(区、市)针对2021年居民收支与生活改善情况的重点分析。第八章是', NULL, 'F126.2', '本书适用于居民家庭收支状况调查研究人员', '精装', '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1170, '978-7-5174-0837-6', '纪检监察干部扶贫手记', '本书编写组编写', '中国方正出版社', 48, '扶贫', NULL, NULL, NULL, NULL, NULL, '326页', '26cm', NULL, NULL, '本书以工作手记的方式真实记录了纪检监察干部扎根深度贫困地区接续奋斗的感人故事。', '文集', 'F126-53', '本书适用于扶贫工作者', NULL, '2021.01', 'chi', NULL);
INSERT INTO `book` VALUES (1171, '978-7-5228-1104-8', '辽宁比较优势研究', '张本刚主编', '社会科学文献出版社·城市和绿色发展分社', 98, '区域经济发展', NULL, NULL, NULL, NULL, NULL, '378页', '24cm', NULL, NULL, '本书梳理了辽宁在区位、资源禀赋、基础设施、创新能力、产业发展、历史文化等方面的比较优势和重要地位，分析了辽宁在发挥比较优势上取得的成就和不足，提出辽宁在“十四五”时期及中长期进一步释放比较优势，推进全面振兴、全方位振兴的对策建议。', NULL, 'F127.31', '本书适用于区域经济发展研究人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1172, '978-7-5164-2547-3', '经济高质量发展的区域经验', '郑明亮，陈祥义著', '企业管理出版社', 68, '区域经济发展', NULL, NULL, NULL, NULL, NULL, '165页', '24cm', NULL, NULL, '本书立足经济高质量发展的内涵，以潍坊地区高质量发展面临的主要问题和典型做法为核心内容，构建了围绕高质量发展内涵的评价指标体系；选择国内外高质量发展的典型案例，对比分析了典型地区的经济发展过程、经验做法及发展模式。', NULL, 'F127.523', '本书适用于区域经济研究人员', NULL, '2022.10', 'chi', NULL);
INSERT INTO `book` VALUES (1173, '978-7-5037-9984-6', '长江经济带统计年鉴', '长江经济带发展统计监测协调领导小组办公室编', '中国统计出版社有限公司', 398, '长江经济带', NULL, '2022', NULL, NULL, NULL, '20,370页', '29cm', '1光盘', NULL, '本书通过数据分析、记载和反映长江经济带2021年经济、社会、资源环境、科技、文化等方面的发展，收录了综合，人口，国民经济核算，就业和工资，价格，人民生活，财政，资源和环境，能源，固定资产投资，对外经济贸易，农业，工业等章节。', '年鉴', 'F127.5-54', '本书适用于区域经济发展研究人员', '精装', '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1174, '978-7-5164-2747-7', '世行贷款贫困片区产业扶贫试点示范项目案例选编', '中国国际扶贫中心编', '企业管理出版社', 88, '世界银行', NULL, NULL, '四川篇', NULL, NULL, '153页', '23cm', NULL, NULL, '本书展示了四川省项目的22个案例，讲述了参与项目的干部群众在项目实施过程中奋斗和成长的故事。', NULL, 'F127.71', '本书适用于扶贫工作者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1175, '978-7-5136-7193-4', '2023年重庆经济展望', '重庆市综合经济研究院，重庆市经济信息中心，重庆统筹城乡发展研究中心编著', '中国经济出版社', 198, '区域经济', NULL, NULL, NULL, 'CQAER智库', NULL, '522页', '29cm', NULL, '重庆市综合经济研究院 重庆市经济信息中心 重庆统筹城乡发展研究中心主办', '本书对2022年重庆经济运行进行了研究，并且通过区县信息系统等交流渠道，邀请市内部分中心城市和经济开发区有关部门共同研究，组织市内外有关经济专家和学者展开研讨，将成果结集出版。本书是社会各界了解国内外政治经济环境，把握重庆市宏观经济运行趋势和行业发展动态的重要载体和窗口，具有重要的决策参考价值。', NULL, 'F127.719', '本书适用于区域经济预测人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1176, '978-7-5096-8734-5', '陆海交汇：全球史视野下的中国社会经济变迁', '魏明孔主编', '经济管理出版社', 98, '中国经济史', '第七届全国经济史学博士后论坛论文精选集', NULL, NULL, NULL, NULL, '218页', '24cm', NULL, '中国社会科学院博士后管委会出版资助 掌尚文化', '本书收录了12篇论文，内容涉及：传统中国的北方(尤其是华北)内部的经济、社会发展状况；唐宋以来北方与南方以及东亚等地的经济联系；运河、海洋在北方经济发展中的作用；对中国北方的“港口－腹地”理论进行新的阐释。', '文集', 'F129-53', '本书适用于中国经济发展研究人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1177, '978-7-5037-9964-8', '国民经济核算', '蒋萍主编', '中国统计出版社有限公司', 69, '国民经济核算', NULL, NULL, NULL, NULL, NULL, '349页', '26cm', NULL, '“十四五”全国统计规划教材', '本书分为基础篇、全套账户序列篇、综合经济账户篇三篇。基础篇讲述国民经济综合核算的基本原理、核算范围、记账规则、分类方法等基础知识；全套账户序列篇讲述全套账户序列对应的核算内容；综合经济账户篇讲述综合经济账户的设置依据、账户的合并与综合表述、矩阵形式的账户平衡表等。', NULL, 'F222.33', '本书适用于国民经济核算相关人员', NULL, '2022.10', 'chi', NULL);
INSERT INTO `book` VALUES (1178, '978-7-115-60126-1', '经济数学', '张天德，叶宏，孙钦福主编', '人民邮电出版社', 49.8, '经济数学', '概率论与数理统计', NULL, NULL, NULL, NULL, '200页', '26cm', NULL, '名师名校新形态通识教育系列教材 山东大学数学学院新形态系列教材 人邮教育', '本书共7章，内容包括随机事件与概率、随机变量及其分布、多维随机变量及其分布、数字特征与极限定理、统计量及其分布、统计推断、相关与回归分析。', '教材', 'F224.0', '本书适用于高等学校经济和管理类专业学生', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1179, '978-7-03-073441-9', '当代会计评论', NULL, '科学出版社', 98, '会计学', NULL, '2022年第15卷第2辑（总第38辑）', NULL, NULL, NULL, '170页', '26cm', NULL, '中文社会科学引文索引（CSSCI）来源集刊 教育部人文社会科学重点研究基地厦门大学会计发展研究中心主办', '本书制度与文献回顾部分包括两篇文章，分别是关于中小股与公司治理作用发挥、文本分析研究，论文在综述各自话题制度特征与变迁、研究文献的基础上，讨论了可能的、新的研究视角和研究机会。研究论文部分包括4篇论文，分别涉及：高管内部薪酬差距与股价崩盘风险、涉税专业服务机构与企业实际税负、国家审计能否抑制地方国有企业过度金融化、CFO事务所经历与公司债务融资成本。本期的评论性论文刊发的是关于数智时代环境改变对财务与会计的可能冲击。', '丛刊', 'F230-55', '本书适用于会计学相关专业师生及从业者', NULL, '2022.10', 'chi', NULL);
INSERT INTO `book` VALUES (1180, '978-7-5095-6434-9', '财务会计', '熊筱燕，向有才，廖浪涛主编', '中国财政经济出版社', 58, '财务会计', NULL, NULL, NULL, '国家一流本科专业会计学建设丛书', NULL, '322页', '26cm', NULL, NULL, '本书共分13章，分别对货币资金、应收款项、存货、固定资产、无形资产、长期股权投资、资产减值、流动负债、非流动负债、所有者权益等内容进行了讲解。', '教材', 'F234.4', '本书适用于会计学、财务管理、金融学等专业的本科生和研究生', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1181, '978-7-5136-1000-1', '人力资本概论', '徐本华著', '中国经济出版社', 68, '人力资本', NULL, NULL, NULL, NULL, NULL, '155页', '24cm', NULL, NULL, '本书围绕人力资本这一中心命题，就关系企业发展和经济增长的一系列重要问题，从人力资本理论综述、人力资本产权的界定与分析、人力资本出资制度的理论分析、人力资本的崛起与企业制度变迁、人力资本参与企业收益分配和经济增长中的人力资本思想等多个方面作了探究。', NULL, 'F241', '本书适用于经济学研究人员', NULL, '2022.08', 'chi', NULL);
INSERT INTO `book` VALUES (1182, '978-7-5223-1729-8', '员工跨界行为的形成及其对创新绩效的作用机制研究', '张征著', '中国财政经济出版社', 50, '职工', NULL, NULL, NULL, NULL, NULL, '223页', '24cm', NULL, '教育部人文社会科学研究规划项目“员工跨界行为的前因组态及其对创新绩效的双刃剑效应研究” 山西省“1331工程”工商管理一流学科建设项目的资助和支持', '本书立足于中国组织情境，基于资源保存理论，分别构建员工跨界行为的形成机理模型与员工跨界行为对创新绩效的影响机制模型，进而通过实证研究揭示员I跨界行为的形成机制、员工跨界行为对创新绩效作用的内在过程和边界条件，从而拓展和丰富已有研究内容，推进员工跨界行为研究的进程，并为员工跨界行为的管理提供理论指导和借鉴。', NULL, 'F241', '本书适用于企业绩效研究者', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1183, '978-7-5230-0077-9', '中国劳动统计年鉴', '国家统计局人口和就业统计司，人力资源和社会保障部规划财务司编', '中国统计出版社有限公司', 260, '劳动经济', NULL, '2022', NULL, NULL, NULL, '439页', '30cm', '1光盘', NULL, '本书共分为13个部分：综合；就业与失业；城镇单位就业人员和工资总额；国有单位就业人员和工资总额；城镇集体单位就业人员和工资总额；其他单位就业人员和工资总额；职业培训与技能鉴定；劳动关系；社会保障；工会工作；香港资料；澳门资料；台湾资料。', '年鉴', 'F249.2-54', '本书适用于统计工作者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1184, '978-7-5165-2880-8', '物流基础', '罗来根，赵斌，陈丽主编', '航空工业出版社', 89, '物流', NULL, NULL, NULL, NULL, NULL, '325页', '26cm', NULL, NULL, '本书分十个项目，内容包括：物流概述、物流职业基础、物流法律法规、仓储管理、运输、物流包装、装卸搬运、流通加工、配送、物流信息技术。', NULL, 'F25', '本书适用于现代物流管理、物流工程技术、采购与供应管理等专业师生', NULL, '2022.01', 'chi', NULL);
INSERT INTO `book` VALUES (1185, '978-7-121-44505-7', '转型破局', 'CIO百人会编著', '电子工业出版社', 100, '产业经济', '中国十大行业数字化转型践行录', NULL, NULL, 'CIO百人会系列丛书', NULL, '17,270页', '24cm', NULL, NULL, '本书介绍了数字化转型的技术应用、商业模式、工具体系以及实践案例。一方面从新兴技术、转型内容到催生新模式；另一方面从数字化转型挑战、趋势到十大行业转型实践，成功推进数字化建设。剖析了行业转型面临的困难、挑战，并提供了值得借鉴和擦考的最佳实践。', NULL, 'F269.2', '本书适用于产业经济研究者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1186, '978-7-5136-7078-4', '企业价值与资产评估', '李贤主编', '中国经济出版社', 69, '企业', NULL, NULL, NULL, NULL, NULL, '299页', '24cm', NULL, NULL, '本书基于当下时代背景，将企业价值和资产评估的相关理论与方法进行了梳理与总结。全书共分为十一章，其中第一至四章对企业价值与资产评估进行概述，并详细介绍了价值评估的理论基础、企业资产评估程序以及在估值中需要考虑的因素。第五至九章对资产评估方法进行了概述，并系统介绍了PE、PB和PS等相对估值法以及DDM与EVA、FCFF与FCFE等绝对估值法。第十至十一章提供了绝对估值法和相对估值法的具体案例，供读者分析与实践。', NULL, 'F270', '本书适用于高等院校财经类研究生和高年级本科生；资产评估和证券业咨询等行业内人员', NULL, '2022.08', 'chi', NULL);
INSERT INTO `book` VALUES (1187, '978-7-5164-2552-7', '企业诚信体系建设研究', '阳芳，李铁国著', '企业管理出版社', 78, '企业管理', NULL, NULL, NULL, NULL, NULL, '207页', '24cm', NULL, '诚信之金', '本书以问题为导向，运用博弈论、利益相关者理论、交易成本理论、道德资本理论等，综合考量诚信与企业诚信的本质，紧扣目前我国企业存在的诚信缺失现象，通过实证研究的方式，挖掘诚信缺失的内在深层次的原因和内在矛盾的本质，试图解答当前现实社会企业诚信建设中的困惑、犹豫、无所适从甚至无能为力等问题。', NULL, 'F270', '本书适用于企业管理研究者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1188, '978-7-5197-7303-8', '解码股权战略', '鲍乐东著', '法律出版社', 76, '股权管理', '一体两翼三步法', NULL, NULL, NULL, NULL, '288页', '23cm', NULL, '独角兽', '本书以“股权战略”作为着眼点，整理了国内外明星企业超百个案例，涵盖了传统制造业、服务业、新兴科技型企业、互联网公司等多个行业。', NULL, 'F271.2', '本书适用于企业管理人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1189, '978-7-100-21837-5', '社会企业的政策创新与扩散', '邓国胜，关珊珊，成鸿庚等著', '商务印书馆', 68, '企业管理', NULL, NULL, NULL, NULL, NULL, '315页', '21cm', NULL, NULL, '本书分别从国内外社会企业发展现状、我国社会企业政策创新与扩散现状、我国社会企业政策创新与扩散的动力机制与影响因素、我国社会企业的营商环境评价等方面进行了深入研究。书中通过对社会企业的社会性、市场化两大特征的深入分析探讨，为社会企业身份识别提供了新思路，为社会企业政策创新与传播扩散提供了新路径。此外，本书建立了社会企业市场营商环境评价分析体系，从政务环境、市场环境、金融环境、法治环境、创新环境、慈善环境等维度构建评价指标，为社会企业在地方的扩散认定提供新的参考。', NULL, 'F272', '本书适用于企业管理人员', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1190, '978-7-5210-1019-0', '企业应急管理基础', '黄盛初主编', '海洋出版社', 89, '企业管理', NULL, NULL, NULL, NULL, NULL, '12,313页', '26cm', NULL, '全国企业应急管理人才培训辅导教材', '本书主要从宏观的角度对应急管理的基本问题进行介绍和阐述，内容包括我国应急管理的体制、机制和法制，应急管理科技、产业和装备及其发展状况，应急救援力量，安全与应急管理文化等。', '教材', 'F272', '本书适用于企业应急管理师及相关培训人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1191, '978-7-5210-1020-6', '企业应急管理能力', '黄盛初主编', '海洋出版社', 89, '企业管理', NULL, NULL, NULL, NULL, NULL, '12,413页', '26cm', NULL, '全国企业应急管理人才培训辅导教材', '本书从操作层面对具体的应急管理实践进行介绍和阐述，内容包括应急管理过程、企业安全风险管理、重点行业领域安全应急管理、自然灾害的防治、城市安全风险管理、应急预案的编制与演练、应急处置、应急保障、安全与应急管理信息化建设、应急管理专业能力建设等。', '教材', 'F272', '本书适用于企业应急管理师', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1192, '978-7-5220-1722-8', '改变游戏', '(澳)格雷厄姆·克里斯蒂(Graham Christie)，(澳)迈克尔·弗林斯(Michael Vullings)著', '中国金融出版社', 42, '企业管理', '一本引领商业转型的工具书', NULL, NULL, NULL, NULL, '136页', '24cm', NULL, 'WILEY', '本书是一本超越特定技术和数字趋势的工具书，它为各个部门的管理者提供了一个战略性高、可操作性强的转型变革框架。作为涉及引领变革重要内、外部方面的实用“路线图”，本书采用直接思路，提供整体方法，借助相关概念和图解进行说明，并通过杰出领导者分享的见解和现实案例使文章更加生动。', NULL, 'F272', '本书适用于企业管理人员', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1193, '978-7-121-44641-2', '数字化增长引擎', '安秋明著', '电子工业出版社', 78, '企业管理', NULL, NULL, NULL, NULL, NULL, '294页', '24cm', NULL, NULL, '本书帮助企业从生态视角和演进视角，观察自身的外部，其所在的行业、产业，区域、国家甚至全球的发展趋势；用一套严谨的思维逻辑解构自身的内部，从上到下逐层分解，从下往上逐层支撑的逻辑分析企业的愿景、使命、价值观、战略、业务设计、技术能力、组织体系、团队人才等，通过“数字化增长引擎”模型理解企业在数字化时代如何增长、如何变革、如何转型。', NULL, 'F272.7', '本书适用于企业管理者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1194, '978-7-121-44540-8', 'SaaS产品增长笔记', '吴平辉著', '电子工业出版社', 89, '企业管理', '从0到100，PLG落地实战', NULL, NULL, NULL, NULL, '10,273页', '24cm', NULL, '博文视点', '本书共有5个部分，以增长三阶段为核心框架展开，包括用户研究、产品设计、集客营销、客户成功、用户运营、SaaS出海等内容。', NULL, 'F272.7-39', '本书适用于SaaS创业者、增长负责人、产品负责人、市场负责人、运营负责人和企业服务赛道投资人', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1195, '978-7-115-60060-8', '企业数字化实战指南', '高福春著', '人民邮电出版社', 129.8, '企业管理', 'CIO职业手册', NULL, NULL, NULL, NULL, '11,437页', '26cm', NULL, '产业数字化系列 异步图书 异步社区', '本书以企业的视角，从企业IT服务价值链出发，聚焦企业信息技术应用中的普遍与共性问题，基于场景目标与要求的分析，结合现有的IT应用方法框架与标准规范，研究提出具体场景的工作方法框架、应遵循的基本原则，基于对需求的分析，从而提出可供参考的解决方案与建议。', '指南', 'F272.7-62', '本书适用于“两化融合”关键用户、数字化规划与设计人员、数字化建设与实施人员、信息系统运行维护人员、IT服务支持人员、信息安全人员', NULL, '2023.02', 'chi', NULL);
INSERT INTO `book` VALUES (1196, '978-7-115-60266-4', '企业数字化转型人工智能技术落地指南', '金玮著', '人民邮电出版社', 79.8, '人工智能', NULL, NULL, NULL, NULL, NULL, '212页', '26cm', NULL, '产业数字化系列 异步图书 异步社区', '本书首先从企业支持人工智能技术发展的视角出发，讲解如何认识数字化转型、数字化转型战略主要包含的方面、支持数字化转型的应用体系设计方法等。其次，承接对应用体系设计的讨论，详细讲解如何研发并落地实施定制化的人工智能应用，内容包括对人工智能应用研发的整体认识，业务需求和业务知识的梳理，数据收集、数据处理与建模分析，模型上线运行和持续迭代等。最后，基于相关讨论，分析人工智能应用研发的特点，探讨人工智能领域在应用研发模式与策略、专业技能人才培养等方面可能产生的一些新变化，以及领域内各种业态可能出现的发展趋势。', '指南', 'F272.7-62', '本书适用于企业技术总监、规划与计划人员、人工智能应用研发管理者与研发技术人员，以及对企业数字化转型和人工智能技术落地感兴趣的读者', NULL, '2023.02', 'chi', NULL);
INSERT INTO `book` VALUES (1197, '978-7-121-44599-6', '打破部门壁垒', '(美)帕特里克·兰西奥尼(Patrick Lencioni)著', '电子工业出版社', 68, '企业管理', '共担责任，共创卓越', NULL, NULL, NULL, NULL, '197页', '21cm', NULL, '世纪波 WILEY', '本书前半部分通过一个寓言故事，再现了不同公司内部激烈的部门冲突，后半部分为理论与总结，介绍了解决“部门壁垒”问题的工具模型及方法、案例，让读者在真正解决问题时找到抓手并取得成功。', NULL, 'F272.9', '本书适用于企业管理人员', '精装', '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1198, '978-7-111-71701-0', '人效管理', '穆胜著', '机械工业出版社', 79, '企业管理', NULL, NULL, NULL, NULL, NULL, '15,208页', '23cm', NULL, '穆胜人效三部曲之二 穆胜作品', '本书分为上中下三篇。上篇“人效管理理念”，介绍了人效对于企业经营的重要意义，明确了关注人效是驱动企业的重要抓手，赋予人效管理以经营意义，明确了人力资源专业应该且可能创造经营价值。中篇“人效管理方法”，介绍了人效管理的“数据诊断－战略制定－人效规划－队伍规划－职能规划－人效管控”六部曲方法论。下篇“人效管理实战”，介绍了企业如何将人效管理的理念和方法运用到实践中。', NULL, 'F272.92', '本书适用于企业人力资源管理人员', '精装', '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1199, '978-7-115-60526-9', '全过程员工关系管理实战案例', '曹锋，韦祎，赵秀荣编著', '人民邮电出版社', 69.8, '企业管理', NULL, NULL, NULL, NULL, NULL, '214页', '24cm', NULL, '普华经管 普华文化', '本书对企业员工关系管理中经常发生的纠纷进行提炼、总结，对案例进行剖析。具体而言，从入职管理、在职管理、离职管理、特殊群体管理、多元化用工管理及情境管理六个方面收集了HR遇到的50多个问题，通过“管理场景”“问题分析”“管理箴言”的形式帮助读者厘清相关案例的法理，让读者知道如何处理相应的问题。', NULL, 'F272.92', '本书适用于企业人力资源管理工作者、工会干部、劳动关系协调员及高等院校相关专业的师生', NULL, '2023.02', 'chi', NULL);
INSERT INTO `book` VALUES (1200, '978-7-121-44742-6', '业务领导者的人才管理', '吴景辉，朱翔著', '电子工业出版社', 69, '企业管理', NULL, NULL, NULL, NULL, NULL, '14,253页', '24cm', NULL, '世纪波', '本书通过重启定位、凝聚共识、知人善任、委责赋能和激励人心这5个核心策略，帮助业务领导者踏上成事、成人、成己的“康庄大道”。', NULL, 'F272.92', '本书适用于企业管理者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1201, '978-7-5130-8476-5', '组织激励与胜任力对中层管理者工作绩效的作用研究', '王平，孙继红著', '知识产权出版社有限责任公司', 78, '企业', NULL, NULL, NULL, NULL, NULL, '233页', '24cm', NULL, '中国教育科学研究院中央级公益性科研院所基本科研业务费专项项目“高等教育满意度理论模型研究”资助', '本书从管理学、组织行为学的视角，聚焦企业中层管理者，将组织激励和胜任力引入工作绩效的研究框架，从外部(组织)和内部(个体)两个层面对中层管理者工作绩效的影响机制进行探讨。', NULL, 'F272.92', '本书适用于中层管理者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1202, '978-7-5226-0973-7', '薪酬激励量化实操全案', '冯涛著', '中国水利水电出版社', 88, '企业管理', NULL, NULL, NULL, NULL, NULL, '279页', '24cm', NULL, '薪酬设计和绩效考核体系必备全书', '本书介绍了薪酬激励量化实操的流程、步骤、方法及注意事项，每一章都分别引用了不同的实际案例，并配合薪酬体系设计的实操步骤进行讲解。', NULL, 'F272.923', '本书适用于薪酬管理从业人员、人力资源管理实务入门者、企业管理者及各高校人力资源管理专业学生', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1203, '978-7-115-60336-4', 'AI战略', '(美)拉希德·哈克(Rashed Haq)著', '人民邮电出版社', 99.8, '人工智能', '企业的数字化转型之路', NULL, NULL, NULL, NULL, '224页', '24cm', NULL, 'WILEY 信通社区', '本书共分为五部分。第一部分讨论了人工智能的不同类型，并建立了它们如何工作的知识框架。第二部分介绍了人工智能在银行业、制造业等各个行业中的用例。第三部分介绍如何定义和实现企业级人工智能战略，以及如何在这种战略下实现一个成功的人工智能项目。第四部分用一个例子对人工智能的架构、多种应用的技术模式及人工智能模型如何工作进行探讨。第五部分聚焦人工智能的未来及它对社会和工作的意义。', NULL, 'F272-39', '本书适用于人工智能的从业者、数据分析师，以及想要利用人工智能技术解决商业问题的企业管理者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1204, '978-7-5217-5112-3', '富足', '吴军著', '中信出版集团股份有限公司', 59, '企业管理', '内有智慧，外有方法，成为富足的人', NULL, NULL, NULL, NULL, '10,331页', '21cm', NULL, '先见', '本书从知识、能力和智慧三个维度出发，分为六个部分，对应构建信心的六个方面——看清规律、转变思维、提高认知、更新经验、刻意练习、创新精进，帮助管理者跳出思维框架，怀揣信心看得更远，走得更高。', '通俗读物', 'F272-49', '本书适用于企业管理人员', '精装', '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1205, '978-7-115-60432-3', '价值创新', '刘全锋著', '人民邮电出版社', 69.8, '企业创新', '从机会洞察到商业变现', NULL, NULL, NULL, NULL, '228页', '24cm', NULL, '普华文化 普华经管', '本书结合华为等标杆企业的创新实践对创新的理念和方法进行了总结和提炼。全书内容分为三个部分，即创新理念、创新实践方法、组织与文化，分别阐述了企业应该树立什么样的创新理念来指引创新、具体的创新工作应该如何开展、什么样的组织与文化才能支撑创新活动的持续有效开展。此外，书中还穿插了30多家优秀企业的创新实践案例，有助于读者结合自身企业的具体情况运用相关的创新理念和实践方法。', NULL, 'F273.1', '本书适用于各行各业的企业经营管理人员及创新创业人员', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1206, '978-7-5164-2741-5', '开创新品类', '玄机著', '企业管理出版社', 66, '企业创新', NULL, NULL, NULL, '新品类咨询专家玄机系列丛书', NULL, '14,236页', '24cm', NULL, NULL, '本书以品类竞争为导向，使用专业化又让中小企业容易理解的语言，在书中列举数十种开创新品类的方法，指出最佳方式是运用“豪威尔环”。', NULL, 'F273.1', '本书适用于企业创新研究者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1207, '978-7-121-44784-6', '产品数据管理原理与应用', '安晶，徐友武主编', '电子工业出版社', 59, '企业', '基于Teamcenter平台', NULL, NULL, NULL, '2版', '242页', '26cm', NULL, '教育部－西门子产学合作专业综合改革项目系列教材', '本书共8章，主要内容包括：PDM概述、电子仓库与文档管理、产品结构与配置管理、工作流与过程管理、零件分类管理、PDM系统集成、项目管理、系统实施定制。', '教材', 'F273.2', '本书适用于高等学校学生本科院校机械工程、智能制造等相关专业学生', NULL, '2023.02', 'chi', NULL);
INSERT INTO `book` VALUES (1208, '978-7-5638-3423-5', '基于多源数据的品牌价值评价方法研究', '段琦[等]著', '首都经济贸易大学出版社', 42, '品牌', NULL, NULL, NULL, NULL, NULL, '155页', '24cm', NULL, NULL, '本书依托当前品牌价值评价工作实践，以提高我国品牌评价技术水平为目标，结合当前品牌价值评价工作中表现出的问题，以国际主流方法为借鉴，研究第三方品牌价值评价方法，为我国品牌价值评价工作的可持续发展及自主品牌建设的深入开展提供理论依据及技术支撑。', NULL, 'F273.2', '本书适用于企业管理人员', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1209, '978-7-121-44625-2', '运营之上', '徐全安著', '电子工业出版社', 79, '互联网络', '互联网业务的全局运营方法论与实践', NULL, NULL, NULL, '2版', '18,256页', '24cm', NULL, '博文视点', '本书共3篇。第1篇介绍如何把握运营的方向、发挥运营的核心价值，以及如何理解和搭建运营体系，同时介绍全局性运营人员需要具备的“抓手”思维、“组织”视角和“规划”逻辑；第2篇介绍高级运营人员拿来就能用的全局性工作方法论，包括“运营的总抓手：目标”“运营的业务抓手：魔法公式”“运营的管理抓手：管理九宫格”“运营的决策抓手：数据驱动”；第3篇复盘呈现一个大型项目从0到1的实操过程，带领读者身临其境地感受运营的价值、全局运营的思维和多种方法论的综合应用。', NULL, 'F273-39', '本书适用于企业运营人员', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1210, '978-7-5142-3599-9', '好懂秒懂的财务思维课', '郝旭烈著', '文化发展出版社', 58, '财务管理', NULL, NULL, NULL, NULL, NULL, '14,271页', '23cm', NULL, NULL, '本书共24课，内容涉及财务管理的三大检视指针与经营关键；三个重点让你掌握赚钱与否的关键；用两种分类检视如何设定好收入的目标；三种方式降低企业经营的风险；透彻分析三元素两公式，建立起快速赚钱系统等。', '通俗读物', 'F275-49', '本书适用于大众读者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1211, '978-7-115-60675-4', '企业会计准则实务应用精解', '企业会计准则编审委员会编著', '人民邮电出版社', 129.8, '企业会计', '会计科目使用+经济业务处理+会计报表编制', NULL, NULL, NULL, NULL, '532页', '26cm', NULL, '企业会计准则培训用书', '本书分别从会计科目使用、经济业务处理和会计报表编制3个方面阐述了企业会计准则的整体要求。经济业务处理是主体部分，针对每一笔业务，“业务概述”部分讲清经济业务的来龙去脉；“账务处理”部分依据企业会计准则的要求，讲解对业务进行会计处理的具体要求；“案例解析”部分则是以实务案例为依托，帮助读者提高处理实际问题的能力。', NULL, 'F279.23', '本书适用于广大会计工作者', NULL, '2023.02', 'chi', NULL);
INSERT INTO `book` VALUES (1212, '978-7-5136-7126-2', '西部典型企业转型案例研究', '张红芳，褚志远，张丹妍等著', '中国经济出版社', 88, '企业升级', NULL, NULL, NULL, NULL, NULL, '244页', '24cm', NULL, '中国西部管理案例研究 第三辑 陕西省2022年经济学科建设项目', '本书以多家不同类型企业的战略发展为背景，采用规范的案例编写方法，通过数个不同主题的管理案例，描绘了近年来相关企业的发展状况，涉及企业的价值网络重组、混合所有制改革、企业文化构建、战略定位变化、劳动关系处理、制造服务化转型等方面，并以此为基础，将研究成果开发为具有一定理论普适性并与西部特殊性相结合的管理案例，对于分析和研究西部企业的持续发展、转型发展有着重要的理论和实践意义。', NULL, 'F279.232.5', '本书适用于企业管理人员', NULL, '2022.10', 'chi', NULL);
INSERT INTO `book` VALUES (1213, '978-7-5230-0025-0', '全国企业创新调查年鉴', '国家统计局社会科技和文化产业统计司编', '中国统计出版社', 380, '企业创新', NULL, '2022', NULL, NULL, NULL, '318页', '30cm', '1光盘', NULL, '本书收录了我国企业创新调查最新年度(2021年)的主要统计数据。全书共分为十二个部分：第一部分为企业创新主要指标情况，第二至第十部分按各行业规模(限额)以上企业创新调查主要指标和主要分组进行了汇总分列，第十一部分为规模以下企业创新调查的主要指标和主要分组情况，第十二部分为国际比较情况。', '年鉴', 'F279.23-54', '本书适用于企业创新研究人员', '精装', '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1214, '978-7-115-60676-1', '企业会计准则及应用指南实务详解', '企业会计准则编审委员会编著', '人民邮电出版社', 129.8, '企业会计', '2023年版', NULL, NULL, NULL, NULL, '10,572页', '26cm', NULL, '企业会计准则培训用书', '本书以《企业会计准则》为依据，对现行的40项具体准则，从逻辑图解、会计准则条文、解释与应用指南和经典案例详解4个方面进行了解读。', '指南', 'F279.23-62', '本书适用于广大会计从业人员', NULL, '2023.02', 'chi', NULL);
INSERT INTO `book` VALUES (1215, '978-7-121-44782-2', '未来栖息地', '腾讯研究院编著', '电子工业出版社', 85, '网络公司', NULL, NULL, NULL, NULL, NULL, '16,255页', '21cm', NULL, NULL, '本书基于以上立意和2022年腾讯科技创新周十大主题，提出科技发展与人文交叉领域几大思考，探索新科技推动下的商业变革与社会价值创新路径。', NULL, 'F279.244.4', '本书适用于网络公司企业管理人员', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1216, '978-7-5230-0047-2', '中国火炬统计年鉴', '科学技术部火炬高技术产业开发中心编', '中国统计出版社有限公司', 180, '高技术产业', NULL, '2022', NULL, NULL, NULL, '166页', '30cm', NULL, NULL, '本书内容共分十一个部分。第一部分为国家高新技术产业开发区内企业的情况；第二部分为全国高新技术企业的情况；第三部分为科技企业孵化器的情况；第四部分为众创空间的情况；第五部分为国家大学科技园的发展情况；第六部分为火炬软件产业基地的发展情况；第七部分为火炬特色产业基地的发展情况；第八部分为创新型产业集群的发展情况；第九部分为全国技术市场发展情况；第十部分为国家技术转移机构发展情况；第十一部分为主要指标解释。', '年鉴', 'F279.244.4-54', '本书适用于高技术产业研究人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1217, '978-7-5615-8851-2', '媒体报道与资本市场', '罗进辉著', '厦门大学出版社', 52, '媒体(新闻)', '讲述中国的公司治理故事', NULL, NULL, NULL, NULL, '208页', '26cm', NULL, NULL, '本书共分十章，主要包括媒体报道与股价信息含量、媒体报道与资本成本、媒体报道与股价崩盘风险、媒体报道与代理成本、媒体报道与高管薪酬、媒体报道与现金持有等。', NULL, 'F279.246', '本书适用于企业管理研究人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1218, '978-7-5228-1312-7', '中国企业海外发展报告', '张新民，王分棉，杨道广主编', '社会科学文献出版社·经济与管理分社', 169, '海外企业', NULL, '2022', NULL, '皮书系列', NULL, '382页', '24cm', NULL, '企业海外发展蓝皮书 No.5 2022版 北京市社会科学基金决策咨询重点项目“中国企业海外发展报告2022”的研究成果 教育部哲学社会科学发展报告项目“中国企业海外发展报告”的研究成果 对外经济贸易大学北京企业国际化经营研究基地的研究成果', '本书首先对中国企业海外发展的最新状况进行了总体分析与评价；其次剖析了入围2021年“世界500强”和“最具价值全球品牌100强”的中国企业，以及中国上市公司海外投资的最新态势；再次围绕北京自贸区与京企“走出去”综合服务平台对企业海外发展的影响进行了专题研究；最后对4家典型企业的国际化发展进行了案例研究，致力于跟踪中国企业在海外市场的最新发展轨迹，并重点讨论了北京自贸区对企业海外发展的影响这一热点问题。', NULL, 'F279.247', '本书适用于企业发展研究人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1219, '978-7-5582-5634-9', '品读武汉老字号', '陈国方，周德钧主编', '武汉出版社', 128, '老字号', NULL, NULL, NULL, NULL, NULL, '10,310页', '24cm', NULL, NULL, '本书选取被认定为武汉市老字号38家，分别从手工业制造业类、饮食类、中药类、生活服务类四个大类进行展开，主要涉及这些老字号的形成、发展与认定，商业文化的传承，城市记忆的构建等多方面内容。', NULL, 'F279.276.31', '本书适用于大众读者', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1220, '978-7-121-44552-1', '项目风险管理实战', '(美)大卫·希尔森(David Hillson)，(美)彼得·西蒙(Peter Simon)著', '电子工业出版社', 78, '工程项目管理', 'ATOM方法论', NULL, NULL, NULL, NULL, '12,275页', '24cm', NULL, '项目管理核心资源库 世纪波', '本书依据最佳实践指南和标准，为项目风险管理提供了一种可以在项目中实际应用的方法，就如何正确、高效和有效地管理风险提出了建议，并呈现为简单的几个步骤，以告诉你下一步应该做什么。对于每个步骤，都提供了建议、提示和技巧，并告诉你如何最大限度地利用风险管理过程，使威胁最小化，机会最大化。', NULL, 'F284', '本书适用于项目经理、项目团队及风险管理实践者', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1221, '978-7-5218-3162-7', '现代西方城市经济理论', '郑长德，钟海燕主编', '经济科学出版社', 98, '城市经济学', NULL, NULL, NULL, NULL, NULL, '423页', '24cm', NULL, '项目依托：文化名家暨“四个一批”人才计划项目“新发展理念与民族地区经济社会发展研究” 四川省研究生教育改革创新项目“民族高校应用经济学研究生创新人才培养模式改革研究” ESP', '本书介绍了西方城市经济学的基本理论。全书把西方城市经济理论划分为宏观城市经济理论和微观城市经济理论两大部分，前半部分介绍的主要是微观城市经济理论，包括城市的起源和成长、城市区位论、城市规模经济理论、城市空间结构理论、城市住房市场理论和城市劳动力市场理论等；后半部份介绍的主要是宏观城市经济理论，包括城市经济增长理论、城市体系理论、城市群理论、城市集聚经济理论、城市竞争力理论、城市与区域发展理论、城市化理论、城市公共经济理论和城市环境经济理论。', NULL, 'F290', '本书适用于区域经济学、城市经济学、经济地理学等专业的高年级本科生和研究生；城市经济、城市管理、城市规划等有关专业人员的教学、科研和管理人员', NULL, '2022.10', 'chi', NULL);
INSERT INTO `book` VALUES (1222, '978-7-5189-9008-5', '城市蔓延成本的构成与测算', '张景奇，纪秀娟著', '科学技术文献出版社', 48, '城市群', '理论方法与实证分析', NULL, NULL, NULL, NULL, '177页', '24cm', NULL, '国家社会科学基金青年项目结项成果', '本书更加关注城市蔓延的成本，希望在国外研究经验的基础上，结合中国城市蔓延的特征，建立一套更为直接、易被国内城市重复检验的城市蔓延成本测量指标体系，探明中国式城市蔓延的生成机制。从成本对比角度，分析中国式城市蔓延在明知其危害的情况下，依然能够发展壮大的原因，探明其生成及演变规律。', NULL, 'F299.21', '本书适用于城市群发展研究人员', NULL, '2022.03', 'chi', NULL);
INSERT INTO `book` VALUES (1223, '978-7-313-22673-0', '住房对城市居民家庭资产配置行为的影响及其经济社会效应研究', '张传勇著', '上海交通大学出版社', 69, '住房', NULL, NULL, NULL, '上海交通大学人文社会科学成果文库', NULL, '206页', '24cm', NULL, '国家自然科学基金面上项目“区域房价分化与家庭资产配置：机制、识别及政策含义”成果', '本书探讨了不同房价风险背景下，中国居民家庭住房投资对家庭资产配置行为产生的影响及其经济和社会效应，如对家庭投资决策、消费支出结构、社会财富分配、地区经济增长差距、个人主观幸福感等方面产生的影响，旨在引导家庭合理投资、优化家庭消费结构、防止居民财富差距过大、促进地方社会经济的平稳健康发展。', NULL, 'F299.233.1', '本书适用于城市与住房研究领域的学者和从业者', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1224, '978-7-5115-7585-2', '基础设施投融资政策与实务', '吴有红著', '人民日报出版社', 38, '基础设施建设', NULL, NULL, NULL, NULL, NULL, '219页', '24cm', NULL, NULL, '本书梳理了基础设施领域补短板、适度超前开展基础设施投资、盘活存量资产等投融资政策，分析了项目资本金筹措问题及分级管理设想，阐释了PPP、XOD、“X+EPC”等基础设施投融资典型模式的应用要点以及城市基础设施、水利工程两大重点领域投融资模式设计思路，力求为读者快速了解基础设施投融资提供有益参考。', NULL, 'F299.24', '本书适用于基本建设投资研究人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1225, '978-7-5096-8817-5', '工程风险认知与治理', '王耀东著', '经济管理出版社', 98, '工程管理', NULL, NULL, NULL, '天津师范大学马克思主义学院学术文库', NULL, '222页', '24cm', NULL, '国家社会科学基金一般项目“基于公共安全视角的工程风险认知与治理机制研究” 掌尚文化', '本书从工程风险的一般概念入手，研究工程风险的认知和社会可接受性，探讨工程风险事前预防、事中协商和适应性治理、事后追责等全生命周期的治理机制和策略。', NULL, 'F40', '本书适用于风险管理研究者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1226, '978-7-5096-8758-1', '现代印刷企业管理', '王关义，李治堂，刘益等编著', '经济管理出版社', 78, '印刷工业', NULL, NULL, NULL, NULL, '4版', '298页', '26cm', NULL, '北京高等学校优质本科重点教材', '本书共分十章，内容包括：印刷企业管理概述、印刷企业组织与文化、印刷企业营销管理、印刷企业生产管理、印刷企业物流管理、印刷企业质量管理、印刷企业技术与设备管理、印刷企业财务管理等。', NULL, 'F407.846', '本书适用于本科生、研究生及各类管理者', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1227, '978-7-5012-6519-0', '实力的边疆', '蔺陆洲著', '世界知识出版社', 79, '航天工业', '从太空竞赛到航天合作', NULL, NULL, NULL, NULL, '259页', '24cm', NULL, NULL, '本书围绕太空竞争与航天合作的主轴，建构了一种国际航天战略博弈的理论框架，用以分析关系的变化和政策的调整。通过历史回顾和文献梳理提出了国际航天战略博弈的理论要素、范式和主要逻辑，形成了理论模型和推论，综合运用相关性分析的定量研究方法和比较分析的定性研究方法进行了假说检验，将商业航天识别为改变当前游戏规则的颠覆性变量。', NULL, 'F416.5', '本书适用于航空航天领域从业人员', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1228, '978-7-5164-2707-1', '中国石油', '《中国石油：价值引领型社会责任管理》编写组编著', '企业管理出版社', 59, NULL, '价值引领型社会责任管理', NULL, NULL, '中央企业社会责任管理之道丛书', NULL, '135页', '24cm', NULL, NULL, '本书系统梳理回顾中国石油取得社会责任管理实践成果背后的工作逻辑与方法，基于中国石油社会责任工作的实践和经验，梳理挖掘社会责任管理的特色和亮点，分析中国石油开展工作面临的机遇和挑战，并为未来更好地开展社会责任工作提供建议。', NULL, 'F426.22', '本书适用于社会责任研究者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1229, '978-7-5164-2706-4', '中国广核集团', '《中国广核集团：使命引领、透明驱动型社会责任管理》编写组编著', '企业管理出版社', 59, '核能工业', '使命引领、透明驱动型社会责任管理', NULL, NULL, '中央企业社会责任管理之道丛书', NULL, '147页', '24cm', NULL, NULL, '本书共五章，包括责任之源，自觉投身核电强国建设征程；责任之纲，打造NICER社会责任管理模式；责任之行，始终贯穿企业发展成长全过程；责任之成，建设世界一流清洁能源企业；责任之愿，推动企业创新发展行稳致远。', NULL, 'F426.23', '本书适用于核能工业企业社会责任研究者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1230, '978-7-5024-9338-7', '济钢年鉴', '《济钢年鉴》编纂委员会编', '冶金工业出版社', 128, '钢铁厂', NULL, '2021', NULL, NULL, NULL, '289页', '26cm', NULL, NULL, '本书记载了济钢集团有限公司2020年的生产经营建设、转型发展及党的建设等方面的情况。设有特载、会议报告、概况、大事记、合作交流、专项工作、专业管理、党群工作、生产经营、先进与荣誉、媒体看济钢、统计资料、附录13个栏目。', '年鉴', 'F426.31-54', '本书适用于各级领导干部', '精装', '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1231, '978-7-03-072409-0', '高端装备制造产业发展战略研究', '卢秉恒等著', '科学出版社', 128, '装备制造业', '2035', NULL, NULL, '中国工程院重大咨询项目成果文库', NULL, '163页', '24cm', NULL, '“十四五”时期国家重点出版物出版专项规划项目·重大出版工程规划', '本书开展高端装备制造产业面向2035年的战略研究。围绕空天海装备(航空、航天、海洋)、智能制造装备(高档数控机床、增材制造装备)、民生装备(农业、食品、纺织、医疗)，以及承压设备四个重点专题方向，分析了高端装备制造产业发展面临的新形势、新问题，以及高端装备制造产业核心工业基础现状及重点领域短板，提出了“十四五”高端装备制造产业发展的重点任务和建议，同时提出了面向2035年的技术和产业发展路线图，研究了高端装备制造产业区域布局及国际化发展战略，提出了相关政策建议。', NULL, 'F426.4', '本书适用于管理决策部门、产业发展研究人员、企业及社会公众', '精装', '2022.10', 'chi', NULL);
INSERT INTO `book` VALUES (1232, '978-7-03-070963-9', '海外电力工程投资中的国家风险管理', '李建平[等]著', '科学出版社', 198, '电力工程', NULL, NULL, NULL, '相关性范式下的风险管理理论与方法丛书', NULL, '285页', '24cm', NULL, NULL, '本书以“海外电力投资”为背景，以“国家风险”为对象，提出海外电力工程投资中国家风险分析、预测与缓释的系统框架，从更加系统全面的视角将国家风险作为一个内在互相关联、动态发展的有机整体进行分析，并在海外电力投资实践中对该理论进行扩展。', NULL, 'F426.61', '本书适用于风险管理从业者、海外投资参与者', '精装', '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1233, '978-7-5096-8456-6', '海南农垦天然橡胶生产经营模式及效率研究', '郑素芳著', '经济管理出版社', 68, '天然橡胶', NULL, NULL, NULL, NULL, NULL, '165页', '24cm', NULL, '福建省社科项目“国内国际双循环背景下福建省数字贸易发展机制与政策研究” 福建省科技厅项目“福建省数字经济发展：测度、国际比较与政策建议” 福建工程学院科研启动基金项目“跨境电商促进农民收入增长的实证研究” 经管出版', '本书试图构建一个橡胶产业经济学的理论框架，采用数据包络方法，对海南农垦的家庭长期承包、股份合作制、职工自营胶园这三种经营模式的技术效率进行测算和比较分析，以期找出一种技术效率相对较高又适合天然橡胶生产的最佳的经营模式。', NULL, 'F426.74', '本书适用于橡胶加工研究者', NULL, '2022.06', 'chi', NULL);
INSERT INTO `book` VALUES (1234, '978-7-5142-3855-6', '2022中国印刷产业创新发展报告', '常晓霞主编', '文化发展出版社', 78, '印刷工业', NULL, NULL, '技术趋势卷', NULL, NULL, '293页', '24cm', NULL, NULL, '本书由三部分内容组成。第一部分，主报告篇。本部分为主报告——《2022中国印刷产业创新发展报告》；第二部分，技术篇。本部分邀请了中国印刷产业各个领域的技术专家和学者，针对印前技术、印刷技术、印后技术及智能制造四个方面的主流技术发展现状及创新技术发展趋势，进行了梳理，旨在展现当前印刷技术发展全貌。第三部分，应用篇。本部分收录了近三年来部分具有实际指导意义的创新技术应用案例，对印刷企业实际应用创新技术具有一定的参考价值。', NULL, 'F426.84', '本书适用于印刷工业产业发展研究人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1235, '978-7-5096-8669-0', '卷烟工厂数字化转型建设指南', '陶智麟，廖柏华，曲振主编', '经济管理出版社', 78, '烟草加工厂', NULL, NULL, NULL, NULL, NULL, '220页', '24cm', NULL, '经管出版', '本书首先结合烟草行业和卷烟工厂的数字化现状进行了分析，提出卷烟工厂数字化转型的两大核心驱动力，即业务需求驱动和技术发展驱动，其次给出一整套卷烟工厂数字化转型的建设方案、实施路径及应用场景，最后为了保障数字化转型建设能够有效落地并且可持续开展，提出了建立组织人才支撑体系和转型结果量化评估体系的建议。', '指南', 'F426.89-39', '本书适用于卷烟制造企业管理者', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1236, '978-7-5164-2657-9', '中交集团', '《中交集团：愿景驱动型社会责任管理》编写组编著', '企业管理出版社', 59, '交通工程', '愿景驱动型社会责任管理', NULL, NULL, '中央企业社会责任管理之道丛书', NULL, '131页', '24cm', NULL, NULL, '本书共六章，讲述了中交集团始终秉承“让世界更畅通、让城市更宜居、让生活更美好”的企业愿景，在经营发展过程中，坚持以企业愿景作为对各利益相关方的承诺，通过明确方向、规划愿景、坚持实践以收获认可的社会责任管理路径，以企业愿景驱动“中交助梦”履责行动，以“中交助梦”履责行动助力企业愿景实现，通过绩效考核、持续改进、闭环管理，不断提升社会责任管理效能及在服.务国家战略、价值链，以及“一带一路”等方面的社会责任工作实践成效，努力落实“让出资人放心、客户满意、利益相关方信任、经营者安心、员工幸福、社会赞誉”，为共建共', NULL, 'F426.9', '本书适用于建筑企业社会责任研究者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1237, '978-7-112-28048-3', '工程保证担保业务实践与指南', '深圳市高新投集团有限公司编', '中国建筑工业出版社', 68, '建筑工程', NULL, NULL, NULL, NULL, NULL, '10,242页', '24cm', NULL, NULL, '本书通过总结提炼高新投保证担保业务的十余年成功经验，对工程保证业务的相关理论、实践探索等进行总结，有效传达高新投对工程施工行业现代化、规范化、节约化的思考和对行业规范经营良性健康发展的思考建议。', '指南', 'F426.9-62', '本书适用于建筑施工企业、工程担保从业人员、建筑施工主管部门等人士', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1238, '978-7-5226-0921-8', '湖北省工业文化遗产保护与利用研究', '李睿著', '中国水利水电出版社', 78, '工业', NULL, NULL, NULL, NULL, NULL, '156页', '26cm', NULL, '2018年教育部人文社会科学研究一般项目《工业遗产的文化价值挖掘与传承研究》 湖北省高等学校马克思主义中青年理论家培育计划（第五批）', '本书对湖北省工业文化遗产的分布、特点进行分析和梳理，总结当前湖北省工业文化遗产保护和利用的主要模式，通过调研访谈、问卷调查和数据分析，分析当前湖北省工业文化遗产保护和利用存在的主要问题，并提出对策建议。', NULL, 'F429.63', '本书适用于从事工业文化遗产保护研究的相关人员', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1239, '978-7-5207-3032-7', '未来制造业2030', '(日)竹内芳久，(日)川上正伸，(日)松岛大辅著', '东方出版社', 58, '制造工业', NULL, NULL, NULL, NULL, NULL, '313页', '21cm', NULL, NULL, '本书围绕“制造业的未来”这一主题，展望未来十年(截至2030年)内的“数字化产业革命”，指出制造业企业应结合自身实际情况制定适应全球化信息革命趋势的发展战略，并给出了四种战略雏形供日本制造业企业借鉴，同时对我国的制造业企业具有借鉴价值。', NULL, 'F431.36', '本书适用于制造工业发展研究人员', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1240, '978-7-5228-1029-4', '日本制造业概览', '褚健主编', '社会科学文献出版社·城市和绿色发展分社', 168, '制造工业', NULL, '2022-2023', NULL, NULL, NULL, '229页', '28cm', NULL, '海曙区科协重点科普项目专项资助 2022年宁波市科学技术局软科学计划支持', '本书对2022-2023年日本产业概况进行了介绍，从日本工业原材料制造、工业器件与部件制造、工业机械与设备制造三个方面，遴选了日本制造业领域43个行业，增加了除上市公司以外的数百家受到日本投资者关注和表现优异的非上市公司，概述了上述行业的上下游关系以及行业地位和现状。', NULL, 'F431.36', '本书适用于制造工业企业研究人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1241, '978-7-111-71618-1', '丰田经营管理经验71条', '(日)野地秩嘉著', '机械工业出版社', 69, NULL, NULL, NULL, NULL, NULL, NULL, '163页', '21cm', NULL, '机工汽车', '本书共12章，分别是何为丰田生产模式中的“改善”、越懒惰越擅于改善、常识是大敌、杜绝积压、即使如此仍然出现积压了怎么办、重要的是感到别扭和讨厌、改善对人的工作、减少会议、改善管理职务、日常生活也可以改善、改善危机管理、改善是创意工作。', NULL, 'F431.364', '本书适用于企业管理人员', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1242, '978-7-5159-2111-2', '透视SpaceX公司20年发展历程', '路建功，涂国勇等编著', '中国宇航出版社', 98, '航空航天工业', '2002-2022年', NULL, NULL, NULL, NULL, '14,382页', '26cm', NULL, NULL, '本书回归马斯克本人及SpaceX公司20年的成长过程，清晰地看到其中闪烁着航天领域复杂时代背景下的新常态——商业航天的光彩；再次扒出诸多过往旧事并细细品味，把目光转向20年前，一起探索SpaceX公司波澜壮阔的发展历程，逐渐把那些大大的问号拉直，期待新的奇迹再次出现。', NULL, 'F471.265', '本书适用于航空航天企业工作人员', NULL, '2022.09', 'chi', NULL);
INSERT INTO `book` VALUES (1243, '978-7-115-59873-8', '元宇宙漫游', '周鹏远，李力恒，许彬等编著', '人民邮电出版社', 59.8, '信息经济', NULL, NULL, NULL, NULL, NULL, '186页', '21cm', NULL, '异步图书 异步社区', '本书结合国内外多位相关领域专家的见解，剖析了元宇宙的重要技术、生态和发展，将读者快速带入元宇宙世界，了解元宇宙的重要知识点。全书从人城交互、空间体验、计算机视觉、边缘计算、系统五个方面介绍了元宇宙的关键技术，内容涉及元宇宙时代城市和人类的沉浸式互动、计算机视觉相关技术、元宇宙的搭建和实现技术等；同时还探讨了元宇宙涉及的社会问题，包括元宇宙对生活、隐私等方面的影响，元宇宙中的恶意用户和检测问题，以及高效构建元宇宙的方式和方法等。', NULL, 'F49', '本书适用于对元宇宙感兴趣的读者；高等院校学生及科研机构研究人员', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1244, '978-7-5001-7242-0', '隐私计算', '零壹智库著', '中译出版社', 79, '数据共享', '数字经济新基建', NULL, NULL, NULL, NULL, '309页', '23cm', NULL, '学术支持：香港科技大学数字金融实验室 中国科技体制改革研究会数字经济发展研究小组 独角兽', '本书从隐私计算崛起、它对数字经济的影响、实现的技术路径，到在各个领域的应用情况以及像阿里巴巴、腾讯、百度、华为、京东、字节跳动等互联网公司在隐私计算上的布局等多个角度深入剖析了隐私计算这个新兴行业。', NULL, 'F49', '本书适用于信息经济研究人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1245, '978-7-5229-0041-4', '元宇宙', '何楚鸿著', '中国纺织出版社有限公司', 58, '信息经济', '下一代互联网新风口', NULL, NULL, NULL, NULL, '184页', '24cm', NULL, NULL, '本书共分为七章，包括解读元宇宙的由来与发展、探寻元宇宙颠覆互联网的奥秘、各国科技巨头抢先布局元宇宙、元宇宙产业投资赛道、NFT使元宇宙经济有序运行、探索传统产业接入元宇宙的入场券、元宇宙的未来展望。', '通俗读物', 'F49-49', '本书适用于大众读者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1246, '978-7-121-44704-4', '互联网+旅游营销', '赵蕾著', '电子工业出版社', 89, '互联网络', NULL, NULL, NULL, NULL, NULL, '184页', '23cm', NULL, '教育部产学研协同育人项目2020年第一批《基于《旅游营销综合实训）课程的线上线下混合式教学培训项目》 湖北省教育科学规划课题《校企合作开发<旅游营销实务>课程的探究》 湖北省教科规划课题2022年重点课题《基于PBL的应用型本科高校旅游专业理实一体化教学模式探索与实践》 湖北省重大调研课题基金项目的支持', '本书结合互联网和大数据在旅游业的应用，分析和研究了“互联网+旅游”背景下的旅游业营销创新模式，一方面使旅游业更有效、更精准地为顾客提供个性化服务；另一方面使读者对“互联网+”背景下的旅游业营销模式的前景有一个认识。', NULL, 'F590.8-39', '本书适用于旅游营销从业人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1247, '978-7-5210-0901-9', '乡村全域旅游', '张雪晶著', '海洋出版社', 88, '乡村旅游', '乡村振兴战略在宁波的实践', NULL, NULL, NULL, NULL, '266页', '24cm', NULL, NULL, '本书分为基础、理念和实践三篇。基础篇在文献综述基础上界定乡村旅游发展的概念，然后借助2015年所做的全市乡村旅游发展调查课题数据，阐述宁波市乡村旅游发展的基础条件，在此基础上，对全市乡村旅游发展进行环境分析，确定总体发展路径和任务；理念篇在现有发展基础上，提出乡村旅游需要进行集聚化发展的理念，分析全市乡村旅游集聚化发展的现状和面临的问题；实践篇首先对乡村全域旅游示范区的评价标准和评分细则进行介绍，然后在大量调研的基础上，选取四个不同特色发展区域，对其工作做法进行研究和介绍，并对乡村全域旅游示范区的工作绩效', NULL, 'F592.755.3', '本书适用于乡村旅游发展研究者', NULL, '2022.02', 'chi', NULL);
INSERT INTO `book` VALUES (1248, '978-7-121-44700-6', '华为军团作战', '易鸣，王安辉著', '电子工业出版社', 79, '通信企业', '重塑行业服务模式与经营格局', NULL, NULL, NULL, NULL, '19,242页', '24cm', NULL, '美迪出版', '本书包括华为的考验、华为战略破局、构建军团组织、统一客户界面、强化技术使能、做深做透行业、助力商业伙伴成功、重构治理体系、实现战略突围9章，介绍了华为如何突破战略困境，以及华为军团聚焦行业、深入场景、使能伙伴、助力客户数字化转型成功的重要举措。', NULL, 'F632.765.3', '本书适用于企业管理人员，企业数字化转型的领导者、规划者、设计者和实施者，以及对华为军团感兴趣的研究、咨询人员', NULL, '2023.02', 'chi', NULL);
INSERT INTO `book` VALUES (1249, '978-7-121-44711-2', '永远没有舒适区', '悦笙著', '电子工业出版社', 59, '通信企业', '华为HR奋斗生涯手记', NULL, NULL, NULL, NULL, '14,224页', '24cm', NULL, '美迪出版', '本书对华为管理政策与员工表现两个方面的分析相互呼应，以此来向读者展示一个立体的华为奋斗者文化。书中作者分析了每一项华为人力资源管理设计的背后所蕴含的对业务实际问题的分析和总结，展现了华为如何把员工转化为奋斗者，让人们不仅看到华为人力资源管理的表象，也能了解其内在逻辑。', NULL, 'F632.765.3', '本书适用于企业管理人员', NULL, '2023.02', 'chi', NULL);
INSERT INTO `book` VALUES (1250, '978-7-5207-3223-9', '华为熵战', '汤献华，刘宏基著', '东方出版社', 68, '通信企业', '中国式现代化企业管理探索', NULL, NULL, '哲科思维应用书系', NULL, '12,306页', '24cm', NULL, NULL, '本书作者从有效对抗熵增的角度来演绎展开华为是如何从几个人发展到近20万人、其间遭遇各种艰难险阻、实现从无序到有序并持续进化的。', NULL, 'F632.765.3', '本书适用于企业管理研究人员', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1251, '978-7-115-58570-7', '电子商务系统分析与设计', '李文立，胡雷主编', '人民邮电出版社', 46, '电子商务', NULL, NULL, NULL, NULL, NULL, '207页', '26cm', NULL, '“十四五”教育部高等学校电子商务类教学指导委员会规划教材 人邮教育', '本书共9章，主要内容包括绪论、电子商务系统开发方法及建模、电子商务项目管理、电子商务系统规划、电子商务系统分析、电子商务系统设计、电子商务系统实施、电子商务系统分析与设计案例，以及电子商务系统分析与设计实验项目等。', '教材', 'F713.36', '本书适用于高等学校电子商务类专业学生', NULL, '2022.04', 'chi', NULL);
INSERT INTO `book` VALUES (1252, '978-7-121-43514-0', '设计，不止于形式', '阿里巴巴设计委员会编著', '电子工业出版社', 158, '电子商务', '阿里巴巴设计实战与解析', NULL, NULL, NULL, NULL, '483页', '26cm', NULL, '有艺', '本书有阿里巴巴集团旗下所有部门从各个方面剖析阿里的品牌、创意、营销、体验设计、产品设计、服务设计等问题。', NULL, 'F713.361.2', '本书适用于电子商务从业人员', '精装', '2022.07', 'chi', NULL);
INSERT INTO `book` VALUES (1253, '978-7-121-44745-7', '区块链技术与电子商务应用', '段伟常编著', '电子工业出版社', 49, '区块链技术', NULL, NULL, NULL, NULL, NULL, '198页', '24cm', NULL, '新工科×新商科·大数据与商务智能系列 华信', '本书梳理了区块链技术的基本原理，从存证应用、协同应用、价值转移三大场景来分析区块链技术在电子商务中的典型应用，通过对多个实例的分析，阐述了区块链技术与电子商务应用需求相结合的原理、模型，以及产生的创新价值。', '教材', 'F713.36-39', '本书适用于高等院校经济管理类专业学生', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1254, '978-7-121-44791-4', '直播电商数据分析与应用', '张雪存，朱名勋，方文英主编', '电子工业出版社', 33, '网络营销', NULL, NULL, NULL, '商务数据分析系列丛书', NULL, '106页', '26cm', NULL, NULL, '本书以国内直播电商为主线，从数据的角度结合自媒体账号的运营场景，从账号定位、选品到短视频引流、直播策划和直播复盘，讲述各场景下如何对数据进行分析，如何利用数据分析的结果指导自媒体账号的运营。', '教材', 'F713.365.2', '本书适用于电子商务、数据分析及相关专业学生', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1255, '978-7-5742-0606-9', '零基础玩转小红书', '杜利明，吴小诺著', '天津科学技术出版社', 42, '网络营销', NULL, NULL, NULL, NULL, NULL, '211页', '21cm', NULL, '竹石文化', '本书共8章，提供了玩转小红书的精进攻略，阐述了小红书的成长与商业模式、账号定位、内容创作、运营技巧、变现等方面的内容。', NULL, 'F713.365.2', '本书适用于网络营销人员', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1256, '978-7-5113-8701-1', '就想开一家自己的小店又赚钱', '宇琦著', '中国华侨出版社', 38, '商店', NULL, NULL, NULL, NULL, NULL, '240页', '21cm', NULL, NULL, '本书讲述一个个店主的成功创业故事，从中揭示其创业灵感、经营艺术、经验教训等，然后总结其行业特点和市场前景等，为后来进入者助力支招。', NULL, 'F717', '本书适用于商业经营人员', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1257, '978-7-5637-4478-7', '民宿管理与运营', '龙飞，虞虎编著', '旅游教育出版社', 49.8, '旅馆', NULL, NULL, NULL, NULL, NULL, '224页', '26cm', NULL, '新编旅游大类专业精品教材 国家自然科学基金青年项目《大都市传统工业区遗产资源游憩化利用的适宜性评价与开发模式——以北京市为例》和北京农学院青年教师科研创新能力提升计划项目资助', '本书从民宿开办、产品开发、组织安排、日常服务与管理、财务管理、市场营销和品牌建设梳理了民宿管理与运营所涉及的各个环节服务要点，内容涵盖了民宿理论、政策法规、行业标准、经典案例等诸多内容。', '教材', 'F719.2', '本书适用于旅游院校学生', NULL, '2022.09', 'chi', NULL);
INSERT INTO `book` VALUES (1258, '978-7-5096-8428-3', '面向共同富裕的地区购买力差异研究', '闫梅著', '经济管理出版社', 68, '购买力', NULL, NULL, NULL, NULL, NULL, '206页', '24cm', NULL, '中国社会科学院创新工程学术出版资助项目 经管出版', '本书内容包括：在借鉴国内外区域发展差距、购买力研究相关成果的基础上，探讨了居民生活水平与收入水平、购买力水平之间的关系，提出了面向共同富裕的购买力差异研究框架；运用购买力平价的理论和方法，构建了适用于我国地区购买力平价的指标体系和计算方法；以100个典型城市居民消费品平均价格为基础数据，计算得到反映地区价格水平和居民收入购买能力的购买力平价指数，并分析地区购买力差异的分异特征；运用购买力平价指数消除地区物价差异导致的名义收入偏差，将名义收入转换为实际收入，解析购买力差异对真实收入差距的影响；从规范研究的视', NULL, 'F723.3', '本书适用于区域差异研究者', NULL, '2022.10', 'chi', NULL);
INSERT INTO `book` VALUES (1259, '978-7-5228-1116-1', '中国商品交易市场', '王雪峰著', '社会科学文献出版社·国际出版分社', 128, '商品市场', '创新发展、综合评价与数字化案例', NULL, NULL, NULL, NULL, '270页', '24cm', NULL, NULL, '本书基于我国商品交易市场数量整体快速减少、亿元以上商品交易市降幅加大等现实，分析了我国亿元以上商品交易市场结构中专业市场数量降幅较大、消费品市场数量减少多等现状及其区域市场结构变化现状，总结了我国商品交易市场发展呈现市场数量供给饱和、数量扩张遇顶回落，市场整体规模边际效应为负、市场规模扩张面临险境，平均摊位面积增加趋势趋缓、商户规模化经营动力趋弱三大特点，提出我国商品交易市场创新发展呈现数字化创新融合新模式显现，市场集聚功能分化融合化，打造供应链、引领新时尚，管理服务向服务管理转变四大趋势。', NULL, 'F723.8', '本书适用于商品交易研究人员', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1260, '978-7-109-29953-5', '基于产品与模式视角下浙江省农产品电子商务发展差异性研究', '陈旭堂，黄艳娴，张燕著', '中国农业出版社', 68, '农产品', NULL, NULL, NULL, '浙江智库', NULL, '160页', '24cm', NULL, NULL, '本书对农产品电子商务发展影响因素进行分析，运用微观调研数据对浙江省农产品电子商务进行比较研究，从农户视角，对新鲜农产品电子商务与干货农产品电子商务发展情况进行比较、农产品电子商务与工业品电子商务进行比较，以及不同农产品电子商务发展模式进行比较，探究其内部差异性原因，发现浙江农产品电子商务发展存在问题及障碍，并提出一系列推动浙江农产品电子商务发展的对策建议，对推动浙江省农产品电子商务发展及全国农产品电子商务发展都有一定的借鉴意义。', NULL, 'F724.72', '本书适用于电子商务研究者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1261, '978-7-5136-7197-2', '中国城市服务业高质量发展指数报告', '余泳泽主编', '中国经济出版社', 98, '城市', NULL, '2021', NULL, NULL, NULL, '353页', '24cm', NULL, '江苏现代服务业协同创新中心和江苏现代服务业智库的研究成果 江苏现代服务业协同创新中心和江苏现代服务业智库以及国家社科基金重大项目“环境目标约束下的产业高质量发展研究”资助', '本书从产业规模、产业结构、产业创新、产业环境和产业协同五个维度对服务业高质量发展进行科学界定与测度，构建了一套全面科学的城市服务业高质量发展评价指标体系，为促进城市服务业结构调整、扩大城市服务业发展规模提供决策参考。', NULL, 'F726.9', '本书适用于服务业研究人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1262, '978-7-5194-6750-0', '茶馆酒肆的经营与管理', '孙绪芹著', '光明日报出版社', 68, '茶馆', '基于南京茶肆的实证研究', NULL, NULL, NULL, NULL, '176页', '24cm', NULL, '光明版', '本书以南京茶肆为实证研究，涵盖了一个茶馆从筹建、经营到品牌建设的全过程。书中既有茶叶的相关知识，也有中国餐饮菜系的相关常识；既有“众筹”商业模式的分析，又有茶馆业营销策略的介绍。', NULL, 'F726.93', '本书适用于当代大学生；茶文化爱好者、茶馆经营者、研究者、建设者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1263, '978-7-5607-7648-4', '山东商务这十年', '山东省商务发展研究院编', '山东大学出版社', 66, '商业经济', '走在前 开新局（2012-2022）', NULL, NULL, NULL, NULL, '286页', '24cm', NULL, NULL, '本书以“山东这10年商务发展成绩”为主题，划分综合、区域、园区、企业四个模块，综合模块主要内容为山东十八大以来的外资、外贸、外经、内贸、消费、流通等相关领域的成绩总结和经验交流；区域模块主要内容为山东十几个地市商务局10年来工发展历程的梳理和发展成就的获取。园区模块主要内容为山东经济技术开发区、综合保税区、自贸试验区各片区等十几家园区经济的发展情况、典型案例总结；企业模块主要为山东商务领域代表性企业的发展历程、发展成绩。', NULL, 'F727.52', '本书适用于地方经济发展研究人员', NULL, '2022.09', 'chi', NULL);
INSERT INTO `book` VALUES (1264, '978-7-301-33199-6', '东南亚南亚商务环境概论', '韩越主编', '北京大学出版社', 52, '国际商务', NULL, NULL, NULL, NULL, '3版', '252页', '26cm', NULL, '首批国际级线上一流课程配套教材 云南省普通高等学校“十二五”规划教材 高等院校经济管理类专业“互联网+”创新规划教材', '本书共22章，分为东南亚国家商务环境和南亚国家商务环境两大部分。每个部分包括区域商务环境特点、区域经济一体化和国别商务环境特点。国别商务环境包括简史和自然、政治、经济、社会、文化、商业。', '教材', 'F733', '本书适用于高等院校经济管理类专业学生', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1265, '978-7-5663-2250-0', '商务英语函电', '张启途，郭艳利主编', '对外经济贸易大学出版社', 42, '国际商务', NULL, NULL, NULL, NULL, NULL, '198页', '26cm', NULL, '全国高等院校基于工作过程的校企合作系列教材 语言技能类 国家精品在线开放课程外贸基础－函电与单证配套教材', '本书倒推外贸工作过程，根据贸易过程的9个基本步骤，设计9个基本贸易情景，涉及整个贸易工作流程中最重要的内容，以外贸人物真实工作过程引导大家学习每个工作任务。9个基本贸易情景分别为岗位认知、找客户、谈价格、谈付款、谈装运、谈包装、谈保险、签合同、谈索赔。每个工作任务情景又分为基础知识、工作背景、电子邮件写作、商务对话、实践拓展、综合练习等模块。', '教材', 'F740', '本书适用于高等院校商务英语专业和国际经济与贸易等专业学生', NULL, '2021.03', 'chi', NULL);
INSERT INTO `book` VALUES (1266, '978-7-300-31131-9', '国际技术与服务贸易', '李军主编', '中国人民大学出版社', 45, '国际贸易', NULL, NULL, NULL, NULL, '4版', '262页', '26cm', NULL, '经济管理类课程教材·国际贸易系列', '本书分为国际技术贸易和国际服务贸易两部分。第一部分是国际技术贸易，分别论述了国际技术贸易基本概念，国际技术贸易相关理论，国际技术贸易的内容、合同条款、交易程序、价格和支付，国际技术贸易法规与惯例等内容。第二部分是国际服务贸易，主要介绍了国际服务贸易基本范畴、相关理论、《服务贸易总协定》、中国服务贸易的发展现状，以及主要的国际服务贸易产业等内容。', '教材', 'F746.17', '本书适用于国际经济与贸易专业学生', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1267, '978-7-5607-7282-0', '自由贸易港概论', '谢逸波，童泽林，陈晓霖主编', '山东大学出版社', 86, '自由贸易区', NULL, NULL, NULL, NULL, NULL, '400页', '24cm', NULL, NULL, '本书分为五章。第一章介绍了自由贸易港的基本概念和理论基础，总结了自由贸易的相关概念、海关特殊监管区的相关概念、贸易保护理论和自由贸易理论。第二章介绍了自由贸易港建设的境外经验，总结了国际上先进的自由贸易港如新加坡、迪拜、中国香港、釜山、汉堡、鹿特丹的一些发展优势、典型做法和经验启示。第三章介绍了自由贸易港的国内探索，梳理了从改革开放初期到建设自由贸易试验区期间对自由贸易港相关功能的探索。第四章介绍了自由贸易港的制度保障，探讨了海关制度、金融法制制度、知识产权制度、环境保护制度。第五章对海南自由贸易港营商环', NULL, 'F752', '本书适用于经济发展研究人员', NULL, '2021.11', 'chi', NULL);
INSERT INTO `book` VALUES (1268, '978-7-109-30221-1', '肉鸡初加工产品生产贸易与对策', '严寒，李瑞丽，吴忠华主编', '中国农业出版社', 48, '肉鸡', NULL, NULL, NULL, '农产品国际贸易标准比对与实务丛书', NULL, '71页', '24cm', NULL, NULL, '本书首先从世界各国鸡肉生产贸易情况写到中国鸡肉生产贸易情况，以便对比发现不足；其次介绍了我国(不包括港、澳、台地区)以及主要贸易伙伴如中国香港、新加坡等的肉鸡初加工产品相关卫生质量管理要求；最后结合相关国家的卫生质量管理要求对中国鸡肉产品出口受阻原因进行了分析并提出了建议和对策。', NULL, 'F752.652.5', '本书适用于科研院所相关研究人员、高等院校师生以及畜禽工作者', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1269, '978-7-5130-8530-4', '浙江自由贸易试验区制度创新研究', '俞树彪著', '知识产权出版社有限责任公司', 88, '自由贸易区', NULL, NULL, NULL, NULL, NULL, '266页', '24cm', NULL, '浙江省哲学社会科学规划课题“浙江自由贸易试验区制度创新研究”研究成果', '本书聚焦全面深化改革开放这个主题，从浙江自由贸易试验区油气全产业链开放创新的现状切入，结合理论和实践案例，呈现浙江自由贸易试验区制度创新的足迹、亮点和成就，旨在建构较为完整的自由贸易试验区制度创新体系，为读者提供一个自由贸易试验区如何发展的整体逻辑框架，从而为我国自由贸易试验区建设提供浙江样本、为国家深化改革提供浙江经验、为扩大开放提供浙江案例、为国家能源安全贡献浙江力量。', NULL, 'F752.855', '本书适用于政府相关部门、工商界人土、经济管理研究机构相关从业者；高等院校师生；投资、贸易、金融、法律、物流、区域经济、数字赋能、一带一路、海事服务、能源安全等感兴趣的其他专业人士', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1270, '978-7-5223-1617-8', '海南自由贸易港“一线放开、二线管住”通关监管模式创新研究', '《海南自由贸易港“一线放开、二线管住”通关监管模式创新研究》课题组编著', '中国财政经济出版社', 69, '自由贸易区', NULL, NULL, NULL, NULL, NULL, '11,10,335页', '24cm', NULL, '海南省委省政府重大研究专项课题', '本书共六部分，主要内容包括：海南自由贸易港建设的背景和任务、境内外自由贸易港(区)政府管理体制与海关监管制度比较分析、海南自由贸易港通关模式创新总体框架、海南自由贸易港通关监管制度创新的核心内容和实施路径等。', NULL, 'F752.866', '本书适用于自由贸易区海关手续研究人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1271, '978-7-5103-4246-2', 'RCEP市场深度分析及投资贸易趋势研究报告（2020-2021）', '云南省商务研究院编', '中国商务出版社', 80, '自由贸易区', '以云南省为例', NULL, NULL, NULL, NULL, '327页', '24cm', NULL, NULL, '本书共分为七篇：第一篇为综合报告篇；第二篇为贸易合作篇；第三篇为单品分析篇；第四篇为投资促进篇；第五篇为产业发展篇；第六篇为物流畅通篇；第七篇为综合保障篇。', NULL, 'F752.874', '本书适用于国际贸易研究者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1272, '978-7-5130-8511-3', '市场监管现代化研究', '闫志刚著', '知识产权出版社有限责任公司', 88, '食品', '以食品药品为例', NULL, NULL, NULL, NULL, '203页', '24cm', NULL, NULL, '本书以食品药品安全监管为例，对市场监管现代化中的监管理论、监管绩效评估、监管的整体形势与战略选择、监管的体制机制、监管的中外经验、监管能力建设、监管的方式创新、互联网下的监管等一系列问题进行了研究。', NULL, 'F768.2', '本书适用于市场监管特别是食品药品监管领域的管理人员、企业相关人员、研究者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1273, '978-7-5228-1113-0', '国际财政发展指数报告', '林光彬等著', '社会科学文献出版社·经济与管理分社', 138, '财政政策', NULL, '2022', NULL, '皮书系列', NULL, '234页', '24cm', NULL, '北京市财经研究基地和中央财经大学一流学科项目经费支持', '本书从“财政是国家治理的基础和重要支柱”这一战略新定位出发，根据财政活动的运行特征和规律，把传统财政“收、支、平、管”分析范式发展为财政“运营、稳定、均等、治理、潜力”分析新范式，按照新范式构建财政发展指数指标体系框架，并用定量方法对世界主要国家的财政发展情况进行对比研究。', NULL, 'F811.0', '本书适用于财政政策研究人员', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1274, '978-7-5223-1799-1', '中国财政', '杨英明主编', '中国财政经济出版社', 116, '财政', NULL, '2021', NULL, NULL, NULL, '209页', '28cm', NULL, NULL, '本书分为policy update、facts and figures、MOF events、local finance、remarks and opinions等五个部分，介绍2021年中国财政重要政策、数据和重要活动，其中，也包括一部分地方财政的内容。', '英文', 'F812', '本书适用于财政人员', NULL, '2022.12', 'eng', NULL);
INSERT INTO `book` VALUES (1275, '978-7-5218-4042-1', '事业单位财务规则与会计核算案例', '王明锐，贲月，王瀛主编', '经济科学出版社', 56, '行政事业单位', NULL, NULL, NULL, NULL, NULL, '273页', '25cm', NULL, NULL, '本书共分十二章，内容包括：总则和附则、单位预算管理、收入管理、支出管理、结转和结余管理、专用基金管理、资产管理、负债管理、事业单位清算、财务报告和决算报告、财务监督等。', NULL, 'F812.2', '本书适用于事业单位会计人员', NULL, '2022.10', 'chi', NULL);
INSERT INTO `book` VALUES (1276, '978-7-5218-3978-4', '上市公司税收激进行为的同群效应研究', '张钦成著', '经济科学出版社', 76, '上市公司', NULL, NULL, NULL, '转型时代的中国财经战略论丛', NULL, '282页', '23cm', NULL, '“十四五”时期国家重点出版物出版专项规划项目 财智睿读', '本书从同群效应的视角来探究公司税收激进行为，在回顾相关文献的基础上，结合公司税收激进理论、环境不确定性与组织决策的有限理性、社会学习理论、动态竞争理论、制度理论和委托代理理论构建理论分析框架，探究上市公司税收激进行为同群效应的存在性、形成机制、影响因素及经济后果，选取2008-2018年间我国沪、深两市A股非金融类上市公司为研究样本进行实证检验，最终形成具体的研究结论并提出了相应的政策建议。', NULL, 'F812.423', '本书适用于税收管理研究人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1277, '978-7-5615-8765-2', '中国离境退税法律制度完善研究', '王婷婷著', '厦门大学出版社', 88, '出口退税', NULL, NULL, NULL, NULL, NULL, '304页', '23cm', NULL, '西南政法大学经济法学系列 国家社会科学基金青年项目“中国离境退税法律制度完善研究”研究成果', '本书立足于中国离境退税法律制度发展的现实问题和世界各国有关离境退税法律制度的比较分析，对各国旅游业发展进程中确立的离境退税这项间接税法律制度进行研究。', NULL, 'F812.424', '本书适用于广大财会工作者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1278, '978-7-5096-8760-4', '县级财政压力测评与影响因素研究', '匡萍著', '经济管理出版社', 98, '县', NULL, NULL, NULL, NULL, NULL, '245页', '24cm', NULL, '经管出版 掌尚文化', '本书共8章，内容涉及县级财政压力的理论分析；县级财政压力现状分析；县级财政压力的测评；县级财政压力影响因素的分析等。', NULL, 'F812.7', '本书适用于地方财政研究者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1279, '978-7-5171-3995-9', '2021地方人大预算审查监督研究报告', '刘蓉等著', '中国言实出版社', 78, '地方各级人民代表大会', '地方人大预算审查监督与能力建设', NULL, NULL, NULL, NULL, '361页', '24cm', NULL, NULL, '本书为西南财经大学的专家学者对中国预算审查监督制度的建设与成效进行考察之后形成的报告成果。全书分为五篇：预算标准与报告文本、预算审查监督与绩效、预算审查监督与公共服务、预算审查监督与法制建设、预算审查监督与地方债。', NULL, 'F812.700.2', '本书适用于政府财务工作人员', NULL, '2022.04', 'chi', NULL);
INSERT INTO `book` VALUES (1280, '978-7-5228-1030-0', '提升地方人大预算监督能力的机制设计研究', '周振超等著', '社会科学文献出版社·城市和绿色发展分社', 88, '地方各级人民代表大会', NULL, NULL, NULL, NULL, NULL, '225页', '24cm', NULL, '国家社科基金项目成果', '本书以提升地方人大的预算监督能力为研究对象，力图归纳健全人大预算监督权的经验、教训和一些规律性内容，从地方人大预算监督能力的实践中提炼有价值的概念和分析框架，了解现实制度安排和政治现象背后的主要制约因素，形成基于中国经验的本土理论。', NULL, 'F812.700.2', '本书适用于财政制度研究人员', '精装', '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1281, '978-7-5220-1792-1', '币值稳定、不平等与商业周期研究', '张振，罗维晗等著', '中国金融出版社', 68, '通货膨胀', NULL, NULL, NULL, NULL, NULL, '224页', '24cm', NULL, NULL, '本书可以分为四个主要部分：第一部分主要描述当前的通胀问题；第二部分是收入分配问题；第三部分是商业周期问题；第四部分为实证。书中以亲身经历者的角度描述了2011-2013年在美国中部城市密尔沃基、格林贝以及芝加哥南部地区生活的所见所闻。', NULL, 'F820.5', '本书适用于经济学研究人员', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1282, '978-7-5218-3940-1', '经济发展新常态下我国货币政策体系建设研究', '潘敏等著', '经济科学出版社', 130, '货币政策', NULL, NULL, NULL, NULL, NULL, '478页', '25cm', NULL, '教育部哲学社会科学研究重大课题攻关项目 “十四五”时期国家重点出版物出版专项规划项目', '本书针对经济发展新常态以来我国推进供给侧结构性改革、落实金融发展三大任务、构建双支柱的宏观金融调控框架、推动经济高质量发展、构建国内大循环为主体、国际国内双循环相互促进的新发展格局等对完善我国货币政策体系提出的任务和挑战；立足于近年来中国中央银行完善货币政策体系的实践，分析了经济发展新常态下我国货币政策环境的变化，研究了经济发展新常态以来我国结构性货币政策工具及其传导效果、货币政策规则、货币政策与宏观审慎政策双支柱调控体系的构建、货币政策与财政政策协调等重大问题，从目标体系完善、政策工具创新、调控方式转变', NULL, 'F822.0', '本书适用于货币政策研究者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1283, '978-7-5615-8491-0', '投资项目评估', '张阿芬，傅庆阳编著', '厦门大学出版社', 65, '投资项目', NULL, NULL, NULL, NULL, '6版', '420页', '26cm', NULL, '21世纪财务管理系列教材', '本书结合《投资项目可行性研究指南》及我国最新的财税、金融、外汇、投资管理法规和制度，阐述投资项目评估的理论与方法。全书各章节以新建投资项目评估的程序和内容为主线展开阐述，主要内容包括：投资项目评估基本概念、基本理论；市场分析与必要性评价；建设生产条件评估与技术可行性评价；财务基础数据预测与财务效益评估；国民经济效益评估与社会效益评估；不确定性分析与风险评价；项目总评估与后评估等。', '教材', 'F830.59', '本书适用于高等学校财务管理专业学生', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1284, '978-7-5226-0691-0', '机器学习在算法交易中的应用', '(美)斯蒂芬·詹森(Stefan Jansen)著', '中国水利水电出版社', 158, '机器学习', NULL, NULL, NULL, NULL, NULL, '20,566页', '24cm', NULL, '智博尚书 冷血博弈', '本书分4部分，其中第1部分主要介绍基于机器学习的交易策略的基础知识，该部分内容围绕机器学习算法以及交易策略相关的数据展开，概述了如何有效捕获数据信号内容、如何准确提取特征，以及如何基于这些数据优化算法评估投资组合。第2部分阐述了在端到端工作流环境中，一些基本的监督学习、无监督学习是如何为交易策略的制定提供帮助的。第3部分是自然语言处理，这部分引入了无监督学习算法，从文本数据这种最关键的另类数据中高质量地提取信号。第4部分通过TensorFlow和PyTorch，介绍深度学习和强化学习在交易策略设计中的应用', NULL, 'F830.59-39', '本书适用于数据分析师、数据科学家、Python开发人员、投资分析师或投资组合经理', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1285, '978-7-5096-8749-9', '金融支付概论', '王蕊，罗航编', '经济管理出版社', 49.8, '支付方式', NULL, NULL, NULL, NULL, NULL, '258页', '24cm', NULL, '博鼎', '本书以金融支付演变的时间线为逻辑，基于“支付中介+支付介质”两个维度，对当前主流支付方式的内涵和操作流程、相关政策规定、未来行业展望等方面进行梳理和介绍，主要包括传统银行支付、互联网金融时代支付以及金融科技时代支付。', NULL, 'F830.73', '本书适用于支付方式研究者', NULL, '2022.10', 'chi', NULL);
INSERT INTO `book` VALUES (1286, '978-7-5096-8911-0', '证券投资学', '丁毅编', '经济管理出版社', 98, '证券投资', NULL, NULL, NULL, NULL, NULL, '193页', '24cm', NULL, '经管出版', '本书共10章，融汇了国内外证券投资的相关理论，内容涉及证券投资基础知识；证券投资的宏观分析；证券投资的公司分析；证券投资收益和风险；证券组合投资分析等。', NULL, 'F830.91', '本书适用于金融、贸易、投资、经管类专业的在校大学生', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1287, '978-7-5159-2028-3', '一本书学会波浪理论', '江道波编著', '中国宇航出版社', 49, '股票市场', NULL, NULL, NULL, NULL, NULL, '214页', '24cm', NULL, '宇航经管', '本书共分六章，从波浪总论、波浪形态、数浪方法、数学原理、几何原理到波浪应用，不仅诠释了理论，同时用波浪理论刻画出了市场行为的脉络，帮助投资者在股市中以一种新的眼光来看待市场，从而认识市场、了解市场、掌握市场一定的规律。', NULL, 'F830.91', '本书适用于股市投资者', NULL, '2023.02', 'chi', NULL);
INSERT INTO `book` VALUES (1288, '978-7-5159-2156-3', '股市交易的十二堂必修课', '景生辉著', '中国宇航出版社', 29, '股票交易', NULL, NULL, NULL, NULL, NULL, '156页', '24cm', NULL, '宇航经管', '本书总结了新股民入市必须要学习和掌握的十二堂必修课，内容包括如何选出大牛股，如何判断主力何时入场，如何确认牛股的爆发点，如何做到精准逃顶。', NULL, 'F830.91', '本书适用于股票投资者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1289, '978-7-5159-2177-8', '主力运作模式与跟庄实战技法', '屠龙刀编著', '中国宇航出版社', 49, '股票交易', NULL, NULL, NULL, NULL, NULL, '213页', '24cm', NULL, '揭开市场底牌三部曲 宇航经管', '本书从三个大的方面来分解主力庄家在各个坐庄阶段的操盘策略和操盘方法，即心理层面、时机把握层面和技术特征层面。', NULL, 'F830.91', '本书适用于广大股民', NULL, '2023.02', 'chi', NULL);
INSERT INTO `book` VALUES (1290, '978-7-5159-2181-5', '游资底层投资逻辑与交易实录', '屠龙刀编著', '中国宇航出版社', 49, '股票投资', NULL, NULL, NULL, NULL, NULL, '212页', '24cm', NULL, '揭开市场底牌三部曲 宇航经管', '本书对游资投机的底层操盘逻辑、炒作策略以及经典做盘模式进行介绍，并结合国内顶级游资的操盘实例，探寻游资交易的精髓。', NULL, 'F830.91', '本书适用于股票投资者', NULL, '2023.02', 'chi', NULL);
INSERT INTO `book` VALUES (1291, '978-7-5159-2182-2', '游资炒作热点的内在逻辑与方向', '屠龙刀编著', '中国宇航出版社', 49, '股票投资', NULL, NULL, NULL, NULL, NULL, '207页', '24cm', NULL, '揭开市场底牌三部曲 宇航经管', '本书从游资炒作这些题材的路径和方法入手，对市场上经常炒作的各类题材进行了归纳与整理。这些概念与题材大致可以分为国际局势、国家政策与规划、上市公司、新能源产业链、其他科技行业概念以及其他类别的热点概念等。', NULL, 'F830.91', '本书适用于股票投资者', NULL, '2023.02', 'chi', NULL);
INSERT INTO `book` VALUES (1292, '978-7-5159-2184-6', '江恩九方图', '江道波编著', '中国宇航出版社', 69, '股票交易', NULL, NULL, NULL, NULL, NULL, '342页', '24cm', NULL, '宇航经管', '本书是学习和运用江恩九方图分析技术的指导书，主要内容包括九方图基本知识、实战运用以及江恩九方图相关手稿三个部分。', NULL, 'F830.91', '本书适用于喜爱技术分析的证券投资者', NULL, '2023.02', 'chi', NULL);
INSERT INTO `book` VALUES (1293, '978-7-5220-1832-4', '机器学习典型技术在金融风险研究领域中的应用', '田浩著', '中国金融出版社', 68, '机器学习', NULL, NULL, NULL, NULL, NULL, '276页', '24cm', NULL, NULL, '本书是作者历时两年多完成的第二部学术专著，对机器学习技术在金融风险研究中的应用进行了探讨，包括机器学习技术概述、偏好建模与金融风险偏好、聚类及其在金融风险研究中的应用等章节。', NULL, 'F830.9-39', '本书适用于机器学习技术应用研究人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1294, '978-7-5096-8745-1', '路径依赖型场外期权定价方法', '肖汉著', '经济管理出版社', 88, '期权定价', NULL, NULL, NULL, NULL, NULL, '228页', '24cm', NULL, '掌尚文化', '本书的主要内容为，对路径依赖型期权定价参数的研究和探讨、对定价理论模型及方法论的比较和改进，以及对风险管理的充分研究和实验分析。', NULL, 'F830.95', '本书适用于对衍生品领域感兴趣的市场参与者、监管者、高校师生', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1295, '978-7-5218-4079-7', '我国金融区块链政策实施的经济后果研究', '王嘉鑫著', '经济科学出版社', 62, '区块链技术', NULL, NULL, NULL, NULL, NULL, '224页', '24cm', NULL, '中南财经政法大学中央高校基本科研业务费专项资金资助 ESP', '本书将研究设置在地方政府搭建“区块链+融资平台”场景下，运用机器学习、主题分析法等技术手段，构建关系借贷密度和类型、金融区块链政策执行确定性(质)和强度(量)以及企业信贷风险信息披露水平和含量等指标，采用双重差分法，从关系借贷视角考察金融区块链对信贷配置效率的影响、具体机制与经济后果。具体考察：从企业需求管理、银行供给管理和官员等压力传导三个维度，探讨“区块链+融资平台”是否影响关系借贷及信贷配置效率，从质和量角度，探讨政策执行确定性和强度的调节作用；从两大视角、六条路径出发，探讨引发上述效应的具体机制；', NULL, 'F832', '本书适用于区块链技术研究人员', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1296, '978-7-5220-1815-7', '乡村振兴金看点', '农银报业有限公司编著', '中国金融出版社', 58, '农村金融', '金融服务乡村振兴论集（2021版）', NULL, NULL, NULL, NULL, '248页', '24cm', NULL, NULL, '本书包含“振兴论坛”“政策精解”“创新实践”“工作建言”“调研真知”等八章，收录了《努力让金融更好服务乡村振兴》《接续为乡村振兴贡献更大金融力量》《践行以人民为中心的发展思想》《新格局下全面推进乡村振兴》《蹬出城乡融合发展新路》等文章。', '文集', 'F832.35-53', '本书适用于农村金融研究人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1297, '978-7-5136-7121-7', '2022年中国信托公司经营蓝皮书', '清华大学法学院金融与法律研究中心编著', '中国经济出版社', 198, '信托公司', NULL, NULL, NULL, NULL, NULL, '231页', '26cm', NULL, NULL, '本书在总结前17部经验的基础上，进一步改进和提升了实证分析模型和研究分析的方法与手段；强调横向分析与纵向比较相结合，通过财务状况实证分析、经营成果实证分析、盈利能力与收益水平分析、经营效率与经营质量以及风险分析等多个方面几十个具体指标进行了归纳、汇总、概括、提炼、分类、排序和分析；突出运用实证分析的方法，反映了当前我国信托公司的概貌，力求为各信托公司和广大投资者以及监管部门提供研究报告。', NULL, 'F832.39', '本书适用于广大投资者', NULL, '2022.10', 'chi', NULL);
INSERT INTO `book` VALUES (1298, '978-7-5096-8800-7', '中小企业信用测度研究', '张坤著', '经济管理出版社', 79, '中小企业', NULL, NULL, NULL, NULL, NULL, '189页', '24cm', NULL, NULL, '本书首先建立信用测度的基本概念和理论框架；其次借助信用风险管理的研究逻辑，分析中小企业信用风险的形成机理，进而尝试从理论角度构建中小企业信用额度的指标体系；最后基于大数据技术，对信用测度模型进行实证研究，并在对比不同实证研究结果的基础上尝试构建中小企业信用测度体系。', NULL, 'F832.4', '本书适用于中小企业信用测度研究者', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1299, '978-7-5218-4071-1', '政府主导型农地经营权抵押贷款的实现与风险管理', '宋坤等著', '经济科学出版社', 98, '农业用地', NULL, NULL, NULL, NULL, NULL, '282页', '24cm', NULL, '国家社科基金后期资助项目', '本书首先在评判收入效应和信贷约束缓解程度的基础上分析履约机制来探讨政府主导型农地经营权抵押贷款的实现，然后分析该业务风险处置这一制约其发展的关键点并对风险处置机制进行优化，从而为激活农地资本的金融属性、释放农地经营权抵押的经济价值提供理论支撑和经验证据。', NULL, 'F832.43', '本书适用于农业用地管理研究者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1300, '978-7-5136-7029-6', '中国消费金融发展研究', '杨运杰，蒋欣，刘鹰主编', '中国经济出版社', 78, '消费贷款', NULL, NULL, NULL, NULL, NULL, '325页', '26cm', NULL, NULL, '本书从宏观到微观、从理论到实践、从国际到国内、从行业到监管，分析了中国消费金融发展现状和问题，开发了行业发展指数，分析了中国消费金融公司治理，研制了消费金融公司评价指标体系，并分析了中国消费金融科技代表性案例。', NULL, 'F832.479', '本书适用于广大金融领域从业人员', NULL, '2022.08', 'chi', NULL);
INSERT INTO `book` VALUES (1301, '978-7-5164-2705-7', '国投集团', '《国投集团：责任投资驱动型社会责任管理》编写组编著', '企业管理出版社', 59, '国有企业', '责任投资驱动型社会责任管理', NULL, NULL, '中央企业社会责任管理之道丛书', NULL, '168页', '24cm', NULL, NULL, '本书共八章，包括发展历程中的社会责任基因；责任投资驱动的社会责任管理模式；投资决策：社会责任是关键一票；投后管理：放大投资项目的社会价值；积极应对气候变化的投资选择；责任投资理念下的生物多样性投资等。', NULL, 'F832.48', '本书适用于国有企业社会责任研究者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1302, '978-7-5218-3859-6', '我国对外大型公共投资项目社会责任风险防范与预警机制研究', '吕臣著', '经济科学出版社', 98, '投资', NULL, NULL, NULL, NULL, NULL, '241页', '24cm', NULL, '国家社会科学基金青年项目结题报告 ESP', '本书在综合研究中国对外大型公共投资项目政治风险、经营风险基础上，重点针对中国对外大型公共投资项目社会责任风险展开具体研究。', NULL, 'F832.48', '本书适用于投资项目管理研究者', NULL, '2022.08', 'chi', NULL);
INSERT INTO `book` VALUES (1303, '978-7-5223-0040-5', '行思录', '安青松著', '中国财政经济出版社', 98, '资本市场', '资本市场制度理论与实践', NULL, NULL, NULL, NULL, '456页', '24cm', NULL, '清华大学五道口金融学院金融学研究基金资助课题', '本书从资本市场运行、国内外经济金融形势、资本市场法治建设、热点问题及对策、进一步推进资本市场改革、加强资本市场监管、支持实体经济发展、防范化解金融风险、理论分析与实证研究层多层面展现世界和我国资本市场最新动态和研究成果，旨在为从业者及关注资本市场研究工作的人士提供交流的平台，同时听取各方意见建议，进一步发挥研究成果政策效用。', NULL, 'F832.5', '本书适用于资本体制改革研究者', '精装', '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1304, '978-7-5218-3999-9', '债券市场质量', '类承曜等著', '经济科学出版社', 78, '债券市场', '评价体系、影响因素与优化设计', NULL, NULL, NULL, NULL, '254页', '24cm', NULL, NULL, '本书构建一个全面度量债券市场运行质量的指标体系，包括流动性、波动性、透明度、有效性和开放度等五个维度的指标。进而，采用实证分析、模拟分析和实验经济学方法等多种研究范式，从若干角度研究影响债券市场运行质量的因素。在此基础上，提出我国债券市场微观机制的优化方案。', NULL, 'F832.51', '本书适用于金融界相关从业人员和债券市场参与者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1305, '978-7-5220-1812-6', '中国信贷资产证券化十年发展回顾与展望', '冯光华等编著', '中国金融出版社', 69, '信贷资产', '2012-2022', NULL, NULL, NULL, NULL, '406页', '24cm', NULL, NULL, '本书梳理和总结了信贷资产证券化十年发展历程和实践经验，展现了我国经济金融在进入新发展阶段、构建新发展格局当中信贷资产证券化市场“防风险”与“促发展”相协调的建设理念，重点突出了信贷资产证券化在服务和促进我国债券市场高质量发展中的作用。', NULL, 'F832.51', '本书适用于信贷资产研究人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1306, '978-7-5220-1802-7', '中国支付清算', '中国支付清算协会编', '中国金融出版社', 40, '支付方式', NULL, '2022第4辑 总第16辑', NULL, NULL, NULL, '155页', '26cm', NULL, NULL, '本书设有论坛专题、金融普惠、金融账户、中小机构、风险防范、案例研究等栏目，收录了《关于老年人支付服务便利化程度的调查与思考——以江西省抚州市为例》《区块链技术在人民币跨境结算中应用模式探索——基于数字人民币模式》《浅析当前POS业务推广中存在的问题及整改建议》等文章。', NULL, 'F832.6', '本书适用于金融领域从业人员', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1307, '978-7-5228-1301-1', '中国稳外资实践与策略', '聂平香等著', '社会科学文献出版社·国际出版分社', 128, '外资引进', NULL, NULL, NULL, NULL, NULL, '277页', '24cm', NULL, '国家社会科学基金重点项目“新形势下我国稳外资的思路及对策研究”的最终成果', '本书分为综合篇、专题篇、国际篇、地区篇，其中综合篇在分析疫情前后全球直接投资的发展特点和发展趋势以及我国稳外资的特点及问题的基础上，剖析了中美贸易摩擦和疫情对我国吸收外资的影响机制；专题篇研究制造业、服务业、核心平台等重要领域的稳外资实践与对策；国际篇研究外国商会对中国投资环境的评价与我国稳外资对策；地区篇研究七个省份稳外资实践与对策。', NULL, 'F832.6', '本书适用于外资引进研究人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1308, '978-7-5220-1771-6', '人民币汇率波动下金融市场的联动研究', '钟永红著', '中国金融出版社', 89, '人民币汇率', NULL, NULL, NULL, NULL, NULL, '181页', '24cm', NULL, '国家自然科学基金－广东联合基金重点项目和华南理工大学中央高校基本科研业务费项目资助', '本书主要研究人民币汇率形成机制市场化改革和人民币国际化进程中，香港离岸人民币金融市场与在岸人民币金融市场的联动关系。全书共九章，内容包括人民币汇率市场化改革和人民币国际化、汇率决定理论与人民币汇率波动的影响因素、人民币在岸市场与离岸市场的发展和联通、人民币在岸市场和离岸市场的汇差及其波动等。', NULL, 'F832.63', '本书适用于汇率金融研究人员', NULL, '2022.10', 'chi', NULL);
INSERT INTO `book` VALUES (1309, '978-7-5220-1669-6', '金融标准化研究', '全国金融标准化技术委员会编', '中国金融出版社', 90, '金融', NULL, '2021', NULL, NULL, NULL, '403页', '26cm', NULL, NULL, '本书研究金融标准化相关内容，收录了《跨境支付清算标准体系建设》《不良资产交易信息披露标准化研究》《金融业开源治理体系与标准化研究》《开放银行标准体系建设研究》《养老金融标准化研究》等文章。', NULL, 'F832-65', '本书适用于金融领域从业人员', NULL, '2022.09', 'chi', NULL);
INSERT INTO `book` VALUES (1310, '978-7-121-44737-2', '重疾险100问', '陈凤山，武效军，严彩霞著', '电子工业出版社', 69, '医疗保险', NULL, NULL, NULL, NULL, NULL, '16,335页', '21cm', NULL, '保险100问系列 美迪出版', '本书从基础知识、常见误区、要点分析、购买指南、保险理赔及常见问题6个角度，以大众经常遇到、容易产生误解的100个问题为切入点，通过案例描述、专业解析及延伸阅读，介绍重疾险的基本概念、功能、投(退)保及理赔等方面的知识，并针对大众对重疾险的常见误解进行分析，给出解答。', NULL, 'F840.4-44', '本书适用于保险从业者', NULL, '2023.03', 'chi', NULL);
INSERT INTO `book` VALUES (1311, '978-7-5223-1717-5', '国际保险中介市场发展及监管体系研究', '中国保险行业协会编著', '中国财政经济出版社', 79, '国际保险', NULL, NULL, NULL, NULL, NULL, '257页', '24cm', NULL, NULL, '本书分别在欧洲篇、北美篇、亚太篇和中国篇，从保险中介的组织类型及发展情况、保险中介监管体系及法律法规、保险中介的准入政策及市场行为监管、保险中介行业组织的地位及作用、保险中介领域创新发展五个方面，详述德国、英国、美国、日本、新加坡、澳大利亚、中国香港、中国台湾、中国内地的保险中介市场监管情况，阐释了各国家和地区之间保险中介监管体系和保险中介市场运行模式的特点与差异。对国际保险中介市场监管制度进行梳理，从中介从业人员资质、市场准入及退出政策、保险公司对中介机构管控要求等方面对各国家和地区保险市场特点进行比较', NULL, 'F840.685', '本书适用于国际保险市场发展研究人员', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1312, '978-7-5136-7101-9', '中国创意产业发展报告', '张京成主编', '中国经济出版社', 128, '文化产业', NULL, '2022', NULL, '创意书系', NULL, '404页', '23cm', NULL, '创意城市蓝皮书 北京市科学技术研究院首都高端智库研究报告', '本书以“科技赋能数字文创”为主题，通过北京、天津、石家庄、哈尔滨、扬州、宁波、南昌、抚州、青岛、武汉、广州、贵阳、西安、西宁、澳门等15个城市创意产业的发展反映全国总体情况。全书以总报告为统领，以各城市报告为支撑，发布“中国城市文化创意指数排行榜”，从文化创意+创意生态、赋能能力、审美驱动力、创新驱动力4个方面16个指标45个数据项对31个省级行政区和香港、澳门2个特别行政区共140个城市的创意产业发展给予评价分析，形成指数报告，对直辖市、副省级城市和地市分级排名，并对“粤港澳大湾区、长三角、京津冀”三大', NULL, 'G124', '本书适用于文化产业研究人员', NULL, '2022.09', 'chi', NULL);
INSERT INTO `book` VALUES (1313, '978-7-5615-8854-3', '文创产品设计', '詹伟锋，谢霖主编', '厦门大学出版社', 52, '文化产品', NULL, NULL, NULL, NULL, NULL, '66页', '26cm', NULL, '校企（行业）合作系列教材 “木雕技艺与文化研究中心福建省高校人文社科研究基地”阶段性成果 2019年福建省教育厅教改重大项目“新生代工艺美术师培养模式探索与实践”阶段性成果', '本书共分六章：文创产品概述、文创产品的设计研究、文创产品形态设计创新方法、文创产品设计创意理论、文创产品的设计表现、文创产品案例赏析。', '教材', 'G124', '本书适用于文创人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1314, '978-7-5228-0942-7', '北京文化创意产业发展报告', '张京成主编', '社会科学文献出版社·经济与管理分社', 168, '文化产业', NULL, '2022', NULL, '皮书系列', NULL, '310页', '24cm', NULL, '创意城市蓝皮书 No.12 2022版 创意书系 北京市科学技术研究院首都高端智库研究报告', '本书跟踪研究北京文化创意产业发展态势，以整体运行、区域发展、数字技术应用、案例研究等为基本内容，综合研究2021年北京文化创意产业的发展状况，分析全市及下辖行政区、国家文化产业创新实验区文化创意产业发展现状与进展，从文化产业高质量发展、新技术在文化创意产业中的应用以及文化消费、文化旅游等方面开展专题讨论和研究。', NULL, 'G127.1', '本书适用于文化产业发展研究人员', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1315, '978-7-5194-6482-0', '城市文化载体与城市文化形象构建', '边坤著', '光明日报出版社', 85, '城市群', '内蒙古城市群文化形象研究', NULL, NULL, '光明社科文库', NULL, '166页', '24cm', NULL, '内蒙古自治区规划办基地课题：《内蒙古城市文化内涵挖掘与形象构建策略研究》 包头社科联课题：《包头市城市文化内含挖掘与城市形象构建策略研究》 光明版', '本书通过对城市文化内涵与外延的分析对内蒙古地区核心城市——呼和浩特、包头、鄂尔多斯三座城市文化载体资源的收集与分析，明确内蒙古地区三座核心城市文化载体的建设现状，借以构建内蒙古三大核心城市形象建设策略。', NULL, 'G127.26', '本书适用于城市文化研究人员', '精装', '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1316, '978-7-5229-0023-0', '彝伦攸叙', '舒伟，汪琼颖，孙福良著', '中国纺织出版社有限公司', 88, '村落文化', '呈坎文创产品设计', NULL, NULL, '徽州古村落文创产品设计丛书', NULL, '152页', '26cm', NULL, '安徽高校人文社会科学重点研究项目', '本书运用徽州在地文化与文化创意产品开发的实例，透过生活化、系统化与品牌化的步骤，有效筛选、撷取在地文化元素，建构文化元素与设计程序的对应模式，同时，配以设计案例，呈现对乡土资源的在地创生，力求将抽象的文化元素具体化，对乡村文创产品设计进行了多样化和开放性的探索。', NULL, 'G127.542', '本书适用于高等院校产品设计专业师生', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1317, '978-7-5615-8592-4', '“内容+”的媒体进阶之路', '申玲玲著', '厦门大学出版社', 88, '传播媒介', '资本、技术、制度影响下的内容生产', NULL, NULL, '云潮文库', NULL, '316页', '24cm', NULL, '西北政法大学新闻传播学院学术出版基金资助', '本书分为三大部分十一章内容。第一部分(第一章至第五章)，介绍研究过程中用到的相关理论，归纳资本、技术、制度对网络媒体内容生产的影响机制及表现，并探讨三者之间的关系；指出资本和技术二者紧密结合，推动网络媒体的发展壮大，在某种程度上决定了我国网络媒体当前的市场格局——商业平台、传统媒体、自媒体三足鼎立。第二部分(第六章至第十章)，从资本、技术和制度三个层面，分类考察我国报业、广电业、商业平台等在当下的内容生产现状和存在的问题。第三部分(第十一章)，是反思与总结，包括对建立在技术和资本基础上的互联网垄断的反思，', NULL, 'G219.1', '本书适用于传播学研究人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1318, '978-7-119-12912-9', '中国时政话语翻译基本规范·英文', NULL, '外文出版社有限责任公司', 58, '中国对外政策', NULL, NULL, NULL, NULL, NULL, '234页', '21cm', NULL, '外译规范 译中国', '本书从时政话语的概念、载体和特点入手，明确其外译工作的原则要求、基本策略和工作流程，就新时代中国核心时政概念和表述提出相应的规范译法，围绕机构、职务、行政区域名称、人名和民族名等专有名词的英译提出通用性原则，并对时政话语英译出版体例进行了初步规范。', '手册', 'G219.20-62', '本书适用于翻译工作者', '精装', '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1319, '978-7-5012-6541-1', '阿拉伯国家主要跨国媒体研究', '曹笑笑著', '世界知识出版社', 68, '传播媒介', NULL, NULL, NULL, '浙江智库', NULL, '205页', '24cm', NULL, '浙江外国语学院国别和区域研究中心建设项目 浙江省“一带一路”研究智库培育单位项目 2021年浙江省哲学社会科学规划课题“中国媒体在阿拉伯国家的传播力提升研究”项目资助', '本书采用“问题框架式”的研究方法，即围绕“阿拉伯国家主要跨国媒体是如何构建其对阿传播力的”这一问题来构建研究框架。', NULL, 'G219.371', '本书适用于传播媒介产业研究人员', NULL, '2022.06', 'chi', NULL);
INSERT INTO `book` VALUES (1320, '978-7-5607-7215-8', '县级台的改革发展与媒体融合之路', '王长涛著', '山东大学出版社', 58, '电视台', NULL, NULL, NULL, NULL, NULL, '202页', '24cm', NULL, NULL, '本书以县级台为研究对象，侧重研究山东省120多家县级台的基本情况、生存现状、改革发展、媒体融合等。课题采用实地调查、政策解读、个案分析、数据比较等方法进行系统研究，重视使用一手数据资料。研究中，重点针对县级台存在的价值，县级台的体制机制改革、“四级办台”政策的必要性，县级融媒体中心建设前景等热点问题进行探讨分析。', NULL, 'G229.275.2', '本书适用于电视台工作研究人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1321, '978-7-5130-8037-8', '融媒体时代的出版与传播', '张聪主编', '知识产权出版社有限责任公司', 65, '电子出版物', NULL, NULL, NULL, NULL, NULL, '195页', '24cm', NULL, '北京印刷学院科技创新服务能力建设－高精尖学科建设－新闻传播学项目基金资助出版', '本书以融媒体时代的出版与传播为研究对象，对融媒体时代出现的新的出版和传播形象进行解读，为宏观把握融媒体时代的发展态势提供了研究与思考。', NULL, 'G237.6', '本书适用于媒体及出版领域相关人员', NULL, '2021.12', 'chi', NULL);
INSERT INTO `book` VALUES (1322, '978-7-5117-4070-0', '海伦话出版', '孙赫男著', '中央编译出版社', 68, '出版业', '一位女出版人二十年的中外出版视野', NULL, NULL, NULL, NULL, '128页', '24cm', NULL, NULL, '本书作者在二十年中，密切跟踪了国际出版业从传统出版到数字出版的全过程，并且不断在实践中进行总结，以一个国际出版人的视角，记录了这个过程。', '文集', 'G239.1-53', '本书适用于广大出版界工作人员', '精装', '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1323, '978-7-5194-6870-5', '晚清贵州图书传播研究', '李明勇著', '光明日报出版社', 95, '图书', '1840-1911', NULL, NULL, '光明社科文库', NULL, '247页', '24cm', NULL, '光明版', '本书从文化传播学的视角，综合运用文献学、目录学、社会学等学科知识，将研究对象置于当时的时代背景之下，对晚清贵州图书的传播环境、刻书机构、刻书内容、流通方式、传播受众、传播效果等方面进行研究。', NULL, 'G239.2', '本书适用于文化传播研究人员', '精装', '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1324, '978-7-121-44436-4', '从事艺人统筹，挺好的', '陈欢科著', '电子工业出版社', 98, '文娱活动', NULL, NULL, NULL, NULL, NULL, '262页', '21cm', NULL, '影响力', '本书作者介绍了艺人统筹职业及行业前景，个人能力和职业要求匹配自测，如何入行，沉浸式了解艺统工作的日常，提供艺统从新手到高手的进阶方法和经验。', NULL, 'G241.3', '本书适用于准备从事艺人统筹职业的人员', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1325, '978-7-5013-7570-7', '文津学志', '《文津学志》编委会编', '国家图书馆出版社', 98, '古籍', NULL, '第十九辑', NULL, NULL, NULL, '316页', '26cm', NULL, '傅增湘先生诞辰一百五十周年纪念专刊', '本书分为5个栏目：傅增湘先生生平研究、藏园群书捐赠公藏研究、藏园遗墨整理与研究、藏园版本目录学研究、藏园群书研究，收录相关研究文章25篇，如：《傅增湘与国家图书馆过往系年》《藏园外库普通书捐赠始末》等。', NULL, 'G255.1-55', '本书适用于古籍研究人员', NULL, '2022.10', 'chi', NULL);
INSERT INTO `book` VALUES (1326, '978-7-5013-7520-2', '嘤其鸣矣', '南江涛主编', '国家图书馆出版社', 98, '文献学', '青年学者说文献学', NULL, NULL, NULL, NULL, '526页', '24cm', NULL, NULL, '本书收录的作者均从自身角度讲述他们眼中的“文献学”，分享求学治学过程中的经历、方法和体悟，对读者了解文献学这门科学提供一些帮助。', '文集', 'G256-53', '本书适用于文献学研究者', '精装', '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1327, '978-7-121-44321-3', '知识图谱', '李娇[等]著', '电子工业出版社', 88, '科技文献', '面向科技文献的构建技术与应用实践', NULL, NULL, NULL, NULL, '182页', '24cm', NULL, NULL, '本书梳理了知识图谱构建涉及的关键技术及典型知识服务案例，提出面向科技文献资源和专题知识的科研知识图谱构建方案及以此为基础支撑的科研综述知识应用实践，是知识图谱在情报学或图书馆学领域交叉集成应用的研究成果。', NULL, 'G257.36', '本书适用于图书情报、管理科学与工程等相关领域的知识组织与智能知识服务研究人员', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1328, '978-7-5615-8479-8', '厦门大学图书馆百年纪事', '厦门大学图书馆编', '厦门大学出版社', 88, NULL, NULL, NULL, NULL, NULL, NULL, '15,345页', '24cm', NULL, '厦门大学百年校庆系列出版物 百年院系史系列', '本书收录厦门大学图书馆1921年4月6日－2021年4月6日的史事，采用编年体进行编纂，一事一条，以年月日的先后排序；以“求真存实”为原则，征引史料以存世文献为主，主要包括文书档案、出版报刊、实物资料等。', NULL, 'G259.256', '本书适用于广大图书馆管理人员', NULL, '2021.12', 'chi', NULL);
INSERT INTO `book` VALUES (1329, '978-7-5010-7750-2', '和田地区博物馆', '和田地区博物馆编著', '文物出版社', 30, '博物馆', NULL, NULL, NULL, NULL, NULL, '108页', '21cm', NULL, '带你走进博物馆', '本书分为历史沿革、馆藏文物、展览陈列、馆际交流等几个部分，介绍了和田地区博物馆，是全面了解和田博物馆和该地区的窗口。', NULL, 'G269.274.52', '本书适用于博物馆爱好者', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1330, '978-7-5028-5298-6', '科学通史', '(美)丹尼尔·J. 麦考恩，(美)霍莉·范德沃尔著', '地震出版社', 188, '科学史', '科学的历史与哲学', NULL, NULL, NULL, NULL, '692页', '23cm', NULL, '通俗学科史 兴盛乐 地震版', '本书讲述了天文学、物理学、化学和生命科学等核心科学领域的科学发展史，涉及亚里士多德、哥白尼、伽利略、笛卡儿、牛顿等伟大科学家及其重要著作和贡献。本书浓缩了两干多年来人类对自然、世界和自身的思考与思想精华，以主题划分，以时间为轴，通过主题式学习引入系统的规程与实践方法及答案，阐明了西方科学历史和哲学中的思想价值和含义。', NULL, 'G3', '本书适用于科学史爱好者', NULL, '2022.10', 'chi', NULL);
INSERT INTO `book` VALUES (1331, '978-7-5130-8352-2', '基因技术高质量专利的创造与运用', '杨帆主编', '知识产权出版社有限责任公司', 118, '基因枪', NULL, NULL, NULL, '高价值专利培育指导丛书', NULL, '360页', '24cm', NULL, NULL, '本书聚焦于基因技术领域，从基因技术的发展现状、相关政策法规、专利申请特点、专利审查实践和专利运用实例出发，介绍了如何从技术创新开始，发现、形成、获得高质量的专利并对其进行运用，为该领域的创新主体提供富有领域特色的专利相关知识，以提升专利申请质量，助力基因技术产业快速、高质量发展。', NULL, 'G306', '本书适用于基因枪专利研究人员', NULL, '2022.10', 'chi', NULL);
INSERT INTO `book` VALUES (1332, '978-7-5130-8385-0', '专利技术交底要义', '赵向阳主编', '知识产权出版社有限责任公司', 118, '专利技术', NULL, NULL, '高端稀土功能材料分册', '高价值专利培育指导丛书', NULL, '372页', '24cm', NULL, NULL, '本书聚焦高端稀土功能材料领域，从技术发展态势和保护需求、申请文件撰写特点、如何选择专利代理及结构、如何写好技术交底书、案例实操等多个方面逐层深入，向创新主体普及富有领域特色的专利基础知识和专利技术交底要点，增强创新主体的知识产权保护意识和能力。', NULL, 'G306.0', '本书适用于专利技术人员', NULL, '2022.10', 'chi', NULL);
INSERT INTO `book` VALUES (1333, '978-7-121-43841-7', '全球科学数据出版态势分析报告', '周园春[等]著', '电子工业出版社', 88, '科学研究', '2016-2020', NULL, NULL, NULL, NULL, '97页', '24cm', NULL, NULL, '本书对科学数据出版的发展历史进行了回顾总结，重点梳理了2016-2020年科学数据出版在不同学科领域的发展趋势、期刊和出版商的数据政策、不同国家的科学数据出版实践等方面的态势，分析了科学数据出版变化情况；同时，从论文和专利引用角度展示了科学数据出版的工作成效；以中国的科学数据出版与共享实践为例进行典型案例分析，并对科学数据出版建设提出建议。', NULL, 'G31', '本书适用于科研人员、学术期刊编辑、科研管理人员，以及其他与科学数据生产、管理、共享工作相关的人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1334, '978-7-121-44732-7', '中国超大城市高学历人才流动的理论与实证研究', '胡磊，孙茂华著', '电子工业出版社', 49, '特大城市', NULL, NULL, NULL, NULL, NULL, '145页', '24cm', NULL, NULL, '本书以中国超大城市的高学历人才为研究对象，探讨他们从超大城市外迁的原因、生活预期的类型及形成机制、对积分落户的参与及其对生活预期的影响、从超大城市外迁的决策机制。', NULL, 'G316', '本书适用于管理科学与工程、公共管理、城市经济学等专业的研究生和教师，以及政府部门相关人员', NULL, '2023.03', 'chi', NULL);
INSERT INTO `book` VALUES (1335, '978-7-5189-9780-0', '中国区域创新能力监测报告', '科学技术部战略规划司编', '科学技术文献出版社', 106, '技术革命', NULL, '2022', NULL, NULL, NULL, '305页', '29cm', NULL, '国家创新调查制度系列报告', '本书包括5个部分的内容：一是区域创新能力监测指标体系的构建；二是全国创新能力监测指标年度间的比较；三是各地区创新能力监测指标按5个子系统的数据发布；四是创新能力监测数据按地区发布；五是区域创新能力监测指标的简要说明。', NULL, 'G322', '本书适用于创造能力研究人员', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1336, '978-7-5222-0013-2', '融合教育学校教学与管理', '彭霞光，杨希洁，冯雅静等编著', '华夏出版社有限公司', 49, '学校教育', NULL, NULL, NULL, NULL, NULL, '271页', '21cm', NULL, NULL, '本书在解读融合教育相关概念，梳理国内外融合教育发展经验的基础上，针对融合教育学校在行政管理、环境创设、班级管理、课程改革、IEP制订、课堂教学、教师培养、家校合作、质量评价等方面面临的困难进行了解读，并为融合教育学校的建立与管理提供了一系列可供参考的建议。', NULL, 'G4', '本书适用于决策者、管理者和一线教师', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1337, '978-7-300-31295-8', '教育发生的地方', '李斌著', '中国人民大学出版社', 68, '教育研究', NULL, NULL, NULL, NULL, NULL, '191页', '24cm', NULL, '源创图书', '本书致力于回答这样的问题：理想的学校教育应该是什么样子，它在何种环境下才会真正发生。每一位读者都可以把自己代入其中，去畅想教育的可能性，去反思自己的成功与失误，并借鉴书中的方法和智慧让真正的教育落地。', NULL, 'G40-03', '本书适用于广大教育工作者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1338, '978-7-5615-8772-0', '现代教育技术应用', '冉新义，刘冰，安素平主编', '厦门大学出版社', 48, '教育技术学', NULL, NULL, NULL, NULL, '2版', '299页', '26cm', NULL, '闽南师范大学教材建设立项资助', '本书按照理论与设计篇、教学资源开发篇、应用篇三部分展开。第一部分是理论与设计篇，内容包括现代教育技术概述、教学设计。第二部分是教学资源开发篇，包括多媒体素材的获取与处理、多媒体课件的设计与制作、微课程的设计与制作。第三篇是应用篇，包括数字化教学环境、网络教育应用、新一代信息技术赋能教育教学改革，重在培养和提高学生构建和应用信息化学习环境的能力。', NULL, 'G40-057', '本书适用于高等师范院校师范类本科专业学生', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1339, '978-7-5334-9495-7', '道德教育的科学与艺术', '(加)季莲·罗森伯格(Gillian R. Rosenberg)著', '福建教育出版社', 45, '德育', 'teaching morally and teaching morality', NULL, NULL, '梦山书系', NULL, '254页', '24cm', NULL, 'Routledge 闽教出版', '本书对道德地教和教以道德进行了理论性和实践性的探索。关于教学实践的道德维度和道德教育，已有西方文献构建了一个涉及四个教学研究领域的概念框架。这四个领域分别是教师有关教与学和课堂生活的观念、传递给学生的道德内容、教道德的策略，以及评价学生社会性和道德发展的方法。虽然这个概念框架为本书提供了理论基础，但是本书的大部分内容用来描述和阐释特里·肯尼迪的行动、作为和实践的道德本质。你将会逐渐了解她、她的学校、她的班级，以及她的学生。', NULL, 'G41', '本书适用于教育工作者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1340, '978-7-5151-0862-9', '积极教育心理学', '朱可可著', '西苑出版社', 49.8, '教育心理学', NULL, NULL, NULL, NULL, NULL, '230页', '21cm', NULL, NULL, '本书运用专业的积极教育的教育理论，结合中国孩子的心理发展特点和教育要求，对孩子在成长教育过程中遇到的各种问题进行分析，无论是能力发展的关键期，情绪问题的处理，心理防御机制的使用，自卑、孤独、自我价值感的缺失问题，性格的养成，性格对未来职业规划的影响等，都有针对性的教育策略，帮助家长以科学的方式培养健康的孩子。', NULL, 'G44', '本书适用于广大家长', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1341, '978-7-115-60352-4', '大学生心理健康教育教师用书', '申子姣，夏翠翠主编', '人民邮电出版社', 59.8, '大学生', '手把手教你打造体验式课堂', NULL, NULL, NULL, NULL, '187页', '26cm', NULL, '人邮教育', '本书共三部分，包括如何更好地发挥本书的价值、大学生心理健康教育课程设计和多元化教学设计及课程建设。第一部分从积极心理学指导下的教学理念、开展教学设计的思路、提高教学效果的方法与技巧以及心理健康教育教学队伍建设的建议几方面介绍如何更好地发挥本书的价值。第二部分介绍十七堂课的课程设计，包括课程导学及班级团队建设、大学生心理健康、大学生健全自我意识塑造、大学生人格发展与心理健康、大学生学习心理、生涯规划与大学生活设计、大学生的人际关系(含两堂课)、大学生健康恋爱及性心理的培养(含两堂课)、大学生情绪管理(含两堂', '教学参考资料', 'G444', '本书适用于高校大学生心理健康教育相关专业师生；思想政治工作者、学生工作者和辅导员', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1342, '978-7-300-31291-0', '读懂小学生', '杨敏毅，吴权著', '中国人民大学出版社', 68, '小学生', '心理特级教师的咨询手记', NULL, NULL, NULL, '修订本', '210页', '24cm', NULL, '源创图书', '本书采用故事叙述和心理对话的方式写作，向读者呈现小学生的行为特征，分析案例中隐藏的主观和客观原因，展示咨询脉络。', NULL, 'G444', '本书适用于中小学教师、班主任', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1343, '978-7-300-31292-7', '读懂初中生', '杨敏毅，黄莉莉著', '中国人民大学出版社', 68, '心理健康', '心理特级教师的咨询手记', NULL, NULL, NULL, '修订本', '221页', '24cm', NULL, '源创图书', '本书帮助读者从心理学层面了解学生的心理困惑，分析学生行为背后的原因，提出引导与干预方法的案例读本。这48个典型案例能让你从中看到你学生的影子，给你带来有益的启示和切实的帮助。', '教学参考资料', 'G444', '本书适用于中小学教师、班主任', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1344, '978-7-300-31293-4', '怎样读懂学生', '杨敏毅，谢晓敏著', '中国人民大学出版社', 68, '高中生', '心理特级教师的建议', NULL, NULL, NULL, '修订本', '235页', '24cm', NULL, '源创图书', '本书共分四辑，围绕学业、亲子沟通、同伴及异性交往、师生关系等学生面临的常见问题展开，共48个案例。每个案例后面还附有“聆听手记”，让教师和家长了解孩子出现这些问题的原因和其隐藏的意义。', NULL, 'G444', '本书适用于中小学教师、班主任', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1345, '978-7-5222-0423-9', '温暖你的大学时光', '丁闽江著', '华夏出版社有限公司', 69, '大学生', NULL, NULL, NULL, '大学生心理健康素养提升工程系列丛书', NULL, '425页', '24cm', NULL, NULL, '本书内容包含：大学心理适应、大学生活规划、成长困惑化解、心理素质提升、心理疾病康复、考研就业帮扶、重大事件应对、家校积极合作、心理教育感悟等。', NULL, 'G444', '本书适用于广大大学生、辅导员、心理工作者、家长', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1346, '978-7-5618-7340-3', '心理委员工作蓝皮书', '詹启生，姚斌，张影侠主编', '天津大学出版社', 48, '大学生', '全国心理委员工作展示与调查', NULL, NULL, NULL, NULL, '13,171页', '24cm', NULL, '2022年', '本书涵盖了177所高校对心理委员工作的全面展示，由此既可以看到中国高校心理委员工作概貌，还可以看到对757所高校的调研所反馈出的中国高校心理委员岗位设置、运行机制、培训方案、表彰激励、综合管理等，为全国高校心理委员工作的开展提供权威的数据。', NULL, 'G444', '本书适用于心理健康教育工作者', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1347, '978-7-5618-7341-0', '高校学生心理健康服务标准化建设与实践', '詹启生，李永慧，莫华敏主编', '天津大学出版社', 48, '大学生', NULL, NULL, NULL, NULL, NULL, '174页', '24cm', NULL, NULL, '本书共分五章，内容包括：心理危机干预体系的标准化建设、心理委员工作的标准化建设、心理健康课程的标准化建设、心理咨询的标准化建设、心理健康活动的标准化建设。', NULL, 'G444', '本书适用于大学生心理健康教育研究人员', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1348, '978-7-5508-3900-7', '劳模工作室教育基建后勤骨干高级研修班论文集', '汪继起主编', '西泠印社出版社', 68, '学校', NULL, NULL, NULL, NULL, NULL, '362页', '26cm', NULL, NULL, '本书汇编内容广泛，有的论文聚焦新建学校建设管理，有的论文聚焦老旧学校维修改造，有的论文则聚焦智慧学校建设。', '文集', 'G48-53', '本书适用于学校教育工作者', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1349, '978-7-5228-0785-0', '中国省域义务教育空间秩序研究', '吕赛鸫著', '社会科学文献出版社', 118, '省', NULL, NULL, NULL, NULL, NULL, '283页', '24cm', NULL, '国家自然科学基金项目成果 教育部人文社会科学研究青年基金项目成果 云南省万人计划教学名师潘玉君工作室成果 云南师范大学博士科研启动项目成果', '本书遵循人地关系地域系统理论，以义务教育均衡发展为研究目标，以全国省域为研究尺度，从地理学角度应用GIS技术和空间统计分析方法研究义务教育空间秩序问题。', NULL, 'G522.3', '本书适用于教育理论研究人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1350, '978-7-5230-0045-8', '中国教育统计年鉴', '中华人民共和国教育部发展规划司[编]', '中国统计出版社有限公司', 268, '教育统计', NULL, '2021', NULL, NULL, NULL, '19,493页', '26cm', NULL, NULL, '本书包括以下部分：综合部分、高等教育、中等教育、初等教育、特殊教育、学前教育、各级各类学校的分布情况、办学条件、科学研究等，是各有关部门研究教育改革发展、制定教育规划等方面的资料性年刊。', '年鉴', 'G526.6-54', '本书适用于统计工作者', '精装', '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1351, '978-7-109-30332-4', '乡村振兴战略视域下青年大学生的角色和定位研究', '章艳涛著', '中国农业出版社', 58, '大学生', NULL, NULL, NULL, NULL, NULL, '115页', '24cm', NULL, '浙江省教育厅课题：从课堂到“田野”：大学生思想政治教育路径转向研究 国家社科基金重点项目“中国共产党一百年乡村建设宝贵经验及经典案例研究”', '本书共八章，以乡村振兴战略为视域，围绕“马克思主义在农村传播的基本历程、山区文化资源的角色和定位、国家‘三大战略’衔接的经验与问题、农业科技创新、乡村振兴战略的实施、‘两山’理论的践行、我国主流意识形态建设、‘田野’理论的实践”八个方面，探讨青年大学生应当扮演怎样的角色、如何定位自身的发展等问题，充分激发当代青年大学生的乡土自觉与国情意识。', NULL, 'G641', '本书适用于思想政治教育研究人员', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1352, '978-7-5226-1030-6', '课程思政十法', '万军，徐金寿等著', '中国水利水电出版社', 60, '高等学校', NULL, NULL, NULL, NULL, NULL, '189页', '24cm', NULL, NULL, '本书对课程思政的途径方法作了研究、梳理与提炼，提出了适合各层面开展课程思政的十种方法，简称“课程思政十法”；并结合学校育人特色，在体系构建、师生认知、教学实施、资源建设、评价监控等方面的实践基础上，阐释十种方法的根源、目的、原理、应用、案例以及相互关系等。', NULL, 'G641', '本书适用于教学研究人员及一线教育人员', NULL, '2022.06', 'chi', NULL);
INSERT INTO `book` VALUES (1353, '978-7-5607-7218-9', '仁侠青春', '郝书翠主编', '山东大学出版社', 48, '大学生', NULL, '第2辑', NULL, NULL, NULL, '206页', '24cm', NULL, '山东省本科教改项目', '本书为山东大学马克思主义学院(威海)“仁侠青春”微信公众号文章精选，主要是思政课的课外实践中学生写的“观后感”、“读后感”等，有助于引导大学生树立和坚持正确的历史观、国家观、文化观，不断增强中华民族的归属感、认同感、尊严感、荣誉感，提升中华优秀传统文化的感染力、感召力，对于思政网络阵地开辟、大学生文化自信教育具有示范作用。', NULL, 'G641', '本书适用于广大大学生', NULL, '2022.01', 'chi', NULL);
INSERT INTO `book` VALUES (1354, '978-7-5615-8711-9', '实践育人', '陈飞主编', '厦门大学出版社', 38, '大学生', '高校思想政治理论课社会实践指导教程', NULL, NULL, NULL, NULL, '274页', '26cm', NULL, NULL, '本书共六章，具体内容包括：第一章“思想政治理论课社会实践概述”、第二章“思想政治理论课社会实践的调查方法”、第三章“思想政治理论课社会实践的选题与方案设计”、第四章“思想政治理论课社会实践的准备与实施”、第五章“思想政治理论课社会实践常用文体的写作”和第六章“思想政治理论课社会实践成果精选”。', '教材', 'G641', '本书适用于高校大学生', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1355, '978-7-5615-8837-6', '奋进新征程 共圆中国梦', '徐进功主编', '厦门大学出版社', 88, NULL, '厦门大学港澳台学生征文集（2018-2021年）', NULL, NULL, '高校思想政治工作队伍培训研修中心（厦门大学）理论与实践丛书', NULL, '244页', '24cm', NULL, NULL, '本书是2018-2021年间厦门大学港澳台学生征文集，或以自身的切实感受，或聚焦历史变迁，或围绕重要节点，用文字记录所见、所闻、所想，讲成长之路、述亲历之事、抒感恩之心、明肩头之志。', NULL, 'G641-53', '本书适用于广大高校师生', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1356, '978-7-5228-1059-1', '大学生自主学习', '廖旭梅著', '社会科学文献出版社·政法传媒分社', 98, '大学生', '影响因素与有效策略', NULL, NULL, '当代教育研究丛书', NULL, '262页', '24cm', NULL, '全国教育科学“十三五”规划教育部青年课题“大学生自主学习能力提升工作坊构建与成效追踪研究”支持', '本书基于鲜活的教育实践，对大学生自主学习的影响因素和促进大学生自主学习的有效路径进行了提炼。', NULL, 'G642.46', '本书适用于教育理论研究人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1357, '978-7-5218-1978-6', '创新专业学位研究生培养模式研究', '贺克斌等著', '经济科学出版社', 148, '研究生教育', NULL, NULL, NULL, NULL, NULL, '551页', '25cm', NULL, '教育部哲学社会科学研究重大课题攻关项目 “十三五”国家重点出版物出版规划项目', '本书是教育部哲学社会科学研究重大课题攻关项目《创新专业学位研究生培养模式研究》的成果。项目下设5个子课题组，分别从专业学位培养理念、模式与趋势研究，专业学位培养模式现状及其国际比较研究，临床医学硕士专业学位培养模式研究，MBA、工程硕士等专业学位培养模式研究以及专业学位培养模式创新的政策研究这五个方面展开研究。经过近五年的实证研究，跨单位科研团队的通力协作，完成了本书的撰写工作，本书分为四个部分：第一部分为总论；第二部分为分类视角下的专业学位研究生培养模式调查分析；第三部分为专业学位研究生培养模式案例分析', NULL, 'G643', '本书适用于研究生教育培养模式研究人员', NULL, '2021.05', 'chi', NULL);
INSERT INTO `book` VALUES (1358, '978-7-5218-4081-0', '专业学位研究生教育质量多主体多维度评价体系研究', '赵莉，吴永春著', '经济科学出版社', 78, '研究生教育', NULL, NULL, NULL, NULL, NULL, '179页', '24cm', NULL, '山东省研究生教育质量提升度计划建设项目（研究生教育教学改革研究项目）“双一流”北景下山东省专业学位研究生教育质量多主体多维度评价体系研究” ESP', '本书是山东省研究生教育质量提升计划建设项目(研究生教育教学改革研究项目)“‘双一流’背景下山东省专业学位研究生教育质量多主体多维度评价体系研究”的最终成果。本书具体内容分为四个部分：第一部分(第1章)，主要介绍本书的选题来源、研究目的及意义，育定本书的研究内容和框架结构，并总结研究的创新点。第二部分(第2-3章)，主要介绍我国研究生教育发展历程、研究生教育评估历史及现状，重点评述专业学位研究生教育质量评价研究的相关文献。第三部分(第4-5章)，主要介绍专业学位研究生教育质量多主体多维度评价指标体系的构建和', NULL, 'G643', '本书适用于研究生教育研究人员', NULL, '2022.10', 'chi', NULL);
INSERT INTO `book` VALUES (1359, '978-7-5194-6868-2', '新时代高校辅导员思想政治教育理论与实践探析', '赵艳芳著', '光明日报出版社', 78, '高等学校', NULL, NULL, NULL, NULL, NULL, '239页', '24cm', NULL, '光明版', '本书分为理论研究篇和实践探索篇，共有十一章的内容。理论研究篇概述了思想政治教育的内涵及特征，分析了辅导员思想政治教育的根本任务，探讨了辅导员思想政治教育的主要内容，研究了辅导员思想政治教育的角色定位，论述了辅导员思想政治教育的基本原则和主要方法，探析了辅导员思想政治教育的创新发展。实践探索篇共有19个案例，分析了以思想政治教育引领学生成长的案例，探讨了把思想政治教育融入学风建设和学生生活的案例，研究了在心理疏导和突发事件处置中彰显思想政治教育的案例，每个案例由案例概述、案例分析、辅导过程、经验启示四个部分', NULL, 'G645.1', '本书适用于高校辅导员', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1360, '978-7-5693-1715-2', '大学教师专业能力发展的支持服务模式', '孙二军著', '西安交通大学出版社', 85, '高等学校', NULL, NULL, NULL, NULL, NULL, '206页', '24cm', NULL, NULL, '本书在借鉴和分析30所国内院校和15所外语院校的基础上，着重探讨了“有组织的讲座”“有指导的培训”“有主题的工作坊”“有互动的沙龙”“有切磋的竞赛(观摩)”等模式，它们之间的协同互补是模式创新的关键所在。', NULL, 'G645.12', '本书适用于高等学校师资培养研究人员', NULL, '2021', 'chi', NULL);
INSERT INTO `book` VALUES (1361, '978-7-5607-6928-8', '“就业困境”下中国大学生学习及教育对策研究', '高静著', '山东大学出版社', 60, '大学生', NULL, NULL, NULL, NULL, NULL, '292页', '24cm', NULL, '全国教育科学规划国家青年项目成果 山东省教育学高峰学科著作出版基金资助', '本书聚焦我国普通高校本科生学习，通过对“就业困境”下大学生的学习现状调查，考察普通高校大学生在大学学习过程中面临的困难与挑战，揭示中国大学生学习文化“黑箱”，并通过对国外相关教育经验的考察与比较，探讨有针对性、可行性的教育支持路径。', NULL, 'G645.5', '本书适用于高校本科生', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1362, '978-7-300-31115-9', '大学生职业生涯规划与就业创业指导', '苏文平主编', '中国人民大学出版社', 49, '大学生', NULL, NULL, NULL, NULL, '2版', '270页', '26cm', NULL, '普通高等学校应用型教材·工商管理', '本书共分5篇8章，涉及生涯唤醒，全面认知自我，认知外部世界，生涯目标决策、行动规划与实施，个人职业生涯管理五大方面，既总结了大量实践经验和规律，又提出了各种方案和建议，同时收录了30多篇原创案例。', '教材', 'G647.38', '本书适用于应用型高校学生', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1363, '978-7-5121-4809-3', '大学生就业服务模式探索', '孙文博著', '北京交通大学出版社', 88, '大学生', NULL, NULL, NULL, NULL, NULL, '166页', '24cm', NULL, NULL, '本书通过研究独立学院“六位一体”大学生就业服务模式，积极探索发挥“六位一体”合力作用，提出通过建设就业制度机制、组织就业协调机构、做好区域公共就业服务项目、搭建区域就业信息服务平台、加大投入和创建优越的人才环境以及增加就业调查和反馈等具体方案，为拓宽大学毕业生就业渠道提供借鉴。', NULL, 'G647.38', '本书适用于大学生就业研究人员', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1364, '978-7-5228-0238-1', '外国高等教育发展创新研究', '彭江，陈功等著', '社会科学文献出版社·国别区域分社', 158, '高等教育', NULL, NULL, NULL, NULL, NULL, '294页', '24cm', NULL, NULL, '本书选取了七个高等教育发展水平较高的国家，分别为美国、英国、德国、法国、澳大利亚、加拿大和瑞士，从其高等教育概况、高等教育的发展历程、主要措施、基本经验等展开论述。其中，各国高等教育发展创新的措施主要从其法律制度、管理体制、教学模式、科研模式、社会服务、国际化、教师队伍建设、经费配置、质量保障等九个维度进行了分析。', NULL, 'G649.1', '本书适用于高等教育研究人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1365, '978-7-5361-6871-8', '基于学习成果评价的大学生高阶思维能力评价框架及其测量研究', '杨翊著', '广东高等教育出版社', 48, '高等教育', NULL, NULL, NULL, '中国高等教育学会高等教育学优秀博士学位论文丛书', NULL, '185页', '24cm', NULL, '北京语言大学院级项目资助（中央高校基本科研业务费专项资金）', '本书通过高阶思维能力概念的解析、教育目标分类学等各类理论的对比分析以及大学生学习成果测试、框架内容的解构、分析等，构建了大学生高阶思维能力评价框架。', NULL, 'G649.2', '本书适用于教育学研究人员', NULL, '2021.01', 'chi', NULL);
INSERT INTO `book` VALUES (1366, '978-7-5194-6819-4', '北京建筑大学校史', '《北京建筑大学校史》编写组编', '光明日报出版社', 99, NULL, '1907-2020', NULL, NULL, NULL, NULL, '12,345页', '24cm', NULL, '光明版', '本书记述了北京建筑大学百年发展的历史进程，围绕国家建筑业和城乡建设发展，围绕首都建设主战场，在人才培育、科学研究、学科发展、服务首都重大项目等方面取得成果和成绩，展现历届师生风采，宣传杰出校友事迹。', NULL, 'G649.281', '本书适用于想了解北京建筑大学校史的读者', '精装', '2022.09', 'chi', NULL);
INSERT INTO `book` VALUES (1367, '978-7-5676-5668-0', '安徽师范大学年鉴', '《安徽师范大学年鉴》编纂工作委员会编', '安徽师范大学出版社', 150, NULL, NULL, '2020卷', NULL, NULL, NULL, '375页', '29cm', NULL, NULL, '本书以文章和条目为基本载体，共分学校基本情况、机构与人员、重要文件与领导讲话、党建工作、教育教学工作、师资队伍建设、学科建设与科学研究、社会服务、对外交流与开放办学、内部管理工作、图书档案与出版编辑工作、学院工作、皖江学院工作、附属单位工作、大事记、获奖情况等栏目。', NULL, 'G659.285.4-54', '本书适用于高校师生', '精装', '2022.09', 'chi', NULL);
INSERT INTO `book` VALUES (1368, '978-7-5426-7686-3', '体育绘图', '王建涛，邵斌，克峰主编', '上海三联书店', 98, '体育', NULL, NULL, NULL, NULL, NULL, '463页', '24cm', NULL, '2019年安徽省高校质量工程教学研究项目 2019年安徽省高校质量工程体操精品线下开放课程', '本书共十章，内容包括：绘图概述、绘图术语、平面图的画法与头高比例、透视规律与斜面图的画法、体操动作简图的画法、田径动作简图的画法、武术动作简图的画法、篮球动作简图的画法、排球动作简图的画法、足球动作简图的画法。', NULL, 'G8', '本书适用于体育教育专业学生', NULL, '2022.06', 'chi', NULL);
INSERT INTO `book` VALUES (1369, '978-7-5207-2980-2', '集邮电台', '任永信主编', '东方出版社', 68, '冬季奥运会', '冬奥盛典 方寸铭记', NULL, NULL, '“集邮电台”系列丛书', NULL, '180页', '21cm', NULL, NULL, '本书以北京冬奥会赛事期间，在喜马拉雅分享平台播放的集邮电台专题访谈节目为基础，将奥运文化和集邮文化进行完美融合，以方寸视野，再现北京冬奥会从申办到筹办的过程、记录北京冬奥会从激烈赛事到精彩的开闭幕式盛典，完成邮政使命。', NULL, 'G811.212', '本书适用于大众读者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1370, '978-7-5029-7882-2', '中国竞技体育国际竞争力评析与发展研究', '朱玉霞著', '气象出版社', 80, '竞技体育', NULL, NULL, NULL, NULL, NULL, '268页', '24cm', NULL, '国家社会科学基金青年项目“中国竞技体育国际竞争力评析与发展研究”资助', '本书主要在竞技体育国际竞争力概念解读与界定的基础上，构建了竞技体育国际竞争力双框架评估模型，并据此对中国竞技体育国际竞争力进行了分析，在与所选国家竞技体育国际竞争力的比较研究基础上，界定我国竞技体育在国际竞争中所处的相对位置，阐述了中国竞技体育在国际竞技体育竞争中的优势和劣势，探讨了中国竞技体育未来的发展战略与实施路径。', NULL, 'G812', '本书适用于体育管理者、体育院校师生以及体育社会学、体育管理学研究方向的硕士、博士研究生', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1371, '978-7-5714-2201-1', '大道太极', '黄震寰著', '北京科学技术出版社', 118, '太极拳', '太极拳道修诀要', NULL, NULL, '人文武术精品书系', NULL, '349页', '24cm', NULL, '拳道薪传', '本书共分六章，内容包括：大道太极的理论基础、大道太极修炼之理法、大道太极修炼功法、大道太极义理详释、大道太极推手、大道太极拳拳架。', NULL, 'G852.11', '本书适用于太极拳爱好者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1372, '978-7-5736-0601-3', '围棋手筋宝典1000题', '胡丹蔚编著', '青岛出版社', 45, '围棋', NULL, NULL, '段位篇', NULL, NULL, '211页', '26cm', NULL, NULL, '本书介绍了围棋手筋，内容包括虎、扑、夹、托、挖等二十一关。每一关都列出了足够详尽的题目，以供大家参阅和研读。', NULL, 'G891.3', '本书适用于围棋爱好者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1373, '978-7-5121-4553-5', '“一带一路”背景下东盟国家语言政策研究', '张捷[等]著', '北京交通大学出版社', 49, '语言政策', NULL, NULL, NULL, NULL, NULL, '150页', '24cm', NULL, NULL, '本书共十二章，从历时和共时的角度分别对文莱、柬埔寨、印度尼西亚、老挝、马来西亚、缅甸、菲律宾、新加坡、泰国、越南等东盟国家的语言政策进行了阐述和分析。此外，书中采用实证的方法对东盟国家民众的语言态度及中文学习需求进行了调查研究，然后在此基础上对我国的对外语言政策进行分析，并与东盟国家的语言政策和语言服务需求进行对比，最后根据研究和调查结果就“一带一路”背景下我国与东盟国家的语言教育交流合作提出政策性建议。', NULL, 'H002', '本书适用于语言政策研究人员', NULL, '2022.10', 'chi', NULL);
INSERT INTO `book` VALUES (1374, '978-7-214-27536-3', '从老是说不对到特别会说话', '(日)田中耕比古著', '江苏人民出版社', 49.9, '语言艺术', NULL, NULL, NULL, NULL, NULL, '192页', '21cm', NULL, '职场系列 凤凰传媒', '本书介绍了简单明了的表达方法，换言之，这本书告诉大家如何去表述一件事物的方法。本书不仅可以学习到说明方法，还可以习得如何厘清事件的方法。', '通俗读物', 'H019-49', '本书适用于大众读者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1375, '978-7-300-31294-1', '学以致用的教师阅读', '王春易主编', '中国人民大学出版社', 68, '阅读教学', NULL, NULL, NULL, NULL, NULL, '214页', '24cm', NULL, '王春易作品 源创图书', '本书5篇“全书导读”呈现了5本著作中有关学习与教学的新维度、新思路和新框架。29个学以致用的课堂案例，涉及13个学科，立足核心素养的落实，聚焦课堂实践，为课堂转型中的真实问题和难点、痛点提供可行的解决方案。', NULL, 'H09', '本书适用于教育工作者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1376, '978-7-100-21732-3', '汉语字词关系与汉字职用学', '李运富主编', '商务印书馆', 98, '汉字', NULL, NULL, NULL, '中国语言学前沿丛书', NULL, '457页', '21cm', NULL, NULL, '本书收录了关于汉字职用研究的前沿成果，全书分理论研究和现象研究两部分，共收文26篇。从理论和现象两个方面阐发汉字的职能和实际使用情况，包括个体字符的职能变化、个体语符的用字变化、类别材料的用字现象、不同类别用字现象的比较、不同时代的用字习惯与特点、汉字的超语符职用、字词关系和字际关系的描写、汉字职用变化的原因等，并对日常生活中习焉不察的语言文字现象进行了准确描述与合理解释，反映语言学研究的最新成果和学科脉动。', NULL, 'H12', '本书适用于汉字研究人员', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1377, '978-7-5702-2933-8', '康辉咬文嚼字', '康辉，严晓冬著', '长江文艺出版社', 58, '汉字', NULL, NULL, NULL, NULL, NULL, '276页', '21cm', NULL, NULL, '本书作者康辉撕掉“怼言大师”的标签，回归温润谦恭的“小康”形象，带着他的新绰号“行走的《康熙字典》”，除“咬文嚼字”纠正发音外，从地名、民俗与美食、姓氏和日常等几个角度带领读者长知识。', '通俗读物', 'H124.1-49', '本书适用于大众读者', '精装', '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1378, '978-7-100-21735-4', '作格与汉语语法', '罗天华主编', '商务印书馆', 88, '汉语', NULL, NULL, NULL, '中国语言学前沿丛书', NULL, '20,387页', '21cm', NULL, NULL, '本书围绕“作格与汉语语法”的研究主题，收录了14篇论文。这些论文涉及古代汉语、现代汉语、一些民族语和外语，包含了各位作者对作格问题的历史回顾和深入思考，呈现了该课题多元化的研究现状。', NULL, 'H14', '本书适用于语言学研究人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1379, '978-7-5213-4126-3', '汉语名词形化比较构式的成因及特征分析', '李瑛著', '外语教学与研究出版社', 59.9, '汉语', NULL, NULL, NULL, NULL, NULL, '11,215页', '23cm', NULL, '西华大学重点项目“‘S比N1还N2’构式的‘2M’压制研究”', '本书在分析“S比N1还N2”构式研究现状及其不足，以及自建“S比N1还N2”构式封闭语料库的基础上，从认知语言学“现实－认知－语言”的基本原理出发，基于认知构式语法理论的传承观和压制观，建构了新的理论框架——“2M”压制模型，以对“S比N1还N2”构式进行分析，重点考察构式外部的多重压制和构式内部的多层压制。', NULL, 'H146.2', '本书适用于汉语研究人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1380, '978-7-100-21734-7', '句法构词的理论与实践', '程工主编', '商务印书馆', 85, '汉语', NULL, NULL, NULL, '中国语言学前沿丛书', NULL, '383页', '21cm', NULL, NULL, '本书围绕句法构词的理论探讨及其在汉语中的应用，梳理出该理论传统在国内的研究现状和未来走向。精选具有代表性的学术论文13篇，探讨与论元结构相关的语义问题，展现该理论用于汉语各种特殊结构生成机制分析的具体实践，并重新审视了词、语素等关键性概念。', NULL, 'H146.3', '本书适用于中国语言学、特别是生产语言学领域的学人', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1381, '978-7-5176-0796-0', '新华大字典', NULL, '商务印书馆国际有限公司', 88, '汉字', '双色插图本', NULL, NULL, NULL, NULL, '88,1238页', '21cm', NULL, NULL, '本书共收汉字14588个(含繁体字、异体字)，包括《通用规范汉字表》中的8105个通用规范汉字。', '字典', 'H163', '本书适用于大众读者', '精装', '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1382, '978-7-200-17246-1', '中文作为第二语言学习者的认知研究', '吴思娜著', '北京出版社', 80, '汉语', NULL, NULL, NULL, NULL, NULL, '290页', '24cm', NULL, NULL, '本书从字词、句子、篇章、隐喻理解、具身认知、个体差异与教材需求等多个方面和层面，展示了中文作为第二语言的研究成果。', NULL, 'H195.3', '本书适用于教学研究者', NULL, '2022.06', 'chi', NULL);
INSERT INTO `book` VALUES (1383, '978-7-5228-0739-3', '国际中文教育研究', '王辉主编', '社会科学文献出版社·人文分社', 128, '汉语', NULL, '第五辑', NULL, NULL, NULL, '201页', '24cm', NULL, '“教育部中外语言交流合作中心国际中文教育实践与研究基地项目”资助', '本书设有“汉语国际教育专业课程思政”“国际中文教育‘三教’问题研究”“语言本体研究”等栏目，收录了《汉语国际教育专业课程思政的思考与实践》《汉语国际教育专业硕士课程思政实践与探索》《多语境下阿尔及利亚语言政策历时演变研究》《兴起与嬗变：新中国成立前对外文化教材的历史回顾》等文章。', NULL, 'H195.3', '本书适用于对外汉语教学研究人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1384, '978-7-313-25647-8', '外语能力标准的国别研究', '张蔚磊著', '上海交通大学出版社', 98, '外语', '加拿大与英国', NULL, NULL, NULL, NULL, '296页', '25cm', NULL, '国家社科基金一般项目：外语能力标准的国际比较研究', '本书主要研究了英国和加拿大的语言能力标准。加拿大篇从语言能力标准的概述、结构框架、相关理论、编排表达模式、素养模型和效度方面展开论述，然后分析了标准及其衍生品的应用，最后将标准与《中国英语能力等级量表》进行比较研究。英国篇从宏观和微观两个视角详述了英国语言教育政策的历时发展，分别剖析了英格兰、苏格兰、北爱尔兰、威尔士的语言教育政策以及《国家课程》中的外语能力标准，并对中英外语能力标准和外语教育政策展开比较研究。', NULL, 'H3', '本书适用于教育政策制定者、测试开发人员', NULL, '2022.03', 'chi', NULL);
INSERT INTO `book` VALUES (1385, '978-7-300-29973-0', '同等学力人员申请硕士学位英语水平全国统一考试历年真题详解及模拟', '白洁主编', '中国人民大学出版社', 89.8, '英语', NULL, NULL, NULL, NULL, NULL, '644页', '26cm', NULL, NULL, '本书精选了2013年至2022年的同等学力人员申请硕士学位英语水平全国统一考试试题和三套模拟试卷，并提供了试题解析。', '题解', 'H310.421-44', '本书适用于参加同等学力人员申请硕士学位英语水平全国统一考试的考生', NULL, '2021', 'chi', NULL);
INSERT INTO `book` VALUES (1386, '978-7-119-12580-0', '赖世雄常用英语词汇6000', '赖世雄著', '外文出版社有限责任公司', 45, '英语', NULL, NULL, NULL, NULL, NULL, '559页', '21cm', NULL, '高频实用单词系列 赖世雄经典知语文化 英语词汇', '本书按照从易到难的顺序编排，每个单元的单词又按照单词出现的频率进行排序。所有收录的单词均出自日常生活及备考最常出现的基础词汇，扩增读者的英语知识面并厚植写作能力。每单元的编排方式以单词出现频率为依据，并以色块突显重点。', '自学参考资料', 'H313', '本书适用于英语学习人员', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1387, '978-7-5180-9063-1', '3500英语单词分类速记超图解', '兰颖编著', '中国纺织出版社有限公司', 49.8, '英语', NULL, NULL, NULL, NULL, NULL, '258页', '21cm', NULL, NULL, '本书收录了3500英语单词，按地貌、建筑、社区、农场、交通、人体、服饰、饮食、爱好等分类区分。', '自学参考资料', 'H313', '本书适用于英语学习者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1388, '978-7-5615-8580-1', '隐喻视野下学术英语写作反思性学习研究', '万婉著', '厦门大学出版社', 69, '英语', NULL, NULL, NULL, NULL, NULL, '198页', '24cm', NULL, '国家社科基金项目“隐喻视野下的学术英语写作认知过程反思性学习模式研究”的最新研究成果 得到华侨大学高层次人才科研启动项目和2016年福建省高校杰出青年科研人才培育计划经费资助', '本书阐述了隐喻构建理论及相关理论在学术写作的认知层面和反思性学习层面的具体应用与实践情况，进一步验证了隐喻不仅是语言工具，而且是认知工具的理论，其成果将隐喻构建的认知功能研究向前推进了一大步，从认知层面上探寻出一条提高写作学习与教学效率、培养学生批判性思维能力的新途径。', NULL, 'H315', '本书适用于英语教学研究人员', NULL, '2022.12', 'chi', NULL);
