INSERT INTO `book` VALUES (1390, '978-7-5619-6125-4', '新完全掌握日语能力考试N3阅读考前冲刺', '(日)熊田道子，(日)福冈理惠子，(日)清水知子著', '北京语言大学出版社', 38, '日语', NULL, NULL, NULL, NULL, NULL, '127页', '26cm', NULL, '从日本3A公司原版引进', '本书分为十二课，共选用了37篇文章，专项练习N3级阅读考试中的短篇内容理解、中篇内容理解、长篇内容理解和信息检索这四大题型。书后还附有答案和图解式的解析。', '自学参考资料', 'H369.37', '本书适用于参加日语能力考试N3级考生', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1391, '978-7-5607-7628-6', '山师学人视阈下的中国古代文学', '李宗刚编', '山东大学出版社', 88, '中国文学', '“山师学报”论文选', NULL, NULL, '《山东师范大学学报》（人文社会科学版）文粹书系', NULL, '394页', '24cm', NULL, NULL, '本书主要选取了《山东师范大学学报》上的中国古代文学论文，内容包括中国古代文学历史、文学思潮和文艺理论等方面，收录文章包括《王渔洋和他的诗》《苏辙在齐州》《陶渊明散文艺术浅论》《西晋辞赋观简论》《从元曲看元代文人的心态》等。', '文集', 'I206.2', '本书适用于文学研究人员', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1392, '978-7-5039-7297-3', '王国维的两个世界', '彭玉平著', '文化艺术出版社', 42, NULL, NULL, NULL, NULL, '文艺研究小丛书', NULL, '148页', '19cm', NULL, NULL, '本书包括彭玉平于2015年至2022年发表在《文艺研究》的王国维研究文章四篇，从多角度、多层次展现了作者近年来研究王国维的成果，呈现了王国维的多维世界。', NULL, 'I206.5-53', '本书适用于文艺思想研究人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1393, '978-7-5115-7439-8', '跟着唐诗宋词去旅游', '孟扬主编', '人民日报出版社', 79, '唐诗', NULL, NULL, NULL, NULL, NULL, '255页', '24cm', NULL, NULL, '本书包括：山岳篇——登高望远 气拔山河；登楼篇——极目远方 襟怀山河；临水篇——碧水滔滔 思绪万千；胜迹篇——昔日烽火 今朝风韵；寻古篇——古今之际 且听且吟五部分。收录了《望岳》《独坐静亭山》《黄鹤楼》《赠汪伦》等文章。', NULL, 'I207.2', '本书适用于文学爱好者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1394, '978-7-5166-6351-6', '古诗流变', '王波平著', '新华出版社', 98, '古典诗歌', NULL, NULL, NULL, NULL, NULL, '300页', '24cm', NULL, '知库 文学与艺术 广西教育厅2020年教改重点项目“文化传承视野下的中国古代文学‘三践’教学改革研究”', '本书从横截面角度来观照中国古代文学发展历史。从体例上透视中国古代文学发展历史脉络，以诗歌发展变化为主体，了解每一历史时期诗歌的主要内容和特征，并掌握其文学意义；从结构上以时间为序，分别阐述了诗歌发展的各个时期特点，为萌芽、奠基、嬗变、繁荣、转折、蜕变、终结、裂变和温习等九个时期；从内容上既有古诗的经典传统样式的理解，也有古诗的现代流行的解析，含新诗流变和古诗教学等。', NULL, 'I207.209', '本书适用于古诗爱好者', '精装', '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1395, '978-7-101-15964-6', '关中诗歌图志', '刘锋焘著', '中华书局', 228, '古典诗歌', NULL, NULL, NULL, NULL, NULL, '2册(1072页)', '22cm', NULL, '国家社科基金项目“关中诗歌图志”成果 国家社科基金重大招标项目“唐代到北宋丝绸之路（陆路）上的驿站、寺庙、重要古迹与文人活动、文学创作及文化传播”阶段性成果 长安与丝路文化传播学科创新引智基地建设成果 陕西师范大学中国语言文学“世界一流学科建设”成果', '本书搜集、整理历代关中诗歌文献，再辅以相关的图像(包括文献图像、古迹图像、出土文物图像等)，按照时代的发展分类排列，互相对应，互相印证，以“志”为主，力图以诗歌文献与图像文献相结合的方式，展现关中诗歌的发展历史，展示关中文化乃至文明的发展史。', '图集', 'I207.209-64', '本书适用于诗歌研究人员', '精装', '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1396, '978-7-5194-6829-3', '中国历代豪放诗词赏析', '王振祥编著', '光明日报出版社', 99, '豪放派', NULL, NULL, NULL, '光明社科文库', NULL, '15,335页', '24cm', NULL, '光明版', '本书精选先秦至当代风格相近之诗词歌赋近300首，每首诗包括“作者简介”“诗歌”“注释”“简析”和“最豪放名句”五个内容，以作者年代和创作时间先后为序编排。', NULL, 'I207.22', '本书适用于古典诗歌爱好者', '精装', '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1397, '978-7-301-33300-6', '唐诗十讲', '刘青海著', '北京大学出版社', 68, '唐诗', NULL, NULL, NULL, NULL, NULL, '295页', '23cm', NULL, '培文', '本书以十种诗歌主题为经，以百首经典作品为纬，一线贯穿初唐、盛唐和中晚唐诗歌发展，展示唐诗的艺术魅力，并以通俗的、文学性的语言来呈现唐代士俗生活的图景。全书共分十讲：宫怨(宫墙内的爱怨)，咏物(秋日蝉声)，田园(诗意栖居)，山水(浓淡相宜)，边塞(慷慨悲歌)，送别(骊歌声声)，爱情(朦胧的胜境)，悼亡(真情独白)，音乐(写声的艺术)，思妇(相思与怨别)。', NULL, 'I207.227.42', '本书适用于唐诗研究人员', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1398, '978-7-5205-3618-9', '唐诗夜航', '张起，张天健著', '中国文史出版社', 89, '唐诗', NULL, NULL, NULL, NULL, NULL, '416页', '24cm', NULL, NULL, '本书从唐人笔记、史志唐书、历朝诗话、传述钩沉等等之中，梳理文字，析出新意，择取唐诗一千多年来流传的逸闻趣事作介绍，以诗传事，以事寻诗，以人求趣，讲究趣味性、知识性、评点性。', NULL, 'I207.227.42', '本书适用于诗歌爱好者', NULL, '2023.03', 'chi', NULL);
INSERT INTO `book` VALUES (1399, '978-7-5180-9760-9', '白香词谱全鉴', '(清)舒梦兰编著', '中国纺织出版社有限公司', 48, '词谱', NULL, NULL, NULL, NULL, NULL, '309页', '24cm', NULL, '国学经典全鉴系列（第八辑） 典藏版', '本书辑录了由唐至清的词作100篇，按词谱的体例，每篇自为一调。本书解译者还结合多年来的读词体验、填词实践及理论探讨，尝试分析各阕词原本搭配的曲调，借以指出其所适合的情感与文字。', NULL, 'I207.23', '本书适用于文学研究人员', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1400, '978-7-5217-4886-4', '叶嘉莹读诵纳兰词全集', '(清)纳兰性德著', '中信出版集团股份有限公司', 256, NULL, '典藏版', NULL, NULL, NULL, NULL, '2册(46,394,436页)', '24cm', '1手简(20cm)', NULL, '本书分上下卷两册，涵盖全部300余首纳兰词原文及精注、音韵说明、词牌解析，以及作者叶嘉莹先生和传承人刘子菲二人读诵纳兰词的音视频，子菲原创纳兰词主题曲。', NULL, 'I207.23', '本书适用于古典诗歌爱好者', '精装', '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1401, '978-7-5039-7298-0', '当下中国文学状况', '孟繁华著', '文化艺术出版社', 42, '小说研究', NULL, NULL, NULL, '文艺研究小丛书', NULL, '10,84页', '19cm', NULL, NULL, '本书以改革开放以来的中国文学中的小说为研究对象，将视野放在乡村文学与城市文学的联系上，归纳总结了这一时期小说从乡村到城市的发展特征以及人文意义。', '文集', 'I207.42-53', '本书适用于小说研究者', NULL, '2022.12', 'chi', NULL);
