INSERT INTO `book` VALUES (1404, '978-7-100-21641-8', '两汉诗选', '曹道衡选注', '商务印书馆', 68, '古典诗歌', NULL, NULL, NULL, NULL, NULL, '15,349页', '21cm', NULL, '名选新刊', '本书是曹道衡先生编选的一部诗歌选集，上起西汉，下至建安时期，全书分为乐府诗、无名氏古诗和文人诗歌三编，包括《安世房中歌》《郊祀歌》《鼓吹曲辞》《上山采蘼芜》《四坐且莫喧》《悲与亲友别》《兰若生春阳》等作品。', NULL, 'I222.734', '本书适用于文学爱好者', '精装', '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1405, '978-7-100-20633-4', '一瓣心香', '叶嘉莹著', '商务印书馆', 48, '诗词', '叶嘉莹诗词论稿', NULL, NULL, '文化中国：横山文丛', NULL, '182页', '21cm', NULL, NULL, '本书讲述了叶先生与莲花及佛法的因缘，探讨了中国词体的起源与特美，论及中印文化交流对中国诗歌的影响，并“别求新声于异邦”谈到西方文论与中国词学。书的最后还记录了叶先生诗中的梦与梦中的诗，并以九十岁的回眸展现了先生诗词一生的心路历程。', NULL, 'I227', '本书适用于文学爱好者', '精装', '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1406, '978-7-5039-7341-3', '天地行咏', '陈亚舟著', '文化艺术出版社', 120, '诗集', '陈亚舟纪游诗集', '二', NULL, NULL, NULL, '189页', '24cm', NULL, NULL, '本书收录了《唐山颂》《咏黄鹤楼》《陪将军回红安》《苗家寨采访》《陪同红军夫妇进太行山寻子记》《麦积山怀古》《西安碑林杂咏》等诗歌作品。', NULL, 'I227', '本书适用于诗歌爱好者', '精装', '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1407, '978-7-5171-3878-5', '跟着河流回家', '林莉著', '中国言实出版社', 58, '诗集', NULL, NULL, NULL, '新时代诗库', NULL, '172页', '21cm', NULL, '中国作协“深入生活，扎根人民”主题重点作品扶持项目、江西文化艺术基金资助项目', '本书分为序曲、风物、众生、节气、田园、大河六个篇章，以中国最美乡村婺源篁岭为原型，用原生态、亲历、野趣、带着泥土味的实践，去探寻揭示生命共同体中人与人、人与自我、人与社会、人与自然和谐共生、同频共振的奥秘。', NULL, 'I227', '本书适用于诗歌爱好者', '精装', '2022.12', 'chi', NULL);
