INSERT INTO `book` VALUES (1409, '978-7-5582-5473-4', '低飞与远航', '李强著', '武汉出版社', 78, '诗集', NULL, NULL, NULL, NULL, NULL, '254页', '23cm', NULL, NULL, '本书收录了《大地上的家乡》《记忆中的小镇》《低飞与远航》《梅家河没有梅花》《三角锚是只好猫》等诗歌作品。这些诗歌书写乡村的风土人情和时代变迁，字里行间充溢着浓浓的乡愁和眷恋。', NULL, 'I227', '本书适用于诗歌爱好者', '精装', '2022.10', 'chi', NULL);
