INSERT INTO `book` VALUES (1483, '978-7-5008-7881-0', '光阴的故事', '读者杂志社编', '中国工人出版社', 138, '散文集', '《读者》杂志365篇情感美文典藏精选', NULL, NULL, NULL, NULL, '3册(318;326;309页)', '24cm', NULL, NULL, '本书所有文章均来自《读者》杂志，分亲情卷、友情卷、爱情卷共三册，收录了《老爸的火炉》《送别》《忧愁上身》《观音老师》《病房里的男人》等一365篇情感美文。', NULL, 'I267', '本书适用于散文爱好者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1484, '978-7-5008-7975-6', '元曲山河', '李元洛著', '中国工人出版社', 88, '散文集', NULL, NULL, NULL, NULL, NULL, '384页', '19cm', NULL, '诗文化散文三部曲', '本书是以元曲和元代读书人为题材创作的专题系列散文。作者带领读者去了解元曲的魅力和元曲背后的典故，将当时的文坛佳话和名人事迹娓娓道来，同时解读了元曲的灵魂及元代读书人的品性，使读者得以直面历史的苍凉及世事的沧桑，深入挖掘元曲背后的文化内涵和历史源头。', NULL, 'I267', '本书适用于文学爱好者', '精装', '2023.02', 'chi', NULL);
INSERT INTO `book` VALUES (1485, '978-7-5008-7980-0', '唐诗天地', '李元洛著', '中国工人出版社', 88, '散文集', NULL, NULL, NULL, NULL, NULL, '448页', '19cm', NULL, '诗文化散文三部曲 尺寸', '本书是以唐诗和唐代诗人为题材创作的专题系列散文。作者抒写了对唐诗的个人生命体验和人生感悟，开掘了唐诗的当下意义和现代价值。', NULL, 'I267', '本书适用于文学爱好者', '精装', '2023.02', 'chi', NULL);
INSERT INTO `book` VALUES (1486, '978-7-5008-7981-7', '宋词世界', '李元洛著', '中国工人出版社', 88, '散文集', NULL, NULL, NULL, NULL, NULL, '444页', '19cm', NULL, '诗文化散文三部曲', '本书是以宋词和宋代词人为题材创作的专题系列散文。作者带领读者重访宋朝故地，娓娓道来文坛佳话和名人掌故，解读宋词的灵魂及词人品性，使读者得以直面历史的苍凉及世事的沧桑，深入了解宋词背后的文化内涵和历史源头。', NULL, 'I267', '本书适用于散文爱好者', '精装', '2023.02', 'chi', NULL);
INSERT INTO `book` VALUES (1487, '978-7-5168-3455-8', '那朵乌云要起飞', '蒋勋，毕飞宇，颜择雅等著', '台海出版社', 52, '散文集', NULL, NULL, NULL, NULL, NULL, '254页', '21cm', NULL, '时代华语国际', '本书选取蒋勋、毕飞宇、路内、张曼娟、黄丽群、颜择雅等34位作家的散文，讲述了许多人生不同阶段的故事。这些故事，或关于个人成长，或关于家庭情感，都围绕着一个主题——希望与救赎。', NULL, 'I267', '本书适用于散文爱好者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1488, '978-7-5194-6536-0', '读书，遇见更好的自己', '王卫国主编', '光明日报出版社', 75, '读后感', NULL, NULL, NULL, '教育知库', NULL, '259页', '24cm', NULL, '光明版', '本书收录了《借口是失败的温床》《工作，用生命去做》《说到不如做到，要做就做最好》《“落实”是成功的基石》《为师者的思考》《以生为本，用爱育人》《好成绩是好课堂的副产品》《也谈爱国》《我爱中华文化经典》等文章。', NULL, 'I267', '本书适用于大众读者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1489, '978-7-5205-3911-1', '每个人都可以从容', '刘奔海著', '中国文史出版社', 57.8, '散文集', NULL, NULL, NULL, '跨度新美文书系', NULL, '237页', '24cm', NULL, NULL, '本书分为风中的故事和故乡在心中两辑，收录了《树坚强》《风中的故事》《我辜负了那个冬天》《每个人都可以从容》《不老的母亲》《父亲的归宿》《当母亲忘记了我的生日》《母爱不咸》等散文作品。', NULL, 'I267', '本书适用于文学爱好者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1490, '978-7-5212-2134-3', '永远的背影', '周良沛著', '作家出版社有限公司', 50, '散文集', NULL, NULL, NULL, NULL, NULL, '370页', '23cm', NULL, NULL, '本书收录了《也谈冯达》《迟送的花圈》《寂寞中的白蝴蝶》《闲话“二萧”》《话说关露》《白色花，阿垅》《永远的同志哥》《“七月”的路翎》《绿原的“七月”》等散文作品。', NULL, 'I267', '本书适用于文学爱好者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1491, '978-7-5402-5734-7', '九月', '北京老舍文学院编', '北京燕山出版社有限公司', 58, '散文集', NULL, NULL, NULL, '老舍文学院新人力作丛书', NULL, '335页', '24cm', NULL, NULL, '本书收录了24位北京中青年作者的优秀散文，包括《散落在京郊的瑰宝》《错过》《白雪唱春》《母亲的生日》《好愿——一盏为你点亮的灯》《住院记》等作品。', NULL, 'I267', '本书适用于文学爱好者', NULL, '2021', 'chi', NULL);
INSERT INTO `book` VALUES (1492, '978-7-5726-0829-2', '告别', '陈丹燕著', '湖南文艺出版社', 98, '散文集', NULL, NULL, NULL, NULL, NULL, '316页', '21cm', NULL, NULL, '本书记录了作家陈丹燕30年“地理阅读”的旅行历程，她是资深的阅读者和旅行者，我们透过她的观察之眼，看到外部世界的美好，意识到旅行和探险、迷人的景物和晴朗的天气、人的耐心和善意，都犹存于世界。', NULL, 'I267', '本书适用于散文爱好者', '精装', '2022.09', 'chi', NULL);
INSERT INTO `book` VALUES (1493, '978-7-5735-0195-0', '梦里花落知多少', '三毛著', '南海出版公司', 49, '散文集', NULL, NULL, NULL, NULL, NULL, '296页', '21cm', NULL, NULL, '本书收录了《背影》《荒山之夜》《克里斯》《离乡回乡》《雨禅台北》《不死鸟》《明日又天涯》《云在青山月在天》《归》《梦里梦外》《不飞的天使》等散文作品。', NULL, 'I267', '本书适用于文学爱好者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1494, '978-7-201-18176-9', '文学的意思', '黄子平著', '天津人民出版社', 88, '随笔', NULL, NULL, NULL, NULL, NULL, '417页', '21cm', NULL, '领读', '本书包含黄子平的随笔作品，共分三辑，分别为文学的意思、边缘阅读、害怕写作。文学的意思是一系列最初连载于《文学自由谈》的专栏文章，旨在阐述文学的意义及趣味性；边缘阅读则希望阅读者能保持旁观者的姿态，并养成一种读缝隙、读字里行间的阅读策略，将阅读当成文本与意义的游击运动；害怕写作惟愿在语言暴力四处泛滥的今天，仍有人喜欢阅读，仍有人敬畏话语的力量。', NULL, 'I267.1', '本书适用于随笔爱好者', '精装', '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1495, '978-7-5142-3690-3', '川味人间', '曾颖著绘', '文化发展出版社', 49, '随笔', NULL, '贰', NULL, NULL, NULL, '286页', '21cm', NULL, NULL, '本书作者用60道菜肴，60幅手绘画，以及小桥流水式的文字，讲述大量来源于生活的喜怒哀乐，洋溢着挥之不去的深情与乡愁。每一道菜、每一种味道，都有一段关于亲情、友情、爱情或家庭或故乡或旅行或童年的故事。', NULL, 'I267.1', '本书适用于随笔爱好者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1496, '978-7-5222-0399-7', '陈勇临证随笔录', '陈勇，程燕主编', '华夏出版社有限公司', 89.8, '随笔', '漫步杏林六十载', NULL, NULL, NULL, NULL, '269页', '25cm', NULL, NULL, '本书是中医名家陈勇医生六十余年行医路的医话、医案及自述，既有他在漫漫行医路上的一段段真实故事，包含幼年生活、学医经历、扎根基层、临床诊治、退休后老骥伏枥创医堂等经历，又有传承“三脉岐黄”的中医学术思想、心得感悟，以及多年临床总结的八大系统辨证论治经验方、代表性验案、中医养生经验等。', NULL, 'I267.1', '本书适用于文学爱好者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1497, '978-7-5356-9858-2', '小猫咪担心你有没有爱自己', '帮帮著', '湖南美术出版社', 59, '随笔', NULL, NULL, NULL, NULL, NULL, '221页', '19cm', NULL, '浦睿文化', '本书是一本小猫写的日记，也是一封写给人类的情书。全书记录了“清华小猫”帮帮生命中的1010天。', NULL, 'I267.1', '本书适用于随笔爱好者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1498, '978-7-5179-1225-5', '流浪的风筝', '罗滋湘著', '中国摄影出版社', 88, '游记', '万水千山骑遍', NULL, NULL, NULL, NULL, '283页', '24cm', NULL, NULL, '本书摄影艺术与文学艺术相结合，记述了作者在川陕、鄂渝、湘西、贵州等地的骑行经历，以及参加自行车比赛的故事，包括气候地理、风光名胜、历史人文、民族风情、社会变迁、赛场风云和各种旅途奇遇等。', NULL, 'I267.4', '本书适用于大众读者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1499, '978-7-5701-1870-0', 'P先生', '祝卉著', '山东教育出版社', 128, '图画故事', NULL, NULL, NULL, NULL, NULL, '1册', '30cm', NULL, NULL, '本书讲述了：P先生是一个线圈，也是一个真实的物理存在，他的身高1.37米，雏形诞生于作者的北京家中，而后在巴黎旅行期间渐渐成型。最早的时候，P先生只是作者拍照时的道具。而后去的地方多了，他遇到的朋友多了，大家都开始询问他的故事。', NULL, 'I287.8', '本书适用于大众读者', '精装', '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1500, '978-7-5722-4903-7', '不便的便利店', '(韩)金浩然著', '浙江教育出版社', 45, '长篇小说', NULL, NULL, NULL, NULL, NULL, '271页', '21cm', NULL, '大鱼读品·韩国文学 磨铁', '本书讲述了：他最后的记忆始于火车站，一觉醒来，身上只剩裤子和衬衫，从此再没出过车站，渐渐连自己的名字也想不起来。直到某天，因为从小偷手里夺回陌生女士的钱包，被对方拉去自家便利店做夜班店员。这家叫“always”的小店，always商品短缺，always人手不足，被社区居民吐槽是“不便的便利店”。阴差阳错从流浪汉变成打工人的他，不仅帮大学生店员摇身一变成为便利店界网红，还帮监视自己的冒牌刑警成功再就业，就连脾气和嘴巴一样臭的毒舌大妈也在他面前卸下心防，背后的秘密武器却是看似平凡的便利店食物。', NULL, 'I312.645', '本书适用于小说爱好者', NULL, '2023.02', 'chi', NULL);
INSERT INTO `book` VALUES (1501, '978-7-5159-2128-0', '日本女性文学解读', '李先瑞，王伟伟著', '中国宇航出版社', 58, '日本文学', '日本著名女性文人评析', NULL, NULL, NULL, NULL, '426页', '21cm', NULL, '浙大宁波理工学院人才引进启动基金项目', '本书以日本女性文人为主线，围绕50位日本著名女性文人展开研究，其中包括小野小町、藤原道纲母、清少纳言、紫式部、和泉式部、冈本可能子、宫本百合子、吉本芭娜娜、青山七惠、金原瞳等，主要讲述各个时代著名女性文人的生平、思想形成情况、主要作品及社会影响力等。', NULL, 'I313.06', '本书适用于文学研究者', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1502, '978-7-221-17341-6', '伊豆的舞女', '(日)川端康成著', '贵州人民出版社', 59, '短篇小说', NULL, NULL, NULL, NULL, NULL, '220页', '21cm', NULL, '新华先锋×川端康成', '本书是川端康成的中短篇小说集，特别收录名篇《伊豆的舞女》，同时收录《篝火》《温泉旅馆》《山雀》等精致短篇。', NULL, 'I313.45', '本书适用于小说爱好者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1503, '978-7-221-17454-3', '千只鹤', '(日)川端康成著', '贵州人民出版社', 59, '中篇小说', NULL, NULL, NULL, NULL, NULL, '219页', '21cm', NULL, '新华先锋×川端康成', '本书以“宿命之禁忌”“生之无奈”为主题，收录了《千只鹤》、续篇《波千鸟》以及《致父母的信》等作品，讲述了川端康成对父母、家庭的眷恋以及在父亲一代影响下产生的纠葛故事。', NULL, 'I313.45', '本书适用于小说爱好者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1504, '978-7-5008-8004-2', '怪奇小店', '(日)有栖川有栖著', '中国工人出版社', 49, '推理小说', NULL, NULL, NULL, NULL, NULL, '306页', '21cm', NULL, '有栖川有栖系列', '本书收录了《古玩之魔》《“烛火堂”的飞来横祸》《砸碎橱窗》《涛声理发店》《怪奇小店》等推理小说。', NULL, 'I313.45', '本书适用于小说爱好者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1505, '978-7-5168-3359-9', '6月31日的同学会', '(日)真梨幸子著', '台海出版社', 58, '长篇小说', NULL, NULL, NULL, '千本樱文库', NULL, '284页', '21cm', NULL, '力潮文创', '本书讲述了：直升制名校兰圣学园拥有一项被称为“六月三十一日的同学会”的神秘传统，但那是一个不存在于公历上的日期。多名毕业生在同一年内收到该邀请函，并接二连三地死去，而死亡顺序竟和当年班上演出的惊悚推理话剧《六月三十一日的同学会》一致。', NULL, 'I313.45', '本书适用于小说爱好者', NULL, '2022.08', 'chi', NULL);
INSERT INTO `book` VALUES (1506, '978-7-5327-9063-0', '跳舞的星座', '(日)青山七惠著', '上海译文出版社有限公司', 62, '中篇小说', NULL, NULL, NULL, NULL, NULL, '244页', '20cm', NULL, '青山七惠作品系列 世纪出版', '本书是青山七惠奇幻短篇集，十三个奇幻小故事，串联起现代女性充满汗与泪，遭遇种种奇葩事件的漫长一天。全书收录了《超级明星》《塞尔玛和路易丝》《茉莉花》《老板娘的漂流时代》等中篇小说。', NULL, 'I313.45', '本书适用于小说爱好者', '精装', '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1507, '978-7-5360-9802-2', '文豪野犬', '(日)朝雾卡夫卡著', '花城出版社', 42, '长篇小说', NULL, '7', 'Storm bringer', NULL, NULL, '314页', '21cm', NULL, '朝雾卡夫卡作品 天闻角川文学', '本书讲述了：中原中也与太宰治一同解决了“荒霸吐事件”并加入港口Mafia。一年后，就在中也想晋升为干部的时候，将中也称作“弟弟”的暗杀王保罗·魏尔伦出现在他的面前，扬言要“把你关心的人，全部暗杀”。为了阻止他的计划，中也决定与欧洲的人造智能探员——亚当合作。这预示着再次吞噬横滨的暴风雨即将来临。', NULL, 'I313.45', '本书适用于小说爱好者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1508, '978-7-5360-9807-7', '刀剑神域', '(日)川原砾著', '花城出版社', 39, '长篇小说', NULL, '007', '进击篇', NULL, NULL, '219页', '19cm', NULL, '川原砾作品 引进版图书', '本书讲述了：在传说曾淘汰一半封测玩家的艾恩葛朗特第七层等待众人的是一座巨大的娱乐城，以及让桐人失去了封测时期积累下来的所有财产的“怪物斗技场”。受情报贩子阿尔戈邀请，桐人与亚丝娜打算探查斗技场的作弊行为，却在不知不觉间踏入了娱乐场的暗处。另一方面，“精灵战争任务”也迎来了意料之外的情节发展，为了与绮兹梅尔会合，桐人一行准备前往黑暗精灵的据点“哈林树宫”，竟在路上遇到了众多危机。', NULL, 'I313.45', '本书适用于小说爱好者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1509, '978-7-5470-4783-5', '寻回犬李奥的奇妙物语', '(日)知念实希人著', '万卷出版有限责任公司', 49.8, '推理小说', NULL, NULL, NULL, NULL, NULL, '289页', '21cm', NULL, '新浪读书 闲闲拾光', '本书讲述：丘上别墅曾是一栋住有“吸血鬼家族”的房子，如今，它被改建成安宁医院，住着等待死亡的人们。某日，一只可爱的金毛寻回犬突然到来，而离奇又神秘的事情开始接二连三地发生。原来，这只名为李奥的金毛寻回犬，其实是被迫来到人间“打工”的死神。因为业绩太差，他被上司派遣到人间，并化身成金毛寻回犬的模样，幸而遇到一位温柔善良的护士，将饥寒交迫的他带到了安宁医院。因此，这里便成了他完美的工作场所。仗着惹人喜欢的毛孩子模样，李奥穿梭于医院各处，这里有看不到色彩的画家、在战乱中错失爱人的耄耋老人，以及其他不同命运的人们', NULL, 'I313.45', '本书适用于小说爱好者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1510, '978-7-5470-6029-2', '黑猫小夜曲', '(日)知念实希人著', '万卷出版有限责任公司', 49.8, '推理小说', NULL, NULL, NULL, NULL, NULL, '282页', '21cm', NULL, '新浪读书 闲闲拾光', '本书讲述：死神的职责是解开人们心中最深的遗憾，从而净化灵魂。这明明是份好工作，但对他而言却并非如此——先被贬职到陌生城镇，然后还变成了一只黑猫。工作还没开始，他就被乌鸦啄得满街跑，直到神秘的声音指引他到安全之处，他才得以逃脱困境。而那神秘的声音源于一抹灵魂。灵魂自称丧失记忆，想和他进行一次不可思议的交易：他帮灵魂找到一副新的躯壳，灵魂会助他找到其他灵魂，完成工作。变成黑猫的他别无他法，接受了这桩交易，而后选中了一名长睡不醒的少女——白木麻矢。失忆的灵魂作为“白木麻矢”苏醒过来，成为黑猫名义上的主人，还给他', NULL, 'I313.45', '本书适用于小说爱好者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1511, '978-7-5596-6503-4', '滨口龙介：那些欢乐时光', '(日)滨口龙介，(日)野原位，(日)高桥知由著', '北京联合出版公司', 78, '随笔', NULL, NULL, NULL, NULL, NULL, '457页', '19cm', NULL, '雅众·电影', '本书作者剖析摄影机的特性、表演的本质及电影的意义。通过毫无保留地分享其重要电影代表作《欢乐时光》的创作过程、剧本文本，绵密轻盈的对话流、巧妙的影像维度。', NULL, 'I313.65', '本书适用于文学爱好者', '精装', '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1512, '978-7-5223-1447-1', '华尔街史上最大的对冲基金交易案', '(马来)安妮塔·拉加万(Anita Raghavan)著', '中国财政经济出版社', 129.9, '纪实文学', NULL, NULL, NULL, NULL, NULL, '429页', '23cm', NULL, '湛庐', '本书讲述了一个很有影响力的股票内幕交易案的交易、调查和审判始末。该案以帆船集团的掌门人拉杰·拉贾拉特南为中心，最终把一个重量级人物拉贾特·古普塔拉下马。全书分为商界精英初露头角、走向美国的权力阶层、内幕交易案浮出水面、财富与权力远去四部分。', NULL, 'I338.55', '本书适用于广大投资爱好者', NULL, '2022.06', 'chi', NULL);
INSERT INTO `book` VALUES (1513, '978-7-5225-1085-9', '镂空与浮雕', '(马来)范俊奇(Fabian Fom)著', '九州出版社', 76, '散文集', NULL, '1', NULL, NULL, NULL, '270页', '21cm', NULL, NULL, '本书书写了有关三十多位文化名人的散文，包含大陆的朴树、顾城，港台地区的张国荣、王家卫、李安，西方的舞蹈家皮娜·鲍什、艺术家弗里达·卡罗、明星大卫·鲍伊等。作者结合真实经历与虚构想象，将他们斑斓过的人生、经历过的大悲大喜与小情小爱，重新剪开、放大、修补与圆满。', NULL, 'I338.65', '本书适用于散文爱好者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1514, '978-7-5726-0706-6', '最后一个故事，就这样啦', '(以)埃特加·凯雷特著', '湖南文艺出版社', 46, '短篇小说', NULL, NULL, NULL, NULL, NULL, '169页', '20cm', NULL, '埃特加·凯雷特作品 睿读经典 浦睿文化', '本书收录了《故事的形状》《肥仔性幻想》《日行一善》《骄傲与欢乐》《蛮可爱的》等小说作品。', NULL, 'I382.45', '本书适用于小说爱好者', NULL, '2022.09', 'chi', NULL);
INSERT INTO `book` VALUES (1515, '978-7-5726-0795-0', '美好的七年', '(以)埃特加·凯雷特著', '湖南文艺出版社', 49, '随笔', NULL, NULL, NULL, NULL, NULL, '178页', '20cm', NULL, '埃特加·凯雷特作品 睿读经典 浦睿文化', '本书收录了《成年婴儿》《来电与应答》《你，真诚的》《人民守卫者》《梦之安魂曲》等随笔作品，是作者写下的生命中至为重要的七年。这七年里，所有的美好都伴随着恐惧与黑暗。', NULL, 'I382.65', '本书适用于文学爱好者', NULL, '2022.10', 'chi', NULL);
INSERT INTO `book` VALUES (1516, '978-7-108-07447-8', '俄罗斯文学的黄金世纪', '张建华著', '生活·读书·新知三联书店', 98, '俄罗斯文学', '从普希金到契诃夫', NULL, NULL, '三联中读文丛', NULL, '411页', '25cm', NULL, '行读', '本书作者从普希金讲起，直至契诃夫，通过俄罗斯文学史上的一颗颗文学巨星窥见俄罗斯民族的内心世界，领略别样的人生世相、人性百态、灵魂奇观。', NULL, 'I512.064', '本书适用于文学研究人员', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1517, '978-7-02-017587-1', '托尔斯泰为伴', '(美)李翊云编著', '人民文学出版社', 68, NULL, '与李翊云共读《战争与和平》的八十五天', NULL, NULL, NULL, NULL, '451页', '22cm', NULL, NULL, '本书是全球线上阅读《战争与和平》活动的总结，收录了《一份关于托尔斯泰的不精确指南》《托尔斯泰之于陀思妥耶夫斯基好比柴可夫斯基之于拉赫玛尼诺夫》《所有的一切都让贝格感到荒唐》等文章。', NULL, 'I512.074', '本书适用于文学研究人员', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1518, '978-7-5327-9146-0', '忏悔录', '(俄罗斯)托尔斯泰(Лев Толстой)著', '上海译文出版社有限公司', 36, '自传体小说', NULL, NULL, NULL, NULL, NULL, '20,111页', '19cm', NULL, '译文经典 世纪出版', '本书是托尔斯泰完整的精神自传，是其毕生思想的总结。追溯了托尔斯泰五十年间的人生经历和心路历程，重点描述了作者在东西方各伟大思想、宗教间的求索过程，以寻找生命的意义和灵魂的慰藉。', NULL, 'I512.44', '本书适用于小说爱好者', '精装', '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1519, '978-7-5210-0944-6', '大海如镜', '张陟著', '海洋出版社', 198, '小说研究', '英美海洋小说研究', NULL, NULL, NULL, NULL, '405页', '23cm', NULL, NULL, '本书以文学文本与国家认同之间的关系为切入点，探讨了18世纪以来英美海洋小说与其时主流观念之间或顺应调和，或否认抵抗的复杂关系，展现了宽阔的学术视野，并梳理了英美海洋小说的文化渊源与流变过程。', NULL, 'I516.074', '本书适用于从事外国文学教学与研究、英美文学、海洋文学、海洋文化研究的高校师生以及文学爱好者', '精装', '2022.04', 'chi', NULL);
INSERT INTO `book` VALUES (1520, '978-7-02-017190-3', '辽阔的原野', '(德)君特·格拉斯著', '人民文学出版社', 138, '长篇小说', NULL, NULL, NULL, NULL, NULL, '670页', '21cm', NULL, '君特·格拉斯文集', '本书以两德统一为中心，回顾了从1848年3月革命到二十世纪九十年代的德国史，展示了一幅从1848年三月革命到20世纪90年代的德国历史全景图。', NULL, 'I516.45', '本书适用于小说爱好者', '精装', '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1521, '978-7-02-017192-7', '相聚在特尔格特', '(德)君特·格拉斯著', '人民文学出版社', 78, '中篇小说', NULL, NULL, NULL, NULL, NULL, '132页', '21cm', NULL, '君特·格拉斯文集', '本书讲述了：十七世纪上半叶，德国符宗教派别、德意志皇帝和各诸侯之间矛盾深重，冲突不断，在经历了30年战争后，一批文人志士相聚在小城特尔格特，他们谈占论今，畅所欲言，在探讨诗歌、戏剧等严肃的学术问题的同时，还痛心疾首哀叹自身和祖国的命运；他们借助诗文，共谋祖国统一人业，可在高谈阔论的同时，又插科打诨，粗话连篇，荤话迭出，还“不拘小节”同女仆和女店主苟合；最后他们甚至还共同草拟了一份“和平呼吁书”，但一场意外的大火将聚会场所和“呼吁书”化为灰烬，文人志士们所有的美好愿望也随之烟消云散。', NULL, 'I516.45', '本书适用于小说爱好者', '精装', '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1522, '978-7-5594-6518-4', '古舟子咏', '(英)塞缪尔·泰勒·柯勒律治(Samuel Taylor Coleridge)著', '江苏凤凰文艺出版社', 88, '诗歌', '插图珍藏版', NULL, NULL, NULL, NULL, '117页', '28cm', NULL, '后浪插图经典', '本书借老水手之口展现了命运那强大而神秘的力量、人类时常面对且身陷其中的生存困境，以及作为个体的人因一时冲动而生的无意识的恶。', NULL, 'I561.24', '本书适用于诗歌爱好者', '精装', '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1523, '978-7-219-11405-6', '生日信', '(英)特德·休斯著', '广西人民出版社', 65.8, '诗集', NULL, NULL, NULL, NULL, NULL, '12,284页', '21cm', NULL, '休斯系列 大雅', '本书是英国传奇桂冠诗人特德·休斯最后的诗集，也是他寄给亡妻西尔维娅·普拉斯的“悼亡信”。这部诗，日记式地记录了两位诗人从初识、热恋、结婚、生子到最后的婚变与纠缠。', NULL, 'I561.25', '本书适用于诗歌爱好者', '精装', '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1524, '978-7-219-11435-3', '乌鸦', '(英)特德·休斯著', '广西人民出版社', 49.8, '诗集', '来自乌鸦的生活和歌', NULL, NULL, NULL, NULL, '149页', '21cm', NULL, '休斯系列 大雅', '本书收录67首诗歌。作者以乌鸦为主人公，以其在遭解构的宗教生活和现代世界中的经历，重铸其具世界性意义的作为古老文化英雄的自我，再造神话的创造性内在生命能量，并以此生命能量拯救诗人自我的危机，实现对现代社会生活中人之心灵创伤的疗救。', NULL, 'I561.25', '本书适用于诗歌爱好者', '精装', '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1525, '978-7-5057-5429-4', '巴黎圣母院', '(法)维克多·雨果(Victor Hugo)著', '中国友谊出版公司', 68, '长篇小说', NULL, NULL, NULL, NULL, NULL, '533页', '21cm', NULL, '创美工厂 轻经典', '本书讲述了：吉普赛少女爱斯美腊达在街头卖艺，圣母院教堂副主教弗洛德欲占为己有。后发现她另有情人，又刺杀她的情人并嫁祸于她，致使她被判死刑。相貌奇丑、对她充满爱慕和谦卑之情的敲钟人卡席莫多把她救到圣母院避难。弗洛德把她劫出，威逼她屈从于他的兽欲。遭到拒绝后，弗洛德把她交给官兵，在圣母院楼上看着她被绞死。卡席莫多在绝望中愤怒地把抚养他长大的弗洛德推下顶楼，活活摔死。自己则到鹰山地穴搂住爱斯美腊达的尸体，与她死在一起，完成了“婚礼”。', NULL, 'I565.44', '本书适用于小说爱好者', '精装', '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1526, '978-7-5426-7923-9', '奢侈', '(法)克里斯蒂安·布朗卡特(Christian Blanckaert)著', '上海三联书店', 88, '随笔', '爱马仕总裁日记', NULL, NULL, NULL, NULL, '184页', '21cm', NULL, '理想国', '本书是布朗卡特从爱马仕总裁离任后以日记体呈现的随笔作品集。在书中，他记录了在爱马仕担任CEO期间与全球各界名流的交往和这个行业的故事。', NULL, 'I565.65', '本书适用于随笔爱好者', '精装', '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1527, '978-7-5607-7261-5', '请叫我汉克', '(加)亨利·潘尼尔(Henry Pennier)著', '山东大学出版社', 32, '回忆录', '第一民族混血儿对伐木业、生存及成长的沉思录', NULL, NULL, NULL, NULL, '22,114页', '24cm', NULL, '山东省教育厅青科创新团队“多语种语言文化翻译与研究”的阶段成果，同时得到临沂大学“外国文学研究与翻译团队”和临沂大学外国语学院创新创业教育示范项目“西方文化翻译教学团队”以及临沂大学人文社科横向科研项目“基于双语平行语料库的翻译教学内涵与路径探微”和“新媒体时代沂蒙红色文化‘翻译工作坊’教学模式探索与实践”的经费资助', '本书的作者是一位印第安“混血儿”汉克，他在加拿大种族政策的“无人区”(白人视他为印第安人，印第安人视他为“烟熏白人”)里花了多年时间来寻求自我身份认同的过程，作为印第安斯道罗社区的一员，汉克有幸在以白人为主的伐木业工作期间，有机会从多个角度、多个个体审视加拿大原住民所处的环境和所面临的问题。严重的工伤迫使他过早退出了伐木业，他娓娓道来发生在身边的一个又一个幽默故事和轶事，时间跨度很长，从童年时代、青年时代、壮年时代，一直到退休后的今天。', NULL, 'I711.55', '本书适用于大众读者', NULL, '2022.10', 'chi', NULL);
INSERT INTO `book` VALUES (1528, '978-7-5222-0406-2', '美国当代生态文学的怀旧书写研究', '马军红著', '华夏出版社有限公司', 56, '文学研究', NULL, NULL, NULL, NULL, NULL, '235页', '21cm', NULL, NULL, '本书结合生态批评、社会生态学、人文地理学和环境史、当代怀旧研究成果等，以美国当代生态文学作家大卫·增本、特丽·威廉斯和司各特·桑德斯的怀旧叙事为研究对象，考察了美国当代生态文学中的以下议题：田园的复杂性与多样性、农业生态中物种多样性的缺失和有机社群的解体；资本纪时代毒化环境与毒性身体的缠绕与交互；移动性文化、现代性进程与恋地情结的丧失、全球化时代地方观的重塑。', NULL, 'I712.065', '本书适用于文学研究人员', NULL, '2022.10', 'chi', NULL);
INSERT INTO `book` VALUES (1529, '978-7-5213-3942-0', '心是孤独的猎手', '(美)卡森·麦卡勒斯(Carson McCullers)著', '外语教学与研究出版社', 65, '长篇小说', NULL, NULL, NULL, NULL, NULL, '366页', '21cm', NULL, '互文', '本书讲述了：在沉闷燠热的美国南方小镇，有一群孤独的人：与妻子感情淡薄的咖啡店老板、热爱音乐的贫穷少女、试图改造社会的狂热工人、为同胞未来而忧虑的黑人医生等。他们向哑巴辛格倾诉，以为他能够理解自己的孤独和抱负。然而，哑巴辛格也面对着自己巨大的孤独，似乎没有人能从他人身上取得长久的慰藉，孤独终会将他们打回原形。', NULL, 'I712.45', '本书适用于小说爱好者', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1530, '978-7-5327-9124-8', '邻人之爱', '(美)西格丽德·努涅斯(Sigrid Nunez)著', '上海译文出版社有限公司', 62, '中篇小说', NULL, NULL, NULL, NULL, NULL, '242页', '19cm', NULL, '世纪出版', '本书讲述的是一个倾听与倾诉的故事，关乎临终关怀、他者之痛等话题。女主人公赶赴异乡探视罹患绝症的老友，两人在病榻前立下誓约，决定共赴一场冒险之旅：一个晴朗的夏夜，一个优美的小镇，一栋漂亮的房子，一次美丽的死亡，以及一瓶藏在抽屉深处的安乐死之药。在小镇经历的这一系列“道听途说”，以及沿途的一景一物，令主人公的回忆绵绵无尽，眼前之人与生命过客，甚至房东家的一只猫也不例外，无一不在困境中挣扎，无不怀有一种倾诉的急迫，渴望道破孤独、自我怀疑和悲伤之深。而她亦被卷入其中，从“聆听者”变为“局中人”，由此留下一段令她难', NULL, 'I712.45', '本书适用于小说爱好者', '精装', '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1531, '978-7-5594-6435-4', '太空孤儿', '(美)罗伯特·海因莱因(Robert A. Heinlein)著', '江苏凤凰文艺出版社', 39.9, '幻想小说', NULL, NULL, NULL, '读客科幻文库', NULL, '170页', '22cm', NULL, '凤凰传媒 读客', '本书讲述：地球末日之际，全人类登上一艘宇宙飞船，以为登上了诺亚方舟，自此开启了长达数百年的星际征途。人类在飞船上待得太久，竟忘了地球，忘了征途的目的，忘了飞船在行进，更忘了飞船外的宇宙，成了太空中的孤儿。', NULL, 'I712.45', '本书适用于小说爱好者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1532, '978-7-5596-6438-9', '漫长的寒冬', '(美)A.G. 利德尔(A.G. Riddle)著', '北京联合出版公司', 59, '幻想小说', NULL, NULL, '失落之城', NULL, NULL, '252页', '21cm', NULL, '新华先锋 先锋', '本书讲述：500万人类幸存者来到新家园厄俄斯，然而这里并非天堂，他们面临着更大的挑战：超强风暴、远古巨兽、嗜血狂蝎、致命孢子。与此同时，幕后大BOSS网格的真相越来越逼近。', NULL, 'I712.45', '本书适用于小说爱好者', NULL, '2023.04', 'chi', NULL);
INSERT INTO `book` VALUES (1533, '978-7-5699-3976-7', '雪的恩典', '(美)安东尼·多尔(Anthony Doerr)著', '北京时代华文书局', 68, '长篇小说', NULL, NULL, NULL, NULL, NULL, '407页', '21cm', NULL, NULL, '本书讲述了：当你能够预知未来，是幸运，还是诅咒。大卫·温克尔自小便拥有这个神奇的能力：可以提前知道未来几天发生的事。长大后，他也预知了自己和妻子会在超市里一见钟情，生下可爱的女儿。但大卫也预知了自己和女儿将卷入洪水，自己无力拯救。为了防止悲剧在现实世界重演，他做了一件让所有人不理解的事：远离妻女，自我放逐。这一走便是20多年，步入老年的大卫得知妻子已经去世，对见到女儿的渴望压倒了一切，他选择回家、寻女。他走遍全国，寻找着和女儿同名同姓的人，他的梦想能成真吗，女儿会原谅他吗。', NULL, 'I712.45', '本书适用于小说爱好者', NULL, '2022.10', 'chi', NULL);
INSERT INTO `book` VALUES (1534, '978-7-5699-4279-8', '本书充满蜘蛛', '(美)贾森·帕金(Jason Pargin)著', '北京时代华文书局', 69, '幻想小说', NULL, NULL, NULL, '阿卡姆书系', NULL, '469页', '21cm', NULL, NULL, '本书讲述了：大卫在家中遭到一种寄生于人体的“蜘蛛怪物”的袭击，拉开了不具名小镇感染事件爆发的帷幕。蜘蛛潜伏在小镇各处，然而普通人无法凭肉眼看见。小镇陷入了末日般的恐慌和混乱中——这里消息被封锁，谣言四起，人们开始囤积罐头食品；在镇上设置的隔离区内，疑似被感染的“丧尸”要穿上不同颜色的衣服加以区分。然而，一个叫REPER的组织空降小镇负责指挥，却决定轰炸整座小镇解决这看不见的感染。时间紧迫，大卫和他的朋友约翰、埃米亟须阻止轰炸计划，并证明他们都是正常人类。', NULL, 'I712.45', '本书适用于小说爱好者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1535, '978-7-5327-9111-8', '疫苗的故事', '(美)保罗·奥菲特(Paul A. Offit)著', '上海译文出版社有限公司', 58, '传记文学', NULL, NULL, NULL, NULL, NULL, '258页', '21cm', NULL, '译文纪实 自然与人 世纪出版', '本书作者奥菲特曾与“现代疫苗之父”希勒曼共同发明了轮状病毒疫苗，在这位伟大的科学家的最后几个月里深入了解了他的生活和职业生涯。他讲述了希勒曼研发各种疫苗的细节，也以此为基础介绍了全球背景下的疫苗开发，勾勒出200多年来的公共卫生史，在展示流行病学得失的同时，也以有力的证据提醒人们重视疫苗的价值以及科学所拥有的拯救生命、保护人类福祉的力量。', NULL, 'I712.55', '本书适用于传记文学爱好者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1536, '978-7-5496-3863-5', '利弹穿心', '(美)米卡尔·吉尔摩著', '文汇出版社', 68, '回忆录', NULL, NULL, NULL, '新经典文库', NULL, '473页', '21cm', NULL, NULL, '本书讲述了：1976年，美国犹他州发生了一起轰动全美的凶杀案，加里·吉尔摩在假释期间无故杀死两人，遭逮捕后被判处死刑。他拒绝使用上诉权，坚持要求对自己执行死刑，引发了巨大的舆论震动。1977年1月，加里·吉尔摩被枪决，成为犹他州恢复死刑以来被处死的第一人。', NULL, 'I712.55', '本书适用于文学爱好者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1537, '978-7-5227-0901-7', '20世纪西方艺术理论精粹', '杨建国主编', '中国社会科学出版社', 118, '艺术理论', NULL, NULL, NULL, '南京大学艺术理论译丛', NULL, '23,318页', '24cm', NULL, NULL, '本书分为“作为知识/学科的艺术理论”“作为一种文化的艺术”“艺术的历史演化与边界”“艺术本体论”“批评与阐释”几个部分，集中收录了20世纪西方艺术理论经典文献。', NULL, 'J0', '本书适用于艺术理论研究者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1538, '978-7-100-20097-4', '艺术哲学', '(法)丹纳著', '商务印书馆', 148, '艺术哲学', NULL, NULL, NULL, '汉译世界学术名著丛书', NULL, '34,557页', '21cm', NULL, '汉译名著', '本书是丹纳在巴黎美术学习讲述美术史的讲义，包括文艺复兴时期的意大利绘画，尼德兰绘画和希腊雕塑。对于读者欣赏欧洲文艺复兴时期的艺术和欧洲哲学起源之一希腊雕塑非常具有指导性。', NULL, 'J0-02', '本书适用于艺术哲学爱好者', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1539, '978-7-5115-7430-5', '古代艺术品鉴论', '成杰著', '人民日报出版社', 68, '艺术品', NULL, NULL, NULL, NULL, NULL, '200页', '24cm', NULL, NULL, '本书为关于中国古代艺术鉴赏理论的专著，分为“艺术融通篇”“艺术体验篇”“艺术言说篇”三部分。作者从诗与画的关系入手，指出古代不同艺术的分界，更阐释了各艺术会通的内在根由。', NULL, 'J051', '本书适用于艺术品收藏爱好者', NULL, '2022.10', 'chi', NULL);
INSERT INTO `book` VALUES (1540, '978-7-5121-4772-0', '吾土吾地', '易雨潇著', '北京交通大学出版社', 48, '艺术评论', '中国当代艺术的在地性', NULL, NULL, NULL, NULL, '194页', '24cm', NULL, '中央高校基本科研业务费专项资金资助', '本书将“在地性”作为面向中国当代艺术创作的视角，解析中国当代艺术的创作现象及方法论。从中国传统文化的传承与创造的角度研究中国当下的艺术创作。通过“地”的文化内核深层次辨析中国当代艺术创作中与西方艺术观念的差异。', NULL, 'J052', '本书适用于广大艺术评论爱好者', NULL, '2022.06', 'chi', NULL);
INSERT INTO `book` VALUES (1541, '978-7-214-27511-0', '沈从文谈艺术', '沈从文著', '江苏人民出版社', 49.8, '艺术评论', '纪念版', NULL, NULL, NULL, NULL, '268页', '21cm', NULL, '沈从文集 凤凰传媒 凤凰含章', '本书收录了沈从文的部分文学评论与文化研究短文，包括《抽象的抒情》《事功和有情》《谈创作》《给一个读者》《古代人的穿衣打扮》《宋元时装》《蜀中锦》《谈刺绣》《龙凤艺术》《鱼的艺术》《塔户剪纸花样》《维摩诘故事画问题》《北京是个大型建筑博物馆》等。', '文集', 'J05-53', '本书适用于广大艺术爱好者', '精装', '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1542, '978-7-111-71772-0', '设计', '陈冬亮，海军编著', '机械工业出版社', 89, '设计', '民生与社会', NULL, NULL, NULL, NULL, '15,195页', '23cm', NULL, NULL, '本书聚焦为社会生态环境而设计、为地区振兴而设计、为公众生活而设计、为持续生态而设计、为传统延续而设计这五大议题，对2020北京国际设计周主论坛各嘉宾的精彩演讲内容进行了提炼和总结，借以记录当代中国设计界的智慧、思维、行动和力量。', '文集', 'J06-53', '本书适用于关心设计、热爱设计的各界人士及设计领域的师生、从业者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1543, '978-7-5494-2539-6', '美之哲思', '马菁汝著', '广西美术出版社有限公司', 68, '美术教育', '中西美术教育比较与社会美术教育理论研究', NULL, NULL, NULL, NULL, '275页', '26cm', NULL, NULL, '本书汇集了笔者多年来在美术教育研究领域的学术成果，分为社会美育理念与实践研究、中西美术教育比较研究、现当代艺术文化研究三大板块来呈现。', NULL, 'J114-4', '本书适用于美术教育研究人员', NULL, '2022.10', 'chi', NULL);
INSERT INTO `book` VALUES (1544, '978-7-5039-7299-7', '美术史的手铲', '郑岩著', '文化艺术出版社', 42, '美术史', NULL, NULL, NULL, '文艺研究小丛书', NULL, '131页', '19cm', NULL, NULL, '本书收录了《古代墓葬与中国美术史写作》《龙缸与乌盆——器物中的灵与肉》《机械之变——论秦始皇陵铜车马》三篇文章。', '文集', 'J120.9-53', '本书适用于美术史研究人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1545, '978-7-101-15996-7', '民国时期的美术期刊与美术发展', '卢培钊著', '中华书局', 68, '美术史', NULL, NULL, NULL, NULL, NULL, '342页', '22cm', NULL, NULL, '本书在全面搜集整理国内现存的民国时期美术期刊珍稀史料的基础上，从美术史与出版史的视角，考察民国时期美术期刊的发展脉络，探讨民国时期美术期刊与美术发展的关系问题，总结美术期刊在美术发展进程中扮演的角色、传播的内容及产生的影响，对民国美术期刊在中国美术发展史中的作用进行初步的分析与评价。', NULL, 'J120.96', '本书适用于美术史研究人员', '精装', '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1546, '978-7-5039-7324-6', '立象尽意', '刘宏昌主编', '文化艺术出版社', 498, '艺术', '中国艺术研究院艺术培训中心教学成果系列：2021级教学作品集', NULL, NULL, NULL, NULL, '589页', '29cm', NULL, NULL, '本书收录了中国艺术研究院艺术培训中心中国画专业、书法篆刻专业、油画专业共20余位名家导师的教学总结、授课实录，近300幅学员作品，不仅体现了新时代中国书画丰富的创作实践经验，也反映了新时代中国书画理论的学术高度，展现了中国艺术研究院艺术培训中心一年来的教学和研究成果，为今后的教学实践积累了经验提供了思路。', NULL, 'J121', '本书适用于艺术爱好者、学习者、创作者、研究者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1547, '978-7-5508-3857-4', '锥迹光明', '阮解著', '西泠印社出版社', 780, '艺术', '阮解篆刻作品集 阮解壶铭作品集 阮解砚铭作品集', NULL, NULL, NULL, NULL, '3册(212;210;214页)', '29cm', NULL, NULL, '本书是篆刻家阮解以王阳明的年表时间为轴，用王阳明的经典心学金句、诗词、故事、典故等为内容，历时五年，刻制了100多方印章、100多个紫砂壶、100多方砚台以及300多幅传拓作品中精选出的550件篆刻、壶铭、砚铭作品。', NULL, 'J121', '本书适用于广大书法艺术爱好者', '精装', '2022.09', 'chi', NULL);
INSERT INTO `book` VALUES (1548, '978-7-5741-0074-9', '绘画三故事', '东门杨译著', '江苏凤凰美术出版社', 89, '绘画', NULL, NULL, NULL, NULL, NULL, '266页', '19cm', NULL, '凤凰传媒', '本书收录了巴尔扎克、缪塞和戈蒂耶的三篇发表在文学杂志上的短章：《不为人知的杰作》《提香之子》和《金羊毛》。三个故事聚焦于19世纪初的绘画，聚焦于真实与虚构的边界，聚焦于画家与模特间的纠葛，都试图去揭示艺术创造的特性和过程，画家面对画布时的险境与困惑。同时，三个故事又因写作者的不同，展现出截然不同的趣味和取向，风格迥异自成一体。', NULL, 'J205.1', '本书适用于绘画艺术爱好者', '精装', '2022.09', 'chi', NULL);
INSERT INTO `book` VALUES (1549, '978-7-5594-7200-7', '古画七棱镜', '陈晨著', '江苏凤凰文艺出版社', 98, '中国画', '看懂中国画的100个问题', NULL, NULL, NULL, NULL, '287页', '23cm', NULL, '凤凰传媒 凤凰空间', '本书围绕中国画的不同方面，划分为七大侧面，包括概念与分类、工具与材料、装裱与修复、笔墨与技法、题款与钤印、鉴定与作伪、收藏与投资等，梳理了100个问题，兼顾不同读者的不同需求。', NULL, 'J212', '本书适用于广大美术爱好者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1550, '978-7-5205-1683-9', '以图为文', '王韶华著', '中国文史出版社', 89, '中国画', '中国古典绘画中的文图对话', NULL, NULL, NULL, NULL, '341页', '24cm', NULL, NULL, '本书分为中国古典绘画中的文图关系、中国绘画艺术中文图关系的经典——题画诗两编，内容包括：文为图本——诗意画、文在图内——题画诗、文图并存——文学插图、文图合体——图像诗、宋元明清题画诗的演变、明代正局式题画诗、徐渭长卷中的题画诗等。', NULL, 'J212.052', '本书适用于绘画研究人员', NULL, '2023.03', 'chi', NULL);
INSERT INTO `book` VALUES (1551, '978-7-121-44499-9', '中国传统工笔画基础入门', '飞乐鸟著', '电子工业出版社', 59.8, '工笔画', NULL, NULL, NULL, NULL, NULL, '128页', '29cm', NULL, '有艺', '本书在介绍白描工具选购与使用的基础上，依次介绍了白描线条的运用技巧、白描临摹的注意事项和最基本的白描技法，进而介绍了各类花卉、鸟兽和鱼虫的白描技巧，最后通过多个综合案例，介绍了如何通过白描展现国画场景，为读者进一步学习国画打下坚实的基础。', NULL, 'J212.2', '本书适用于工笔画初学者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1552, '978-7-5401-5424-0', '走出巴颜喀拉', '赵丽艳著', '河南美术出版社', 68, NULL, '李伯安绘画研究', NULL, NULL, NULL, NULL, '202页', '29cm', NULL, NULL, '本书以李伯安的绘画艺术作为个案进行研究，将其代表作品巨幅人物长卷《走出巴颜喀拉》为重点研究对象，深入感悟画家的精神世界，通过对李伯安个人性格成因的剖析，分析画家在时代、政治、经济、社会文化的背景下，其水墨人物的绘画语言形成的过程及思想变化，以探究《走出巴颜喀拉》最终形成极其震撼的、独具特点的中国水墨人物作品之成因，进而分析20世纪中国人物画的现代转型中，李伯安的绘画艺术对画坛的重要影响及启示作用。', NULL, 'J212.25', '本书适用于广大美术爱好者', NULL, '2022.09', 'chi', NULL);
INSERT INTO `book` VALUES (1553, '978-7-115-59397-9', '疯狂素描', '白旭功编著', '人民邮电出版社', 69.9, '素描技法', '素描1000例', NULL, NULL, NULL, NULL, '272页', '26cm', NULL, '绘客 优枢课堂', '本书以素描为主题，对素描局部训练与绘画步骤进行了讲解，并将每个类别的事物从不同视角进行绘制。', NULL, 'J214', '本书适用于学习素描和速写的初学者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1554, '978-7-115-59316-0', '透明国度', '至尚君著/绘', '人民邮电出版社', 79.8, '漫画', '马克笔动漫人物插画手绘技法', NULL, NULL, NULL, NULL, '119页', '26cm', NULL, '绘客 爱林博悦', '本书共五章。第一章介绍用马克笔创作动漫人物插画所需的基本绘画工具；第二章介绍手绘动漫人物插画需要掌握的马克笔上色技法；第三部分讲解人物局部的上色技法，强化读者对人物的刻画与塑造能力；第四章和第五章分别以梦幻系人物和糖果色系人物为创作主题，讲解8个动漫人物案例。', NULL, 'J218.2', '本书适用于绘画初学者和马克笔绘画爱好者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1555, '978-7-5039-7283-6', '年画研究', '冯骥才主编', '文化艺术出版社', 128, '年画', NULL, '2022秋', NULL, NULL, NULL, '259页', '26cm', NULL, '天津大学冯骥才文学艺术研究院 中国木版年画研究中心主办', '本书分特稿、推荐、文化研究、地域研究、专题研究、海外展讯等专栏，以年画为主题，收录了中国、俄罗斯、日本等作者的相关研究文章25篇，对年画的研究与现状进行了专业的探讨，为年画的保护与发展提供理论的支持。', NULL, 'J218.3', '本书适用于年画研究者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1556, '978-7-5205-3532-8', '闯关东年画', '曹保明著', '中国文史出版社', 52, '版画', NULL, NULL, NULL, NULL, NULL, '11,193页', '23cm', NULL, NULL, '本书作者结合多年的田野考察，走访民间传承人，记录口述史，亲身体验制作程序以及查阅相关文献等方法，对闯关东年画的起源、历史、文化特征、艺术特色等做出概括，将闯关东年画从兴到衰以及地域演变的过程进行多角度研究，揭示闯关东年画艺术演变发展的意义。', NULL, 'J218.3', '本书适用于年画研究人员', NULL, '2023.02', 'chi', NULL);
INSERT INTO `book` VALUES (1557, '978-7-313-25794-9', '动画机器', '(加)托马斯·拉马尔(Thomas Lamarre)著', '上海交通大学出版社', 88, '动画', '动画的媒体理论', NULL, NULL, NULL, NULL, '374页', '24cm', NULL, '四川省社会科学重点研究基地四川动漫研究中心资助项目', '本书通过对一些重要的日本动画作品、工作室、动画师和动画理论的分析，定义了日本动画的视觉特征以及那些特定的、“动画性”效果的具体意义。不同于从文化内涵、创作理念、叙事目的、内容主旨进入文本的研究，作者从技术的角度出发，将动画作为一种独特的运动影像来考察，通过其生产方式和技术、创作者之间的互动，揭示出动画本身是如何“处理”技术问题的，或者说，人们是如何运用动画的思维方式来思考技术问题的。', NULL, 'J218.7', '本书适用于动画研究人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1558, '978-7-5508-3886-4', '先生归来', '西泠印社美术馆编', '西泠印社出版社', 680, '汉字', NULL, NULL, '马衡和他的时代', NULL, NULL, '274页', '29cm', NULL, '西泠印社集团主办', '本书收录马衡和有关人员作品，回溯马衡的人生道路，勾勒出马衡之于“现代金石学”的学术贡献及其与西泠印社半个世纪的不解之缘。', NULL, 'J222', '本书适用于书画爱好者', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1559, '978-7-5508-3892-5', '追寻吴兴文脉', '浙江省湖州市吴兴区政协编', '西泠印社出版社', 48, '汉字', '书画里的吴兴', NULL, NULL, NULL, NULL, '185页', '24cm', NULL, NULL, '本书以图文的形式，从雅与俗的两个角度，追寻释放无穷魅力的文化密码，梳理了吴兴的文脉脉络。', NULL, 'J222', '本书适用于书画爱好者', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1560, '978-7-02-017582-6', '山海图', '李云中绘', '人民文学出版社', 198, '中国画', '山海经异兽图录', NULL, NULL, NULL, NULL, '393页', '26cm', NULL, NULL, '本书集画家李云中多年来的《山海经》国画彩图一百八十余张，配文并且采用中华书局权威版本《山海经》原文与翻译，以最中国化的形式展现出《山海经》中神怪世界。', NULL, 'J222.7', '本书适用于大众读者', '精装', '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1561, '978-7-5508-3905-2', '王志良西湖山水画集', '王志良著', '西泠印社出版社', 189, '山水画', NULL, NULL, NULL, NULL, NULL, '15,157页', '29cm', NULL, NULL, '本书收录了王志良的山水画作品，包括《宝山生紫图》《西湖烟岚图》《西泠之光》《双峰烟云》《云林禅寺》等。', NULL, 'J222.76', '本书适用于广大美术爱好者', '精装', '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1562, '978-7-5229-0022-3', '世界珍稀灵长目动物速写', '张伟鹏，朱琦著', '中国纺织出版社有限公司', 69.8, '动物画', NULL, NULL, NULL, '动物速写丛书', NULL, '167页', '29cm', NULL, NULL, '本书精选了全球各地三十余种珍稀灵长目动物，运用简洁明快的速写手法将灵长目动物的行坐跳跃、喜怒哀乐定格于纸上，同时配以每种动物的文字介绍，既展现了珍稀灵长目动物的造型之美，又向读者科普了其各具特色的绝技和生存趣闻。', NULL, 'J224', '本书适用于广大美术专业的师生', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1563, '978-7-115-59317-7', '织星白画本', '五方莲子绘', '人民邮电出版社', 79, '插图(绘画)', '五方莲子线稿插画集', NULL, NULL, NULL, NULL, '156页', '24cm', NULL, '数艺设', '本书分为三部分，其中“渔梦谣”收录了古风类的线稿插画，“寻花集”收录了洋装类的线稿插画，“往月记”收录了幻想类的线稿插画。', NULL, 'J228.5', '本书适用于绘画爱好者', '精装', '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1564, '978-7-5356-9903-9', '画猫', '天闻角川编', '湖南美术出版社', 78, '猫', NULL, NULL, '历史宝藏插画集', NULL, NULL, '127页', '29cm', NULL, '画猫系列 天闻角川画集', '本书通过插画展示了60件珍贵的文物，结合“画猫”的创意，以文物拟猫，将历经千百年的文物历史铺陈在读者眼前，同时配以对应的文物知识，加深读者对文物和历史的认识。', NULL, 'J228.5', '本书适用于大众读者', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1565, '978-7-5402-6612-7', '清河月圆何予安', '伊依以翼著', '北京燕山出版社有限公司', 59.8, '插图(绘画)', NULL, NULL, NULL, NULL, NULL, '103页', '26cm', NULL, '力潮文创 快看！', '本书为漫画《穿越成反派要如何活命》的衍生画集。漫画讲述了总裁萧予安得了绝症意外穿书，竟穿成要被小说男主杀死的反派君王，而这个小说男主还是被自己锁在牢笼的敌国皇子晏河清。为了活命萧予安只能抱紧晏河清的大腿怒刷好感度。画集收录漫画中的上百张精彩画面和经典画面。', NULL, 'J228.5', '本书适用于插图爱好者', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1566, '978-7-5514-4259-6', '杰夫·奥本海默：逆向度的人', 'UCCA尤伦斯当代艺术中心编', '浙江摄影出版社', 268, '绘画', NULL, NULL, NULL, NULL, NULL, '135页', '21cm', NULL, NULL, '本书收录杰夫·奥本海默于中国举办的首个个展——“逆向度的人”的展览作品，展览通过雕塑作品与其互文环境展现了人们对当代艺术与现代社会经济中符号、形象和原型的愈发关注，审视了图像和人物如何成为符号象征的过程，以及这种思维方式如何影响了我们对现实的理解。在解释这种联系的同时，鼓励观众重新思考自身在当代社会的地位以及人与人之间的关联。', NULL, 'J231', '本书适用于绘画爱好者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1567, '978-7-5356-9514-7', '史塔克的世界', '(美)威廉·史塔克绘', '湖南美术出版社', 118, '插图(绘画)', '猫猫狗狗、男男女女、傻瓜与小丑', NULL, NULL, NULL, NULL, '342页', '24cm', NULL, '浦睿文化', '本书收录了威廉·史塔克从未公开发表的450多幅插画珍品，并穿插以他的妻子珍妮对史塔克的生活回忆。', NULL, 'J238.5(712)', '本书适用于广大美术爱好者', NULL, '2022.09', 'chi', NULL);
INSERT INTO `book` VALUES (1568, '978-7-5314-9191-0', '文字的结构与装饰', '杨友妮编著', '辽宁美术出版社', 59, '美术字', NULL, NULL, NULL, NULL, NULL, '187页', '21cm', NULL, NULL, '本书共五章，内容包括：中文字体的结构与装饰、现代文字的装饰设计、现代文字字体结构的变化、英文字体的结构与装饰、字体的创意综合设计——字体的版面编排设计。', NULL, 'J292.13', '本书适用于字体设计人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1569, '978-7-5598-5451-3', '从字体开始', 'The Type编著', '广西师范大学出版社', 98, '美术字', '设计改变的生活与社会', NULL, NULL, NULL, NULL, '395页', '19cm', NULL, NULL, '本书从日常生活入手，对字体这一概念逐步剖析，并向读者推广字体、文字设计和视觉传达设计文化相关知识。全书共六大章节，串联起国内外多个不同的案例，对各个案行剖析，并以类似展览的方式组织而成。全书将中文和西文的历史和文化混合在一行阐述，没有刻意区分，而是强调其中的共性和通用性。', NULL, 'J292.13', '本书适用于字体设计专业人士', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1570, '978-7-5508-3915-1', '书海谭粹', '徐煜著', '西泠印社出版社', 198, '汉字', NULL, NULL, NULL, NULL, NULL, '333页', '26cm', NULL, NULL, '本书所集结的是作者二十余年来的全部书法论文，共三十三篇，包括《章法探源》《论两汉史书与文法及刀笔吏之关系》《章草臆说》《晋尚韵说质疑》《唐太宗“神气冲和”理念对褚遂良的意义》等。', '文集', 'J292.1-53', '本书适用于书法爱好者', NULL, '2022.12', 'chi', NULL);
