INSERT INTO `book` VALUES (1594, '978-7-5039-7295-9', '文化记忆', '金蕊，朱贺琴等编著', '文化艺术出版社', 68, '刺绣', '新疆非遗刺绣传承人群口述实录', NULL, NULL, '“文艺叙事与非物质文化遗产研究”系列丛书', NULL, '186页', '24cm', NULL, '新疆维吾尔自治区社科普及基地新疆文化发展研究中心', '本书立足于记忆理论的探索与实践，以访谈、口述方式搜集历史资料，记录见闻和撰写新疆非遗刺绣传承人群“身体技艺”的经验知识及相关的民间“文化记忆”。全书的口述实录共分为三个篇章：第一篇章以“乡愁记忆：记忆作为苦难宝藏”为题，围绕新疆各民族刺绣传承人群回忆人生史、技艺磨砺的往事等展开，通过个体记忆与交流记忆的阐述重述回忆空间；第二篇章以“存储记忆：记忆作为传承要素”为题，突出新疆各民族刺绣传承人群口头叙事中的存储记忆不仅是传统文化“复兴”的前提条件，而且是技艺传承、文化知识更新的基本资源；第三篇章以“功能记忆：', NULL, 'J523.6', '本书适用于非物质文化遗产研究者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1595, '978-7-5039-7313-0', '文化符号', '王敏，王俊涛等编著', '文化艺术出版社', 118, '刺绣', '新疆非遗刺绣衍生品', NULL, NULL, '“文艺叙事与非物质文化遗产研究”系列丛书', NULL, '330页', '24cm', NULL, '新疆维吾尔自治区社科普及基地新疆文化发展研究中心', '本书立足文化符号学的基本原理，以新疆各民族的交往交流交融为文化视角，对新疆历史与在民间搜集的各民族具有代表性的刺绣图案在针法、纹样、色彩上的共通符码意义进行重点解读，结合新疆非遗刺绣衍生品在题材反映方面的特点，从反映地方历史文化之美、自然风物之美与民俗文化思想、生产生活方式之美三个角度创新对新疆非遗刺绣如何进行产业开发的意义理解，结合笔者所负责的2016-2019年文化和旅游部、教育部中国非物质文化遗产传承人群研培计划新疆大学各民族刺绣项目中大量鲜活且富有传播力的新疆非遗刺绣衍生品设计稿、产品概念宣传稿以', NULL, 'J523.6', '本书适用于非物质文化遗产研究者', NULL, '2022.11', 'chi', NULL);
