INSERT INTO `book` VALUES (1597, '978-7-115-57399-5', '品牌标志设计方法与应用', '冯炳焜编著', '人民邮电出版社', 99.8, '品牌', NULL, NULL, NULL, NULL, NULL, '256页', '24cm', NULL, '数艺设', '本书以商业设计为切入点，结合众多具有代表性的案例介绍品牌标志的设计方法。内容颠覆了人们对于品牌标志的常规理解，拆解品牌设计中关于标志设计的核心要素，还原品牌标志在市场中的真实作用，通过品牌标志设计为客户解决实际问题并为品牌赋能。', NULL, 'J524.4', '本书适用于品牌设计师、平面设计师', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1598, '978-7-5180-9852-1', '针对弱势群体公共空间导视系统创新设计与应用研究', '倪春洪著', '中国纺织出版社有限公司', 198, '弱势群体', NULL, NULL, NULL, NULL, NULL, '208页', '24cm', NULL, '2014年国家社科基金艺术学青年项目“针对弱势群体公共空间导视系统创新设计与应用研究”', '本书通过对国家政策的解读以及各个时期学术资料的梳理，利用实地调查发现我国当前城市建设中导视系统的发展优势和不足之处，探究导视系统与社会群体生活之间密不可分的关系，分析弱势群体在社会公共空间中的实际需求。书中利用先进的设计理念和设计方法，从弱势群体、公共空间、导视系统等概念出发，剖析典型案例，针对客观问题提出更加有效的设计策略，帮助导视系统在城市公共空间中发挥更大的作用。', NULL, 'J524.4', '本书适用于设计学专业师生', '精装', '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1599, '978-7-5508-3847-5', '花术', '秦雷，励群主编', '西泠印社出版社', 98, '插花', NULL, NULL, NULL, NULL, NULL, '100页', '26cm', NULL, NULL, '本书收录了近些年秦雷、励群等老师在开展浙江省秦雷技能大师工作室的技能教学指导过程中创作的优秀作品，一个个步骤、一个个细节，折射出老师们的从艺心得。全书包括中国传统插花之盘花、中国传统插花之碗花、中国传统插花之篮花、中国传统插花之瓶花、中国传统插花之缸花、中国传统插花之筒花六个章节。', NULL, 'J525.12', '本书适用于插花爱好者', NULL, '2022.09', 'chi', NULL);
INSERT INTO `book` VALUES (1600, '978-7-5180-9936-8', '云南建水紫陶工艺文化研究', '聂磊明著', '中国纺织出版社有限公司', 98, '紫砂陶', NULL, NULL, NULL, NULL, NULL, '160页', '24cm', NULL, '2017年教育部人文社会科学青年基金项目“云南建水紫陶工艺文化研究”', '本书是一本立足于云南建水紫陶的文化属性，从建水紫陶的制作技艺、工艺持征、历史发展等角度进行探究的学术专著。内容包括：建水紫陶的文化艺术特征；建水紫陶与建水地域历史和文化的具体结合情况，并由此理解建水紫陶文化、艺术的产生、传承与发展；建水紫陶装饰艺术受到来自社会经济与文化生活的哪些影响；建水紫陶的现状与未来发展方向。', NULL, 'J527', '本书适用于从事陶艺研究的专业人员及热爱陶艺文化的广大读者', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1601, '978-7-5184-4122-8', '石湾陶瓷', '林倩倩，陈超编著', '中国轻工业出版社', 68, '陶瓷艺术', NULL, NULL, NULL, '非物质文化遗产丛书', NULL, '102页', '24cm', NULL, '轻艺术', '本书是对广东省佛山石湾的传统民间陶瓷技艺的采集和整理，记录了佛山石湾地区独特的陶瓷艺术和地域文化，对保护民族优秀文化遗产，传承和弘扬岭南文化具有重要意义。书中对石湾的陶瓷艺术进行了整体梳理，尤其对石湾美术陶瓷(陶塑)的技艺、形态等方面给予介绍。', NULL, 'J527', '本书适用于对石湾陶瓷感兴趣的读者', NULL, '2022.08', 'chi', NULL);
INSERT INTO `book` VALUES (1602, '978-7-5411-6530-6', '重金属音乐', '(比)雅克·德·皮埃尔庞编', '四川文艺出版社', 48, '摇滚乐', NULL, NULL, NULL, NULL, NULL, '59页', '21cm', NULL, '图文小百科 文化 后浪', '本书介绍了重金属音乐的方方面面。从速度金属到黑金属，讲述了重金属乐的繁多分支；从魔鬼之角到魔鬼三全音，解读了重金属乐里的特殊文化内含；从黑色安息日到活结，总结了全球知名重金属乐队的风格和代表作品；从“地狱音乐节”到“奥兹重金属巡回音乐节”，介绍了重金属乐国际盛典并勾勒出重金属乐迷群体的特点。', '通俗读物', 'J609.9-49', '本书适用于重金属爱好者', '精装', '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1603, '978-7-121-44290-2', '古典吉他入门完全自学教程', '谷茂宁编著', '电子工业出版社', 69, '六弦琴', NULL, NULL, NULL, NULL, NULL, '294页', '29cm', NULL, '有艺', '本书的内容从古典吉他的发展简史开始，从最基层、最基础的专业知识一步一步科学讲解，再到中级的学会弹理解掌握该如何弹，最后到高级的能弹好，专业有效地解决学生们在学习古典吉他过程中遇到的各项问题。', '教材', 'J623.26', '本书适用于广大音乐爱好者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1604, '978-7-5194-6475-2', '陶埙文化研究', '侯李游美著', '光明日报出版社', 95, '民族管乐器', '传承与创新', NULL, NULL, '光明社科文库', NULL, '218页', '24cm', NULL, '教育部人文社会科学基金青年项目“中国传统乐器陶埙的传承与创新设计研究”资助成果 成都大学·中国东盟艺术学院科研资助成果 光明版', '本书立足于“陶埙文化传承与制作创新”的角度，研究陶埙文化传承的核心内容及其哲学内涵，特别是与中国文化起源的关系，为陶埙文化景象储存的中华文化符码，提供学理性阐释，把陶埙作为民族文化精神象征所传承的文化气韵展示出来，从埙的文化内涵丰富中华雅文化，并提供理论支撑。', NULL, 'J632.19', '本书适用于音乐文化研究者', '精装', '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1605, '978-7-5039-7252-2', '西安鼓乐', '闫若彧著', '文化艺术出版社', 88, '锣鼓音乐', NULL, NULL, NULL, '中国非物质文化遗产代表作丛书', NULL, '252页', '24cm', NULL, NULL, '本书对西安鼓乐的发展和保护进行了阐述。作者深入田野，跟随学者探究了西安鼓乐的乐理和诸多特色，将西安鼓乐的价值进行梳理，并提出了对西安鼓乐的保护。', NULL, 'J632.52', '本书适用于锣鼓音乐研究人员', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1606, '978-7-5039-7328-4', '逐梦·文明之光', '阮昆申[曲]', '文化艺术出版社', 98, '二胡', '二胡与民族管弦乐队', NULL, NULL, NULL, NULL, '127页', '32cm', NULL, '中国音乐学院资助出版', '本书分为三个乐章，第一乐章《夸父追日、大禹治水》，音乐由二胡与琵琶、筝、埙及打击乐一静一动两个主题构成，表现了勇于抗争、追求梦想的勇气豪情和大无畏气概，成就了意志与情怀的交响；第二乐章《周公礼乐、情见德尊》，《礼记·乐记》中的《乐象篇》以礼乐的实施为达到天下大治的大道，故提倡乐教。第三乐章《孔子怀仁、大同理想》，由二胡引入的音乐主题经不断变化发展，体现孔子仁爱心怀及大同理想；沉稳之音节，如其人生足迹，坚定不移。', NULL, 'J648.213', '本书适用于音乐爱好者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1607, '978-7-5039-7254-6', '舞蹈表演研究', '段妃著', '文化艺术出版社', 78, '舞蹈艺术', NULL, NULL, NULL, NULL, NULL, '24,504页', '21cm', NULL, '2014年度文化部文化艺术科学研究项目“舞蹈表演的表现力研究”成果', '本书共分六章，内容包括：舞蹈表演概述、身体表现力、心理表现力、美的表现力、作品表现力、综合表现力。', NULL, 'J7', '本书适用于舞蹈表演艺术研究人员', NULL, '2022.06', 'chi', NULL);
INSERT INTO `book` VALUES (1608, '978-7-5194-6840-8', '拉班舞谱与舞蹈编导理论', '刘丹著', '光明日报出版社', 95, '舞蹈创作', NULL, NULL, NULL, '光明社科文库', NULL, '256页', '24cm', NULL, '光明版', '本书以舞蹈创作和拉班动作体系基础进行阐述，其中涵盖了作者多年来专门对拉班动作体系进行教学、研究所积累的学术成果。不仅介绍了人类舞蹈起源与舞谱产生的相关历史背景与发展特点、拉班舞谱和人体动律学的来龙去脉，还从人类学视角出发，追根溯源到中国原始舞蹈形象中的具体动作动态。', NULL, 'J704', '本书适用于舞蹈创作研究人员', '精装', '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1609, '978-7-04-059485-0', '中国舞蹈十五讲', '仝妍编著', '高等教育出版社', 48.5, '舞蹈史', NULL, NULL, NULL, NULL, NULL, '289页', '26cm', NULL, '广东省高水平大学建设专项', '本书以中国舞蹈从古至今的发展历程为线，以舞蹈艺术发展的文化内涵为点，勾勒出中国舞蹈的历史发展图卷。全书综合舞蹈学、艺术学、美学、社会学等相关学科，史论结合、点线交织，注重培养舞蹈史学研究的综合学科素养与理论研究意识。', '教材', 'J709.2', '本书适用于国内各高等院校舞蹈史课程学习者及广大舞蹈爱好者与研究者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1610, '978-7-5039-7246-1', '中国气派芭蕾训练', '邹之瑞主编', '文化艺术出版社', 58, '芭蕾舞', NULL, NULL, NULL, NULL, NULL, '130页', '24cm', NULL, NULL, '本书主要讲述中国气派芭蕾教学方法，主要是指那些将古典芭蕾动作风格与中国戏曲和民族民间舞等表演艺术形式结合起来，创作出的具有中国气派和民族风格的芭蕾训练方法。', '教材', 'J732.5', '本书适用于芭蕾舞学习者', NULL, '2022.10', 'chi', NULL);
INSERT INTO `book` VALUES (1611, '978-7-5180-9961-0', '解构现代喜剧：观看与叙事', '张文著', '中国纺织出版社有限公司', 88, '喜剧', '以无厘头为例的一种解读方式', NULL, NULL, NULL, NULL, '310页', '24cm', NULL, NULL, '本书内容包括无厘头：一种另类的叙事风格；观看、叙事与媒介；与无厘头叙事对应的观看方式；无厘头叙事与后现代主义；无厘头叙事中的解构；无厘头叙事的衰落六章。', NULL, 'J80', '本书适用于编剧、脱口秀创作者、短视频创作者及高等院校在校学生', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1612, '978-7-5039-7296-6', '前海学派新论', '贾志刚，毛小雨，王馗著', '文化艺术出版社', 42, '戏曲', NULL, NULL, NULL, '文艺研究小丛书', NULL, '16,12,120页', '19cm', NULL, NULL, '本书收录了《“乐本体”是一个伪命题——兼与董健、施旭升商榷》《“剧诗”说是东西方戏剧文化的结晶——兼与施旭升及其他“剧诗”说质疑者商榷》《“前海学派”的戏曲史研究在新世纪以来的意义——从董健“脸谱主义”的误断说起》《“讴”与中国戏曲声腔之演进》四篇文章。', NULL, 'J82', '本书适用于戏曲研究人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1613, '978-7-5039-7317-8', '阿甲戏曲笔记续编', '阿甲著', '文化艺术出版社', 88, '戏曲', NULL, NULL, NULL, NULL, NULL, '302页', '24cm', NULL, NULL, '本书是对阿甲先生未被出版的遗稿的收集、整理，在这些笔记中，阿甲先生对中国戏曲表导演艺术进行了思考，而他所发表的诸多具有代表性的戏曲理论研究论文均能在这些笔记中找到理论思考来源。从这些笔记中我们能看到这位戏曲理论大家探索的思路和脉络。', '文集', 'J82-53', '本书适用于戏曲研究人员', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1614, '978-7-111-70920-6', '光影荣格', '童欣著', '机械工业出版社', 65, '电影学', '在电影中邂逅分析心理学', NULL, NULL, NULL, NULL, '10,267页', '24cm', NULL, NULL, '本书通过将荣格心理学与艺术治疗相结合，致力于使用影视作品达成疗愈的目的，将相关影视作品分为面具类、阴影类、冲突类和整合类四种类型，充分剖析了利用影视作品，在治疗过程中对来访者进行心理唤醒、让来访者放下防御、整合冲突能量的可能性；补充了作者先前专注于表达性艺术治疗的作品《绘画心理分析：追寻画外之音》与《绘画心理调适：表达人设外的人生》，呈现了一套基于欣赏模式的艺术治疗方案。', NULL, 'J90-05', '本书适用于艺术心理学研究人员', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1615, '978-7-5039-7300-0', '被“看”和被“玩”的影像', '孙绍谊著', '文化艺术出版社', 42, '电影学', NULL, NULL, NULL, '文艺研究小丛书', NULL, '126页', '19cm', NULL, NULL, '本书收录孙绍谊教授生前在《文艺研究》发表的四篇论文，包括《当代西方后人类主义思潮与电影》《电影产业研究：理论与方法》《被“看”的影像与被“玩”的影像：走向成熟的游戏研究》等。', '文集', 'J90-53', '本书适用于电影学研究者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1616, '978-7-5194-6871-2', '电影色彩模型与应用研究', '吴晓慧著', '光明日报出版社', 85, '电影美术', '基于数理分析', NULL, NULL, '光明社科文库', NULL, '143页', '24cm', NULL, '光明版', '本书以电影《黄土地》和《霸王别姬》为研究样本，以两部电影镜头画面色彩数据为基础，以色彩与人物、色彩与叙事、色彩与主题为框架，涉及电影学、物理学、色彩学、色彩心理学、色彩文化学等学科领域。', NULL, 'J913', '本书适用于色彩学研究人员', '精装', '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1617, '978-7-5229-0003-2', '电影色彩美学', '唐晋著', '中国纺织出版社有限公司', 168, '电影美术', NULL, NULL, NULL, NULL, NULL, '271页', '24cm', NULL, NULL, '本书解读了电影色彩基本原理、色彩构成节奏、色彩的性格特点、电影用色的客观遵循法则、电影作品中的经典用色与配色、创作者的习惯性用色，构建起了独属于电影的用色体系。', NULL, 'J913', '本书适用于电影美学研究人员', '精装', '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1618, '978-7-5607-7224-0', '历史学英文文献阅读与论文写作', '冯佳著', '山东大学出版社', 76, '史学', NULL, NULL, NULL, '山大史学丛刊', NULL, '297页', '24cm', NULL, NULL, '本书由三部分组成。第一部分由一个章节构成，是为理念阐述，从学术制度层面分析美国英文学术论文、专著生产的过程，从论文写作者所接受的学术训练入手反推读者可以采取的阅读策略以及母语非英语的学生获取学术英文写作技能的路径。第二部分由三章组成，分别是文献阅读方法概论、泛读训练和精读训练。第三部分探讨写作，由三章构成，分别是模板、学术词汇和修改。', NULL, 'K0', '本书适用于历史学专业教学研究人员', NULL, '2022.10', 'chi', NULL);
INSERT INTO `book` VALUES (1619, '978-7-5217-4942-7', '人类的探索', '(加)安德鲁·雷德(Andrew Rader)著', '中信出版集团股份有限公司', 69, '世界史', '走出非洲，迈向太空的冒险之旅', NULL, NULL, NULL, NULL, '321页', '23cm', NULL, '先见', '本书以探索如何塑造今日文明并将如何改变人类未来为核心主线，描绘了宏大的人类历史进程，是一部具有启发性的探索编年史。', '通俗读物', 'K109', '本书适用于世界史爱好者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1620, '978-7-5139-3961-4', '罗马千年征战史', '刘威编著', '民主与建设出版社有限责任公司', 359.8, '古罗马', NULL, NULL, NULL, NULL, NULL, '3册(1258页)', '23cm', '1手册(64页)', '战争事典特辑 026 指文', '本书是一部罗马通史，自特洛伊木马屠城起，至君士坦丁堡陷落止，整整2207年，包括王政、共和、帝国三个时期，也涵盖了罗马文明的主要对手，包括迦太基、继业者诸国、帕提亚、萨珊波斯、阿拉伯帝国、奥斯曼帝国等主要国家史，阐述罗马战争史上的传奇以及罗马社会演进的整个历史进程。', NULL, 'K126', '本书适用于古罗马历史爱好者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1621, '978-7-5327-9153-8', '拜占庭兵法', '(美)迈克尔·德克(Michael J. Decker)著', '上海译文出版社有限公司', 75, '拜占庭帝国', NULL, NULL, NULL, NULL, NULL, '345页', '21cm', NULL, '世纪出版', '本书讲述了拜占庭帝国如何在军事上经受住了千年风雨，从一次次足以摧毁大多数文明的灭顶之灾中学习、调整、演化，针对层出不穷的新敌人发展出针锋相对的新战法，从而一次次地获得新生。本书作者凭借扎实的专业知识介绍了相关的历史背景、帝国军队的组织与领导、训练与装备，以及各个时期帝国所面对的主要敌人，将宏观的战略与具体的战例相结合，最终提炼归纳出了拜占庭赖以生存的战争哲学。', NULL, 'K134', '本书适用于兵法研究人员', '精装', '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1622, '978-7-5607-7452-7', '中华优秀传统文化概论', '林国标编著', '山东大学出版社', 150, '中华文化', NULL, NULL, '思想篇', NULL, NULL, '629页', '24cm', NULL, '国家社会科学基金重大项目“中国特色社会主义与中华文明的内在渊源关系研究”的阶段性研究成果', '本书着眼于中华优秀传统文化的创造性转化和创新性发展，描述中国传统思想文化的发展与演变历程，揭示中华优秀传统文化主要是优秀的价值观、世界观、人生观的起源、汇聚、整合、发展与流变的规律，进而总结中华民族精神的传承和发展的经验。全书分三个部分：“先秦诸子之学”“经学、玄学与宗教”“宋明理学”。', NULL, 'K203', '本书适用于中华文化爱好者', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1623, '978-7-5213-4100-3', '中华思想文化术语', '《中华思想文化术语》编委会编', '外语教学与研究出版社', 48, '中华文化', NULL, '11', NULL, NULL, NULL, '20,179页', '24cm', NULL, '教育部、国家语委重大文化工程“中华思想文化术语传播工程”成果 国家社会科学基金重大项目“中国核心术语国际影响力研究” “十四五”国家重点出版物出版规划项目 第二届向全国推荐中华优秀传统文化普及图书', '本书收录了100条中华思想文化术语，以中英文双语的方式进行阐释。这些术语反映了中国传统文化特征和民族思维方式，体现了中国核心价值。', NULL, 'K203-61', '本书适用于大众读者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1624, '978-7-100-21652-4', '春秋战国的社会变迁', '晁福林著', '商务印书馆', 260, '社会变迁', NULL, NULL, NULL, NULL, '增订版', '876页', '24cm', NULL, NULL, '本书以一种大历史的视角，将春秋战国作为一个上承夏商西周而来的完整历史阶段进行阐述，分别从社会政治历史的演进、社会经济的发展与社会生活的进步、社会性质的演变、社会结构与社会制度的变动、社会文化的发展五个方面，就春秋战国时期的社会政治、经济和文化的演进，进行了深入的研究，系统地展现了春秋战国时期社会变迁的历史脉络、相关内容的发展沿革以及前因后果，勾勒了一幅全景式春秋战国社会演变史。增加的两篇附录，一篇是关于清华简《系年》与两周之际史事的研究，一篇是利用新出土战国竹简探讨《诗经》成书的若干问题，反映了近年来春秋', NULL, 'K225.07', '本书适用于历史研究人员', '精装', '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1625, '978-7-5190-4965-2', '趣话春秋战国', '龙飞著', '中国文联出版社有限公司', 89, '春秋战国时代', NULL, NULL, NULL, NULL, NULL, '11,594页', '21cm', NULL, NULL, '本书所述的事件全部发生在春秋战国时期，作者选择了最有代表性的几个诸侯：齐国、晋国、楚国、吴国、越国和秦国，按照称霸的时间顺序讲述了他们故事。这些国家各有特色，比如齐国最传奇、楚国最有个性、秦国最励志等。', '通俗读物', 'K225.09', '本书适用于大众读者', NULL, '2022.10', 'chi', NULL);
INSERT INTO `book` VALUES (1626, '978-7-5445-6817-3', '秦汉史论稿', '张鹤泉著', '长春出版社', 128, '中国历史', NULL, NULL, NULL, '松江丛书', NULL, '412页', '24cm', NULL, NULL, '本书考证了东汉募兵、屯驻营兵的发生及作用；考察了东汉东辽将军的设置及作用；阐释了东汉持节官的设置及持节的影响。同时对东汉的郊祀礼、五郊迎气祭祀礼、丧礼中的吊祭、送葬、护丧、墓祭问题以及东汉的山岳祭祀等问题，都做了考证。', '文集', 'K232.07-53', '本书适用于历史研究人员', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1627, '978-7-5445-6818-0', '魏晋南北朝史论稿', '张鹤泉著', '长春出版社', 128, '中国历史', NULL, NULL, NULL, '松江丛书', NULL, '352页', '24cm', NULL, NULL, '本书收录了《西晋丧葬赏赐考》《两晋郊祀礼试探》《两晋南朝迎气祭祀礼考》《前秦国家民族政策的失误及其对国家统一局面的影响》《北魏迎气祭祀礼试探》《北魏皇帝赐宴考略》等文章。', '文集', 'K235.07-53', '本书适用于历史研究人员', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1628, '978-7-5546-2020-5', '陶庵梦忆', '(明)张岱著', '古吴轩出版社', 42, '笔记', NULL, NULL, NULL, NULL, NULL, '180页', '21cm', NULL, '竹石文化', '本书描述了明代江浙地区的社会生活，如茶楼酒肆、说书演戏、斗鸡养鸟、放灯迎神以及山水风景、工艺书画等，更多的是对社会生活和风俗人情的反映。', NULL, 'K248.066', '本书适用于文学爱好者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1629, '978-7-5607-7243-1', '义和团·中国与世界', '(英)毕可思，(德)狄德满编', '山东大学出版社', 58, '义和团运动', NULL, NULL, NULL, '义和团运动研究丛书', NULL, '231页', '24cm', NULL, NULL, '本书是2001年6月伦敦大学亚非学院召开的“1900：义和团与中国和世界”学术研讨会论文选编。共10篇文章，包括《庶民眼中的义和团：一位印度士兵对中国与世界的描述(1900-1901)》《掠夺及其批判：道德话语与北京大抢掠(1900-1901)》《义和团运动与印度：全球化的神话》等。', '文集', 'K256.707-53', '本书适用于义和团运动研究人员', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1630, '978-7-5117-4212-4', '南方军防疫给水部留守名簿', '杨彦君主编', '中央编译出版社', 980, '日本关东军', NULL, NULL, NULL, NULL, '[影印本]', '14,429页', '29cm', NULL, '国家社科基金抗日战争研究专项工程 侵华日军细菌战罪行史料整理及专题数据库建设', '本书内容为二战时期日军南方军防疫给水部的原始档案，记录了该部队的人员构成与来源、部队的机构设置，以及该部队的主要业务、生物战准备等。', '史料', 'K265.606', '本书适用于历史爱好者', '精装', '2022.07', 'chi', NULL);
INSERT INTO `book` VALUES (1631, '978-7-108-07537-6', '国家记忆与文化表征', '赵静蓉等著', '生活·读书·新知三联书店', 69, '中国历史', NULL, NULL, NULL, NULL, NULL, '279页', '23cm', NULL, NULL, '本书将“国家”化解为“中国”“民族”“祖国”“政党”，指明国家记忆不等于国家历史，更倾向于一种社会化的“黏结记忆”。一是研究“国家记忆是什么”，关注国家记忆如何形成、某种个体记忆或地方记忆如何上升为国家记忆、社会文化如何表征国家记忆等；二是研究“国家如何记忆”，关注国家作为记忆主体的角色功能，最终导向的是关于国家记忆的历史或国家的历史问题。', NULL, 'K270.7', '本书适用于历史研究者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1632, '978-7-5205-3841-1', '津门掌故', '周利成著', '中国文史出版社', 68, NULL, NULL, NULL, NULL, NULL, NULL, '268页', '24cm', NULL, NULL, '本书通过讲述天津卫、紫竹林、水西庄、官银号等数十个地名故事，记述了天津的历史变迁、地名的由来、遗存的古迹、名人故居、历史故事，反映了旧天津的政权的统治、经济的兴衰、城市的演进、百姓的悲喜。', NULL, 'K292.1-53', '本书适用于地方史爱好者', NULL, '2023.03', 'chi', NULL);
INSERT INTO `book` VALUES (1633, '978-7-5533-3970-2', '天教此水占风流', '高安宁著', '南京出版社', 72, '文化史', '漫话秦淮文脉', NULL, NULL, NULL, NULL, '10,268页', '21cm', NULL, NULL, '本书分为22个篇目，将包括地名、风俗、建筑、名人、小吃、非遗、方言等在内的秦淮文化的各个面向娓娓道来，向读者展示了“中国历史文化第一河”——秦淮河的悠久历史和丰厚文化。', '通俗读物', 'K295.31', '本书适用于文化史爱好者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1634, '978-7-5508-3889-5', '两宋流韵传千古', '宋宪章著', '西泠印社出版社', 80, '文化史', NULL, NULL, NULL, NULL, NULL, '248页', '24cm', NULL, NULL, '本书包括两宋遗迹、遗事和遗风三个有着历史关联的文化内容，题材并不限于饮食，而与当下努力发掘的作为中华优秀传统文化之“宋韵”相呼应。该书还考证了宋代许多名胜古迹、民俗风情，以两宋历史为轴，勾勒出中华传统文化的辉煌时代。', NULL, 'K295.51', '本书适用于大众读者', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1635, '978-7-5508-3891-8', '桐庐微村志', '桐庐县地方志研究室编', '西泠印社出版社', 158, '村史', NULL, '第四辑', NULL, '桐庐县微村志系列丛书', NULL, '289页', '29cm', NULL, NULL, '本书收录桐庐县的美丽乡村建设中的金溪村、彰坞村、凤联村、上唐村、柳岩村等名村，以碎片化采编和新媒体传播的方式修志用志，既有村史沿革又有乡贤人才，既有历史传说又有民俗家风，既有文化古迹又有自然风光，既有村庄建设又有产业发展，展示了乡村的深厚底蕴和无限魅力。', NULL, 'K295.55', '本书适用于地方志研究人员', '精装', '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1636, '978-7-5506-3714-6', '江南旧志图选', '苏州市地方志办公室编', '凤凰出版社', 260, NULL, NULL, NULL, '境域', NULL, NULL, '593页', '26cm', NULL, '志说江南 苏州方志', '本书所录江南旧志图涵盖今江苏、上海、浙江、安徽十六个现行设区市。', NULL, 'K295-64', '本书适用于地方史研究人员', '精装', '2022.09', 'chi', NULL);
INSERT INTO `book` VALUES (1637, '978-7-5506-3716-0', '江南旧志图选', '苏州市地方志办公室编', '凤凰出版社', 240, '城市史', NULL, NULL, '城池', NULL, NULL, '459页', '26cm', NULL, '志说江南 苏州方志', '本书从江南地区历代方志中精选珍贵图谱，涉及江苏、上海、浙江、安徽十六个现行设区市，包括南京城池图、镇江城池图、苏州城池图、嘉兴城池图、湖州城池图等重要城池图，展现江南地区地域联系之紧密、文化之交融。', '图集', 'K295-64', '本书适用于城市史研究人员', '精装', '2022.09', 'chi', NULL);
INSERT INTO `book` VALUES (1638, '978-7-5144-5029-3', '剑川沙溪古镇二十年图志', '剑川县地方志编纂委员会办公室编', '方志出版社', 680, '乡镇', NULL, NULL, NULL, NULL, NULL, '324页', '31cm', NULL, NULL, '本书记述时限上起2001年，下至2020年，记述了剑川沙溪镇2001-2020年的历史和发展进程。', '图集', 'K297.45-64', '本书适用于地方史研究人员', '精装', '2021.12', 'chi', NULL);
INSERT INTO `book` VALUES (1639, '978-7-5426-7913-0', '坂本龙马与明治维新', '(美)马里乌斯·詹森(Marius B. Jansen)著', '上海三联书店', 102, '明治维新(1868)', NULL, NULL, NULL, '理想国译丛', '2版', '437页', '23cm', NULL, '马里乌斯·詹森作品 01', '本书以坂本龙马这位传奇人物的一生为线索，广罗维新时期的文件、法令、书信，结合后人编写的地方志、传记和相关研究资料，力图还原土佐、长州、萨摩等藩“尊王攘夷”的过程，勾勒出志士、藩国、幕府、公家和西方列强五方势力在倒幕与维新中扮演的角色，重现了那个伟大时代风云激荡的真实面貌。', NULL, 'K313.41', '本书适用于历史研究人员', '精装', '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1640, '978-7-5201-9284-2', '海洋之间的欧洲', '(英)巴里·坎利夫(Barry Cunliffe)著', '社会科学文献出版社·甲骨文工作室(分社)', 268, NULL, '主线与变化（公元前9000年－公元1000年）', NULL, NULL, NULL, NULL, '580页', '29cm', NULL, '甲骨文', '本书选取从大约公元前9000年的末次冰川期到公元后第一个千年，围绕流动性与创新性这两个主题，从考古学、人类学、地理学等多个角度阐释空间、时间和人类的互动概念，突出他的“海洋是欧洲独特发展轨迹背后的驱动力”的观点。', NULL, 'K500', '本书适用于历史爱好者', '精装', '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1641, '978-7-5730-0870-1', '马丁·盖尔归来', '(美)娜塔莉·泽蒙·戴维斯著', '海南出版社', 58, '社会生活', NULL, NULL, NULL, NULL, NULL, '30,243页', '21cm', NULL, '理想国', '本书作者利用婚姻、遗嘱、交易文书、纠纷与仲裁等多种材料，试图通过讲述这一离奇的冒名顶替故事，再现16世纪法国农民的经济社会生活和精神世界，以及近代早期法国的国家司法体系。她带领我们一起去追寻真假马丁·盖尔和他们的妻子贝特朗·德罗尔斯的世界，从各方面刻画他们丰富的生活细节，揭示当时的婚姻关系、亲属关系、继承法、社会流动、兵役、乡村习俗和司法程序。', NULL, 'K565.3', '本书适用于历史爱好者', '精装', '2023.02', 'chi', NULL);
INSERT INTO `book` VALUES (1642, '978-7-5029-7733-7', '字里寻名', '洪澜编著', '气象出版社', 78, '姓名学', '《康熙字典》起名用字精选', NULL, NULL, NULL, NULL, '397页', '24cm', NULL, NULL, '本书从《康熙字典》(王引之校改本)中选取部分起名常用汉字，并从字典原文中节选字义，辅助以东汉许慎所著经典字书《说文解字》及宋代徐铉为之所做的注释解读，帮助读者了解这些当代社会常用于起名的汉字，知道这些字的缘起由来。', NULL, 'K810.2', '本书适用于大众读者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1643, '978-7-5146-2163-1', '改变世界历史的17位将领', '(英)丹·皮尔编著', '中国画报出版社', 72, '军事家', NULL, NULL, NULL, NULL, NULL, '199页', '26cm', NULL, '萤火虫 042', '本书介绍了军事史上一系列杰出的人物——从古代世界无畏的勇士到中世纪的征服者，从早期现代革命者到20世纪著名的将军。探索亚历山大大帝如何建立世界上最强大的帝国之一，并了解古斯塔夫·阿道夫的军事创举。本书还呈现了美国最受人尊敬的两位将军在内战中的正面交锋，您还会读到击败纳粹、结束“二战”的苏联将军的故事。', NULL, 'K811', '本书适用于大众读者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1644, '978-7-5115-7595-1', '榜样的故事', '任初轩编', '人民日报出版社', 58, '人物', '奋进新征程', NULL, NULL, NULL, NULL, '294页', '24cm', NULL, NULL, '本书收录《人民日报》等权威媒体发表的“时代楷模”群体事迹文章，包括陆军工程大学“星火”理论宣讲服务政治教员群体、闽宁对口扶贫协作援宁群体、敦煌研究院文物保护利用群体等，他们的事迹感人至深、影响广泛深远。', NULL, 'K820.7', '本书适用于大众读者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1645, '978-7-5508-3876-5', '宋韵“史”话', '宁波市鄞州区档案馆编著', '西泠印社出版社', 168, '家族', NULL, NULL, NULL, NULL, NULL, '117页', '29cm', NULL, NULL, '本书以《史家祖宗画像及传记、题跋》为核心，对四明史氏历史、有影响的家族成员生平事迹、“八行”垂训及文物遗存等进行了集中梳理，同时也对史氏家族历代集中聚居的绿野岙、下水、史家湾、史家码、月湖等地进行介绍。', '史料', 'K820.9', '本书适用于家族史研究人员', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1646, '978-7-5147-1114-1', '朱瑞', '军事科学院解放军党史军史研究中心[编]', '学习出版社', 20, NULL, NULL, NULL, NULL, NULL, NULL, '154页', '19cm', NULL, '中华人物故事汇 中华先烈人物故事汇（第三辑）', '本书通过5个章节，呈现了朱瑞在大革命、抗战时期投身革命积极为党工作，解放战争时期建设人民军队尤其是炮兵部队的的事迹，彰显其个人崇高之精神和共产党人奋斗之品格。', NULL, 'K825.2', '本书适用于大众读者', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1647, '978-7-5147-1124-0', '贺锦斋', '军事科学院解放军党史军史研究中心[编]', '学习出版社', 22, NULL, NULL, NULL, NULL, NULL, NULL, '172页', '19cm', NULL, '中华人物故事汇 中华先烈人物故事汇（第三辑）', '本书以贺锦斋一生重要的时间、事件节点为主要线索，从贺锦斋参加湘军到加入中国共产党、而后加入红军、领导革命根据地的军事斗争和在反“围剿”中屡立战功，直至为革命事业壮烈牺牲的短暂一生中，以具有代表性的故事为串联，勾勒出贺锦斋同志坚定的革命信仰、深沉的家国情怀和崇高的献身精神。', NULL, 'K825.2', '本书适用于大众读者', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1648, '978-7-5147-1190-5', '毛泽建', '军事科学院解放军党史军史研究中心[编]', '学习出版社', 20, NULL, NULL, NULL, NULL, NULL, NULL, '151页', '19cm', NULL, '中华人物故事汇 中华先烈人物故事汇（第三辑）', '本书以讲故事的形式展现了革命烈士毛泽建的光辉一生。全书分为“多难的童年”“走上革命道路”“投身工农运动”“坚持游击战争”等五个章节，以毛泽建命运多舛的童年为开篇，阐述了毛泽建从韶山冲里的“菊妹子”走上探求救国救民真理的革命道路的前提和背景，描绘了毛泽建在女三师积极领导学生运动，后投身工农运动成为游击队长，智斗敌人，直至被捕后英勇牺牲的革命经历。', NULL, 'K825.2', '本书适用于大众读者', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1649, '978-7-5194-6647-3', '徽州儒商', '桑良至著', '光明日报出版社', 95, '商人', NULL, NULL, NULL, '光明社科文库', NULL, '227页', '24cm', NULL, '光明版', '本书描述了10个徽州商人，包含郑侠如、马曰琯、鲍廷博、汪启淑、胡正言、程梦星、程晋芳、江春、汪梧凤、胡雪岩。他们既是商人，又是学士，或官士。他们既发展了商品经济，又为社会文化、家乡文化做出了贡献。', NULL, 'K825.38', '本书适用于人物研究人员', '精装', '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1650, '978-7-5010-7114-2', '天香如绣', '王小朋，霍宏伟主编', '文物出版社', 680, NULL, '王绣艺文六十载', NULL, NULL, NULL, NULL, '237页', '31cm', NULL, NULL, '本书分为风起青苹、纸上花开、潮平岸阔、盛世绣色等共六部分，以多人视角展现了王绣的个人成长之路，折射出世纪交汇之间的沧桑变化。', NULL, 'K825.41', '本书适用于传记文学爱好者', NULL, '2022.10', 'chi', NULL);
INSERT INTO `book` VALUES (1651, '978-7-02-017569-7', '蒋南翔传', '方惠坚[等]编著', '人民文学出版社', 78, NULL, NULL, NULL, NULL, NULL, '修订版', '463页', '22cm', NULL, NULL, '本书梳理和还原了一位革命教育家的非凡人生，彰显出蒋南翔同志不忘初心、牢记使命的高尚精神和坚毅品格。', NULL, 'K825.46', '本书适用于大众读者', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1652, '978-7-303-28173-2', '情系北师大', '魏礼群著', '北京师范大学出版社', 89, NULL, '十年耕耘纪实', NULL, NULL, NULL, NULL, '203页', '26cm', NULL, NULL, '本书以第一人称叙述魏院长在退休后回报母校的工作经历，回顾和总结北京师范大学社会治理智库和社会学科建设的历程与成效。', NULL, 'K825.46', '本书适用于传记爱好者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1653, '978-7-02-017592-5', '颜延之生平与著述考论', '杨晓斌著', '人民文学出版社', 98, NULL, NULL, NULL, NULL, NULL, NULL, '224页', '24cm', NULL, '国家社科基金后期资助项目 陕西省“百人计划”特聘教授资助项目', '本书内容包括三个方面。第一部分是关于颜延之里籍、门第、家世，及其生平中重要问题如绰号，如三十初仕、免官原委等的考辨。第二部分是关于颜延之散在现存各种文献中诗文作品的考证。第三部分是关于《颜延之集》的研究，作者对《颜延之集》的结集、散佚、重编，以及今存颜集的版本源流做了梳理。', NULL, 'K825.6', '本书适用于文学研究人员', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1654, '978-7-214-27457-1', '从文家书', '沈从文著', '江苏人民出版社', 49.8, NULL, '纪念版', NULL, NULL, NULL, NULL, '264页', '21cm', NULL, '沈从文集 凤凰传媒 凤凰含章', '本书是沈从文的家书选集，全书从当年作者追求张兆和开始，到两人终于结为夫妻，经历新婚远别，乱世飘零，期间沈从文写出大量家信，信中有湘情景致，有离愁别恨，无不洋溢着这位作家真挚的情感。一九四九年，正准备投入大量精力创作更多更好的文学作品的沈从文，终止了文学事业，也走下了北大中文系的讲台。这之后沈从文的愁苦以及人生的转变，对文学生命的思考，无不在家书中再一次体现出来。', NULL, 'K825.6', '本书适用于文学爱好者', '精装', '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1655, '978-7-214-27514-1', '从文自传', '沈从文著', '江苏人民出版社', 45, NULL, '纪念版', NULL, NULL, NULL, NULL, '220页', '21cm', NULL, '沈从文集 凤凰传媒 凤凰含章', '本书写了沈从文20岁之前的那段岁月。有对故乡的记忆，对亲人的怀念，还有个人种种经历，少了他后来作品中那种美丽的哀愁，却充满了生命原始的炽烈和真诚。', NULL, 'K825.6', '本书适用于传记文学爱好者', '精装', '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1656, '978-7-5402-6542-7', '赤子其人沈从文', '范鹏飞，张恺新著', '北京燕山出版社有限公司', 32, NULL, NULL, NULL, NULL, NULL, NULL, '168页', '21cm', NULL, '纪念沈从文诞辰120周年', '本书由两部分组成：上篇是梳理沈从文的一生经历，下篇是记叙于善浦和沈从文的交往。', NULL, 'K825.6', '本书适用于人物研究人员', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1657, '978-7-5699-0542-7', '苏青：风从海上来', '江晓英著', '北京时代华文书局', 46, NULL, NULL, NULL, NULL, NULL, '2版', '242页', '21cm', NULL, '丑牛系列之民国的婉约 新业文化', '本书以时间为脉络，从独特的视角出发，注重深度和热点，并用细腻的笔触抽丝剥茧地剖析因由和结果，重新解读一个真实的苏青，关于她的梦，她的爱，她的苦，她的难，以及她的人生底事。', NULL, 'K825.6=74', '本书适用于传记文学爱好者', NULL, '2021.05', 'chi', NULL);
INSERT INTO `book` VALUES (1658, '978-7-5699-0190-0', '张爱玲：最是清醒落寞人', '牧来著', '北京时代华文书局', 46, NULL, NULL, NULL, NULL, NULL, '2版', '224页', '21cm', NULL, '丑牛系列之民国的婉约 珍藏版', '本书讲的是张爱玲传奇的一生，全书分为她是海上吹不停的风、爱是一场蒙眼的远行、附录三部分。', NULL, 'K825.6=75', '本书适用于人物研究者', NULL, '2021.05', 'chi', NULL);
INSERT INTO `book` VALUES (1659, '978-7-5741-0282-8', '李方膺研究', '中国美术南通现象课题研究编委会编', '江苏凤凰美术出版社', 186, NULL, NULL, NULL, NULL, NULL, NULL, '441页', '25cm', NULL, '江苏艺术基金资助项目 南通市文化广电和旅游局重点课题 凤凰传媒', '本书对“扬州八怪”之一的清代著名画家贤吏、南通人李方膺生平成就进行了梳理和研究，在对其书法、绘画研究的同时，还考证了李方膺的家世生平、诗文论著，以及后世评价。', NULL, 'K825.72', '本书适用于人物研究人员', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1660, '978-7-215-12308-3', '张伯行', '刘海潮著', '河南人民出版社', 460, '政治人物', NULL, NULL, NULL, NULL, NULL, '6册(2068页)', '24cm', NULL, NULL, '本书以张伯行的一生轨迹为脉络，以清正廉洁、刚直不阿为主线，以其与各种黑恶势力斗争为矛盾冲突，本着“大真实、小虚构”的原则，进行艺术再创作，刻画了一个律己、廉洁、勤政的官员形象，展现了张伯行的精神和人格。', NULL, 'K827=49', '本书适用于人物研究者', NULL, '2022.05', 'chi', NULL);
INSERT INTO `book` VALUES (1661, '978-7-5147-1141-7', '邹容', '张海鹏主编', '学习出版社', 20, NULL, NULL, NULL, NULL, NULL, NULL, '161页', '19cm', NULL, '中华人物故事汇 中华先烈人物故事汇（第三辑）', '本书就以讲故事的形式，展现了邹容短暂而精彩的一生。', NULL, 'K827=52', '本书适用于大众读者', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1662, '978-7-5205-3897-8', '远行记', '马力著', '中国文史出版社', 69, '外交人员', '晚清官民的海外实录', NULL, NULL, NULL, NULL, '355页', '24cm', NULL, NULL, '本书选取了谢清高、斌椿、张德彝、郭嵩焘、黎庶昌、李圭、何如璋、王韬、徐建寅、傅云龙、薛福成、康有为、梁启超这十余位代表性人物，记述了晚清时代中国第一批出洋人士的游历事略。', NULL, 'K827=52', '本书适用于外交史研究人员', NULL, '2023.03', 'chi', NULL);
INSERT INTO `book` VALUES (1663, '978-7-5147-1123-3', '周逸群', '军事科学院解放军党史军史研究中心[编]', '学习出版社', 20, NULL, NULL, NULL, NULL, NULL, NULL, '149页', '19cm', NULL, '中华人物故事汇 中华先烈人物故事汇（第二辑）', '本书以故事的形式展现了周逸群战斗的一生，歌颂了他对党和革命事业的无限忠诚，在中国革命史上谱写了不朽的篇章。', NULL, 'K827=6', '本书适用于大众读者', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1664, '978-7-221-17339-3', '我的前半生', '爱新觉罗·溥仪著', '贵州人民出版社', 99, NULL, NULL, NULL, NULL, NULL, NULL, '568页', '24cm', NULL, '新华先锋 先锋', '本书作者溥仪从自己的家族背景写起，回顾了他入宫做皇帝、遭遇辛亥革命、清朝皇帝退位、中华民国成立、北洋军阀混战、出宫、客寓天津，做伪满洲国皇帝、逃亡，直至中华人民共和国建立后接受改造，成为普通公民的全部经历。', NULL, 'K827=7', '本书适用于人物研究人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1665, '978-7-5147-1189-9', '毛岸英', '军事科学院解放军党史军史研究中心[编]', '学习出版社', 26, NULL, NULL, NULL, NULL, NULL, NULL, '211页', '19cm', NULL, '中华人物故事汇 中华先烈人物故事汇（第三辑）', '本书讲述了毛岸英烈士的历经磨难、多姿多彩的短暂人生，刻画了他的光辉形象，以讲故事的形式，选取毛岸英短暂一生中闪光的关键节点，讲述了他从漂泊的童年、在苏联的日子、新中国成立初期在基层锻炼、参加抗美援朝战争并国捐躯的事迹，以及他和毛泽东杨开慧、毛泽民、毛岸青、刘思齐等的亲情、爱情故事。', NULL, 'K827=7', '本书适用于大众读者', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1666, '978-7-5207-3025-9', '“挑战者”稻盛和夫', '(日)涩泽和树著', '东方出版社', 68, '通信', NULL, NULL, NULL, NULL, NULL, '374页', '19cm', NULL, NULL, '本书共分6章，内容包括冲入旋涡的挑战者；十九少壮的起航；一跃居首的弱者；“葡萄串”和“旁系诸侯”；凤凰涅槃，浴火重生等。', NULL, 'K833.135.38', '本书适用于企业管理者', '精装', '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1667, '978-7-5159-2148-8', '太空对接故事', '(俄罗斯)弗拉基米尔·谢尔盖耶维奇·谢拉苗尼科夫(В.С. Сыромятников)著', '中国宇航出版社', 148, NULL, NULL, '第二部', NULL, NULL, NULL, '11,552页', '24cm', NULL, NULL, '本书延续《太空对接故事》第一部的时间轴，叙述了苏联航天自1975年至21世纪初的发展历程。此外，作者还叙述了自己参与的一系列航天研制工作，包括六自由度综合试验台、新型对接机构APAS-89、转位机构和太空机械臂、多次可展太阳电池阵，以及太阳帆项目等。', NULL, 'K835.126.14', '本书适用于航天科学爱好者', '精装', '2022.04', 'chi', NULL);
INSERT INTO `book` VALUES (1668, '978-7-108-07498-0', '柯鲁克自传', '(英)柯鲁克著', '生活·读书·新知三联书店', 59, NULL, '从汉普斯特德荒野到十里店', NULL, NULL, NULL, NULL, '313页', '23cm', NULL, NULL, '本书讲述了柯鲁克的传奇人生，他的一生走过了整个20世纪。他在伦敦东区度过童年，成年后赴美留学，赶上了经济大萧条。他在进步的学生组织中十分活跃，大学毕业后参加西班牙内战的国际纵队英国旅，并负伤。在“二战”中，他被派往印度作战，期间两次来到中国。战后他留在中国，参加华北的土地改革运动，和妻子伊莎白撰写了关于土改的名著《十里店》，还参与创建了北京外国语大学。他将后半生献给了新中国的建设。', NULL, 'K835.615.4', '本书适用于传记爱好者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1669, '978-7-5680-9034-6', '凡·高', '(西)劳拉·加西亚·桑切斯(Laura García Sánchez)著', '华中科技大学出版社', 138, NULL, NULL, NULL, NULL, NULL, NULL, '95页', '27cm', NULL, '巨匠与名作 有书至美 华中出版', '本书撷取凡·高艺术生涯中重要的历史片段，通过语言介绍了他的生平和主要艺术作品，对艺术家的创作节点进行了精准定位，以时间和地点为轴，还原了他在不同时期、辗转于不同城市间的生活与创作经历；同时，还将他与家人的书信原文穿插其中，带领读者快速了解凡·高的精神世界与创作理念。', NULL, 'K835.635.72', '本书适用于大众读者', '精装', '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1670, '978-7-5010-7812-7', '浙江省文物考古研究所学刊', '浙江省文物考古研究所编', '文物出版社', 390, '考古学', NULL, '第十二辑', NULL, NULL, NULL, '347页，88页彩版', '29cm', NULL, NULL, '本书收录了《绍兴市越城区小亭山汉晋墓葬发掘简报》《绍兴市上虞区梁湖街道苦竹山墓地发掘简报》《杭州市余杭区塘山遗址卢村段H1发掘简报》《宁波奉化江口街道龙舌山墓地发掘简报》《上虞积山西晋墓葬发掘简报》《云和东山头东晋徐氏家族墓地发掘报告》等文章。', '丛刊', 'K85-55', '本书适用于考古工作者', '精装', '2022.10', 'chi', NULL);
INSERT INTO `book` VALUES (1671, '978-7-5462-3521-9', '墙', '(美)大卫·弗莱(David Frye)著', '广州出版社', 79, '城墙', '血与砖的文明史', NULL, NULL, NULL, NULL, '292页', '21cm', NULL, NULL, '本书分为建造者与蛮族人、墙的伟大时代、转变中的世界、符号的冲撞四个部分，内容包括：“筑城声酸嘶”、连接欧亚大陆的墙、哈德良长城、失乐园、可怕的轰击炮、围栏之外、最后的战斗等。', NULL, 'K868.3', '本书适用于文明史爱好者', '精装', '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1672, '978-7-5133-5084-6', '秦汉名物丛考', '王子今著', '新星出版社', 158, '秦汉考古', NULL, NULL, NULL, '吉金文库', '增订版', '2册(858页)', '23cm', NULL, NULL, '本书从特定的角度，选择一些常人比较陌生而冷僻的秦汉名物进行系列考证，从内容和形式上都展示出许多新特点和新贡献。考证研究的对象大多是近几十年来考古发现的出土文物、建筑遗迹、秦汉简牍、摩崖石刻上出现的颇有争议或尚未解决的名物术语。对名物的考证不仅仅局限在名物本身的形貌、生成、功用等方面，而是同时从广阔的视野和多侧面多角度揭示了各种名物所蕴含的社会意义。', NULL, 'K871.414', '本书适用于秦汉考古研究人员', '精装', '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1673, '978-7-5010-7834-9', '渭河上游天水段考古调查报告', '甘肃省文物考古研究所，天水市文物保护和考古研究中心编著', '文物出版社', 460, '文物', NULL, NULL, NULL, NULL, NULL, '27,359页，104页彩版', '29cm', NULL, '国家重点文物保护专项补助经费资助', '本书分三部分：第一部分包括渭河上游地区的田野考古历程和2016-2017年早期秦文化联合考古队在天水市秦州区、麦积区、甘谷县和武山县的调查资料；第二部分是2005、2008年在清水县、张家川县的调查资料；第三部分是项目组人员的一些初步研究。', NULL, 'K872.423', '本书适用于文物研究者', '精装', '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1674, '978-7-5333-4522-8', '风雨兼程40年', '山东省文物考古研究院编', '齐鲁书社', 450, '文物', '山东省文物考古研究院建院纪念文集', NULL, NULL, NULL, NULL, '2册(665页)', '29cm', NULL, NULL, '本书上篇收录山东省文物考古研究院纪念文章20余篇，下篇收录本院在职人员和退休人员学术论文30余篇。内容包括：《考古札记》《回眸昔日的摄影时光》《积涓流以成江海——我的考古生涯》《再论东夷文明的诞生与发展》等。', '文集', 'K872.52-24', '本书适用于考古工作者', '精装', '2021.12', 'chi', NULL);
INSERT INTO `book` VALUES (1675, '978-7-5010-7719-9', '束带矜庄', '王仁湘著', '文物出版社', 120, '带钩(考古)', '古代带钩与带扣', NULL, NULL, NULL, NULL, '289页', '24cm', NULL, NULL, '本书分为古代带钩用途考实、良渚文化玉带钩漫说、出土玉带钩散论、带扣略论、钩扣约束录等共九章，介绍了有关古代带钩的相关知识。', NULL, 'K875.24', '本书适用于考古工作者', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1676, '978-7-213-10771-9', '浙江考古与中华文明', '陈明辉[等]著', '浙江人民出版社', 1458, '考古学', NULL, '第一辑', NULL, NULL, NULL, '12册', '29cm', NULL, NULL, '本书共12册，涉及城邑、玉器、农业、陶器，美术和瓷器，涵盖了新石器时代文化和社会的大部分方面。', '丛刊', 'K87-55', '本书适用于考古学研究人员', '精装', '2022.10', 'chi', NULL);
INSERT INTO `book` VALUES (1677, '978-7-5228-1233-5', '考古学集刊', '朱岩石主编', '社会科学文献出版社·历史学分社', 168, '考古学', NULL, '第27集', NULL, NULL, NULL, '218页，20页图版', '26cm', NULL, '中国历史研究院学术性集刊资助 中国社会科学院考古研究所主办', '本书分为“调查与发掘”“研究与探索”“考古学家与考古学史”三部分，收录了《太原市金胜村东周铜器墓1989-1993年发掘简报》《河南鹤壁市后营汉代墓地发掘简报》《两汉帝陵出土云纹瓦当的比较研究》《夏鼐与中国历史博物馆》等文章。', '丛刊', 'K87-55', '本书适用于考古学研究人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1678, '978-7-5010-7837-0', '枫林瓷印', '湖南省文物考古研究院，株洲博物馆，醴陵窑管理所编著', '文物出版社', 380, '民窑', '醴陵窑唐家坳窑址出土瓷器精粹', NULL, NULL, NULL, NULL, '256页', '29cm', NULL, NULL, '本书精选了唐家坳窑址出土的瓷器和窑具标本约300件进行集中展示，并对窑址整体发掘情况略作介绍，为陶瓷考古研究提供了新资料。', NULL, 'K876.3', '本书适用于考古工作者', '精装', '2022.10', 'chi', NULL);
INSERT INTO `book` VALUES (1679, '978-7-5184-4104-4', '中国紫砂名壶', '岩泉著', '中国轻工业出版社', 208, '紫砂陶', '公元一三六八年至公元一九四八年', NULL, NULL, NULL, NULL, '208页', '29cm', NULL, NULL, '本书中的200只名紫砂壶，截至民国三十七年(1948年)，其中，明代33只，清代138只，民国29只，展现作品的雅致和时代印记，以及独有的格调定位，在紫砂陶艺史上具有一定的意义。', '图集', 'K876.32', '本书适用于紫砂陶爱好者', '精装', '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1680, '978-7-5134-1491-3', '一目了然古陶瓷', '贺原著', '故宫出版社', 66, '古代陶瓷', '给大家的中国古代陶瓷思维导图', NULL, NULL, NULL, NULL, '165页', '26cm', NULL, '故宫出版', '本书共五篇，分别从产生与发展、器型与分类、装饰工艺、经典纹饰和欣赏方面入手，以思维导图的模式，构建中国古代瓷器各种釉色、各器类、各种纹样等的发展、吸纳与融合的发展线条，注重器物与用途、生产工艺的合一，让读者既把握历史发展脉络，又不忘每一件和每一类器物、釉色在所处历史时期断面上的位置。', NULL, 'K876.34', '本书适用于古代陶瓷研究人员', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1681, '978-7-5010-7854-7', '洗磁录', '阎焰著', '文物出版社', 180, '古代陶瓷', '古代陶瓷论集', NULL, NULL, '望野博物馆文物研究辑丛', NULL, '200页', '26cm', NULL, NULL, '本书集选作者近20年在国内外辑刊、杂志所发表有关古代陶瓷研究的专题论稿计14篇，内容包括城市和瓷窑遗址调查、博物馆藏品比较研究、相关文献资料解读、中外文化交流等。', '文集', 'K876.34-54', '本书适用于考古工作者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1682, '978-7-5010-7776-2', '吉金藏礼', '蔡友振，王云鹏，郭俊峰著', '文物出版社', 410, '青铜器(考古)', '山东济南刘家庄商代青铜器保护修复与研究', NULL, NULL, '山东文物保护修复与研究系列丛书', NULL, '302页', '29cm', NULL, '国家重点文物保护专项补助资金资助 “考古中国”项目海岱地区夏商西周考古研究（2021-2025）阶段性成果', '本书是对济南刘家庄遗址出土青铜器保护修复项目的系统梳理与总结。全书分为九个章节，主要介绍了济南刘家庄青铜器考古发掘概况、文物病害及成因、本体保护修复、铸造工艺研究、矿料来源探究以及对文物保护成果的展望，从多方面展现了济南刘家庄青铜器科技分析内容，为商晚期青铜器生产来源与流通关系提供了新的研究资料，为研究海岱地区与殷墟之间的关系提供了新的视角。', NULL, 'K876.414', '本书适用于考古工作者', '精装', '2022.09', 'chi', NULL);
INSERT INTO `book` VALUES (1683, '978-7-5010-7820-2', '沈阳故宫清代纺织品保护与修复', '孟繁涛主编', '文物出版社', 180, '故宫', NULL, NULL, NULL, NULL, NULL, '145页', '26cm', NULL, '国家文物保护专项资金补助项目', '本书是沈阳故宫博物院藏11件纺织品保护与修复的成果。本书中分专论详细介绍各件藏品的保护、修复的步骤与保护、修复的过程，记录修复前后细节，对纺织品修复的从业人员都有一定的参考价值。', NULL, 'K876.9', '本书适用于纺织品修复的从业人员', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1684, '978-7-5228-0720-1', '甲骨文祭祀动词句型研究', '贾燕子著', '社会科学文献出版社', 168, '甲骨文', NULL, NULL, NULL, '出土文献与中国文学研究丛书', NULL, '383页', '24cm', NULL, NULL, '本书选取殷墟甲骨文中的107个祭祀动词，按照每个祭祀动词所在句法结构的不同进行分类，描述其类别，呈现句型的层级性；依照五期分期标准对各句型进行断代，讨论了祭祀动词句结构分布与甲骨文分期的关系；通过大量的数据统计，分析了祭祀动词句的句型结构、句法成分、句型变换手段、施事主语出现与否等使用频率的差异；从“语义价”和“句法向”两个层面，探讨了祭祀动词的句法语义关系；指出祭祀动词已有分类方法存在的不足，根据句法结构和宾语语义类型的不同对祭祀动词重新进行了多重分类。本研究可为甲骨文句法研究和古汉语句型研究提供参考。', NULL, 'K877.14', '本书适用于甲骨文句法研究和古汉语句型研究人员', '精装', '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1685, '978-7-5010-6620-9', '镌刻的峡', '李宏松编', '文物出版社', 800, '石刻', '三峡石刻研究与保护', NULL, '图版卷', NULL, NULL, '2册(473页)', '29cm', NULL, NULL, '本书以广义概念“石刻”即“石质文物”为研究对象，从山、水、人三篇九个章节进行了系统阐述。', NULL, 'K877.404', '本书适用于石刻研究者', '精装', '2021.12', 'chi', NULL);
INSERT INTO `book` VALUES (1686, '978-7-5228-0332-6', '隋唐五代僧人塔铭研究', '李谷乔著', '社会科学文献出版社·城市和绿色发展分社', 88, '僧侣', NULL, NULL, NULL, NULL, NULL, '272页', '24cm', NULL, '国家社科基金后期资助项目', '本书以塔铭为研究对象，不仅整理、辑录了隋唐五代僧人塔铭文献400余篇，还对其中留有书家姓名的塔铭做了梳理与统计工作。', NULL, 'K877.42', '本书适用于碑刻研究人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1687, '978-7-5010-7813-4', '宁夏海原南华山地区史前遗存考古调查报告', '杨剑主编', '文物出版社', 360, '史前文化', NULL, NULL, NULL, '宁夏回族自治区文物考古研究所丛刊', NULL, '22,322页，64页彩版', '29cm', NULL, '国家重点文物保护专项补助经费资助 河套地区聚落与社会研究项目阶段性成果', '本书主要包含概况、旧石器时代、新石器时代及结语四个部分。概况主要介绍了调查区域的地理环境、历史沿革、以往工作、本次调查概况等。旧石器部分主要介绍了本次旧石器地点调查情况及采集标本等。新石器部分主要介绍了调查发现的新石器遗址的情况，主要包括遗址和采集标本。结语主要是对发现的旧石器和新石器时代遗存的初步研究和认识。', NULL, 'K878.04', '本书适用于考古工作者', '精装', '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1688, '978-7-5228-1254-0', '价值再现', '王新文著', '社会科学文献出版社·经济与管理分社', 158, '古城遗址(考古)', '统万城国家考古遗址公园规划研究', NULL, NULL, NULL, NULL, '374页', '24cm', NULL, '国家社科基金西部项目“国家考古公园视域下统万城遗址价值及规划策略研究”成果', '本书基于“基本价值－直接应用价值－区域整合价值”的分析框架创新建构了大遗址价值评估指标体系，不仅着眼于统万城遗址本身，也对中国大遗址保护的历程，以及考古遗址公园建设，进行了回顾，对统万城的历史演变及考古发现进行了总结，同时提出了适用于生态脆弱地带建设考古遗址公园的规划策略。', NULL, 'K878.04', '本书适用于文物保护研究人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1689, '978-7-5010-7832-5', '镇江孙家村遗址发掘报告', '何汉生，周明磊主编', '文物出版社', 460, '文化遗址', NULL, NULL, NULL, NULL, NULL, '18,368页，152页彩版', '29cm', NULL, '国家重点文物保护专项补助经费资助 国家社科基金项目“江苏镇江孙家村吴国铸铜遗址发掘资料整理与研究” 国家社科基金重大项目“先秦时期中原与边疆地区冶金手工业考古资料整理与研究”的资助', '本书分为地层堆积、新石器时代遗存、周代遗迹、周代遗物、结语共六章，介绍了镇江孙家村遗址发掘的相关内容。', NULL, 'K878.05', '本书适用于考古工作者', '精装', '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1690, '978-7-5010-7766-3', '合浦大浪古城', '广西文物保护与考古研究所，北海市博物馆，合浦县申报海上丝绸之路世界文化遗产中心编著', '文物出版社', 420, '古城遗址(考古)', '2019-2021年考古发掘报告', NULL, NULL, NULL, NULL, '22,257页', '29cm', NULL, NULL, '本书为合浦大浪古城遗址2019-2020年考古发掘报告，对于重新认识合浦，推进合浦乃至岭南早期区域史研究具有重要意义。', NULL, 'K878.35', '本书适用于考古工作者', '精装', '2022.09', 'chi', NULL);
INSERT INTO `book` VALUES (1691, '978-7-5010-7783-0', '曹湾山', '浙江省文物考古研究所，温州市文物考古研究所，温州博物馆编著', '文物出版社', 560, '陵墓', NULL, NULL, NULL, NULL, NULL, '14,298页，170页彩版', '29cm', NULL, '浙江省文物考古研究所田野考古报告 第48号 国家重点文物保护专项补助经费资助', '本书分为地理环境与发现经过、遗址、第3层下文化遗存、地层出土遗物概况、第3层出土遗物、第2层出土遗物、第1层出土及采集遗物等共九章，介绍了曹湾山的考古内容。', NULL, 'K878.85', '本书适用于考古工作者', '精装', '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1692, '978-7-5010-7756-4', '古格遗址壁画保护项目竣工报告', '傅鹏[等]著', '文物出版社', 620, '古格', NULL, NULL, NULL, NULL, NULL, '343页', '29cm', NULL, NULL, '本书分为项目介绍、信息收集、价值评估、现状调查、前期研究、保护实施共六章，包括项目概况、实施方法、环境概况、文献资料收集等。', NULL, 'K879.414', '本书适用于文物保护研究者', '精装', '2022.08', 'chi', NULL);
INSERT INTO `book` VALUES (1693, '978-7-5010-7793-9', '自贡天灯会民俗考', '戴燕灵[等]著', '文物出版社', 88, '节日', NULL, NULL, NULL, NULL, NULL, '234页', '24cm', NULL, '2021文化和旅游部国家非遗保护自贡灯会项目 2021四川省文物博物馆领域科研课题 2021自贡市哲学社会科学规划课题', '本书基于作者的田野调查，搜集、整理了有关自贡天灯会丰富的文化遗存资料，结合历史文献与民间传说，将自贡天灯会与当今被誉为“天下第一灯”的自贡灯会进行了比较，首次从民俗学的角度，分析了自贡灯会“从哪里来”的问题。本书共六章，从不同角度解读自贡天灯会的起源、发展、演变和传承脉络，及其与近现代自贡地区经济、文化、社会发展的关系，并对天灯会的当代流布进行了梳理，展示了包括历史文献、碑刻拓片、遗址遗迹、文物实物、历史档案等与自贡天灯会相关的文化遗存情况。', NULL, 'K892.1', '本书适用于节日风俗研究者', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1694, '978-7-201-17577-5', '春节', '萧放著', '天津人民出版社', 64, '春节', NULL, NULL, NULL, NULL, NULL, '244页', '21cm', NULL, '节日里的中国 领读', '本书是一部传统节日知识读本。本书中，作者以一年的轮回为切入点，剖析了作为中国节俗中最重要节点的春节所蕴含的时间观念；讲述了春节在其三千多年历史中的演变历程；进而以微观的视角，再现了一个传统国人从腊月初八到正月十五的完整的过年历程；并在最后展示了春节在海外的节日现状。', NULL, 'K892.18', '本书适用于大众读者', NULL, '2023.02', 'chi', NULL);
INSERT INTO `book` VALUES (1695, '978-7-5030-4427-4', '中国地理国情', '北京市规划和自然资源委员会，北京市测绘设计研究院编', '测绘出版社', 198, '地理', NULL, NULL, '北京卷', NULL, NULL, '269页', '26cm', NULL, NULL, '本书分四篇十五章，主要介绍北京市第一次地理国情普查情况及相关研究成果应用。第一篇主要介绍北京市的基本概况，北京市第一次地理国情普查开展的概况及国内外发展现状；第二篇主要介绍北京市地理国情普查组织实施、内容与指标、技术与方法、主要成果及应用服务；第三篇重点阐述数据建库及基本统计汇总、综合统计分析、专题图及评价等内容；第四篇在新时代背景下对城市地理国情监测的新领域、新技术和新服务进行展望。', NULL, 'K92', '本书适用于政府有关部门领导、自然资源调查监测与评价行业从业人员者', '精装', '2022.06', 'chi', NULL);
INSERT INTO `book` VALUES (1696, '978-7-5619-6135-3', '100个视角看中国', '本书项目组编', '北京语言大学出版社', 98, NULL, NULL, NULL, NULL, NULL, NULL, '236页', '20cm', NULL, '外国人看中国 高校主题出版', '本书选取外国留学生和在华工作人员所写文章100篇，分为“我看中国新时代、我看中国生活、我看中国传统文化、我看中国风景、我看中国抗疫、我看中国留学生活、我看中国美食、我看汉语汉字、我看中国教育、我看中国人”10个主题，反映了外国人在中国学习、生活、工作的点点滴滴，记录了他们对于中国社会和中国人民的朴素印象，展现了中国社会在中国共产党的领导下发生的翻天覆地的变化，以及中国人积极向上的精神风貌。', NULL, 'K92-53', '本书适用于大众读者', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1697, '978-7-5508-3932-8', '浙江灵鹫山', '政协衢州市柯城区委员会编', '西泠印社出版社', 58, '山', NULL, NULL, NULL, NULL, NULL, '241页', '24cm', NULL, NULL, '本书分为浙江灵鹫山志、灵鹫山赋、灵鹫山文化考、灵鹫山自然景观、灵鹫山游记等部分，挖掘和整理灵鹫山的自然景观和人文价值。', NULL, 'K928.3', '本书适用于大众读者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1698, '978-7-5508-3860-4', '运河明珠', '政协杭州市拱墅区委员会编', '西泠印社出版社', 388, NULL, '南望是拱墅', NULL, NULL, '杭州市拱墅区政协文史丛书', NULL, '174页', '26cm', NULL, NULL, '本书作为“新拱墅政协”的第一份“三亲”材料，呈现了新时代“新拱墅”的活力，展示了运河儿女当下的幸福生活场景，旨在保护、传承、利用中华优秀文化，向世界人民推荐大运河南端的文化宝藏，让更多人了解拱墅、走进拱墅、爱上拱墅，向世界讲好中国大运河故事，让运河南端拱墅这颗古老而又正焕发年轻光彩的明珠成为“最中国”“最杭州”的窗口。', NULL, 'K928.42', '本书适用于大众读者', NULL, '2022.09', 'chi', NULL);
INSERT INTO `book` VALUES (1699, '978-7-5228-0733-1', '故乡记忆', '贾滕著', '社会科学文献出版社', 98, '村落文化', '晚清以来中西部村落文化变迁', NULL, NULL, NULL, NULL, '284页', '24cm', NULL, '2014年度国家社科基金项目“近代以来传统农区村落文化共同体研究” 河南省第九批重点学科“思想政治教育”学科经费资助', '本书以“村落中‘国家’在场的历时变化”“村落中随时代而变化的‘我们””以及“逸出村落秩序群体”的历时性考察，探究了中原传统农区村落文化主体的变化、国家对村落文化主体的影响乃至形塑，总结晚清以来传统农区村落文化共同体三个历史时期(晚清至1949年、1949-1976年、1976年以来)演变特点。', NULL, 'K928.5', '本书适用于村落文化研究人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1700, '978-7-200-17381-9', '小平原 大城市', '侯仁之著', '北京出版社', 48, '历史地理', NULL, NULL, NULL, NULL, NULL, '152页', '18cm', NULL, '大家小书', '本书是一部从历史地理学角度研究北京城的著作，讲述了北京城的兴起、平原门户、北京海淀附近的地形、水道和聚落、从金中都到元大都、大都城的宫城和主要宫殿、大都城的城墙建筑、大都城的河湖水系、元大都旧城南北城墙的迁建、北京城主要的排水渠道和街道沟渠、从北京到华盛顿——城市设计主题思想试探等，囊括了侯仁之先生关于北京城市历史地理研究的成果。', NULL, 'K928.6', '本书适用于历史地理研究者', '精装', '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1701, '978-7-5010-7861-5', '丝从东方来', '张成渝，张乃翥著', '文物出版社', 380, NULL, '隋唐洛阳城东运河两岸的胡人部落与丝绸之路的东方起点', NULL, NULL, NULL, NULL, '397页', '26cm', NULL, '国家自然科学基金项目资助', '本书回溯北魏“四里”“四坊”沿伊洛河岸边而设置的地望选择取向，从唐都洛阳定鼎门遗址大规模骆驼蹄印痕迹的考古发现，管窥交通史视域下展现出的李唐王朝对丝绸贸易市场管理职能及行政责任职能的重视。', NULL, 'K928.6', '本书适用于考古工作者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1702, '978-7-5228-0789-8', '丝绸之路研究集刊', '陕西师范大学历史文化学院，陕西历史博物馆，陕西师范大学人文社会科学高等研究院编', '社会科学文献出版社·历史学分社', 198, NULL, NULL, '第八辑', NULL, NULL, NULL, '502页', '29cm', NULL, '陕西师范大学历史文化学院 陕西历史博物馆 陕西师范大学人文社会科学高等研究院主办 J集刊', '本书收录32篇论文，包括《公元1世纪前后的中亚高附国史初探》《三国及其以前焦侥民迁徙考》《内蒙古盛乐古城的考古发现与研究进展》《中古时期粟特人对丝路贸易的掌控》等。', NULL, 'K928.6-55', '本书适用于丝绸之路研究人员', '精装', '2022.06', 'chi', NULL);
INSERT INTO `book` VALUES (1703, '978-7-5010-7232-3', '古月桥保护修缮工程报告', '黄美燕，张荣主编', '文物出版社', 680, '古建筑', NULL, NULL, NULL, NULL, NULL, '268页', '29cm', NULL, NULL, '本书内容涉及古代桥梁史的回顾、保护理念的探索、价值评估、勘察研究、方案设计、保护材料研究等。', NULL, 'K928.78', '本书适用于古建筑研究者', '精装', '2022.09', 'chi', NULL);
INSERT INTO `book` VALUES (1704, '978-7-5228-0685-3', '加纳', '赵蜀蓉主编', '社会科学文献出版社·国别区域分社', 89, NULL, NULL, NULL, NULL, '列国志', NULL, '253页', '23cm', NULL, '“十四五”国家重点图书出版规划项目', '本书介绍了加纳的历史、政治、经济、文化、社会与外交等，对于国人了解海地的历史与现实具有重要意义。', NULL, 'K944.5', '本书适用于大众读者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1705, '978-7-5228-0645-7', '危地马拉', '谷俊娟编著', '社会科学文献出版社·国别区域分社', 98, NULL, NULL, NULL, NULL, '列国志', NULL, '312页', '23cm', NULL, '“十四五”国家重点图书出版规划项目', '本书介绍了危地马拉的历史、政治、经济、文化、社会与外交等，对于国人了解危地马拉的历史与现实具有重要意义。', NULL, 'K974.1', '本书适用于大众读者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1706, '978-7-5638-3377-1', '现代自然科学技术概论', '徐丕玉主编', '首都经济贸易大学出版社', 45, '自然科学', NULL, NULL, NULL, NULL, '9版', '335页', '24cm', NULL, '高等院校素质教育系列教材 北京高等教育精品教材', '本书分析了现代科学技术发展的基本特点；阐述了科学技术对社会经济和生产力发展的重要作用；介绍了现代自然科学的几个基本问题、当代高新技术及人类社会共同面临的环境和可持续发展问题。', NULL, 'N1', '本书适用于高等学校文科类学生', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1707, '978-7-121-44353-4', '走近前沿', '王握文著', '电子工业出版社', 98, '科学技术', NULL, NULL, NULL, NULL, NULL, '10,350页', '24cm', NULL, '科普中国 国防电子信息基地作品', '本书通过采访近百位各领域科技专家，以讲述、答问等形式，讲述科技前沿知识、解读当今科技热点、介绍最新科技成果、回答大众关心的科技问题。', '普及读物', 'N49', '本书适用于大众读者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1708, '978-7-5198-6306-7', '系统工程原理与应用', '施应玲主编', '中国电力出版社', 42, '系统工程', NULL, NULL, NULL, NULL, NULL, '178页', '26cm', NULL, '“十四五”普通高等教育规划教材 产教融合系列教材', '本书共分为8章，第1章介绍了系统思想与系统理论的产生与发展，系统、系统结构与系统功能的基本概念与特点等。第2章介绍了系统工程的古代与现代实践、系统工程的主要方法和实践应用。第3章介绍了系统工程在实施中的重要环节——系统分析的主要内容与主要原则，以及系统建模的基本内容。第4章介绍了系统工程常用的运筹学模型与算法。第5章和第6章分别介绍了在系统工程实施中对系统进行评价与决策分析的原则与方法。第7章介绍了基于系统要素间的联系，对复杂系统的结构进行分解分析的方法。第8章介绍了系统仿真的基本概念和主要步骤，探讨了系', '教材', 'N945', '本书适用于高等学校管理类和工业工程类本科及硕士研究生', NULL, '2022.06', 'chi', NULL);
INSERT INTO `book` VALUES (1709, '978-7-111-71521-4', '数学实验', '王洁编', '机械工业出版社', 49.8, '高等数学', NULL, NULL, NULL, NULL, NULL, '225页', '26cm', NULL, '浙江省普通高校“十三五”新形态教材', '本书结合线性代数、微积分、概率论与数理统计、常微分方程、优化方法、插值与拟合等知识，利用MATLAB软件做数学实验，从而帮助学生掌握MATLAB软件操作方法，深入了解数学理论和方法，激发学生学习数学的兴趣，培养学生应用数学知识和MATLAB软件解决实际问题的意识和能力。全书共分14章：第1-5章是软件篇，介绍MATLAB基本操作、数值运算、符号运算、图形功能和程序设计；第6-11章是应用篇，利用MATLAB软件做线性代数、微积分、概率论与数理统计、常微分方程、最优化方法、插值与拟合等实验，解决相应的数学问', '教材', 'O13-33', '本书适用于高等学校数学专业学生及一般工程技术人员、经济管理人员和MATLAB、Octave软件的爱好者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1710, '978-7-5223-1368-9', '线性代数', '熊波主编', '中国财政经济出版社', 46, '线性代数', NULL, NULL, NULL, NULL, NULL, '183页', '26cm', NULL, '财政部规划教材 全国高等院校财经类教材', '本书包含6章。第1章介绍线性方程组和矩阵的基本概念，并利用高斯消元法研究线性方程组的求解问题。第2章主要是行列式的定义、性质和计算方法。第3章对矩阵的相关运算进行全面介绍，包括矩阵的线性运算、乘法、可逆性、初等变换和秩等内容。第4章主要介绍向量的线性相关性和极大无关组理论，并将其用于分析线性方程组和矩阵问题。第5章对矩阵的特征值进行了分析，给出了特征值和特征向量的计算方法以及矩阵可对角化的条件。第6章主要研究二次型的标准形和正定性。', '教材', 'O151.2', '本书适用于普通本科经管类相关专业学生', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1711, '978-7-5121-4789-8', '稳健矩阵回归优化方法', '陈丙振，孔令臣编著', '北京交通大学出版社', 59, '矩阵', NULL, NULL, NULL, NULL, NULL, '165页', '24cm', NULL, NULL, '本书介绍了弹性网正则化小二乘矩阵回归、核范数正则化Huber矩阵回归、低秩弹性网正则化Huber矩阵回归和行弹性网正则化Huber矩阵回归等模型。对于这些模型，书中研究了其统计性质、快速优化算法及算法的收敛性，并进行了大量的模拟数据实验和真实数据实验，在实际问题中，可根据这些模型的统计特征进行相应数据集的分析。', NULL, 'O151.21', '本书适用于统计学研究生、稳健矩阵回归方向科研人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1712, '978-7-5615-8807-9', '复变函数论', '陈省江，邱凎俤主编', '厦门大学出版社', 36, '复变函数', NULL, NULL, NULL, NULL, NULL, '107页', '24cm', NULL, NULL, '本书共分8章，第1章介绍解析函数所需的预备知识，即复数与复变函数；第2章介绍解析函数的概念和特征，包括解析与可微的关系、解析的必要条件与充分条件；第3章介绍解析函数的积分学(复积分)，突出柯西积分定理和柯西积分公式这两个在复变函数论中占有极为重要地位的相关结果；第4章介绍解析函数的幂级数理论，突显解析函数的特有性质；第5章介绍解析函数的洛朗展式，包括解析函数的孤立奇点的相关性质；第6章介绍解析函数的留数理论，包括留数定理在积分计算中的应用以及辐角原理与儒歇定理；第7章介绍解析函数的映射性质，包括分式线性变', '教材', 'O174.5', '本书适用于广大高校师生', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1713, '978-7-115-60544-3', '时间序列分析实战', '(美)艾琳·尼尔森著', '人民邮电出版社', 139.8, '时间序列分析', '基于机器学习和统计学', NULL, NULL, NULL, NULL, '16,358页', '24cm', NULL, 'O\'REILLY 图灵教育', '本书共有17章，首先概览时间序列分析的历史，然后介绍数据的获取、清洗、模拟和存储，接着关注可用于时间序列分析的建模技术，最后探讨时间序列分析在几个常见领域中的应用。', NULL, 'O211.61', '本书适用于与时间序列打交道的数据分析师、数据工程师、数据科学家及其他相关从业人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1714, '978-7-5230-0044-1', '复杂数据的空间自回归模型研究及其应用', '黄婷婷著', '中国统计出版社有限公司', 42, '自回归模型', NULL, NULL, NULL, '统计前沿系列丛书', NULL, '102页', '24cm', NULL, '北京市自然科学基金青年项目的资助', '本书的主要组成部分如下：首先，分别回顾了函数型数据和成分数据的基本定义、数据空间、常用方法等。其次，对空间数据做了简要说明。之后，提出了具有空间相关性的函数型数据线性回归模型。最后，将成分数据、函数型数据、数值型数据结合在一起，提出了混合三种数据类型的空间自回归模型。', NULL, 'O212.1', '本书适用于自回归模型研究者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1715, '978-7-5730-0845-9', '直到时间的尽头', '(美)布莱恩·格林(Brian Greene)著', '海南出版社', 79, '物理学', '追寻宇宙、生命和意识的最终意义', NULL, NULL, NULL, NULL, '513页', '21cm', NULL, '理想国自然科普', '本书带读者从宇宙的诞生开始，一路见证宇宙中的结构体(包括恒星、星系等)、生命、意识都可能是怎样产生的，而意识特别是人类思维又为宇宙和自身带来了怎样瑰丽的产物。直到去设想、去面对那宏大又暗淡但终将到来的“时间的尽头”“万物的黄昏”时，我们依然可以从思维这种约等于不可能的活动形态中，得到怎样的收获、启示、意义和慰藉。', NULL, 'O4', '本书适用于物理学学习者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1716, '978-7-121-44717-4', '量子力学入门', '(美)M. Suhail Zubairy著', '电子工业出版社', 79, '量子力学', '量子力学在量子通信与量子计算中的应用', NULL, NULL, NULL, NULL, '12,300页', '26cm', NULL, NULL, '本书分为五部分：第一部分介绍一些基本的数学工具，如复数、矢量分析、概率论，以及粒子和波的经典描述；第二部分介绍量子力学的基本概念，如波粒二象性、互补性、海森堡不确定性关系、量子干涉和纠缠、不可克隆定理，以及量子力学的基础问题，如延迟选择量子擦除、薛定谔的猫、EPR悖论和贝尔定理；第三部分介绍量子力学在量子通信安全、量子隐形态、反事实通信中的应用；第四部分介绍量子力学在量子计算中的应用；第五部分介绍薛定谔方程与牛顿动力学的关系及其在势箱内的粒子和氢原子应用。', '教材', 'O413.1', '本书适用于本科阶段量子力学或量子信息学课程的学生', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1717, '978-7-115-59787-8', '量子矩阵', '(以)格申·库里茨基(Gershon Kurizki)，(以)戈伦·戈登(Goren Gordon)著', '人民邮电出版社', 69.9, '量子论', '奇异的量子世界之旅', NULL, NULL, '科学新经典文丛', NULL, '325页', '21cm', NULL, NULL, '本书作者塑造了一位名叫亨利·巴尔的量子超级英雄，以连环画的形式描写了他的一系列冒险经历，然后详细阐述了每一段故事背后的关键概念，进而介绍了与这些概念有关的背景知识，包括历史上的观点、前沿的发展状况以及未来的发展趋势；最后，从更深层的意义上讨论了这些概念所具有的哲学和文化意蕴。', '普及读物', 'O413-49', '本书适用于量子物理学感兴趣的读者', NULL, '2023.02', 'chi', NULL);
INSERT INTO `book` VALUES (1718, '978-7-109-29357-1', '中国生态系统定位观测与研究数据集', '陈宜瑜总主编', '中国农业出版社', 98, '生态系', '2005-2015', NULL, '草地与荒漠生态系统卷', NULL, NULL, '366页', '29cm', NULL, NULL, '本书对奈曼站的地理位置、学科定位、生态要素监测体系、主要研究内容，长期观测与生态系统过程研究的野外观测场及实验设施做了介绍。整理编撰了2005-2015年奈曼站代表性生态系统的水、土、气、生要素长期监测数据。', NULL, 'Q147', '本书适用于林业科研院所管理、技术人员', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1719, '978-7-121-44030-4', '肠道微生物与生命健康', '刘洪涛主编', '电子工业出版社', 198, '肠道微生物', NULL, NULL, NULL, NULL, NULL, '10,546页', '26cm', NULL, NULL, '本书从肠道微生物群的角度，探讨疾病发生中的菌群调控机制及潜在的干预方法，总结了国内外最新科学研究成果，并阐释了肠道微生物群与宿主的交互作用机制。', NULL, 'Q939', '本书适用于肠道微生物群相关专业的科研人员', NULL, '2022.08', 'chi', NULL);
INSERT INTO `book` VALUES (1720, '978-7-5013-7429-8', '南方草木状新编', '(晋)嵇含著', '国家图书馆出版社', 68, '植物志', NULL, NULL, NULL, NULL, NULL, '165页', '24cm', NULL, NULL, '本书以晋代嵇含所著《南方草木状》为底本，加上译文和注释，并对每种植物的药用功效和使用方法等略作介绍，每种植物配以水彩绘图。', NULL, 'Q948.52', '本书适用于植物爱好者', NULL, '2022.08', 'chi', NULL);
INSERT INTO `book` VALUES (1721, '978-7-5210-0950-7', '中国海草植物图鉴', '于硕[等]编著', '海洋出版社', 98, '海草', NULL, NULL, NULL, NULL, NULL, '79页', '21cm', NULL, NULL, '本书共6章，包括海草植物类群；海草分布及物种多样性；鳗草科；丝粉草科等，对我国海草的物种及其分布、系统发育、形态分类特征等进行了重新梳理与介绍说明。', '图集', 'Q949.208-64', '本书适用于植物研究者', NULL, '2022.05', 'chi', NULL);
INSERT INTO `book` VALUES (1722, '978-7-5210-1001-5', '绿球藻多糖的提取优化及其在食品保鲜技术中的开发与应用', '刘旭东著', '海洋出版社', 78, '绿球藻目', NULL, NULL, NULL, '藻类资源开发与利用丛书', NULL, '149页', '24cm', NULL, '山西大学120周年学术专著', '本书围绕绿球藻多糖在食品保鲜技术中的开发应用，首先通过单因素实验和响应面法对绿球藻多糖的提取工艺进行了优化，在测定分离纯化后多糖的纯度和结构信息分析基础上，进一步研究了其抗氧化性能和对常见食品致病菌的抑菌活性，并最终成功制备了壳聚糖/绿球藻多糖复合膜。通过对复合膜各项性能进行测定，分析了将其应用于复合膜的食品工艺制备前景，为壳聚糖/绿球藻多糖复合膜的进一步开发提供了科学依据，也为绿球藻的应用开辟了新的路径。', NULL, 'Q949.21', '本书适用于藻类研究者', NULL, '2022.09', 'chi', NULL);
INSERT INTO `book` VALUES (1723, '978-7-111-71932-8', '常见花草野外识别图鉴', '(日)山田隆彦著', '机械工业出版社', 88, '植物', NULL, NULL, NULL, NULL, NULL, '349页', '21cm', NULL, NULL, '本书选取了300余种常见的野外花草植物，按照开花季节进行分类，通过叶图、果图、花图和文字介绍了该植物茎、叶和花的主要特征和生存状态，以及相关的一些小知识等。', '图谱', 'Q949-64', '本书适用于植物爱好者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1724, '978-7-5074-3550-4', '动物园动物的学习与训练', '(英)维基·A. 梅尔菲(Vicky A. Melfi)，(美)妮科尔·R. 多雷(Nicole R. Dorey)，(英)萨曼莎·J. 沃德(Samantha J. Ward)编', '中国城市出版社', 99, '动物', NULL, NULL, NULL, NULL, NULL, '345页', '26cm', NULL, 'WILEY', '本书结合了来自科学界的最新信息和当前的最佳实践，揭示了训练动物园动物的复杂性。全书由动物学习理论概述开篇。书中对动物学习类型的主要范畴进行了描述；考虑到动物园动物自然史的多样性；回顾了那些证明学习的终极益处的研究；同时强调了各种训练方法的优缺点。阐述了如何将学习理论的直接应用融入动物园动物的管理中；探讨了其他因素可能对训练进展造成的影响；并从动物学习中研究情境和活动。本书还探索了决定丰容能否成功的理论基础。', NULL, 'Q95', '本书适用于动物园生物学、兽医科学和心理学专业的高年级本科生以及动物管理、行为学、动物保护，以及动物园生物学专业的研究生', '精装', '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1725, '978-7-121-44433-3', '动态人体结构', '(美)罗伯特·奥斯提著', '电子工业出版社', 198, '人体结构', '艺用人体解剖、姿势与动态解析', NULL, NULL, NULL, NULL, '347页', '25cm', NULL, '费城艺术大学 宾夕法尼亚州美术学院 纽约艺术学院 艺用解剖学及人体素描教学讲义 有艺', '本书讨论了古风时期、古典时期、希腊化时期的希腊艺术作品以及文艺复兴时期艺术作品的人体比例、解剖知识、文化价值观以及美学发展；从运动、动态和比例和谐的角度研究解剖学和人体结构；探讨了手部和头部的结构和表现特征；探究审视与观察人体形态的一种具体方法等。', NULL, 'Q983', '本书适用于艺术专业学生及绘画爱好者', '精装', '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1726, '978-7-5596-6512-6', '大脑简识', '(意)马克·马格里尼(Marco Magrini)著', '北京联合出版公司', 78, '脑科学', NULL, NULL, NULL, NULL, NULL, '244页', '23cm', NULL, '低音', '本书探讨目前人类所知道的人脑可以实际做到的事情，主体共12个部分，内容涵盖脑地形图；主要特征；操作性能；控制面板；常见问题；生命终止(EOL)；扩展；未来版本等。', '普及读物', 'Q983-49', '本书适用于普通大众，尤其是脑科学爱好者', '精装', '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1727, '978-7-5217-4950-2', '救命饮食', '(美)T. 柯林·坎贝尔(T. Colin Campbell)，(美)托马斯·M. 坎贝尔 Ⅱ(Thomas M. Campbell Ⅱ)著', '中信出版集团股份有限公司', 75, '合理营养', '中国健康调查报告', NULL, NULL, NULL, '2版', '31,456页', '23cm', NULL, '漫游者', '本书共分为四部分，内容包括：中国健康调查及营养研究、有钱人的富贵病、最佳营养指南、为什么你以前没听过。', NULL, 'R151.4', '本书适用于健康调查研究人员', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1728, '978-7-5214-3595-5', '药学综合知识与技能', '王建新主编', '中国医药科技出版社', 41, '药物学', NULL, NULL, NULL, NULL, NULL, '2册(162;60页)', '26cm', NULL, '2023国家执业药师职业资格考试考点精练与冲刺卷 医药科技考试 2023年国家执业药师职业资格考试系列图书 执业药师考试类图书 2023执业药师考试丛书及复习建议 题辅结合类 国家执业药师职业资格考试大纲', '本书分考点精练册、冲刺卷与答案精析册两册，可以帮助考生在较短时间内迅速掌握重点内容，并且通过试卷进行考前模拟实战练习，检验自己的学习成果，及时查漏补缺，提高复习效果。', '自学参考资料', 'R9-44', '本书适用于参加国家执业药师职业资格考试的考生', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1729, '978-7-5214-3484-2', '中国新药注册与审评技术双年鉴', '韩培主编', '中国医药科技出版社', 158, '新药', NULL, '2022年版', NULL, NULL, NULL, '440页', '29cm', NULL, NULL, '本书记录了近两年我国持续深化药品审评审批制度改革的过程，刊登了新冠肺炎疫情下药物和疫苗研发的成果，还增加了新药上市后再评价的内容。全书分四章，第一章为药品的科学监管，第二章为新药研发与注册管理，第三章为临床试验风险管理，第四章为药品上市后再评价。附录列有2020年度和2021年度国家药品监督管理局药品审评中心发布的药品审评年度报告。', '年鉴', 'R951-54', '本书适用于广大医务工作者', '精装', '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1730, '978-7-5214-3458-3', '2022药品监管前沿研究', '张伟主编', '中国医药科技出版社', 128, '药品管理', NULL, NULL, NULL, NULL, NULL, '416页', '24cm', NULL, NULL, '本书共5个部分，内容涉及境外药品上市许可持有人指定境内企业法人管理模式研究；论药学在细胞治疗产品开发中若干问题的思考；我国基因和细胞治疗产品监管体系的调研及完善建议；医药互联网交易市场的新监管对策等。', NULL, 'R954', '本书适用于药品监管制度研究人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1731, '978-7-5214-3692-1', '化学药品中遗传毒性杂质的评估控制', '张庆生，陈华，黄海伟主编', '中国医药科技出版社', 100, '药品', NULL, NULL, NULL, NULL, NULL, '155页', '26cm', NULL, NULL, '本书介绍了遗传毒性杂质监管发展的历程以及理念的演变、遗传毒性杂质评估的策略和遗传毒性杂质分析方法；结合药品中遗传毒性杂质全流程评估决策树，对潜在风险及来源识别、遗传毒性评估、可接受风险水平表征、检测分析方法建立和杂质控制方法等进行了阐述。', NULL, 'R961', '本书适用于药品监管机构、制药及药品安全检测等领域人员', '精装', '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1732, '978-7-5090-1699-2', '止痛毒丸', '(美)巴里·迈耶(Barry Meier)著', '当代世界出版社', 59, '药瘾', '药王家族与致命药瘾', NULL, NULL, NULL, NULL, '208页', '21cm', NULL, '活字文化 纪实+ 02', '本书由“纽约时报”调查记者、普利策国际报道奖获得者巴里·迈耶带领我们走进“药瘾”这场灾难的深处，看到藏匿其中的逐利巨头——奥施康定的生产商普渡制药公司及其幕后控制者萨克勒家族；看到包括医生、法官、媒体人和平民在内的许多人的觉醒反抗；看到隐藏于身体疾痛、压力背后的社会创痛与人性之思。', NULL, 'R969.3', '本书适用于大众读者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1733, '978-7-121-35912-5', '机器行为学', '谭浩著', '电子工业出版社', 118, '智能设计', '以人为中心的智能设计', NULL, NULL, NULL, NULL, '17,257页', '26cm', NULL, NULL, '本书提出机器行为学的设计与研究框架，以“人”作为出发点，将智能产品、智能系统及算法置于“自然－人类社会－人为事物”的三重本体系统中，面向行为科学、智能科学和设计科学，研究智能机器对人与社会的影响及其交互作用的模式，形成以人为中心的智能设计理论、方法与研究体系，为从人和社会的角度来设计人工智能提供科学知识、研究工具与设计方法。', '教材', 'TB21', '本书适用于设计、计算机、机械、电子、自动化、心理学、社会学等相关专业学生', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1734, '978-7-111-71549-8', '现代工程制图', '朱菊香，郭业才，李鹏主编', '机械工业出版社', 59.8, '工程制图', NULL, NULL, NULL, NULL, NULL, '270页', '26cm', NULL, '双色版', '本书主要内容包括：制图的基本知识、正投影法基础、基本立体的投影、立体表面交线、组合体、轴测图、图样基本表示法、标准件与常用件、零件图、装配图、焊接图、三维软件应用等。', '教材', 'TB23', '本书适用于高等院校工科专业学生', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1735, '978-7-5180-9870-5', '工业设计策略与方法', '郑枫主编', '中国纺织出版社有限公司', 58, '工业设计', NULL, NULL, NULL, NULL, NULL, '134页', '26cm', NULL, '“十四五”普通高等教育本科部委级规划教材', '本书以介绍产品开发过程中(主要是产品企划和开发过程)的设计方法为主，并阐述了设计理念、设计模型和工业设计师应具备的基本素质及能力，有利于读者加深对设计的理解并建立对设计的系统认知，为读者构建设计理念提供帮助。', '教材', 'TB47', '本书适用于高等院校工业设计、产品设计及相关专业学生以及从事产品开发的人员以及设计爱好者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1736, '978-7-5184-4060-3', '为新老龄而设计', '董玉妹著', '中国轻工业出版社', 69.8, '老年人', '设计赋能积极老龄化的理论与方法', NULL, NULL, NULL, NULL, '188页', '24cm', NULL, NULL, '本书主张将老年人视为资源，将设计视为赋能的手段，重塑设计师的新观念，重建老龄化设计的新角色和新品质。本书还通过一系列探索，产出了导向积极老龄化的设计赋能工具。', NULL, 'TB472', '本书适用于从事老龄化设计的研究者、实践者、设计专业学生', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1737, '978-7-5184-4094-8', '包装设计', '王丽梅，肖丽编著', '中国轻工业出版社', 49.8, '包装设计', NULL, NULL, NULL, NULL, NULL, '141页', '28cm', NULL, '高等院校艺术设计专业精品系列教材 “互联网+”新形态立体化教学资源特色教材 视觉传达', '本书以包装设计为核心内容，探讨包装设计的基础理论、设计形式及设计规律。从“趣味性包装设计”“国潮包装设计”“智能包装设计”“绿色包装设计”“体验包装设计”等五个专题呈现现代包装设计发展样貌，通过“走向国际的中国包装”展现当代中国包装设计取得的成绩。', NULL, 'TB482', '本书适用于视觉传达设计、产品设计、工业设计等相关专业学生', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1738, '978-7-111-71571-9', '制冷原理与装置', '何国庚，陈光明，郑贤德编', '机械工业出版社', 65, '制冷', NULL, NULL, NULL, NULL, '3版', '326页', '26cm', NULL, '普通高等教育“十一五”国家级规划教材', '本书内容以压缩式蒸气制冷机为重点，阐述蒸气制冷机的工作原理、制冷剂的热物理性质、制冷循环特性和热力计算方法，以及制冷换热器的结构特点、传热机理和设计计算方法等。', '教材', 'TB61', '本书适用于高等院校热能（能源）与动力工程和建筑环境与能源应用工程类专业学生', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1739, '978-7-111-71952-6', '耐热钢接头的蠕变损伤与评价', '王啸，孟鑫，张福豹著', '机械工业出版社', 59, '耐热钢', NULL, NULL, NULL, NULL, NULL, '148页', '24cm', NULL, '南通大学学术专著出版基金资助', '本书聚焦机组高温段管道的一种典型耐热钢接头，即T92钢接头，在其蠕变寿命范围内，开展接头的损伤演化规律研究；蠕变过程中的应力应变分析研究；接头的损伤模拟与寿命预测研究，以及接头的无损检测评价方法研究。', NULL, 'TG142.73', '本书适用于材料类相关领域科研人员、研究生和高年级本科生', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1740, '978-7-111-71260-2', '加工中心选用指南', '张书桥主编', '机械工业出版社', 78, '加工中心', NULL, NULL, NULL, '先进制造装备选用系列丛书', NULL, '12,186页', '26cm', NULL, NULL, '本书介绍了加工中心的组成部件、典型结构、加工原则、编程方法、分类及智能发展动态，分析了加工中心的设计原则、标准及优点，阐述了加工中心的选型原则、选型参数评估、安装调试方法与验收，并重点介绍了镗铣加工中心、车削加工中心及复合加工中心的选用要点及典型案例。', '指南', 'TG659-62', '本书适用于从事机械加工的工艺人员、加工中心销售/采购人员及技术人员', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1741, '978-7-115-59881-3', 'SOLIDWORKS 2022中文版机械设计自学速成', '马久河，朱齐平编著', '人民邮电出版社', 99.8, '机械设计', NULL, NULL, NULL, NULL, NULL, '351页', '26cm', NULL, '云课版 信通社区', '本书共12章，讲解了SOLIDWORKS 2022概述、草图相关技术、基于草图的特征、基于特征的特征、装配体的应用、工程图基础、连接紧固类零件、轴系零件、箱盖零件、叉架类零件等内容。', NULL, 'TH122', '本书适用于机械工程相关专业学生', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1742, '978-7-121-44432-6', '机械数字化设计与制造实例教程', '殷海丽主编', '电子工业出版社', 58, '机械设计', 'Inventor 2022', NULL, NULL, NULL, NULL, '291页', '29cm', NULL, '“1+X职业技能等级证书”新型活页教材（初级）', '本书采用实例的形式对职业技能等级的考核项目进行介绍，每个实例均包含任务导入、知识准备、任务流程、任务实施等内容，介绍了Inventor 2022软件的基本应用，同时对每章的知识点以思维导图的形式进行总结。', '教材', 'TH122', '本书适用于参加“1+X机械数字化设计与制造职业技能等级证书（初级）”认证的人员', NULL, '2022.10', 'chi', NULL);
INSERT INTO `book` VALUES (1743, '978-7-121-44699-3', '机械设计实践及应用', '薛云娜，王宝林，高立营主编', '电子工业出版社', 45, '机械设计', NULL, NULL, NULL, NULL, NULL, '227页', '26cm', NULL, '普通高等教育机械类系列教材 华信', '本书共分为3篇，第1篇包括机械设计类课程的各项验证性和设计型实验。第2篇包括课程设计环节的一系列设计题目。第3篇为机械设计应用实践，通过几个设计实践案例。', '教材', 'TH122', '本书适用于高等学校学习机械设计基础、机械原理、机械设计实践课程的学生', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1744, '978-7-5226-0645-3', '中文版Pro/ENGINEER Wildfire 5.0从入门到精通', '天工在线编著', '中国水利水电出版社', 99.8, '机械设计', '实战案例版', NULL, NULL, NULL, NULL, '487页', '26cm', NULL, 'CAD/CAM/CAE微视频讲解大系 智博尚书', '本书基于Pro/ENGINEER Wildfire 5.0软件，介绍了该软件的基本操作和零件造型、装配、钣金、工程图等模块的功能和具体的实例操作。全书共13章，讲述了Pro/ENGINEER Wildfire 5.0入门、绘制草图、基准特征、基础实体特征建立、工程实体特征建立、高级特征建立、实体特征编辑、曲面造型、曲面编辑、零件实体装配、钣金设计、钣金编辑、工程图绘制等相关知识和实例应用。', NULL, 'TH122', '本书适用于从事Pro/ENGINEER进行相关设计的读者；Pro/ENGINEER培训班、高等院校计算机辅助设计相关专业师生', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1745, '978-7-5618-7302-1', '液压传动与控制', '蔺国民编著', '天津大学出版社', 42, '液压传动', NULL, NULL, NULL, NULL, NULL, '218页', '26cm', NULL, NULL, '本书阐述了液压传动与控制的基本理论，在介绍液压元件的基础上，对液压基本回路与典型液压传动系统进行综合分析，并介绍了液压传动系统的设计方法以及液压伺服控制的相关知识。', NULL, 'TH137', '本书适用于高等院校机械、机械电子、车辆、工程机械等专业研究生和本科生，从事相关专业的工程技术人员', NULL, '2022.09', 'chi', NULL);
INSERT INTO `book` VALUES (1746, '978-7-121-44684-9', '机械制造技术基础', '杜劲主编', '电子工业出版社', 52, '机械制造工艺', NULL, NULL, NULL, NULL, NULL, '10,270页', '26cm', NULL, '普通高等教育机械类系列教材 华信', '本书主要介绍机械制造技术的相关内容，包括零件成形方法、机械制造中的加工方法、生产过程与组织、金属切削原理及刀具、金属切削机床、工件定位与夹具设计、机械零件加工质量分析与控制、机械加工工艺规程设计、先进制造技术等。', '教材', 'TH16', '本书适用于高等工科院校机械类专业及相关专业的专业学生', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1747, '978-7-121-44333-6', '面向智能制造的机器智能理论与方法', '秦威，孙衍宁著', '电子工业出版社', 148, '智能制造系统', NULL, NULL, NULL, NULL, NULL, '12,265页', '24cm', NULL, '工业智能与工业大数据系列 工信学术出版基金', '本书面向智能制造，将机器智能领域的结构模拟、功能模拟、行为模拟与机制模拟方法贯穿应用于制造全过程，阐明机器智能技术发展对制造业的深刻影响，为制造业的智能化改造升级提供参考。', NULL, 'TH166', '本书适用于高等院校智能科学与技术、机械工程、工业工程、自动化、计算机科学、数据科学等相关专业高年级本科生和研究生', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1748, '978-7-5229-0010-0', '电力应急指挥信息系统研究、设计与建设', '徐希源等著', '中国纺织出版社有限公司', 78, '电力工业', NULL, NULL, NULL, NULL, NULL, '356页', '26cm', NULL, NULL, '本书对电力应急工作的重要意义进行了分析，阐述了应急管理的基本理论和电力企业应急指挥体系的构成，提炼出近年来在电力灾害事件特别是大面积停电事件应急处置方面最新的关键技术，设计了电力应急指挥信息系统的架构方案，对应急准备、监测预警、应急处置、后期恢复各阶段业务特点及其对应的系统功能进行了说明，并对辅助系统运行的移动应用和基础支撑技术做了介绍，最后提出了系统的建设实施方案。', NULL, 'TM08', '本书适用于电力企业相关人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1749, '978-7-111-71569-6', '电机控制', '年珩主编', '机械工业出版社', 68, '电机', NULL, NULL, NULL, '中国能源革命与先进技术丛书', NULL, '206页', '26cm', NULL, '“十四五”时期国家重点出版物出版专项规划项目', '本书内容包括直流电动机的控制、鼠笼式异步电动机的控制、绕线式异步电机的控制、同步机的控制、无位置传感器控制和先进控制技术等。', '教材', 'TM301.2', '本书适用于高等学校电气工程及其自动化专业学生', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1750, '978-7-111-71508-5', '电气与PLC智能控制技术', '胡国文[等]编著', '机械工业出版社', 57, '电气控制', NULL, NULL, NULL, NULL, NULL, '10,284页', '26cm', NULL, '普通高等教育电气工程、自动化（工程应用型）系列教材', '本书共13章，主要内容为电气控制常用低压电器及智能控制电器、电气控制常用继电接触控制电路与典型控制系统分析、可编程序控制器(PLC)的基本组成及工作原理、三菱FX系列小型PLC及编程方法、三菱FX系列PLC的步进顺序控制和数据控制功能、西门子S7-200 SMART系列PLC及编程方法、西门子S7-200 SMART系列PLC的步进顺序控制和数据控制功能、西门子S7-300系列PLC及编程方法、西门子S7-1200系列PLC及编程方法、PLC的联网与通信技术、电气控制系统中的PLC自动控制及智能控制技术、', '教材', 'TM571.2', '本书适用于普通高校电气、自动化、机械等专业学生', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1751, '978-7-111-71959-5', '“双碳”目标下新型电力系统技术与实践', '周勤勇，何泽家主编', '机械工业出版社', 129, '电力系统', NULL, NULL, NULL, '中国能源革命与先进技术丛书', NULL, '284页', '24cm', NULL, '“十四五”时期国家重点出版物出版专项规划项目', '本书从碳中和背景下国内外的能源转型发展概况出发，介绍了“双碳”目标下新型电力系统的定义和内涵，梳理了新型电力系统的主要特征及其所对应的发展挑战和主要对策，阐述了新型电力系统的发展技术，总结了新型电力系统的典型案例与实践经验，并对新型电力系统未来的发展趋势和构建进行了展望。', NULL, 'TM7', '本书适用于高等院校控制工程、能源动力工程、电气工程、计算机等相关专业研究生与本科生', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1752, '978-7-5226-0717-7', '电力SDH网络规划建设实践', '戴睿，徐婧劼等编著', '中国水利水电出版社', 78, '电力系统', NULL, NULL, NULL, NULL, NULL, '142页', '26cm', NULL, NULL, '本书介绍电力SDH网络的规划建设情况，从项目全生命周期的角度介绍电力骨干SDH网络在规划建设中的主要方法，并通过方法论和规划建设实例相结合的方式，介绍电力SDH网络规划建设过程中的要点和注意事项。', NULL, 'TM73', '本书适用于电力通信从业人员', NULL, '2022.06', 'chi', NULL);
INSERT INTO `book` VALUES (1753, '978-7-111-71379-1', '电力电子技术及应用', '吴新开主编', '机械工业出版社', 65, '电力电子技术', NULL, NULL, NULL, NULL, NULL, '11,326页', '26cm', NULL, '普通高等教育电气工程、自动化（工程应用型）系列教材', '本书分为3篇，第1篇介绍不/半控型器件及相控变流器，第2篇介绍全控型器件及脉冲控制变流器，第3篇介绍电力电子技术的工程应用。', '教材', 'TM76', '本书适用于应用型本科院校自动化、电气工程及其自动化、仪器仪表、新能源、机器人工程等专业学生', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1754, '978-7-5618-7280-2', '电力电子技术', '曾凡琳[等]主编', '天津大学出版社', 45, '电力电子技术', NULL, NULL, NULL, NULL, NULL, '163页', '26cm', NULL, NULL, '本书共8章，第1章为电力电子技术概述；第2章为电力电子器件，主要介绍电力电子电路需要用到的半导体开关器件；第3-6章，分别讲解4种电能变换电路，实现交流－直流、直流－直流、直流－交流、交流－交流的电能转换；第7章为脉冲宽度调制控制技术；第8章为电力电子开关应用相关技术。', NULL, 'TM76', '本书适用于高等学校自动化专业、电气工程及其自动化等电类专业的学生', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1755, '978-7-121-44480-7', '电子产品制造工艺多场多尺度建模分析', '李辉[等]著', '电子工业出版社', 98, '电子产品', '英文版', NULL, NULL, NULL, NULL, '10,167页', '24cm', NULL, NULL, '本书共10章，汇集了两种典型电子产品的关键工艺过程，包括铜－铜引线键合工艺中微观接触过程，六种典型材料引线键合工艺性能比较，汽车压力传感器灌封工艺中芯片残余应力分析，汽车压力传感器引线键合焊点的热循环失效分析，FPCB化锡工艺分子动力学研究，FPCB曝光工艺中光场分析，FPCB蚀刻工艺中蚀刻剂喷淋特性研究，FPCB蚀刻腔中蚀刻剂浓度分布与流场特性分析，FPCB蚀刻工艺中蚀刻腔几何形貌演化过程分析，FPCB多蚀刻腔蚀刻过程分析。', '英文', 'TN05', '本书适用于电子产品系统建模人员', NULL, '2022.11', 'eng', NULL);
INSERT INTO `book` VALUES (1756, '978-7-121-44746-4', '微电子制造科学原理与工程技术', '(美)Stephen A. Campbell著', '电子工业出版社', 159, '微电子技术', NULL, NULL, NULL, NULL, '2版', '11,595页', '26cm', NULL, '国外电子与通信教材系列 华信', '本书覆盖了集成电路制造所涉及的所有基本单项工艺，包括光刻、等离子体和反应离子刻蚀、离子注入、扩散、氧化、蒸发、气相外延生长、溅射和化学气相淀积等，新增了制作纳米集成电路及其他半导体器件所需的各种基本单项工艺，还介绍了22nm的FinFET器件、氮化镓LED及薄膜太阳能电池、新型微流体器件的制造工艺流程。', '教材', 'TN4', '本书适用于高等院校微电子、集成电路专业本科生和研究生', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1757, '978-7-121-44504-0', '统计信号处理', '罗忠涛著', '电子工业出版社', 99, '脉冲噪声', '脉冲噪声与相位噪声分析及处理', NULL, NULL, NULL, NULL, '12,324页', '24cm', NULL, NULL, '本书共10章，第1章从随机过程分析和统计信号处理的角度，介绍噪声分析与检测基础；第2章针对脉冲噪声，介绍脉冲噪声模型、参数估计和非参数估计、相关程序；第3章介绍脉冲噪声非线性变换设计路线；第4章给出效能最大化的非线性变换函数设计实例；第5章进行更多问题的扩展讨论；第6章介绍相位噪声模型与莱斯相位分布；第7章分析相位噪声的数字特征计算与近似；第8章研究相位噪声下的信号检测；第9章考虑实用场景，分析相位域的弱信号检测；第10章分析相位域的局部最优检测。', NULL, 'TN911.4', '本书适用于统计信号处理领域的初学者、对脉冲噪声及色噪声处理问题感兴趣的研究者，以及对复高斯白噪声的相位域信号感兴趣的研究者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1758, '978-7-121-44354-1', '正则化对偶模型研究及在图像重构中的应用', '李旭超著', '电子工业出版社', 88, '泛函数', NULL, NULL, NULL, '大数据图像处理丛书', NULL, '206页', '24cm', NULL, NULL, '本书阐述正则化对偶模型研究及在图像重构中的应用，主要内容包括迫近算子基本原理，正则化对偶模型基本原理，正则化原始－对偶模型基本原理，以及迫近算子、正则化对偶模型、正则化原始－对偶模型在图像重构中的应用。', NULL, 'TN911.73', '本书适用于计算机科学与技术、数据科学与大数据技术、工业、医学图像处理及人工智能等专业高年级本科生、研究生', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1759, '978-7-115-58590-5', '天地一体化信息网络时间统一技术', '蔚保国等编著', '人民邮电出版社', 169.8, '通信网', NULL, NULL, NULL, '天地一体化信息网络丛书', NULL, '269页', '24cm', NULL, '国家出版基金项目 信通社区', '本书站在天地一体化信息网络系统工程角度，从天地一体化时间统一的角度进行阐述，特别是从时间基准的建立、到时间基准的维持、再到时间的广域播发和监测评估，描述网络化的时间及其传递。全书首先介绍了时间的概念以及天地一体化信息网络的定义和特征，然后介绍了天地一体化信息网络的时间基准体系框架以及时间基准的维持技术，在此基础上介绍了天地一体化信息网络多节点时间同步技术以及网络授时监测评估技术。', NULL, 'TN915', '本书适用于天地一体化信息网络、卫星导航、高精度时间传递、测控等相关专业领域的高年级本科生、研究生和工程技术人员', '精装', '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1760, '978-7-115-60034-9', '深入浅出密码学', '(美)戴维·王(David Wong)著', '人民邮电出版社', 119.8, '密码学', NULL, NULL, NULL, NULL, NULL, '314页', '24cm', NULL, '异步图书 异步社区 MANNING', '本书分为两个部分，第一部分介绍密码原语，涉及密码学基础概念、哈希函数、消息认证码、认证加密、密钥交换、非对称加密和混合加密、数字签名与零知识证明、随机性和秘密性等内容；第二部分涉及安全传输、端到端加密、用户认证、“加密货币”、硬件密码学、后量子密码、新一代密码技术等内容。', NULL, 'TN918.1', '本书适用于密码学领域的师生及信息安全从业人员；对密码学及其应用感兴趣的读者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1761, '978-7-115-59973-5', '自主管理身份', '(西)亚历克斯·普鲁克夏特(Alex Preukschat)，(美)德拉蒙德·里德(Drummond Reed)主编', '人民邮电出版社', 119.8, '电子签名技术', '分布式数字身份和可验证凭证', NULL, NULL, NULL, NULL, '330页', '26cm', NULL, '信通社区 MANNING 星火', '本书共分为4个部分，第1部分介绍了自主管理身份(SSI)的基础——它从哪里来、它是如何工作的，以及它的主要特性和优点。第2部分主要介绍SSI技术。第3部分侧重于介绍SSI的文化和哲学起源，以及它对互联网和社会的最终影响。第4部分通过行业专家探讨SSI对企业和政府的意义，介绍了SSI如何应用于特定的垂直市场。', NULL, 'TN918.912', '本书适用于对SSI技术及网络安全和隐私保护感兴趣的读者，以及希望深入了解SSI架构的主要组件和设计模式的专业技术人员、产品经理和管理者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1762, '978-7-121-44376-3', '光通信波段激光频率环的实现及测速应用', '郭善龙著', '电子工业出版社', 88, '光通信', NULL, NULL, NULL, NULL, NULL, '186页', '26cm', NULL, NULL, '本书主要介绍了光通信波段1560nm激光的非线性光学频率转换过程及应用，包括1560nm激光的非线性光学倍频过程(1560nm-780nm)、1560nm激光与倍频所得激光的非线性光学和频过程(1560nm+780nm-520nm)、所得和频激光经非线性光学参量振荡再次获得1560nm激光的过程(520nm-1560nm+780nm)及光通信波段激光在固体目标测速和面型方面的测量应用。', NULL, 'TN929.1', '本书适用于量子光学、非线性光学及相近研究方向的师生', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1763, '978-7-121-35982-8', '移动互联网信息推荐技术研究', '曹洪江[等]著', '电子工业出版社', 88, '移动通信', NULL, NULL, NULL, NULL, NULL, '172页', '24cm', NULL, NULL, '本书以推荐系统理论为基础，以探究移动互联网环境下用户如何精确获取信息这一问题为出发点，研究面向用户个性化需求的移动互联网信息推荐技术，分别从移动用户需求获取、移动情境感知推荐、社会化推荐、群组推荐等方面对移动互联网信息推荐技术展开研究和探讨。', NULL, 'TN929.5', '本书适用于从事电子商务推荐系统研究、教学的相关人员及高等院校研究生、电子商务工作者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1764, '978-7-115-59965-0', '鸿蒙入门', '张方兴著', '人民邮电出版社', 59.8, '移动终端', 'HarmonyOS应用开发', NULL, NULL, NULL, NULL, '222页', '24cm', NULL, '异步图书 异步社区', '本书的主要目的是帮助不熟悉此系统的开发者掌握HarmonyOS的诸多关键特性，从HarmonyOS基础知识、Java UI框架，到Ability开发、HarmonyOS高级特性开发；从HarmonyOS数据管理、HarmonyOS与媒体、HarmonyOS与智能设备，到面向实战的仿微信程序开发、仿淘宝程序开发等。', NULL, 'TN929.53', '本书适用于程序设计人员', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1765, '978-7-115-60529-0', 'Serverless核心技术和大规模实践', 'ByteFaaS函数计算团队著', '人民邮电出版社', 79.8, '移动终端', NULL, NULL, NULL, NULL, NULL, '198页', '24cm', NULL, '异步图书 异步社区', '本书着眼于Serverless方向，重点介绍Faas的架构和实现原理。全书从Serverless的理念和基础知识出发，介绍业内具有代表性的Serverless产品，进而引出字节跳动函数计算产品ByteFaaS，并介绍ByteFaaS的基本能力和特点，以及整体架构等；详细介绍传统FaaS架构，包括FaaS控制面、FaaS数据面、FaaS运行时、FaaS触发器、FaaS弹性伸缩等核心组件的设计与实现；阐述FaaS助推PaaS演进的思路和技术实践，并延伸到FaaS轻量级函数与云边一体；介绍Serverless在', NULL, 'TN929.53', '本书适用于Serverless领域的用户、开发者和架构师', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1766, '978-7-115-58008-5', '太赫兹无线通信', '陈智[等]编著', '人民邮电出版社', 349.8, '第六代移动通信系统', NULL, NULL, NULL, '6G丛书', NULL, '10,648页', '23cm', NULL, '工信学术出版基金 “十四五”时期国家重点出版物出版专项规划项目 信通社区', '本书介绍了太赫兹器件技术、太赫兹传播和信道建模方法、太赫兹通信物理层信号处理的关键技术，以及上层潜在无线电资源分配和优化方法；列举了待解决的问题、面临的主要挑战以及未来的研究方向；还介绍了太赫兹通信在3GPP、IEEE和ITU标准的应用，及其在物理实验测试台中的现场实验性能；通过介绍太赫兹通信的原理、关键技术及其在6G系统中的应用，反映了太赫兹无线通信技术的前沿进展，使读者对太赫兹无线通信的发展有整体认识。', NULL, 'TN929.59', '本书适用于从事太赫兹通信技术领域研究与应用的科技人员；高等院校信息与通信工程、电子科学与技术等学科师生', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1767, '978-7-111-71631-0', '数字电视原理', '卢官明，卢峻禾编著', '机械工业出版社', 79, '数字电视', NULL, NULL, NULL, NULL, '4版', '12,350页', '26cm', NULL, '“十二五”普通高等教育本科国家级规划教材 普通高等教育“十一五”国家级规划教材 “十二五”江苏省高等学校重点教材 江苏省本科优秀培育教材 高等院校通信与信息专业系列教材 机工教育 双色印刷', '本书介绍了数字电视的基础理论、系统组成和关键技术。全书共10章，主要介绍了彩色电视基础知识、数字电视概论、电视信号的数字化、信源编码原理、信源编码标准、数字电视中的码流复用及业务信息、信道编码及调制技术、数字电视传输标准、数字电视机顶盒与条件接收系统的组成及工作原理以及各种显示器和接口的工作原理、发展现状。', '教材', 'TN949.197', '本书适用于高等院校广播电视、电子信息和通信类专业的本科生；从事相关领域的工程技术人员和技术管理人员', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1768, '978-7-118-12637-2', '雷达原理与系统', '汪枫主编', '国防工业出版社', 79, '雷达系统', NULL, NULL, NULL, NULL, NULL, '10,366页', '26cm', NULL, '高等学校电子信息类专业特色精品教材', '本书以对空警戒/引导雷达为主要研究对象，阐述了雷达的基本概念、工作原理和典型雷达体制。', '教材', 'TN95', '本书适用于电子信息工程、雷达工程、电子对抗等专业本科生', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1769, '978-7-115-59556-0', '雷达波形设计与处理导论', '孙进平，白霞，王国华编著', '人民邮电出版社', 149.8, '雷达波形', NULL, NULL, NULL, NULL, NULL, '228页', '24cm', NULL, '先进信号处理系列 信通社区', '本书共分为9章，第1-4章介绍雷达波形设计与处理的基础理论，第5-9章介绍与现代雷达应用密切相关的内容，包括脉冲多普勒雷达波形、低截获概率雷达波形、最佳检测波形、最佳跟踪波形，以及MIMO雷达波形。', NULL, 'TN951', '本书适用于从事雷达系统分析、雷达信号处理工作的工程技术人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1770, '978-7-121-44266-7', '干涉仪测向原理、方法与应用', '石荣著', '电子工业出版社', 108, '干涉仪', NULL, NULL, NULL, '国防电子信息技术丛书', NULL, '280页', '26cm', NULL, NULL, '本书主要从干涉仪通道间信号的相位差测量及误差特性、干涉仪的单元天线及其模型近似、干涉仪的各种天线阵型、同频多信号同时进入干涉仪的效应四个专题方向对干涉仪测向工程应用中面临的挑战与解决措施进行了透彻的分析，最后介绍了干涉仪测向在无源定位中的应用，以及无线电波干涉测量技术在雷达、通信、测控、导航、深空探测与电子干扰等领域中的应用。', NULL, 'TN965', '本书适用于从事电子对抗系统设计、设备研制、综合应用的工程技术人员与科研人员', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1771, '978-7-115-59492-1', '群体智能与演化博弈', '张建磊编著', '人民邮电出版社', 99.8, '人工智能', NULL, NULL, NULL, NULL, NULL, '212页', '24cm', NULL, '工信学术出版基金', '本书共7章，主要内容包括基于粒子群优化算法的群体演化博弈、有限群体中任务分配博弈的动力学、带有破坏者的任务分配博弈演化动力学、基于演化博弈的多智能体覆盖控制、基于演化博弈理论的集群编队、基于深度优先策略的区域协同搜索等。', NULL, 'TP18', '本书适用于自动化、计算机科学与技术、电子信息工程、机器人工程等专业研究生和高年级本科生', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1772, '978-7-121-44423-4', '人工智能数据素养', '孙越，龚超，袁中果等编著', '电子工业出版社', 89, '人工智能', NULL, NULL, NULL, 'AI小科学家系列丛书', NULL, '16,257页', '26cm', NULL, NULL, '本书以人工智能下的大数据时代为背景，从数据素养、数据分析基础、统计分析、机器学习几个维度介绍了如何探索数据、整理数据和分析数据。', NULL, 'TP18', '本书适用于人工智能数据分析人员', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1773, '978-7-121-44715-0', '计算机博弈案例教程', '王静文[等]编著', '电子工业出版社', 55, '人工智能', NULL, NULL, NULL, NULL, NULL, '217页', '27cm', NULL, '新工科建设之路·人工智能系列教材', '本书讲述计算机博弈及其实现的过程。第1章介绍计算机博弈的一些基本情况。第2章介绍极大极小算法，并以井字棋为例实现。第3章介绍Alpha-Beta算法，并以亚马逊棋为例实现。第4章介绍期望极大极小算法，并以爱恩斯坦棋为例实现。第5章介绍UCT算法，并以海克斯棋为例实现。第6章介绍强化学习在计算机博弈游戏中的应用，并以不围棋为例实现。第7章介绍西洋跳棋的算法，重点介绍可下位置的生成算法。第8章介绍非完备信息博弈游戏的实现方法，并以军棋为例实现。', '教材', 'TP18', '本书适用于中国大学生；高等学校应用数学、计算机科学、人工智能等专业学生', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1774, '978-7-111-71782-9', '传感器与检测技术', '佟维妍[等]编著', '机械工业出版社', 69, '传感器', NULL, NULL, NULL, NULL, '2版', '316页', '26cm', NULL, '普通高等教育电气信息类系列教材 立体化·新形态教材', '本书分四篇共18章，第一篇为传感器与检测技术基础，第二篇介绍每类传感器的原理及应用，第三篇介绍四类热工参数检测仪表和气体检测传感器，第四篇介绍抗干扰技术和三种检测新技术。', '教材', 'TP212', '本书适用于高等学校电气信息类专业本、专科生学生', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1775, '978-7-111-71678-5', '集散控制与现场总线', '刘国海主编', '机械工业出版社', 69, '集散控制系统', NULL, NULL, NULL, NULL, '3版', '263页', '26cm', NULL, '“十二五”普通高等教育本科国家级规划教材 21世纪高等院校电气信息类系列教材 立体化·新形态教材 机工教育', '本书将目前控制领域中的两大技术热点——集散控制和现场总线有机结合，从集散控制系统的硬件结构、软件体系、人机接口、控制算法，以及典型现场总线等方面进行介绍。', '教材', 'TP273', '本书适用于高等院校电气信息类专业学生', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1776, '978-7-5198-5994-7', '西门子RWG控制平台进阶及实践', '韩嘉鑫主编', '中国电力出版社', 48, '自动控制系统', NULL, NULL, NULL, NULL, NULL, '174页', '26cm', NULL, NULL, '本书共7章，主要内容包括暖通空调控制基础知识、多功能通用控制RWG简介、RWG控制器硬件构成、RWG编程工具入门、逻辑功能块、应用案例及实现、RWG控制器与物联网的结合。', NULL, 'TP273', '本书适用于自动控制系统工作人员', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1777, '978-7-115-59501-0', 'Spark编程基础', '林子雨，赖永炫，陶继平编著', '人民邮电出版社', 65, '数据处理软件', 'Scala版', NULL, NULL, NULL, '2版', '278页', '26cm', NULL, '大数据创新人才培养系列 人邮体育', '本书以Scala作为开发Spark应用程序的编程语言，介绍了Spark编程的基础知识。全书共9章，内容包括大数据技术概述、Scala语言基础、Spark的设计与运行原理、Spark环境搭建和使用方法、RDD编程、Spark SQL、Spark Streaming、Structured Streaming和Spark MLlib。', NULL, 'TP274', '本书适用于高等院校计算机、软件工程、数据科学与大数据技术等专业师生及相关技术人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1778, '978-7-115-59609-3', '数据分析实战', '曾津，韩知白编著', '人民邮电出版社', 109.9, '数据处理', '方法、工具与可视化', NULL, NULL, NULL, NULL, '255页', '23cm', NULL, '异步图书 异步社区', '本书通过实战案例和可视化的图形讲解数据分析的知识。全书共9章，主要内容包括不同场景下的数据分析方法，从业者应具备的数据分析基本知识，数据分析师应具有的思维方式，海盗法则和指标体系建模，用户画像赋能数据分析，数据可视化的实操技巧等。', NULL, 'TP274', '本书适用于产品经理、运营人员、市场营销人员', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1779, '978-7-121-43297-2', '跨境数据流动', '张丽，孙菲阳主编', '电子工业出版社', 98, '数据管理', '全球治理趋势与我国规制策略', NULL, NULL, NULL, NULL, '11,213页', '24cm', NULL, NULL, '本书从数据和跨境数据流动的基本概念入手，分析了数字贸易的发展态势及其对跨境数据流动的影响，梳理了全球治理趋势、主要国家规制思路和我国跨境数据流动规制现状，尝试提出适合我国国情和发展需要的规制建议。', NULL, 'TP274', '本书适用于数据治理领域研究人员、从业人员和感兴趣人士', NULL, '2022.05', 'chi', NULL);
INSERT INTO `book` VALUES (1780, '978-7-121-44614-6', '数据故事化', '朝乐门编著', '电子工业出版社', 98, '数据管理', '从数据感知到数据认知', NULL, NULL, NULL, NULL, '10,285页', '26cm', NULL, '新时代·新文科×新工科·数字经济高质量人才培养系列（数字产业化） 华信', '本书分为三篇：基础篇主要讲解数据故事与文学故事的联系及区别，以及数据故事的定义、特征、认知和应用；理论篇主要分析数据故事化的核心理论、数据故事化的理论基础及数据故事化的方法与技术；实战篇主要讲解Tableau的数据故事化功能和基于Tableau的数据故事化实践。', '教材', 'TP274', '本书适用于高校科学与大数据技术、大数据管理与应用、计算机科学与技术、管理工程、工商管理、数据统计、数据分析、信息管理与信息系统、商业分析、数据新闻、数字人文等专业的学生', '精装', '2022.09', 'chi', NULL);
INSERT INTO `book` VALUES (1781, '978-7-115-58233-1', '全国计算机等级考试一本通', '策未来编著', '人民邮电出版社', 49.9, '电子计算机', NULL, NULL, '一级计算机基础及MS Office应用', NULL, NULL, '224页', '29cm', NULL, '无纸化考试专用 根据新版考试大纲编写 未来教育 异步社区', '本书共8章，主要内容包括考试指南、计算机基础知识、计算机系统、Word 2016的使用、Excel 2016的使用、PowerPoint 2016的使用、因特网基础与简单应用、新增无纸化真考套卷及其答案解析。', '自学参考资料', 'TP3', '本书适用于参加全国计算机等级考试考生', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1782, '978-7-121-44661-0', '算法设计技巧与分析', '(沙特)M.H. Alsuwaiyel', '电子工业出版社', 79, '电子计算机', NULL, NULL, NULL, NULL, '2版', '12,337页', '26cm', NULL, '国外计算机科学教材系列 经典教材 华信', '本书分七部分共18章，从算法设计与算法分析的基本概念和方法入手，先后介绍了递归、分治、动态规划、贪心算法、图的遍历等技术，对NP完全问题进行了讨论。', '教材', 'TP301.6', '本书适用于计算机学科及相关学科学生', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1783, '978-7-121-44671-9', '计算机视觉三维测量与建模', '李明磊著', '电子工业出版社', 69, '计算机视觉', NULL, NULL, NULL, NULL, NULL, '10,244页', '26cm', NULL, '南京航空航天大学研究生教育教学改革专项（优质教学资源建设）项目资助', '本书介绍摄像机成像的基本数学模型，分析摄像机的标定原理、影像的特征提取与匹配算法、由运动恢复结构的理论和流程，以及由立体视觉重建稠密三维点云的方法。此外，进一步延伸到图形学几何建模的相关知识，介绍三维点云的空间滤波理论和表面网格化的建模方法。', '教材', 'TP302.7', '本书适用于高等院校计算机视觉、摄影测量与遥感和电子信息工程等相关专业的研究生或高年级本科生', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1784, '978-7-115-59746-5', '数据结构', '袁凌主编', '人民邮电出版社', 69.8, 'C语言', 'C语言 微课版', NULL, NULL, NULL, NULL, '288页', '26cm', NULL, '新工科计算机专业卓越人才培养系列教材 工信学术出版基金 人邮教育', '本书共9章，主要内容包括线性表、栈与队列，串、数组与广义表，树和二叉树，图，查找，排序，大数据存储与检索等。', '教材', 'TP311.12', '本书适用于普通高等院校计算机和信息技术相关专业学生', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1785, '978-7-115-59500-3', '数据库原理与MySQL应用', '李月军编著', '人民邮电出版社', 69.8, 'SQL语言', '微课版', NULL, NULL, '软件开发人才培养系列丛书', NULL, '324页', '26cm', NULL, '人邮教育', '本书共分为3篇。上篇主要介绍数据库系统的基本原理、关系数据库基础理论及MySQL的基础操作；中篇主要介绍数据库事务管理、数据库的保护及MySQL的高级应用；下篇主要介绍关系数据库的设计与实现，并给出一个具体的数据库设计案例。', NULL, 'TP311.132.3', '本书适用于高等院校相关专业本科生', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1786, '978-7-115-59876-9', '数据库原理与应用', '赵杰，杨丽丽，陈雷编著', '人民邮电出版社', 69.8, '关系数据库系统', 'MySQL', NULL, NULL, NULL, '4版', '299页', '26cm', NULL, '新工科计算机专业卓越人才培养系列教材 工信学术出版基金 人邮教育', '本书分为4篇，共11章，主要内容包括数据库系统、关系数据库数学模型、关系数据库设计理论、数据查询、数据管理、简单数据库设计与操作、前端开发及工具、后端开发及工具、复杂数据库设计、大数据管理系统、综合案例——会展管理系统开发。', '教材', 'TP311.132.3', '本书适用于普通高等学校计算机及相关专业学生', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1787, '978-7-121-44733-4', '剑指MySQL 8.0', '尚硅谷教育编著', '电子工业出版社', 105, '关系数据库系统', '入门、精练与实战', NULL, NULL, '程序员硬核技术丛书', NULL, '10,361页', '28cm', NULL, '尚硅谷', '本书总计12章，第1-4章，从数据库的基本概念讲起，一步步带领读者搭建MySQL开发环境，分别以命令行和SQLyog图形化界面两种方式展示了数据库和表的基本操作，以及表中数据的增删改查等日常操作。第5-6章，介绍了MySQL数据类型、运算符以及各种系统函数，包括MySQL 8.0最新引入的窗口函数等。第7-12章，带领读者探究MySQL的高级查询、约束、视图、变量、存储过程和函数、视图、简单事务管理和用户权限管理等更加复杂和专业的功能。', NULL, 'TP311.132.3', '本书适用于MySQL数据库初学者、MySQL数据库开发人员和MySQL数据库管理员', NULL, '2023.02', 'chi', NULL);
INSERT INTO `book` VALUES (1788, '978-7-115-58943-9', 'Solidity智能合约开发技术与实战', '李晓黎编著', '人民邮电出版社', 86, '区块链技术', NULL, NULL, NULL, NULL, NULL, '288页', '26cm', NULL, '区块链技术开发系列 “十四五”时期国家重点出版物出版专项规划项目 工信学术出版基金 人邮教育', '本书介绍使用Solidity开发以太坊智能合约DApp的方法，内容涵盖Solidity的基本语法，使用开发框架Truffle开发、部署、测试以太坊DApp的方法，通过以太坊测试网络进行交易的方法，开发安全智能合约所应遵守的规范和对智能合约进行安全审计的方法等。', NULL, 'TP311.135.9', '本书适用于高等院校计算机、软件工程、大数据、人工智能等专业学生', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1789, '978-7-115-60041-7', 'Go语言Hyperledger区块链开发实战', '李晓黎编著', '人民邮电出版社', 89, '区块链技术', NULL, NULL, NULL, NULL, NULL, '324页', '26cm', NULL, '区块链技术开发系列 “十四五”时期国家重点出版物出版专项规划项目 微课版 人邮教育', '本书涵盖Hyperledger Fabric区块链平台的体系结构、各组件的工作原理和管理方法、Go语言的基本编程方法，以及使用Go语言开发Hyperledger Fabric智能合约和应用程序的方法。', NULL, 'TP311.135.9', '本书适用于高等院校计算机相关专业学生', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1790, '978-7-115-60198-8', 'Access 2016数据库应用基础实践教程', '韦昌法，罗铁清主编', '人民邮电出版社', 36, '关系数据库系统', '微课版', NULL, NULL, NULL, '2版', '121页', '26cm', NULL, '高等学校信息技术人才能力培养系列教材 人邮教育', '本书共两部分：内容涉及数据库概述的实验；数据库和表的实验；窗体的实验；宏的实验；VBA程序设计基础的实验等。', '教材', 'TP311.138', '本书适用于高等院校相关专业学生', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1791, '978-7-121-44652-8', '基于案例的软件构造教程', '李劲华，陈宇，周强编著', '电子工业出版社', 65, '软件开发', NULL, NULL, NULL, NULL, '2版', '15,299页', '26cm', NULL, '2020年山东省普通高等教育一流教材 新工科建设·计算机类教材 华信', '本书以一个案例的演变模拟不断变化的用户需求，按照增量迭代的开发模式，将碎片化的功能开发、用户交互、数据处理等知识，以及软件设计、软件测试及敏捷开发的最佳实践，与软件开发的原理、技术和工具融合到设计、编码、调试及测试的构造过程。', '教材', 'TP311.52', '本书适用于计算机类专业的本科学生', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1792, '978-7-115-51977-1', 'Python编程零基础入门', '(美)安娜·贝尔(Ana Bell)著', '人民邮电出版社', 119.8, '软件工具', NULL, NULL, NULL, NULL, NULL, '385页', '24cm', NULL, 'MANNING 异步图书 异步社区', '本书共有9个部分，第1部分介绍了学习编程的意义；第2部分介绍编程的基本知识；第3部分讲解了字符串、元组以及与用户交互的代码；第4部分介绍如何编写程序来进行选择；第5部分涉及循环的相关知识；第6部分引入了函数和模块化的相关概念；第7部分介绍了一些高级对象类型，如可变对象、列表、字典等；第8部分为面向对象编程的相关知识；第9部分介绍了一些可供读者使用的现成代表库。', NULL, 'TP311.561', '本书适用于Python语言的初学者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1793, '978-7-115-60380-7', 'R语言编程', '张敬信著', '人民邮电出版社', 89.8, '程序语言', '基于tidyverse', NULL, NULL, NULL, NULL, '13,302页', '26cm', NULL, '异步图书 异步社区', '本书结合新的R语言编程范式，让读者学习更高效率的R编程，尤其是真正用整洁优雅的数据化编程思维解决一系列数据问题，包括数据清洗、数据处理、数据可视化、统计建模、文档沟通等，并在附录中收录了透视表、网络爬虫、高性能计算、机器学习等典型应用。', NULL, 'TP312', '本书适用于热爱R语言编程的读者及统计学、数据分析、数据可视化等领域的读者', NULL, '2023.02', 'chi', NULL);
INSERT INTO `book` VALUES (1794, '978-7-115-59005-3', 'HTML5移动Web开发任务教程', '叶品菊主编', '人民邮电出版社', 69.8, '超文本标记语言', '慕课版', NULL, NULL, NULL, NULL, '302页', '26cm', NULL, '名校名师精品系列教材 人邮教育', '本书从移动端Web开发实际应用的角度以任务式的教学方式讲解HTML5、CSS3、移动Web前端开发框架Bootstrap等新技术。全书共分为10个单元，按移动Web开发概述、初识HTML5、CSS3基础、CSS3常用样式、CSS3高级应用、HTML5智能表单、基于HTML5的移动Web应用和响应式Web设计“神器”Bootstrap等进行分类教学；最后通过Bootstrap工具开发来整合HTML5、CSS3和移动端Web开发的知识进行快捷开发，使读者将所学知识应用到实际开发中。', '教材', 'TP312.8', '本书适用于高等院校前端与移动Web开发相关专业本、专科师生；广大网站开发人员', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1795, '978-7-5618-7409-7', '语言智能研究', '周建设主编', '天津大学出版社', 58, '人工智能语言', NULL, '第1卷', NULL, NULL, NULL, '224页', '26cm', NULL, '学术支持：国家语委中国语言智能研究中心', '本书收录了《语言智能新文科建设与发展探索》《知识图谱研究进展》《面向智能语言处理的汉语句法语义研究》《智能语音技术与产业发展》《机器自动文本生成研究进展》《诗歌自动生成技术——以清华九歌为例》等文章。', NULL, 'TP312.8', '本书适用于人工智能语言研究人员', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1796, '978-7-115-59734-2', '统信UOS应用开发详解', '统信软件技术有限公司著', '人民邮电出版社', 148, '操作系统', NULL, NULL, NULL, '中国自主基础软件技术与应用丛书', NULL, '587页', '26cm', NULL, '“十四五”时期国家重点出版物出版专项规划项目 统信软件技术有限公司', '本书是统信UOS应用开发指南，包括5篇，共16章。背景知识篇介绍操作系统简史以及国产操作系统；应用开发篇包括开发设计原则、开发环境与开发工具、Qt开发框架、DTK开发框架、DTK插件开发、服务开发、调试与性能优化；发布与部署篇介绍包格式、上架部署；桌面应用开发实战篇介绍6个难度从初级、中级到高级的经典应用案例，以及系统接口案例；常见问题篇介绍内核与驱动、开发工具相关、常用资源，帮助读者进一步提升开发水平。', '教材', 'TP316', '本书适用于统信UOS的开发人员；有志于参与统信生态构建的信创厂商、应用开发者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1797, '978-7-121-44787-7', '零基础趣学Linux', '孙亚洲著', '电子工业出版社', 118, 'Linux操作系统', NULL, NULL, NULL, '嵌入式人工智能开发丛书', NULL, '456页', '26cm', NULL, NULL, '本书从走进Linux世界、熟练使用Linux、玩转Shell编程、掌握企业主流Web架构、部署常见的企业服务五个方面展开，讲解了Linux操作系统的安装、命令、权限和软件管理，数据库，防火墙，文本处理，Shell脚本编程，Web服务器架构以及常见的企业服务等内容。', NULL, 'TP316.85', '本书适用于Linux开发工程师、行业从业人员和对编程感兴趣的读者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1798, '978-7-111-71526-9', '可编程序控制器原理与应用基础', '刘凤春[等]编著', '机械工业出版社', 53.8, '可编程序控制器', NULL, NULL, NULL, NULL, '3版', '11,284页', '26cm', NULL, '普通高等教育电气信息类系列教材 “十二五”普通高等教育本科国家级规划教材 普通高等教育计算机类系列教材', '本书分上、下两篇。上篇为理论教学篇，阐述了可编程序控制器(PLC)的基本原理和硬件配置，介绍了西门子S7-200 SMART PLC的指令系统以及编程方法。下篇为实验教学篇，介绍了西门子S7-200 SMART PLC的编程软件及其使用方法，并配置了实验内容。', '教材', 'TP332.3', '本书适用于高等院校工科各个专业的学生', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1799, '978-7-111-71735-5', '工业现场总线及应用技术', '张乐，匡程主编', '机械工业出版社', 55, '总线', NULL, NULL, NULL, NULL, NULL, '267页', '26cm', NULL, '“十三五”江苏省高等学校重点教材 普通高等教育电气工程与自动化（应用型）系列教材 移动学习型教材', '本书共11章。第1章介绍了现场总线的基本概念；第2章主要介绍了工业串行通信的基础知识；第3章介绍了现场总线PROFIBUS的协议体系和工作原理；第4章介绍了西门子博途软件的基本使用方法；第5章以PROFIBUS-DP为主，介绍了PROFIBUS的组网技术；第6章介绍了西门子公司工业以太网及PROFINET技术的原理；第7章介绍了西门子公司工业以太网的组网技术；第8章介绍了西门子公司OPC技术；第9章介绍了基于现场总线的人机界面技术；第10章介绍了现场总线应用系统的设计方法和设计过程，并给出了两个案例；第1', '教材', 'TP336', '本书适用于普通高校电气、自动化、物联网等专业学生', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1800, '978-7-115-59802-8', '微型计算机原理及应用', '赵全利主编', '人民邮电出版社', 79.8, '微型计算机', NULL, NULL, NULL, NULL, NULL, '312页', '26cm', NULL, '河南省“十四五”普通高等教育规划教材 高等学校信息技术人才能力培养系列教材 人邮教育', '本书概述了计算机基础知识及微型计算机系统组成，以8086 CPU为基础，介绍80x86微处理器的硬件结构、工作原理、指令系统、汇编语言程序设计及EMU8086仿真软件应用示例；对微型计算机存储器系统及接口、输入 输出接口、总线、中断系统及应用进行描述，并融入Proteus仿真示例调试；以常用集成可编程芯片为对象，以应用为核心，介绍了串行通信、并行通信、定时器计数器、数模/模数转换及接口电路软硬件设计，并通过Proteus电路原理设计对应用实例进行了仿真调试。', '教材', 'TP36', '本书适用于高等院校电子、通信、计算机、自动化、测控、机电及机械等相关专业师生及相关技术人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1801, '978-7-111-71885-7', '单片机原理与工程应用设计开发教程', '张青春，白秋产主编', '机械工业出版社', 48, '单片微型计算机', NULL, NULL, NULL, NULL, NULL, '229页', '26cm', NULL, '“十四五”江苏省高等学校重点教材 江苏省高等学校重点教材', '本书主要内容包括微型计算机基础、MCS-51单片机的结构及原理、MCS-51单片机的指令系统与汇编语言程序设计、C51语言程序设计、单片机应用系统的开发工具，以及MCS-51单片机的中断系统、定时/计数器、串行接口和单片机接口技术应用设计等。', '教材', 'TP368.1', '本书适用于电气工程、自动化、测控技术与仪器、电子信息、通信、物联网、应用物理、机器人等应用型本科专业师生及相关专业技术人员', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1802, '978-7-115-60237-4', '计算机图形学基础与应用', '匡平，何明耘，李凡编著', '人民邮电出版社', 59.8, '计算机图形学', '基于WebGL', NULL, NULL, NULL, NULL, '223页', '26cm', NULL, '新工科计算机专业卓越人才培养系列教材 工信学术出版基金 人邮教育', '本书首先搭建基本的计算机图形学知识体系，然后在此基础上，围绕GPU管线的处理结构分析管线各个阶段的实现算法；同时介绍了WebGL在编程方面的应用。', '教材', 'TP391.411', '本书适用于高等院校计算机、数字媒体、软件工程等相关专业学生', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1803, '978-7-121-43950-6', '计算机图形学', '(美)Donald Hearn，(美)M. Pauline Baker，(美)Warren R. Carithers著', '电子工业出版社', 139, '计算机图形学', NULL, NULL, NULL, NULL, '3版', '16,17,595页', '26cm', NULL, '国外计算机科学教材系列 经典教材 Pearson 华信', '本书分为24章，首先对计算机图形学进行了综述；然后讲解了二维图形的对象表示、算法和应用，以及三维图形的相关技术、建模和变换等；接着介绍了光照模型、颜色模型和动画技术；最后还介绍了计算机图形学中用到的基本数学概念、图形文件格式及OpenGL的相关内容。', '教材', 'TP391.411', '本书适用于信息技术等相关专业本科生和研究生', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1804, '978-7-115-58895-1', 'Python图像处理经典实例', '(印)桑迪潘·戴伊(Sandipan Dey)著', '人民邮电出版社', 119.8, '图象处理软件', NULL, NULL, NULL, NULL, NULL, '10,296页', '24cm', NULL, '异步图书 异步社区', '本书给出了60余个具体实例的解决方法，采用“菜谱式”方式组织内容，以期指导读者快速实践图像的分析和可视化处理，应对图像处理中的常见挑战，并学习如何执行复杂的任务，如对象检测、图像分割和使用大型混合数据集的图像重建，以及各种图像增强和图像恢复技术，如卡通化、梯度混合和稀疏字典学习。', NULL, 'TP391.413', '本书适用于计算机视觉工程师、图像处理工程师、软件工程师和机器学习工程师等专业人士；具有一定Python编程基础并希望进一步了解图像处理的细节和复杂性的读者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1805, '978-7-5226-0643-9', '中文版Photoshop 2022数码照片处理从入门到精通', '唯美世界，瞿颖健编著', '中国水利水电出版社', 108, '图像处理软件', '微课视频 全彩版', NULL, NULL, NULL, NULL, '374页', '26cm', NULL, '唯美 智博尚书', '本书共3部分，第1部分是Photoshop基础知识，概括介绍了Photosbop中照片文件的基本操作、图层的基本操作和编钳、辅助工具的使用等：第2部分是Photoshop核心功能，主要学习照片的细节修饰、调色、抠图与合成、模糊与锐化、滤镜的使用、照片排版、批处理、Camera Raw照片处理等：第3部分为综合实战，通过大量案例介绍Photoshop在风光照片处理、人像细节修饰、高端人像精修、静物照片处理、照片创意合成等方面的具体应用。', NULL, 'TP391.413', '本书适用于Photoshop初、中级用户，特别是摄影爱好者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1806, '978-7-5618-7337-3', 'Photoshop 2020精通与实战', '李丹，张书静编著', '天津大学出版社', 63, '图像处理软件', NULL, NULL, NULL, NULL, NULL, '258页', '29cm', NULL, '“十四五”规划精品系列教材 互联网+创新型系列', '本书包括Photoshop 2020软件操作、影楼图像处理技巧、宣传海报设计与制作、交互图标设计与制作、产品效果图设计与表现等主要内容，共分为10章。', '教材', 'TP391.413', '本书适用于工业设计、艺术设计、数字媒体设计本科专业学生', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1807, '978-7-115-59888-2', 'Photoshop人像摄影后期处理技法100问', '山武编著', '人民邮电出版社', 119.8, '图像处理软件', NULL, NULL, NULL, NULL, '2版', '252页', '26cm', NULL, '航骋文化 数艺设', '本书主要讲解人像修图师需要掌握的修图技巧。全书共15章，第1-2章讲解光影、色彩对于后期修图的重要性；第3章介绍后期处理必备的基本工具；第4-7章讲解调色工具、图层混合模式、滤镜和通道的使用方法与技巧；第8章讲解Camera Raw的使用方法和技巧；第9章讲解人物的脸形和身材的修饰方法；第10章讲解照片的调色技巧；第11章讲解简单实用的修图技巧；第12章讲解画面的合成技术；第13章讲解在日常修图工作中会遇到的后期处理问题及解决办法；第14章讲解排版设计的方法；第15章是作者在修图过程中的经验分享，并对前面', NULL, 'TP391.413-44', '本书适用于人像摄影后期处理从业人员、摄影师和对人像摄影修图感兴趣的读者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1808, '978-7-5153-6734-7', 'Maya 2022中文全彩铂金版案例教程', '桑莉君[等]主编', '中国青年出版社', 69.9, '三维动画软件', NULL, NULL, NULL, NULL, NULL, '246页', '26cm', NULL, '中青雄狮', '本书共分为12章。第1-9章为基础知识部分，分别对Maya软件的入门知识、基本操作、多边形建模技术、材质与纹理应用、摄影机与灯光应用，渲染操作，动画技术以及绑定与变形技术的应用进行了详细介绍，并在每个功能模块介绍完成时以具体案例的形式，拓展读者的实际操作能力。第10-12章实战应用部分的案例，是根据Maya软件的几大功能特点，有针对性、代表性和侧重点，并结合工作中的实际应用进行选择的。通过对这些实用性案例的学习，读者能真正达到学以致用的目的。', '教材', 'TP391.414', '本书适用于动画制作相关专业师生及从业者', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1809, '978-7-5226-0868-6', '中文版Cinema 4D R25从入门到精通', '唯美世界，曹茂鹏编著', '中国水利水电出版社', 99.8, '三维动画软件', '微课视频 全彩版', NULL, NULL, NULL, NULL, '358页', '27cm', NULL, '唯美 智博尚书', '本书基于Cinema 4D R25版本，讲述了Cinema 4D三维绘图的基本操作和建模、动画、渲染、角色、粒子、插画等核心技术，以及Cinema 4D在电商广告设计、影视动画设计、CG游戏、室内设计等诸多领域中的实际应用。', NULL, 'TP391.414', '本书适用于三维动画从业人员', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1810, '978-7-121-44801-0', 'SOLIDWORKS 2020产品设计基本功特训', '陈胜利[等]编著', '电子工业出版社', 79, '计算机辅助设计', NULL, NULL, NULL, NULL, '3版', '10,508页', '26cm', NULL, 'CAD/CAM职场技能特训视频教程 华信', '本书共19章，主要包括SOLIDWORKS 2020的认识与操作，草图绘制基本功特训，基准的创建、图形的显示与隐藏，实体设计基本功特训，机械零件设计特训，实体设计实例(计算机显示器托盘、自动控制阀顶盖、水晶笔筒)，3D曲线设计基本功特训，曲面设计基本功特训，曲线与曲面设计提高专题特训，综合设计实例(塑料衣服箱提手)，曲面设计实例(儿童汤匙)，装配体设计基本功特训，钣金设计基本功特训，工程图设计基本功特训，产品动画制作，产品渲染输出和产品工艺介绍等。', '教材', 'TP391.72', '本书适用于高等院校机械设计相关专业学生', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1811, '978-7-5153-6728-6', 'AutoCAD 2022中文全彩铂金版案例教程', '刘静[等]主编', '中国青年出版社', 69.9, 'AutoCAD软件', NULL, NULL, NULL, NULL, NULL, '198页', '26cm', NULL, '中青雄狮', '本书分为基础知识和综合案例两个部分，从实用性角度出发，帮助读者掌握AutoCAD 2022的绘图方法。', '教材', 'TP391.72', '本书适用于绘图相关专业师生及从业者', NULL, '2022.10', 'chi', NULL);
INSERT INTO `book` VALUES (1812, '978-7-115-60618-1', '可编程网络自动化', '(美)贾森·埃德尔曼，(美)斯科特·S. 洛，(美)马特·奥斯瓦尔特著', '人民邮电出版社', 139.8, '计算机网络', NULL, NULL, NULL, NULL, NULL, '20,436页', '24cm', NULL, 'O\'REILLY 图灵教育', '本书提供了可编程网络自动化的基本技能，使用了包括Linux、Python、JSON和XML在内的一系列技术。涵盖以下内容：Python编程基础、网络自动化所需的Linux基础、数据格式和数据模型，并介绍了Jinja模板及其在创建网络设备配置中的适用性、应用程序接口在网络自动化中的作用、使用Git进行源代码管理以在自动化过程中管理代码更改等。', NULL, 'TP393', '本书适用于网络工程师、系统管理员和软件工程师', NULL, '2023.01', 'chi', NULL);
