INSERT INTO `book` VALUES (1814, '978-7-115-60338-8', '华为VPN学习指南', '王达主编', '人民邮电出版社', 189.8, '虚拟网络', NULL, NULL, NULL, 'ICT认证系列丛书', '2版', '488页', '26cm', NULL, '华为技术认证', '本书共9章，分别介绍了各种IP VPN技术的基础知识和技术原理，以及IPSec VPN、L2TP VPN、GRE VPN、DSVPN、PKI、SSL VPN等技术原理和功能配置与管理方法。', '指南', 'TP393.01-62', '本书适用于ICT认证相关人员', NULL, '2023.01', 'chi', NULL);
