INSERT INTO `book` VALUES (1817, '978-7-115-60221-3', 'ESP32物联网智能硬件开发实战', '李永华编著', '人民邮电出版社', 79.8, '物联网', '视频讲解版', NULL, NULL, NULL, NULL, '298页', '26cm', NULL, '普通高等教育物联网工程类规划教材 人邮教育 工信学术出版基金', '本书以智能物联网发展为时代背景，分别从开发板基础、功能模块和运行结果等角度，通过ESP32开发板的示例应用，讲解ESP-IDF、Arduino、MicroPython等开发环境的使用方法和相关程序。', '教材', 'TP393.4', '本书适用于对ESP32编程有兴趣的爱好者，高等院校物联网工程类专业学生，从事物联网应用创新开发的专业人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1818, '978-7-121-44662-7', '物联网安全技术与实践', '(澳)武传坤，郭锋编著', '电子工业出版社', 59, '物联网', NULL, NULL, NULL, '网络空间安全系列丛书', NULL, '14,225页', '26cm', NULL, '工信学术出版基金 华信', '本书共11章。第1-3章介绍物联网概念与架构、物联网安全架构、密码学基础知识，第4-10章介绍典型物联网安全技术、智能家居物联网安全技术、智慧医疗物联网安全技术、智慧物流物联网安全技术、智慧交通物联网安全技术、智慧城市物联网安全技术、工业物联网安全技术，第11章介绍物联网安全标准及部分安全指标的测评技术。', '教材', 'TP393.48', '本书适用于高等院校物联网相关专业学生', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1819, '978-7-5030-4455-7', '合成孔径雷达（SAR）卫星遥感原始数据质量检验技术规程', NULL, '测绘出版社', 26, '卫星遥感', NULL, NULL, NULL, NULL, NULL, '18页', '30cm', NULL, '中华人民共和国测绘行业标准 CH/T 1049-2021 2021-05-10发布 2021-08-01实施', '本书规定了合成孔径雷达卫星遥感原始数据质量检验的基本要求、工作流程、抽样程序、检验内容及方法、质量评定、报告编制及资料整理等。', NULL, 'TP72', '本书适用于合成孔径雷达卫星遥感原始数据质量检测研究者', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1820, '978-7-5508-3949-6', '龙泉青瓷传统龙窑烧制技艺', '张晞著', '西泠印社出版社', 86, '龙泉窑', NULL, NULL, NULL, NULL, NULL, '178页', '24cm', NULL, NULL, '本书作者不仅介绍和记录龙泉青瓷的龙窑烧制技艺甚至秘密，也对龙泉百年龙窑的传承，包括器形、审美、传承、发展等进行研究或讨论，提出自己的观点，还对诸如近些年来的书画入瓷和点彩技艺，提出自己的观点，尤其是结合自己对于龙泉青瓷烧制过程中的一些经验进行分享。', NULL, 'TQ174.72', '本书适用于青瓷爱好者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1821, '978-7-5180-9695-4', '机织物组织与结构', '郑天勇，赵健编著', '中国纺织出版社有限公司', 58, '机织物', '双语', NULL, NULL, NULL, NULL, '323页', '26cm', NULL, '“十四五”普通高等教育本科部委级规划教材 纺织科学与工程一流学科建设教材 纺织工程一流本科专业建设教材', '本书以中英文对照的形式介绍了机织物的基本特点、织物上机图的表达、各种经典机织物组织的特点、结构与外观形成原理、组织设计方法、织物上机生产要点和机织物几何结构的基本原理。', '教材', 'TS105.1', '本书适用于非织造工程、服装工程专业在华留学生', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1822, '978-7-5229-0020-9', '中国古代纺织文化研究', '赵金龙，王燕，李斌著', '中国纺织出版社有限公司', 158, '纺织', NULL, NULL, NULL, NULL, NULL, '332页', '26cm', NULL, '湖北省档案馆委托项目 武汉纺织大学出版基金支持 湖北省服饰艺术与文化研究中心开放课题 教育部人文社会科学研究规划项目 国家发改委、文旅部国家非遗保护设施建设重大项目 文化和旅游部恭王府博物馆项目 湖北省教育厅哲学社会科学研究一般项目', '本书以中国古代文化的视角阐述纺织与其的关系，突显中国古代纺织文化的辉煌。全书分为“古代字词中的纺织篇”“古代典籍中的纺织篇”“古代图像信息中的纺织技术篇”“纺织品中的文字篇”四篇，共十五章。', '文集', 'TS1-53', '本书适用于中国古代纺织技术史研究人员', '精装', '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1823, '978-7-5184-4157-0', '食品微生物检测技术', '杜欣军主编', '中国轻工业出版社', 50, '食品微生物', NULL, NULL, NULL, NULL, NULL, '278页', '26cm', NULL, '高等学校食品质量与安全专业适用教材 中国轻工业“十四五”规划立项教材 高校教材', '本书分3部分，包括食品微生物及检验基础知识、食品微生物的标准检测方法和食品微生物检测新技术。', '教材', 'TS201.3', '本书适用于高等学校食品质量与安全、食品科学与工程等专业本科生', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1824, '978-7-5214-3142-1', '家庭食品安全与营养科普手册', '刘晓峰，赵耀，马晓晨主编', '中国医药科技出版社', 59, '食品安全', NULL, NULL, NULL, NULL, NULL, '216页', '23cm', NULL, NULL, '本书介绍了与人们日常生活关系密切的食品安全和饮食营养的相关知识，内容包括食品安全标准的解读、各种食源性疾病的预防、生活中食品安全误区的警惕、一日三餐的营养搭配。', '手册', 'TS201.6-62', '本书适用于大众读者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1825, '978-7-5184-2529-7', '食品感官评价原理与技术', '(美)哈里·T. 劳利斯(Harry T. Lawless)，(美)希尔德加德·海曼(Hildegarde Heymann)主编', '中国轻工业出版社', 120, '食品感官评价', NULL, NULL, NULL, NULL, NULL, '14,625页', '26cm', NULL, '国外优秀食品科学与工程专业教材 Springer', '本书含近20年来感官评价科学的先进方法、理论和分析，共19章，介绍了食品感官分析的基本原理、食品感官评价员的选择与考核方法、培训考核方法、常见的食品感官分析方法以及食品感官分析中统计方法的应用介绍等。', NULL, 'TS207.3', '本书适用于高等学校学习食品感官评价课程的学生，从事食品科学领域研究的科研院所和科研人员，食品行业产品研发、生产、管理和营销人员', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1826, '978-7-5184-4147-1', '食品分析', '王永华，戚穗坚主编', '中国轻工业出版社', 68, '食品分析', NULL, NULL, NULL, NULL, '4版', '11,467页', '26cm', NULL, '省级一流本科课程配套教材 普通高等教育“十五”国家级规划教材 中国轻工业“十三五”规划教材 食品科学与工程专业主干课程 高校教材', '本书共五篇，包括食品分析基础知识、食品中营养成分分析、食品添加成分及食品安全分析、其他食品检测、食品分析方法汇编，主要介绍各组分分析原理、分析要点以及国家标准分析方法和国内外先进分析方法或标准，满足食品分析专业型人才知识体系的构建需求。', '教材', 'TS207.3', '本书适用于高等学校食品科学与工程、食品质量与安全、食品营养与健康等专业师生；食品卫生检验、质量监督、各类食品企业等单位的有关科技人员', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1827, '978-7-5184-4111-2', '法国蓝带面包宝典', '法国蓝带厨艺学院著', '中国轻工业出版社', 198, '面包', NULL, NULL, NULL, NULL, NULL, '319页', '23cm', NULL, '蓝带', '本书会让读者爱上各种类型的面包，以及陶醉于制作的每一个步骤。烘烤一个面包，是发出一份与感官重新建立起联结的邀约，是发酵的魔法，是闻到炉中面团散发的美妙香气，是触碰一份独一无二的质地，是倾听面包掰开时发出的噼啪声响，亦是品尝刚出炉面包所赋予的美妙滋味。', NULL, 'TS213.2', '本书适用于厨艺爱好者', '精装', '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1828, '978-7-5184-3914-0', '专业面包师的精选配方', '张锡源著', '中国轻工业出版社', 88, '面包', '面包的创新风味美学', NULL, NULL, NULL, NULL, '239页', '26cm', NULL, NULL, '本书围绕备受好评的三款经典面包——吐司、贝果和可颂，以专业角度的1100张关键步骤图，烤制50款经典又具有创新风味的面包。作者教大家掌握酵种基础知识、面包不同烘焙方法，体验风味馅料与面包搭配的微妙滋味。', NULL, 'TS213.21', '本书适用于烘焙爱好者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1829, '978-7-301-33621-2', '了解食用油', '周晴中，吴磊编著', '北京大学出版社', 49, '食用油', NULL, NULL, NULL, NULL, NULL, '223页', '24cm', NULL, NULL, '本书梳理了食用油的相关知识，全书分为10章，内容包括：食用油的概念及标准体系、食用油的成分、多种多样的脂肪酸、多种多样的食用油、食用油的制备、食用油的储存和包装等。', '普及读物', 'TS225-49', '本书适用于大众读者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1830, '978-7-111-71293-0', '艺娜的秘密咖啡厅', '(韩)艺娜著', '机械工业出版社', 88, '咖啡', NULL, NULL, NULL, NULL, NULL, '229页', '23cm', NULL, '开家咖啡馆', '本书作者介绍了值得推荐给大家的特色食材、基础工具、绝不会失败的味道的比例、表现颜色的方法、冰块制作等小贴士。', NULL, 'TS273', '本书适用于想要开发与众不同的标志性饮品的咖啡厅运营者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1831, '978-7-5184-3942-3', '完全咖啡知识手册', '日本枻出版社编辑部编', '中国轻工业出版社', 68, '咖啡', '升级版', NULL, NULL, NULL, NULL, '127页', '23cm', NULL, '元气满满下午茶系列', '本书分享了手冲咖啡的基础知识，介绍了选豆、烘焙、研磨、冲泡技巧、混合咖啡、冲泡水温、保存方法等内容，以及咖啡大师们给读者带来的咖啡冲泡技巧，并解说了拉花手法及技巧。', NULL, 'TS273', '本书适用于咖啡从业者和爱好者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1832, '978-7-5184-4042-9', '新型烟用香料开发及加香技术', '许春平等著', '中国轻工业出版社', 98, '卷烟', NULL, NULL, NULL, NULL, NULL, '325页', '24cm', NULL, NULL, '本书第一部分介绍了采用微生物发酵、酶催化反应、美拉德反应、焦糖化反应、膜分离技术等现代化技术手段开发的一系列香味丰富、加香效果显著的新型烟用香料，并进行了卷烟加香应用研究；第二部分介绍了微胶囊技术在保护和控制香料释放方面的研究进展及应用；第三部分介绍了多种新型烟用爆珠的开发、规模化生产和应用。', NULL, 'TS452', '本书适用于烟草工业领域的科研人员及相关专业院校师生', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1833, '978-7-5596-6276-7', '风·落·之·光', '(挪)拉斯·穆勒编著', '北京联合出版公司', 298, '唱片', 'ECM唱片的视觉语言', NULL, NULL, NULL, NULL, '447页', '26cm', NULL, '乐府', '本书从不同角度探索ECM唱片在1969-2010年间的视觉设计与音乐的关系。书中收录1200余幅图片，读者可从中了解这一伟大唱片厂牌的美学理念及幕后故事。', NULL, 'TS881', '本书适用于乐迷、艺术设计爱好者', '精装', '2022.10', 'chi', NULL);
INSERT INTO `book` VALUES (1834, '978-7-5229-0012-4', '唐代首饰、金银器活化研究', '段丙文著', '中国纺织出版社有限公司', 88, '首饰', NULL, NULL, NULL, NULL, NULL, '163页', '29cm', NULL, '西安美术学院学术著作出版基金 陕西省教育厅重点项目“唐代首饰活化设计与数字化展演应用研究”', '本书期盼通过对唐代首饰和金银器的器形、纹样、工艺以及所承载东西方文化交流现象等的研究，分析其折射出的造物观、价值观、佩戴理念等文化内涵，探析活化设计研究的价值和时代意义，尝试从道与器、理论与实践之间的辩证关系寻找如何解读传统文化，并应用到现代设计之中，以求建构新中式风格的设计文化价值体系。', NULL, 'TS934.3', '本书适用于高等院校服装与服饰专业的师生、首饰设计研究人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1835, '978-7-5229-0009-4', '服装设计师手稿速成图册', '刘笑妍著', '中国纺织出版社有限公司', 88, '服装设计', NULL, NULL, '马克笔手绘时装画', NULL, NULL, '138页', '29cm', NULL, '设计师手稿系列', '本书主要分为三个章节，分别是服装品类篇、服装风格篇、面料质感篇。其中，服装品类篇中分为裙装、套装、外套大衣、皮草、羽绒服；服装风格篇中分为时尚风格、街头风格、运动风格、中式传统风格；面料质感篇中分为纱质面料、缎面面料、绒类面料、棉毛织物、印花面料。', '图集', 'TS941.2-64', '本书适用于服装设计专业院校师生', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1836, '978-7-5229-0103-9', '服装创新设计手绘效果图', '姜兰英著', '中国纺织出版社有限公司', 88, '服装设计', NULL, NULL, NULL, NULL, NULL, '123页', '26cm', NULL, NULL, '本书立足服装探索设计创新。以回顾服装设计经典、激发创新意识、弘扬民族创新精神为目标，从无结构与解构主义、波普与欧普艺术、中国传统文化、自然、建筑、家具、生活及娱乐用品中获取服装设计灵感，运用多种设计表现手法，绘制了99幅各具特色的服装创意设计手绘效果图。', NULL, 'TS941.28', '本书适用于广大师生、服装设计从业人员乃至爱好者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1837, '978-7-5180-9899-6', '实用服装裁剪与缝制轻松入门', '侯东昱著', '中国纺织出版社有限公司', 59.8, '服装量裁', NULL, NULL, '上装篇', NULL, NULL, '186页', '26cm', NULL, '服装实用技术·基础入门', '本书为实用女上装裁剪与缝制的基础入门书籍，内容包括：女上装概述，服装裁剪与缝制基础知识，女上装裁剪制图部位，常用面料与辅料，结构制图，样板制作与缝制实例。书中实例采用近年来流行的服装款式，以女性人体的生理特征、服装的款式设计为基础，阐述了衬衫、连衣裙、卫衣、马甲、夹克、大衣、西服套装的结构设计原理、变化规律和设计技巧。', NULL, 'TS941.63', '本书适用于服装制作爱好者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1838, '978-7-214-27182-2', '天上取样人间织', '东阳欢娱影视文化有限公司编著', '江苏人民出版社', 198, '服饰文化', '《玉楼春》里的服饰之美', NULL, NULL, NULL, NULL, '239页', '24cm', NULL, '凤凰传媒 凤凰空间', '本书以热播影视剧《玉楼春》中的服饰为素材，梳理展示了包括剧中的近70种服装，以及各种不同款式的衣冠、饰品等。书中图片选取的定妆照服饰，包括皇帝常服、皇后礼服、士庶男子服饰、妇女便服、军戎服饰等，配以文字，解释不同服饰背后蕴含的文化。', NULL, 'TS941.742.2', '本书适用于服饰文化研究人员', '精装', '2022.10', 'chi', NULL);
INSERT INTO `book` VALUES (1839, '978-7-5228-0525-2', '唐风拂槛', '(美)陈步云著', '社会科学文献出版社', 158, '服饰文化', '织物与时尚的审美游戏', NULL, NULL, NULL, NULL, '283页', '23cm', NULL, '方寸 美国社会科学研究委员会和富布赖特奖学金研究资助项目', '本书揭示了一个充满活力的唐代时尚体系，作者以考古资料——绘画、雕塑和丝绸制品，以及年鉴、诗歌、税务文件、经济论文和法律文书的记录为依据，审视唐朝的时尚风格。', NULL, 'TS941.742.42', '本书适用于服饰文化研究人员', '精装', '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1840, '978-7-5130-8393-5', '汉服穿搭指南', '许威，唐侯翔，纪策著', '知识产权出版社有限责任公司', 68, '汉族', NULL, NULL, NULL, '西塘汉服文化周系列丛书', NULL, '130页', '24cm', NULL, NULL, '本书介绍了自先秦至明的汉服款式及适用场合，内容涉及历史、文化、礼仪、审美等诸多方面，帮助读者构建汉服认知体系，并学习在当代各礼仪场合中如何合理搭配和穿戴汉服。', '指南', 'TS941.742.811-62', '本书适用于汉服文化爱好者及相关从业者', NULL, '2022.10', 'chi', NULL);
INSERT INTO `book` VALUES (1841, '978-7-121-44365-7', '华夏衣橱', '顾小思，杜田编著', '电子工业出版社', 138, '汉族', '图解中国传统服饰', NULL, NULL, NULL, NULL, '248页', '26cm', NULL, '有艺', '本书分为古人汉服科普和现代汉服穿搭建议两大部分，共包含5章内容，展示了从隋代到明代的帝后、官员、平民的服装、首饰、妆造、服饰纹样等，然后讲解了戏曲服饰，以及以24节气为主题的现代汉服穿搭指南。', '图集', 'TS941.742.811-64', '本书适用于传统服饰爱好者', '精装', '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1842, '978-7-5180-9890-3', '百搭简约风手编包', '(日)奥铃奈著', '中国纺织出版社有限公司', 52, '包袋', NULL, NULL, NULL, NULL, NULL, '63页', '26cm', NULL, NULL, '本书收录了20款用麻线、棉线和棉草线钩织的百搭简约风手编包。书中作品从便于日常使用的基础款，到设计感很强的独特款式都有涉猎，集合了众多实用且可爱的包袋。制作方法上作者选用了以短针为主的钩针针法，每款作品都配有制作指导。', '图集', 'TS941.75', '本书适用于编织爱好者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1843, '978-7-5180-9878-1', '自然系棉草草帽和配饰37款', '日本诚文堂新光社编著', '中国纺织出版社有限公司', 59.8, '帽', NULL, NULL, NULL, NULL, NULL, '95页', '26cm', NULL, NULL, '本书选择了最具人气的23号线来钩织帽子和发饰小物。除了日常必备的带檐草帽、平顶硬草帽、贝雷帽等款式，还介绍了可搭配成人款的亲子帽。此外，书中作品还包括各种发饰小物，能让你尽享夏日自然风的搭配。', '图集', 'TS941.763.8-64', '本书适用于编织爱好者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1844, '978-7-5713-1916-8', '创意灯饰与照明设计', '王友斌著', '江苏凤凰科学技术出版社', 78, '灯具', NULL, NULL, NULL, NULL, NULL, '160页', '24cm', NULL, '室内设计专业教材 凤凰传媒 凤凰空间', '本书共5章，内容包括：灯饰概述、灯饰设计基础、灯饰的构件与照明设计、创意灯饰设计、灯饰设计实践。', NULL, 'TS956', '本书适用于室内设计专业人员', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1845, '978-7-5508-3950-2', '江山味道', '江山市政协文化文史和学习委员会编', '西泠印社出版社', 128, '饮食', NULL, NULL, NULL, NULL, NULL, '370页', '26cm', NULL, NULL, '本书深入挖掘了江山美食故事，梳理了江山美食品类。全书围绕“味道”二字，分为“美食故事”“制作工艺”两大篇章。以美食为主角，有的细述了它们的前世今生，有的还原了它们的制作过程，有的道出了美食背后的人生百态，或作客观描写，或作感性抒情，林林总总写出了江山人的市井味、烟火气，读来色香味俱存。', NULL, 'TS971.202.554', '本书适用于江山饮食文化研究人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1846, '978-7-5680-8688-2', '全鱼料理', '(澳)乔希·尼兰德(Josh Niland)著', '华中科技大学出版社', 298, '鱼', NULL, NULL, NULL, NULL, NULL, '253页', '29cm', NULL, '有书至美 华中出版', '本书作者向读者展示了如何以一种全新的方式来思考鱼类烹饪的各个方面——从采购和屠宰，到干式熟成和腌制，挑战并扩充了我们原本对相关知识领域的认知；还邀请读者去深层理解鱼的丰富价值。全书包含了60多种鱼类的食谱，从烟熏马林鱼火腿到烤鱼骨髓，从完美的炸鱼薯条到令人垂涎的惠灵顿鱼卷，能很快让读者领略到新的鱼料理风格，提升购买、烹饪和食用鱼的技术水平。', NULL, 'TS972.126', '本书适用于美食爱好者', '精装', '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1847, '978-7-111-71506-1', '日本主厨笔记', '日本旭屋出版编辑部编', '机械工业出版社', 78, '面条', NULL, NULL, '拉面专业教程', NULL, NULL, '171页', '26cm', NULL, '主厨秘密课堂', '本书精选了日本生意最火爆的21家拉面店的畅销拉面产品，内容包含拉面制作的全流程，从熬汤开始，包括肉类、海鲜、禽畜类材料的准备，到处理、熬制、过滤等环节，以及面条面粉的选择，配菜的制作，调味的流程等全部技巧和细节。', '教材', 'TS972.132', '本书适用于国内的日式拉面的厨师和餐馆老板，以及普通的烹饪爱好者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1848, '978-7-111-71586-3', '日本主厨笔记', '日本旭屋出版编辑部编', '机械工业出版社', 88, '肉制品', NULL, NULL, '烤肉专业教程', NULL, NULL, '207页', '26cm', NULL, '主厨秘密课堂', '本书收罗日本生意最火爆的10家烤肉店的点菜率高的产品的做法；还介绍了适合各部位的腌肉酱料，以及烤后的调味蘸酱的做法，展示了这些烤肉店营业数十年一直生意兴隆的秘密。', '教材', 'TS972.183.13', '本书适用于烹饪爱好者、国内的日式烤肉厨师和餐馆老板', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1849, '978-7-5180-9740-1', '超实用整理收纳全书', '邓美著', '中国纺织出版社有限公司', 59.8, '家庭生活', NULL, NULL, NULL, NULL, NULL, '272页', '21cm', NULL, NULL, '本书介绍了日常生活和工作中的整理收纳方法，内容涵盖衣橱、厨房、卫生问、书房等居家分区整理收纳方法，办公、出行整理收纳方法，手机、电脑的整理方法，以及如何维持居家环境的整洁、如何养成良好的整理收纳习惯等。此外，还分享了居家整理好用的收纳工具，并对新房的收纳空间布局规划和如何DIY收纳工具做了补充，以满足不同年龄段人群的整理收纳需求。', NULL, 'TS976.3', '本书适用于大众读者', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1850, '978-7-5180-9239-0', '中国男士着装美学集', '《中国男士着装美学集》编写委员会编著', '中国纺织出版社有限公司', 298, '男性', NULL, NULL, NULL, NULL, NULL, '11,356页', '26cm', NULL, NULL, '本书针对男士着装共分四章进行介绍：第一章美学篇，以时间为线，主要介绍从先秦到当代的中国男装美学基本精神和男子服饰的美学追求；第二章发展篇，梳理了中国男装设计理念、市场格局、国内外男装品牌设计流派、当代中国男装设计师代表、近当代中国男士着装特色、当代男装发展新趋势；第三章场景篇，介绍了中国男士着装分类、场合穿搭、定制男装发展，将男性着装置于职业、场合、消费力等多种场景之下，提出了适合的服饰搭配与选择，为男士着装提供指导和建议；第四章色彩篇，包括色彩概述、男装流行色、男装色彩搭配、色彩的妙用和场合着装的色彩选', NULL, 'TS976.4', '本书适用于服饰美学研究人员', '精装', '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1851, '978-7-5201-9294-1', '泛江河源区岛状生态地位论', '文艳林著', '社会科学文献出版社', 99, NULL, NULL, NULL, NULL, NULL, NULL, '12,236页', '24cm', NULL, NULL, '本书梳理了泛江河源区相关文献和重要论述，引证了青藏高原隆升引发季风变化的最新研究成果，在大量文献和证据支持下，对泛江河源区岛状生态环境变迁进行了分析；阐述了一个世纪以来青藏高原岛状生态环境变迁的基本走向；立足学科交叉的综合研究范式，指出了以青藏高原为核心的泛江河源区的岛状生态地位的重要性，并以此为基础，提出了以泛江河源区为核心的系统性生态建设思路；创新性地提出了区域融合发展、加强青藏高原综合研究和制定“青藏高原法”等思路。', NULL, 'X143', '本书适用于生态环境保护研究人员', NULL, '2022.11', 'chi', NULL);
INSERT INTO `book` VALUES (1852, '978-7-5226-1003-0', '江汉平原典型河流（汉北河）生态需水研究', '闫少锋，熊瑶著', '中国水利水电出版社', 30, NULL, NULL, NULL, NULL, NULL, NULL, '78页', '24cm', NULL, NULL, '本书以江汉平原典型河流——汉北河为研究对象，结合国内外相关文献，基于水力学中的流速法对汉北河生态需水量进行了研究，主要内容包括研究流域概况、河流生态需水计算方法、河流生径比基本理论、流速法生态流量组合计算、基于流速法的生态需水计算及分析、生径比计算及分析、结论与展望等。', NULL, 'X143', '本书适用于水利设计单位的科研、技术人员及高等学校相关专业的教师、研究生、本科生', NULL, '2022.10', 'chi', NULL);
INSERT INTO `book` VALUES (1853, '978-7-109-29829-3', '土壤环境指标测定方法与分析指导', '王小林，徐伟洲，卜耀军主编', '中国农业出版社', 50, '土壤环境', NULL, NULL, NULL, NULL, NULL, '253页', '21cm', NULL, '榆林学院应用型人才培养系列教材', '本书结合目前土壤生态环境失衡，从土壤水肥有效性波动和可持续生产力下降方面出发，重点对土壤微生物基础指标体系建立，土壤主要酶活性测定分析进行介绍和改进。', NULL, 'X21', '本书适用于土壤学及相关专业、土壤专业技术人员和推广人员', NULL, '2022.10', 'chi', NULL);
INSERT INTO `book` VALUES (1854, '978-7-111-71867-3', '环境管理体系基础考试宝典', '张崇澧编著', '机械工业出版社', 156, '环境管理系统', NULL, NULL, NULL, NULL, NULL, '541页', '24cm', NULL, '国家注册审核员考试辅导用书 按最新标准编写', '本书对考试大纲的要求进行了剖析，分为环境管理体系(EMS)标准、环境管理领域专业知识、法律法规和其他要求三部分。', '自学参考资料', 'X32', '本书适用于参加国家注册审核员考试的考生', '精装', '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1855, '978-7-5223-1758-8', '山区旅游地社会－生态系统恢复力研究', '贾垚焱著', '中国财政经济出版社', 60, '山区', NULL, NULL, NULL, NULL, NULL, '239页', '24cm', NULL, '国家自然科学基金“鄂西山区旅游地农户生计脆弱性引致返贫的风险评估及预警机制研究”、山西省“1331工程”工商管理一流学科建设项目的资助和支持', '本书以人地关系理论为指导，将社会－生态系统理论与恢复力分析框架应用于生态环境脆弱、经济发展较为落后、少数民族世代聚居的山区旅游地典型区域恩施州，并依循“分析框架建构－演变特征及扰动辨识－恢复力测度与影响机理揭示－适应对策论证”的逻辑主线，对县域、社区、农户不同尺度下案例地恢复力水平进行分析，在对比评估结果的基础上，提出具有针对性的适应性管理对策。', NULL, 'X321.263.2', '本书适用于山区生态环境建设研究人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1856, '978-7-5607-7605-7', '碧水蓝天', '董昭和，吴光伟，王润主编', '山东大学出版社', 68, '环境污染', NULL, NULL, NULL, NULL, NULL, '190页', '26cm', NULL, NULL, '本书介绍有关环境污染及危害和治理环境污染。让大家进一步了解今天环境污染源主要有那些，污染的状况如何，污染的根源在那里，污染的特点是什么，污染的危害及对人类的今天特别是未来会产生什么样的后果和如何加快环境治理，最终实现生态平衡，从而希望和启发人们更加自觉。', '普及读物', 'X506-49', '本书适用于大众读者', NULL, '2022.08', 'chi', NULL);
INSERT INTO `book` VALUES (1857, '978-7-5096-8781-9', '排放权交易机制中的策略性行为研究初探', '王许，朱磊著', '经济管理出版社', 78, '二氧化碳', NULL, NULL, NULL, NULL, NULL, '264页', '24cm', NULL, NULL, '本书阐述了不完全竞争的市场结构对排放权交易机制有效性的影响，重点分析了策略性配额交易者的识别、排放权交易成本节约效应的实现、不完全竞争市场结构下减排技术的扩散、交易成本与排放权交易机制的有效覆盖范围等重要问题，形成了一个有效的不完全竞争的排放权交易机制分析框架。', NULL, 'X511', '本书适用于环境经济学与温室气体减排机制设计研究人员', NULL, '2023.01', 'chi', NULL);
INSERT INTO `book` VALUES (1858, '978-7-5226-1051-1', '江汉平原河湖生态治理技术', '李瑞清等编著', '中国水利水电出版社', 68, NULL, NULL, NULL, NULL, '湖北水安全研究丛书', NULL, '181页', '26cm', NULL, NULL, '本书包含理论篇和实践篇。理论篇内容包括平原水网区城市河流特性与功能、平原水网区水系连通、城市河流廊道-生境共建共生、河道型海绵建设技术、河湖生态岸坡防护技术、河湖生态清淤技术、河道生态景观营建技术、现代城市水利工程建筑设计艺术研究；实践篇内容包括东湖港概况、工程目标与任务、港渠工程、海绵建设工程、景观园林工程、建筑工程、工程效果。', NULL, 'X52', '本书适用于平原水网区城市河流生态治理的工作人员', NULL, '2022.10', 'chi', NULL);
INSERT INTO `book` VALUES (1859, '978-7-5029-7835-8', '江苏大气环境观测与预报技术研究', '康志明，刘端阳，严文莲等著', '气象出版社', 160, '大气监测', NULL, NULL, NULL, '区域环境气象系列丛书', NULL, '246页', '26cm', NULL, '国家出版基金项目', '本书介绍了江苏省气象局近10年来的大气环境科研、业务和服务成果，主要包括大气环境观测站网建设、江苏大气环境的时空分布特征、浓雾生消机制及预报方法和大气环境数值模拟预报技术，重点介绍了江苏影响大气环境的气象条件和相关预报方法及大气环境的影响预报研究，最后，本书介绍了江苏省气象部门的大气环境业务体系建设。', NULL, 'X831', '本书适用于气象、生态、环境等相关行业的业务人员', '精装', '2022.10', 'chi', NULL);
INSERT INTO `book` VALUES (1860, '978-7-5121-4619-8', '机器人环境监测和化学羽流追踪技术', '李伟，田宇著', '北京交通大学出版社', 98, '水下作业机器人', NULL, NULL, NULL, NULL, NULL, '243页', '24cm', NULL, '国家出版基金项目', '本书化学羽流进行介绍，主要内括羽流模型和、飞蛾行为仿生的化学羽流追踪策略设计和优化、自主水下机器人基于多化学传感器和基于多自主水下机器人的化学羽流追踪策略和算法等。', NULL, 'X834', '本书适用于从事化学羽流追踪、机器人环境监测及自主机器人的科研和工程技术人员', '精装', '2022.09', 'chi', NULL);
INSERT INTO `book` VALUES (1861, '978-7-5615-8838-3', '环境在线监测系统运维', '黄华斌，李大治主编', '厦门大学出版社', 39, '环境监测系统', NULL, NULL, NULL, NULL, NULL, '203页', '26cm', NULL, NULL, '本书共11章，第一章主要概述了环境在线监测技术的发展与现状；第二至十一章涵盖了在线监测系统在空气质量、地表水水质、烟气排放、水污染源等领域的应用，每个模块均包含在线监测系统的整体介绍、方法原理、运营维护三大模块的内容。', NULL, 'X84', '本书适用于环境监测人员', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1862, '978-7-5228-1278-6', '国学论衡', '陈声柏主编', '社会科学文献出版社·人文分社', 138, '国学', NULL, '第十二辑 2022年第2辑', NULL, NULL, NULL, '323页', '24cm', NULL, '甘肃中国传统文化研究会 兰州大学哲学社会学院主办 兰州大学哲学社会学院“明达同泽”学科建设基金资助', '本书收录了《传统辩证思维方式的意涵及其现代转换的意义》《孔子“正名”考辨》《纵横家之“飞箝”论式研究》《韩非游说思想研究》《何为推类：评述与新解》《“名辩思潮”视野下的先秦论证思想论析》等文章。', '文集', 'Z126.27-53', '本书适用于国学爱好者', NULL, '2022.12', 'chi', NULL);
INSERT INTO `book` VALUES (1863, '978-7-5205-3663-9', '云行雨施', '赵伟著', '中国文史出版社', 88, '国学', '生活国学与人生智慧', NULL, NULL, NULL, NULL, '501页', '24cm', NULL, NULL, '本书从七个方面来展示生活国学的内容，分别为天道篇、学习篇、事业篇、社会篇、修养篇、情感篇和智慧篇。', '通俗读物', 'Z126-49', '本书适用于国学爱好者', NULL, '2023.03', 'chi', NULL);
INSERT INTO `book` VALUES (1864, '978-7-5538-1698-2', '张一南北大国文课', '张一南著', '岳麓书社', 58, '国学', NULL, NULL, '六朝文学篇', NULL, NULL, '401页', '21cm', NULL, '浦睿文化', '本书讲述了从汉末建安三曹父子到萧梁三萧父子的文学变革，同时论及赋与小说等文体，解读了魏晋六朝“文学自觉”的方方面面。', '通俗读物', 'Z126-49', '本书适用于国学爱好者', NULL, '2022.09', 'chi', NULL);
INSERT INTO `book` VALUES (1865, '978-7-5488-5504-0', '经典常谈', '朱自清著', '济南出版社', 29.8, '社会科学', NULL, NULL, NULL, '名著导读与鉴赏丛书', NULL, '104页', '24cm', NULL, NULL, '本书分十三个章节，由朱自清先生介绍了我国文化传统中的经典作品，包括《说文解字》《周易》《尚书》《诗经》、三《礼》《春秋》三传、四书、《战国策》《史记》《汉书》、诸子、辞赋、诗、文等。', NULL, 'Z835', '本书适用于大众读者', NULL, '2023.01', 'chi', NULL);
